package snakesandladders

import (
  "strconv"
)

// TYPES

type Player struct {
  Address string
  Position int
}

type Game struct {
  Players []Player
  Turn int
  Started bool
  Winner string
}

// STATE

var game Game

var snakes = Map[int]int{
  16: 6, 49: 11, 62: 19, 64: 60,
  87: 24, 93: 73, 95: 75, 98: 78,
}

var ladders = Map[int]int{
  1: 38, 4: 14, 9: 31, 21: 42,
  28: 84, 36: 44, 51: 67, 71: 91, 80: 100,
}

// FUNCTIONS

// Join game before it starts
func JoinGame() string {
  if game.Started {
    return "Game already started"
  }
  for i := 0; i < len(game.Players); i++ {
    if game.Players[i].Address == string(Caller()) {
      return "Already joined"
    }
  }
  player := Player{
    Address: string(Caller()),
    Position: 0,
  }
  game.Players = append(game.Players, player)
  return "Player joined: " + player.Address
}

// Start the game
func StartGame() string {
  if game.Started {
    return "Game already started"
  }
  if len(game.Players) < 2 {
    return "Need at least 2 players"
  }
  game.Started = true
  game.Turn = 0
  return "Game started with " + strconv.Itoa(len(game.Players)) + " players"
}

// Roll the dice (value passed by caller)
func RollDice(roll int) string {
  if !game.Started {
    return "Game not started"
  }
  if game.Winner != "" {
    return "Game over. Winner: " + game.Winner
  }

  current := game.Players[game.Turn]
  if current.Address != string(Caller()) {
    return "Not your turn"
  }

  newPos := current.Position + roll
  if newPos > 100 {
    newPos = current.Position // do not move
  }

  if snakes[newPos] != 0 {
    newPos = snakes[newPos]
  } else if ladders[newPos] != 0 {
    newPos = ladders[newPos]
  }

  game.Players[game.Turn].Position = newPos

  result := current.Address + " moved to " + strconv.Itoa(newPos)

  if newPos == 100 {
    game.Winner = current.Address
    result += " and WON!"
  }

  game.Turn = (game.Turn + 1) % len(game.Players)
  return result
}

// View current state
func GetGame() Game {
  return game
}

