import (
	"errors"
	"fmt"
	"math/rand"
	"time"
)

type Player struct {
	Address  string
	Position int
}

type Game struct {
	Players []Player
	Turn    int
	Started bool
	Winner  string
}

var game Game

var snakes = map[int]int{
	16: 6, 49: 11, 62: 19, 64: 60,
	87: 24, 93: 73, 95: 75, 98: 78,
}

var ladders = map[int]int{
	1: 38, 4: 14, 9: 31, 21: 42,
	28: 84, 36: 44, 51: 67, 71: 91, 80: 100,
}

func JoinGame(address string) error {
	if game.Started {
		return errors.New("game already started")
	}
	for _, p := range game.Players {
		if p.Address == address {
			return errors.New("already joined")
		}
	}
	game.Players = append(game.Players, Player{Address: address})
	return nil
}

func StartGame() error {
	if game.Started {
		return errors.New("game already started")
	}
	if len(game.Players) < 2 {
		return errors.New("need at least 2 players")
	}
	game.Started = true
	game.Turn = 0
	return nil
}

func RollDice(address string, roll int) (string, error) {
	if !game.Started {
		return "", errors.New("game not started")
	}
	if game.Winner != "" {
		return "", errors.New("game over, winner: " + game.Winner)
	}
	if game.Players[game.Turn].Address != address {
		return "", errors.New("not your turn")
	}

	player := &game.Players[game.Turn]
	newPos := player.Position + roll
	if newPos > 100 {
		newPos = player.Position // can't overshoot
	}
	if val, ok := snakes[newPos]; ok {
		newPos = val
	} else if val, ok := ladders[newPos]; ok {
		newPos = val
	}

	player.Position = newPos
	msg := fmt.Sprintf("%s rolled %d, moved to %d", player.Address, roll, newPos)

	if newPos == 100 {
		game.Winner = address
		msg += " and WON!"
	}

	game.Turn = (game.Turn + 1) % len(game.Players)
	return msg, nil
}

func main() {
	rand.Seed(time.Now().UnixNano())

	// Sample playthrough
	JoinGame("alice")
	JoinGame("bob")
	StartGame()

	players := []string{"alice", "bob"}

	for game.Winner == "" {
		current := players[game.Turn]
		roll := rand.Intn(6) + 1
		msg, err := RollDice(current, roll)
		if err != nil {
			fmt.Println("Error:", err)
			break
		}
		fmt.Println(msg)
		time.Sleep(300 * time.Millisecond)
	}

	fmt.Println("Game over. Winner:", game.Winner)
}

