package main

import (
	"strconv"
	"strings"
)

// TYPES

type Player struct {
	Address  string
	Position int
}

type GameState struct {
	Players []Player
	Turn    int
	Started bool
	Winner  string
	Log     []string
}

// STATE

var game GameState

var snakes = map[int]int{
	16: 6, 49: 11, 62: 19, 64: 60,
	87: 24, 93: 73, 95: 75, 98: 78,
}

var ladders = map[int]int{
	1: 38, 4: 14, 9: 31, 21: 42,
	28: 84, 36: 44, 51: 67, 71: 91, 80: 100,
}

// HELPERS

func findPlayer(addr string) (int, bool) {
	for i, p := range game.Players {
		if p.Address == addr {
			return i, true
		}
	}
	return -1, false
}

func applySnakesAndLadders(pos int) int {
	if newPos, ok := snakes[pos]; ok {
		return newPos
	}
	if newPos, ok := ladders[pos]; ok {
		return newPos
	}
	return pos
}

// PUBLIC INTERFACE

func JoinGame() string {
	if game.Started {
		return "Game already started"
	}
	addr := std.GetCaller()
	_, exists := findPlayer(addr)
	if exists {
		return "Already joined"
	}
	game.Players = append(game.Players, Player{Address: addr, Position: 0})
	log := addr + " joined the game"
	game.Log = append(game.Log, log)
	return log
}

func StartGame() string {
	if game.Started {
		return "Game already started"
	}
	if len(game.Players) < 2 {
		return "Need at least 2 players"
	}
	game.Started = true
	game.Turn = 0
	log := "Game started with " + strconv.Itoa(len(game.Players)) + " players"
	game.Log = append(game.Log, log)
	return log
}

func RollDice(roll int) string {
	if !game.Started {
		return "Game not started"
	}
	if game.Winner != "" {
		return "Game over. Winner: " + game.Winner
	}
	caller := std.GetCaller()
	curr := &game.Players[game.Turn]
	if curr.Address != caller {
		return "Not your turn"
	}

	newPos := curr.Position + roll
	if newPos > 100 {
		newPos = curr.Position
	}
	newPos = applySnakesAndLadders(newPos)
	curr.Position = newPos

	log := caller + " rolled " + strconv.Itoa(roll) + ", now at " + strconv.Itoa(newPos)

	if newPos == 100 {
		game.Winner = caller
		log += " and WON!"
	}

	game.Log = append(game.Log, log)
	game.Turn = (game.Turn + 1) % len(game.Players)
	return log
}

func GetState() string {
	s := []string{}
	for _, p := range game.Players {
		s = append(s, p.Address+"@"+strconv.Itoa(p.Position))
	}
	return strings.Join(s, " | ")
}

func GetLog() []string {
	return game.Log
}

