package snakesandladders

import (
	"std"
	"strings"
)

// Define the game board with snakes and ladders
var board = map[int]int{
	3:  22, // Ladder from 3 to 22
	5:  8,  // Ladder from 5 to 8
	11: 26, // Ladder from 11 to 26
	20: 29, // Ladder from 20 to 29
	17: 4,  // Snake from 17 to 4
	19: 7,  // Snake from 19 to 7
	27: 1,  // Snake from 27 to 1
	21: 9,  // Snake from 21 to 9
}

// Player struct to hold player information
type Player struct {
	Address  std.Address
	Position int
}

// Game state variables
var (
	players            = []Player{}
	currentPlayerIndex int
	gameStarted        bool
)

// Initialize the game
func init() {
	gameStarted = false
	currentPlayerIndex = 0
}

// Join the game
func JoinGame() string {
	caller := std.OriginCaller()
	for _, player := range players {
		if player.Address == caller {
			return "You are already in the game."
		}
	}
	players = append(players, Player{Address: caller, Position: 0})
	return "You have joined the game."
}

// Start the game
func StartGame() string {
	if gameStarted {
		return "Game has already started."
	}
	if len(players) < 2 {
		return "Need at least 2 players to start the game."
	}
	gameStarted = true
	return "Game started!"
}

// Roll the dice and move
func RollDice() string {
	if !gameStarted {
		return "Game has not started yet."
	}
	caller := std.OriginCaller()
	if players[currentPlayerIndex].Address != caller {
		return "It's not your turn."
	}
	diceRoll := std.RandIntn(6) + 1
	newPosition := players[currentPlayerIndex].Position + diceRoll
	if newPosition >= 30 {
		players[currentPlayerIndex].Position = 30
		gameStarted = false
		return "Congratulations! You have won the game."
	}
	if destination, ok := board[newPosition]; ok {
		newPosition = destination
	}
	players[currentPlayerIndex].Position = newPosition
	currentPlayerIndex = (currentPlayerIndex + 1) % len(players)
	return "You rolled a " + itoa(diceRoll) + ". Your new position is " + itoa(newPosition) + "."
}

// Render the game state
func Render(_ string) string {
	var sb strings.Builder
	sb.WriteString("# Snakes and Ladders\n\n")
	if !gameStarted {
		sb.WriteString("Game has not started yet.\n")
	} else {
		sb.WriteString("Game is in progress.\n")
	}
	sb.WriteString("\n## Players:\n")
	for _, player := range players {
		sb.WriteString("- Address: " + player.Address.String() + ", Position: " + itoa(player.Position) + "\n")
	}
	return sb.String()
}

// Helper function to convert int to string
func itoa(i int) string {
	return std.Sprintf("%d", i)
}

