// StartGame sets up the game with exactly 2 players.
func StartGame(opponent std.Address) string {
	caller := std.OriginCaller()

	// Ensure the game hasn't started yet
	if game.Started {
		return "Game already started."
	}
	// Ensure caller and opponent are two distinct addresses
	if caller == opponent {
		return "Cannot play against yourself."
	}

	// Reset game state
	game = Game{
		Players: []Player{
			{Address: caller, Position: 0},
			{Address: opponent, Position: 0},
		},
		Turn:    0,
		Started: true,
		Logs:    []string{},
	}

	logEvent("Game started between " + caller.String() + " and " + opponent.String())
	return "Game started."
}

func RollDice(roll int) string {
	caller := std.OriginCaller()
	if !game.Started {
		return "Game not started yet."
	}
	if game.Winner != "" {
		return "Game already finished. Winner: " + game.Winner.String()
	}

	currPlayer := &game.Players[game.Turn]
	if currPlayer.Address != caller {
		return "Not your turn."
	}

	pos := currPlayer.Position + roll
	if pos > 100 {
		pos = currPlayer.Position // overshoot doesn't move
	}
	finalPos := applySnakesAndLadders(pos)
	currPlayer.Position = finalPos

	msg := caller.String() + " rolled " + strconv.Itoa(roll) + " → " + strconv.Itoa(finalPos)
	if finalPos == 100 {
		game.Winner = caller
		msg += " 🎉 WINNER!"
	}
	logEvent(msg)

	game.Turn = (game.Turn + 1) % len(game.Players)
	return msg
}

func GetGameState() string {
	var states []string
	for i, p := range game.Players {
		player := p.Address.String() + " at " + strconv.Itoa(p.Position)
		if i == game.Turn && game.Winner == "" {
			player += " 👈"
		}
		states = append(states, player)
	}
	return strings.Join(states, "\n")
}

func GetLog() string {
	logs := game.Logs
	if len(logs) > 5 {
		logs = logs[len(logs)-5:] // show last 5
	}
	return strings.Join(logs, "\n")
}

