package boards2

import (
	"net/url"
	"std"
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	commondao "gno.land/p/devx000/gnoland/commondao/v1rc1"
	"gno.land/p/jeronimoalbi/pager"
	"gno.land/p/moul/realmpath"
	"gno.land/p/moul/txlink"
)

type BoardID uint64

func (id BoardID) String() string {
	return strconv.Itoa(int(id))
}

func (id BoardID) Key() string {
	return padZero(uint64(id), 10)
}

type Board struct {
	id        BoardID // only set for public boards.
	name      string
	aliases   []string
	creator   std.Address
	threads   avl.Tree // Post.id -> *Post
	postsCtr  uint64   // increments Post.id
	createdAt time.Time
	deleted   avl.Tree // TODO reserved for fast-delete.
	perms     Permissions
	readonly  bool
}

func newBoard(id BoardID, name string, creator std.Address, p Permissions) *Board {
	return &Board{
		id:        id,
		name:      name,
		creator:   creator,
		threads:   avl.Tree{},
		createdAt: time.Now(),
		deleted:   avl.Tree{},
		perms:     p,
	}
}

func (board *Board) GetID() BoardID {
	return board.id
}

// GetName returns the name of the board.
func (board *Board) GetName() string {
	return board.name
}

// GetURL returns the relative URL of the board.
func (board *Board) GetURL() string {
	return strings.TrimPrefix(std.CurrentRealm().PkgPath(), "gno.land") + ":" + url.PathEscape(board.name)
}

// GetURL returns relative board path relative.
//
// Note: returned result is not escaped. Use GetURL to get URL-encoded path.
func (board *Board) GetPath() string {
	return strings.TrimPrefix(std.CurrentRealm().PkgPath(), "gno.land") + ":" + board.name
}

func (board *Board) GetPermissions() Permissions {
	return board.perms
}

// SetReadonly updates board's read-only status.
func (board *Board) SetReadonly(readonly bool) {
	board.readonly = readonly
}

// IsReadonly checks if the board is a read-only board.
func (board *Board) IsReadonly() bool {
	return board.readonly
}

func (board *Board) GetThread(threadID PostID) (_ *Post, found bool) {
	v, found := board.threads.Get(threadID.Key())
	if !found {
		return nil, false
	}
	return v.(*Post), true
}

func (board *Board) AddThread(creator std.Address, title string, body string) *Post {
	pid := board.incGetPostID()
	thread := newPost(board, pid, creator, title, body, pid, 0, 0)
	board.threads.Set(pid.Key(), thread)
	return thread
}

// NOTE: this can be potentially very expensive for threads with many replies.
// TODO: implement optional fast-delete where thread is simply moved.
func (board *Board) DeleteThread(pid PostID) {
	_, removed := board.threads.Remove(pid.Key())
	if !removed {
		panic("thread does not exist with ID " + pid.String())
	}
}

// Render renders a board into Markdown.
func (board *Board) Render(path, menu string) string {
	var sb strings.Builder

	sb.WriteString("# " + board.GetName() + "  \n")
	sb.WriteString("Board created by " + newUserLink(board.creator) + " ")
	sb.WriteString("on " + board.createdAt.Format(dateFormat))
	sb.WriteString(", #" + board.id.String())

	if board.IsReadonly() {
		sb.WriteString("  \n_**Starting new threads and commenting is disabled**_")
	}

	// XXX: Menu is rendered by the caller to deal with links and sub-menus
	// TODO: We should have the render logic separated from boards so avoid sending menu as argument
	if menu != "" {
		sb.WriteString("\n\n")
		sb.WriteString(menu)
		sb.WriteString("\n")
	}

	sb.WriteString("\n\n---\n")

	if board.threads.Size() == 0 {
		sb.WriteString("### This board doesn't have any threads\n")
		if !board.IsReadonly() {
			url := board.GetPostFormURL()
			sb.WriteString("Do you want to [start a new conversation](" + url + ") in this board ?")
		}
		return sb.String()
	}

	p, err := pager.New(path, board.threads.Size(), pager.WithPageSize(pageSizeThreads))
	if err != nil {
		panic(err)
	}

	render := func(_ string, v any) bool {
		thread := v.(*Post)
		if thread.isHidden {
			return false
		}

		sb.WriteString(thread.RenderSummary())
		sb.WriteString("\n")
		return false
	}

	sb.WriteString("Sort by: ")
	r := realmpath.Parse(path)
	if r.Query.Get("order") == "desc" {
		r.Query.Set("order", "asc")
		sb.WriteString(newLink("newest first", r.String()))
		sb.WriteString("\n\n")
		board.threads.ReverseIterateByOffset(p.Offset(), p.PageSize(), render)
	} else {
		r.Query.Set("order", "desc")
		sb.WriteString(newLink("oldest first", r.String()))
		sb.WriteString("\n\n")
		board.threads.IterateByOffset(p.Offset(), p.PageSize(), render)
	}

	if p.HasPages() {
		sb.WriteString("\n---\n")
		sb.WriteString(pager.Picker(p))
	}

	return sb.String()
}

func (board *Board) incGetPostID() PostID {
	board.postsCtr++
	return PostID(board.postsCtr)
}

func (board *Board) GetURLFromThreadID(threadID PostID) string {
	return board.GetURL() + "/" + threadID.String()
}

func (board *Board) GetURLFromReplyID(threadID, replyID PostID) string {
	return board.GetURL() + "/" + threadID.String() + "/" + replyID.String()
}

func (board *Board) GetRenameFormURL() string { // TODO: These type of functions shouldn't be part of Board type
	return txlink.Call(
		"RenameBoard",
		"name", board.name,
		"newName", "",
	)
}

func (board *Board) GetFreezeFormURL() string {
	return txlink.Call(
		"FreezeBoard",
		"boardID", board.id.String(),
	)
}

func (board *Board) GetUnfreezeFormURL() string {
	return txlink.Call(
		"UnfreezeBoard",
		"boardID", board.id.String(),
		"threadID", "",
		"replyID", "",
	)
}

func (board *Board) GetFlaggingThresholdFormURL() string {
	return txlink.Call(
		"SetFlaggingThreshold",
		"boardID", board.id.String(),
		"threshold", "",
	)
}

func (board *Board) GetInviteMemberFormURL() string {
	return txlink.Call(
		"InviteMember",
		"boardID", board.id.String(),
		"user", "",
		"role", "",
	)
}

func (board *Board) GetPostFormURL() string {
	return txlink.Call(
		"CreateThread",
		"boardID", board.id.String(),
		"title", "",
		"body", "",
	)
}

func (board *Board) GetMembersURL() string {
	return board.GetURL() + "/members"
}

func createDefaultBoardPermissions(owner std.Address) *DefaultPermissions {
	perms := NewDefaultPermissions(commondao.New())
	perms.SetSuperRole(RoleOwner)
	perms.AddRole(
		RoleAdmin,
		PermissionBoardRename,
		PermissionBoardFlaggingUpdate,
		PermissionMemberInvite,
		PermissionMemberRemove,
		PermissionThreadCreate,
		PermissionThreadEdit,
		PermissionThreadDelete,
		PermissionThreadRepost,
		PermissionThreadFlag,
		PermissionThreadFreeze,
		PermissionReplyCreate,
		PermissionReplyDelete,
		PermissionReplyFlag,
		PermissionReplyFreeze,
		PermissionRoleChange,
	)
	perms.AddRole(
		RoleModerator,
		PermissionThreadCreate,
		PermissionThreadEdit,
		PermissionThreadRepost,
		PermissionThreadFlag,
		PermissionReplyCreate,
		PermissionReplyFlag,
	)
	perms.AddRole(
		RoleGuest,
		PermissionThreadCreate,
		PermissionThreadRepost,
		PermissionReplyCreate,
	)
	perms.AddUser(owner, RoleOwner)
	return perms
}

package boards2

import (
	"std"
	"time"
)

// ReadonlyBoard defines an interface for readonly boards.
type ReadonlyBoard interface {
	// ID returns the unique identifier of the board.
	ID() BoardID

	// Name returns the name of the board.
	Name() string

	// Aliases returns a list with previous names assigned to the board.
	Aliases() []string

	// Creator returns board's creator address.
	Creator() std.Address

	// LastPostID returns the ID assigned to the last created thread.
	LastPostID() uint64

	// CreatedAt returns the time when board was created.
	CreatedAt() time.Time

	// IsReadonly returns true when board shouldn't allow the creation of threads and comments.
	IsReadonly() bool

	// MembersCount returns the number of invited board members.
	MembersCount() int

	// IterateMembers allows reading the list of invited board members.
	IterateMembers(offset, count int, fn UsersIterFn)
}

type readonlyBoard struct {
	board *Board
}

func (b readonlyBoard) ID() BoardID {
	return b.board.id
}

func (b readonlyBoard) Name() string {
	return b.board.name
}

func (b readonlyBoard) Aliases() []string {
	aliases := make([]string, len(b.board.aliases))
	for i, s := range b.board.aliases {
		aliases[i] = s
	}
	return aliases
}

func (b readonlyBoard) Creator() std.Address {
	return b.board.creator
}

func (b readonlyBoard) LastPostID() uint64 {
	return b.board.postsCtr
}

func (b readonlyBoard) CreatedAt() time.Time {
	return b.board.createdAt
}

func (b readonlyBoard) IsReadonly() bool {
	return b.board.readonly
}

func (b readonlyBoard) MembersCount() int {
	return b.board.perms.UsersCount()
}

func (b readonlyBoard) IterateMembers(offset, count int, fn UsersIterFn) {
	b.board.perms.IterateUsers(offset, count, fn)
}

package boards2

import (
	"std"

	"gno.land/p/demo/avl"
)

var (
	gPerms        Permissions
	gNotice       string
	gHelp         string
	gLastBoardID  BoardID
	gBoardsByID   avl.Tree // string(id) -> *Board
	gBoardsByName avl.Tree // string(name) -> *Board
	gLocked       struct {
		realm        bool
		realmMembers bool
	}
)

func init() {
	// TODO: Define and change the default realm owner (or owners)
	owner := std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5") // @test1

	// Initialize default realm permissions
	gPerms = createDefaultPermissions(owner)

	// Import relevant initial state from previous version
	initFromPrevVersion()
}

// incGetBoardID returns a new board ID.
func incGetBoardID() BoardID {
	gLastBoardID++
	return gLastBoardID
}

// getBoard returns a board for a specific ID.
func getBoard(id BoardID) (_ *Board, found bool) {
	v, exists := gBoardsByID.Get(id.Key())
	if !exists {
		return nil, false
	}
	return v.(*Board), true
}

// mustGetBoardByName returns a board or panics when it's not found.
func mustGetBoardByName(name string) *Board {
	v, found := gBoardsByName.Get(name)
	if !found {
		panic("board does not exist with name: " + name)
	}
	return v.(*Board)
}

// mustGetBoard returns a board or panics when it's not found.
func mustGetBoard(id BoardID) *Board {
	board, found := getBoard(id)
	if !found {
		panic("board does not exist with ID: " + id.String())
	}
	return board
}

// mustGetThread returns a thread or panics when it's not found.
func mustGetThread(board *Board, threadID PostID) *Post {
	thread, found := board.GetThread(threadID)
	if !found {
		panic("thread does not exist with ID: " + threadID.String())
	}
	return thread
}

// mustGetReply returns a reply or panics when it's not found.
func mustGetReply(thread *Post, replyID PostID) *Post {
	reply, found := thread.GetReply(replyID)
	if !found {
		panic("reply does not exist with ID: " + replyID.String())
	}
	return reply
}

func mustGetPermissions(bid BoardID) Permissions {
	if bid != 0 {
		board := mustGetBoard(bid)
		return board.perms
	}
	return gPerms
}

package boards2

import (
	"std"
	"strconv"

	"gno.land/p/demo/avl"
)

// DefaultFlaggingThreshold defines the default number of flags that hides flaggable items.
const DefaultFlaggingThreshold = 1

var gFlaggingThresholds avl.Tree // string(board ID) -> int

type Flag struct {
	User   std.Address
	Reason string
}

type Flaggable interface {
	// AddFlag adds a new flag to an item.
	//
	// Returns false if item was already flagged by user.
	AddFlag(flag Flag) bool

	// FlagsCount returns number of times item was flagged.
	FlagsCount() int
}

// flagItem adds a flag to a flaggable item (post, thread, etc).
//
// Returns whether flag count threshold is reached and item can be hidden.
//
// Panics if flag count threshold was already reached.
func flagItem(item Flaggable, flag Flag, threshold int) bool {
	if item.FlagsCount() >= threshold {
		panic("item flag count threshold exceeded: " + strconv.Itoa(threshold))
	}

	if !item.AddFlag(flag) {
		panic("item has been already flagged by a current user")
	}

	return item.FlagsCount() == threshold
}

func getFlaggingThreshold(bid BoardID) int {
	if v, ok := gFlaggingThresholds.Get(bid.String()); ok {
		return v.(int)
	}
	return DefaultFlaggingThreshold
}

package boards2

import (
	"std"
	"strconv"
	"strings"

	"gno.land/r/sys/users"
)

func padLeft(s string, length int) string {
	if len(s) >= length {
		return s
	}
	return strings.Repeat(" ", length-len(s)) + s
}

func padZero(u64 uint64, length int) string {
	s := strconv.Itoa(int(u64))
	if len(s) >= length {
		return s
	}
	return strings.Repeat("0", length-len(s)) + s
}

func indentBody(indent string, body string) string {
	var (
		res   string
		lines = strings.Split(body, "\n")
	)
	for i, line := range lines {
		if i > 0 {
			res += "\n"
		}
		res += indent + line
	}
	return res
}

// NOTE: length must be greater than 3.
func summaryOf(text string, length int) string {
	lines := strings.SplitN(text, "\n", 2)
	line := lines[0]
	if len(line) > length {
		line = line[:(length-3)] + "..."
	} else if len(lines) > 1 {
		// len(line) <= 80
		line = line + "..."
	}
	return line
}

// newLink returns a Markdown link.
func newLink(label, uri string) string {
	return "[" + label + "](" + uri + ")"
}

// newButtonLink returns a Makdown link with label wrapped in brackets.
func newButtonLink(label, uri string) string {
	return `[\[` + label + `\]](` + uri + ")"
}

// newUserLink returns a Markdown link for an account to the users
// realm or the address as string when a user is not registered.
func newUserLink(addr std.Address) string {
	user := users.ResolveAddress(addr)
	if user != nil {
		return user.RenderLink("")
	}
	return addr.String()
}

package boards2

import (
	v1rc1 "gno.land/r/devx000/gnoland/boards2/v1rc1"
)

func initFromPrevVersion() {
	importRealmMembers()
}

func importRealmMembers() {
	v1rc1.IterateRealmMembers(0, func(member v1rc1.User) bool {
		// XXX: This wouldn't be required if a boards2 package is created
		roles := make([]Role, len(member.Roles))
		for i, r := range member.Roles {
			roles[i] = Role(r)
		}

		gPerms.AddUser(member.Address, roles...)
		return false
	})
}

package boards2

import "std"

const (
	PermissionRealmHelp           Permission = "realm:help"
	PermissionRealmLock                      = "realm:lock"
	PermissionRealmNotice                    = "realm:notice"
	PermissionBoardCreate                    = "board:create"
	PermissionBoardFlaggingUpdate            = "board:flagging-update"
	PermissionBoardFreeze                    = "board:freeze"
	PermissionBoardRename                    = "board:rename"
	PermissionThreadCreate                   = "thread:create"
	PermissionThreadDelete                   = "thread:delete"
	PermissionThreadEdit                     = "thread:edit"
	PermissionThreadFlag                     = "thread:flag"
	PermissionThreadFreeze                   = "thread:freeze"
	PermissionThreadRepost                   = "thread:repost"
	PermissionReplyCreate                    = "reply:create"
	PermissionReplyDelete                    = "reply:delete"
	PermissionReplyFlag                      = "reply:flag"
	PermissionReplyFreeze                    = "reply:freeze"
	PermissionMemberInvite                   = "member:invite"
	PermissionMemberRemove                   = "member:remove"
	PermissionRoleChange                     = "role:change"
	PermissionPermissionsUpdate              = "permissions:update"
)

const (
	RoleGuest     Role = ""
	RoleOwner          = "owner"
	RoleAdmin          = "admin"
	RoleModerator      = "moderator"
)

type (
	// Permission defines the type for permissions.
	Permission string

	// Role defines the type for user roles.
	Role string

	// Args is a list of generic arguments.
	Args []interface{}

	// User contains user info.
	User struct {
		Address std.Address
		Roles   []Role
	}

	// UsersIterFn defines a function type to iterate users.
	UsersIterFn func(User) bool

	// Permissions define an interface to for permissioned execution.
	Permissions interface {
		// HasRole checks if a user has a specific role assigned.
		HasRole(std.Address, Role) bool

		// HasPermission checks if a user has a specific permission.
		HasPermission(std.Address, Permission) bool

		// WithPermission calls a callback when a user has a specific permission.
		// It panics on error.
		WithPermission(std.Address, Permission, Args, func(Args))

		// AddUser adds a new user to the permissioner.
		AddUser(std.Address, ...Role) error

		// SetUserRoles sets the roles of a user.
		SetUserRoles(std.Address, ...Role) error

		// RemoveUser removes a user from the permissioner.
		RemoveUser(std.Address) (removed bool)

		// HasUser checks if a user exists.
		HasUser(std.Address) bool

		// UsersCount returns the total number of users the permissioner contains.
		UsersCount() int

		// IterateUsers iterates permissions' users.
		IterateUsers(start, count int, fn UsersIterFn) bool
	}
)

package boards2

import (
	"errors"
	"std"

	"gno.land/p/demo/avl"
	commondao "gno.land/p/devx000/gnoland/commondao/v1rc1"

	"gno.land/r/sys/users"
)

// DefaultPermissions manages users, roles and permissions.
type DefaultPermissions struct {
	superRole Role
	dao       *commondao.CommonDAO
	users     *avl.Tree // string(std.Address) -> []Role
	roles     *avl.Tree // string(role) -> []Permission
}

// NewDefaultPermissions creates a new permissions type.
// This type is a default implementation to handle users, roles and permissions.
func NewDefaultPermissions(dao *commondao.CommonDAO) *DefaultPermissions {
	if dao == nil {
		panic("default permissions require a DAO")
	}

	dp := &DefaultPermissions{
		dao:   dao,
		roles: avl.NewTree(),
		users: avl.NewTree(),
	}

	// Make sure that DAO users are indexed when DAO has members
	dao.Members().IterateByOffset(0, dao.Members().Size(), func(m std.Address) bool {
		dp.users.Set(m.String(), []Role(nil))
		return false
	})

	return dp
}

// SetSuperRole assigns a super role.
// A super role is one that have all permissions.
// These type of role doesn't need to be mapped to any permission.
func (dp *DefaultPermissions) SetSuperRole(r Role) {
	dp.superRole = r
}

// AddRole add a role with one or more assigned permissions.
func (dp *DefaultPermissions) AddRole(r Role, p Permission, extra ...Permission) {
	dp.roles.Set(string(r), append([]Permission{p}, extra...))
}

// RoleExists checks if a role exists.
func (dp DefaultPermissions) RoleExists(r Role) bool {
	if dp.superRole != "" && r == dp.superRole {
		return true
	}

	return dp.roles.Iterate("", "", func(name string, _ interface{}) bool {
		return Role(name) == r
	})
}

// GetUserRoles returns the list of roles assigned to a user.
func (dp DefaultPermissions) GetUserRoles(user std.Address) []Role {
	v, found := dp.users.Get(user.String())
	if !found {
		return nil
	}
	return v.([]Role)
}

// HasRole checks if a user has a specific role assigned.
func (dp DefaultPermissions) HasRole(user std.Address, r Role) bool {
	for _, role := range dp.GetUserRoles(user) {
		if role == r {
			return true
		}
	}
	return false
}

// HasPermission checks if a user has a specific permission.
func (dp DefaultPermissions) HasPermission(user std.Address, perm Permission) bool {
	for _, r := range dp.GetUserRoles(user) {
		if dp.superRole == r {
			return true
		}

		v, found := dp.roles.Get(string(r))
		if !found {
			continue
		}

		for _, p := range v.([]Permission) {
			if p == perm {
				return true
			}
		}
	}
	return false
}

// AddUser adds a new user to permissions.
func (dp *DefaultPermissions) AddUser(user std.Address, roles ...Role) error {
	if dp.users.Has(user.String()) {
		return errors.New("user already exists")
	}

	dp.dao.Members().Add(user)
	return dp.setUserRoles(user, roles...)
}

// SetUserRoles sets the roles of a user.
func (dp *DefaultPermissions) SetUserRoles(user std.Address, roles ...Role) error {
	if !dp.users.Has(user.String()) {
		return errors.New("user not found")
	}
	return dp.setUserRoles(user, roles...)
}

// RemoveUser removes a user from permissions.
func (dp *DefaultPermissions) RemoveUser(user std.Address) bool {
	_, removed := dp.users.Remove(user.String())
	dp.dao.Members().Remove(user)
	return removed
}

// HasUser checks if a user exists.
func (dp DefaultPermissions) HasUser(user std.Address) bool {
	return dp.users.Has(user.String())
}

// UsersCount returns the total number of users the permissioner contains.
func (dp DefaultPermissions) UsersCount() int {
	return dp.users.Size()
}

// IterateUsers iterates permissions' users.
func (dp DefaultPermissions) IterateUsers(start, count int, fn UsersIterFn) bool {
	return dp.users.IterateByOffset(start, count, func(k string, v interface{}) bool {
		return fn(User{
			Address: std.Address(k),
			Roles:   v.([]Role),
		})
	})
}

// WithPermission calls a callback when a user has a specific permission.
// It panics on error or when a handler panics.
// Callbacks are by default called when there is no handle registered for the permission.
func (dp *DefaultPermissions) WithPermission(user std.Address, perm Permission, args Args, cb func(Args)) {
	if !dp.HasPermission(user, perm) || !dp.dao.Members().Has(user) {
		panic("unauthorized")
	}

	switch perm {
	case PermissionBoardCreate:
		dp.handleBoardCreate(args, cb)
	case PermissionBoardRename:
		dp.handleBoardRename(args, cb)
	case PermissionMemberInvite:
		dp.handleMemberInvite(args, cb)
	case PermissionRoleChange:
		dp.handleRoleChange(args, cb)
	default:
		cb(args)
	}
}

func (dp *DefaultPermissions) setUserRoles(user std.Address, roles ...Role) error {
	for _, r := range roles {
		if !dp.RoleExists(r) {
			return errors.New("invalid role: " + string(r))
		}
	}

	dp.users.Set(user.String(), append([]Role(nil), roles...))
	return nil
}

func (DefaultPermissions) handleBoardCreate(args Args, cb func(Args)) {
	name, ok := args[0].(string)
	if !ok {
		panic("expected board name to be a string")
	}

	assertBoardNameIsNotAddress(name)
	assertBoardNameBelongsToCaller(name)

	cb(args)
}

func (DefaultPermissions) handleBoardRename(args Args, cb func(Args)) {
	newName, ok := args[2].(string)
	if !ok {
		panic("expected new board name to be a string")
	}

	assertBoardNameIsNotAddress(newName)
	assertBoardNameBelongsToCaller(newName)

	cb(args)
}

func (dp DefaultPermissions) handleMemberInvite(args Args, cb func(Args)) {
	// Make sure that only owners invite other owners
	role, ok := args[1].(Role)
	if !ok {
		panic("expected a valid new member role")
	}

	if role == RoleOwner {
		if !dp.HasRole(std.PreviousRealm().Address(), RoleOwner) {
			panic("only owners are allowed to invite other owners")
		}
	}

	cb(args)
}

func (dp DefaultPermissions) handleRoleChange(args Args, cb func(Args)) {
	// Owners and Admins can change roles.
	// Admins should not be able to assign or remove the Owner role from members.
	if dp.HasRole(std.PreviousRealm().Address(), RoleAdmin) {
		role, ok := args[2].(Role)
		if !ok {
			panic("expected a valid member role")
		}

		if role == RoleOwner {
			panic("admins are not allowed to promote members to Owner")
		} else {
			member, ok := args[1].(std.Address)
			if !ok {
				panic("expected a valid member address")
			}

			if dp.HasRole(member, RoleOwner) {
				panic("admins are not allowed to remove the Owner role")
			}
		}
	}

	cb(args)
}

func createDefaultPermissions(owner std.Address) *DefaultPermissions {
	perms := NewDefaultPermissions(commondao.New())
	perms.SetSuperRole(RoleOwner)
	perms.AddRole(RoleAdmin, PermissionBoardCreate)
	perms.AddUser(owner, RoleOwner)
	return perms
}

func assertBoardNameIsNotAddress(s string) {
	if std.Address(s).IsValid() {
		panic("addresses are not allowed as board name")
	}
}

func assertBoardNameBelongsToCaller(name string) {
	// When the board name is the name of a registered user
	// check that caller is the owner of the name.
	user, _ := users.ResolveName(name)
	if user != nil && user.Addr() != std.PreviousRealm().Address() {
		panic("board name is a user name registered to a different user")
	}
}

