package boards2

import (
	"regexp"
	"std"
	"strconv"
	"strings"
)

const (
	// MaxBoardNameLength defines the maximum length allowed for board names.
	MaxBoardNameLength = 50

	// MaxThreadTitleLength defines the maximum length allowed for thread titles.
	MaxThreadTitleLength = 100

	// MaxReplyLength defines the maximum length allowed for replies.
	MaxReplyLength = 300
)

var (
	reBoardName = regexp.MustCompile(`(?i)^[a-z]+[a-z0-9_\-]{2,50}$`)

	// Minimalistic Markdown line prefix checks that if allowed would
	// break the current UI when submitting a reply. It denies replies
	// with headings, blockquotes or horizontal lines.
	reDeniedReplyLinePrefixes = regexp.MustCompile(`(?m)^\s*(#|---|>)+`)
)

// SetHelp sets or updates boards realm help content.
func SetHelp(content string) {
	content = strings.TrimSpace(content)
	caller := std.PreviousRealm().Address()
	args := Args{content}
	gPerms.WithPermission(caller, PermissionRealmHelp, args, func(Args) {
		gHelp = content
	})
}

// SetPermissions sets a permissions implementation for boards2 realm or a board.
func SetPermissions(bid BoardID, p Permissions) {
	assertRealmIsNotLocked()

	if p == nil {
		panic("permissions is required")
	}

	if bid != 0 {
		assertBoardExists(bid)
	}

	caller := std.PreviousRealm().Address()
	args := Args{bid}
	gPerms.WithPermission(caller, PermissionPermissionsUpdate, args, func(Args) {
		assertRealmIsNotLocked()

		// When board ID is zero it means that realm permissions are being updated
		if bid == 0 {
			gPerms = p

			std.Emit(
				"RealmPermissionsUpdated",
				"caller", caller.String(),
			)
			return
		}

		// Otherwise update the permissions of a single board
		board := mustGetBoard(bid)
		board.perms = p

		std.Emit(
			"BoardPermissionsUpdated",
			"caller", caller.String(),
			"boardID", bid.String(),
		)
	})
}

// SetRealmNotice sets a notice to be displayed globally by the realm.
// An empty message removes the realm notice.
func SetRealmNotice(message string) {
	message = strings.TrimSpace(message)
	caller := std.PreviousRealm().Address()
	gPerms.WithPermission(caller, PermissionRealmNotice, nil, func(Args) {
		gNotice = message

		std.Emit(
			"RealmNoticeChanged",
			"caller", caller.String(),
			"message", message,
		)
	})
}

// GetBoardIDFromName searches a board by name and returns it's ID.
func GetBoardIDFromName(name string) (_ BoardID, found bool) {
	v, found := gBoardsByName.Get(name)
	if !found {
		return 0, false
	}
	return v.(*Board).id, true
}

// CreateBoard creates a new board.
func CreateBoard(name string) BoardID {
	assertRealmIsNotLocked()

	name = strings.TrimSpace(name)
	assertIsValidBoardName(name)
	assertBoardNameNotExists(name)

	caller := std.PreviousRealm().Address()
	id := incGetBoardID()
	args := Args{name, id}
	gPerms.WithPermission(caller, PermissionBoardCreate, args, func(Args) {
		assertRealmIsNotLocked()
		assertBoardNameNotExists(name)

		perms := createDefaultBoardPermissions(caller)
		board := newBoard(id, name, caller, perms)
		gBoardsByID.Set(id.Key(), board)
		gBoardsByName.Set(name, board)

		std.Emit(
			"BoardCreated",
			"caller", caller.String(),
			"boardID", id.String(),
			"name", name,
		)
	})
	return id
}

// RenameBoard changes the name of an existing board.
//
// A history of previous board names is kept when boards are renamed.
// Because of that boards are also accesible using previous name(s).
func RenameBoard(name, newName string) {
	assertRealmIsNotLocked()

	newName = strings.TrimSpace(newName)
	assertIsValidBoardName(newName)
	assertBoardNameNotExists(newName)

	board := mustGetBoardByName(name)
	assertBoardIsNotFrozen(board)

	bid := board.GetID()
	caller := std.PreviousRealm().Address()
	args := Args{bid, name, newName}
	board.perms.WithPermission(caller, PermissionBoardRename, args, func(Args) {
		assertRealmIsNotLocked()
		assertBoardNameNotExists(newName)

		board := mustGetBoard(bid)
		board.aliases = append(board.aliases, board.name)
		board.name = newName

		// Index board for the new name keeping previous indexes for older names
		gBoardsByName.Set(newName, board)

		std.Emit(
			"BoardRenamed",
			"caller", caller.String(),
			"boardID", bid.String(),
			"name", name,
			"newName", newName,
		)
	})
}

// CreateThread creates a new thread within a board.
func CreateThread(boardID BoardID, title, body string) PostID {
	assertRealmIsNotLocked()

	title = strings.TrimSpace(title)
	assertTitleIsValid(title)

	body = strings.TrimSpace(body)
	assertBodyIsNotEmpty(body)

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	caller := std.PreviousRealm().Address()
	assertHasBoardPermission(board, caller, PermissionThreadCreate)

	thread := board.AddThread(caller, title, body)

	std.Emit(
		"ThreadCreated",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", thread.GetPostID().String(),
		"title", title,
	)

	return thread.id
}

// CreateReply creates a new comment or reply within a thread.
//
// The value of `replyID` is only required when creating a reply of another reply.
func CreateReply(boardID BoardID, threadID, replyID PostID, body string) PostID {
	assertRealmIsNotLocked()

	body = strings.TrimSpace(body)
	assertReplyBodyIsValid(body)

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	caller := std.PreviousRealm().Address()
	assertHasBoardPermission(board, caller, PermissionReplyCreate)

	thread := mustGetThread(board, threadID)
	assertThreadIsVisible(thread)
	assertThreadIsNotFrozen(thread)

	var reply *Post
	if replyID == 0 {
		// When the parent reply is the thread just add reply to thread
		reply = thread.AddReply(caller, body)
	} else {
		// Try to get parent reply and add a new child reply
		parent := mustGetReply(thread, replyID)
		if parent.IsHidden() || parent.IsReadonly() {
			panic("replying to a hidden or frozen reply is not allowed")
		}

		reply = parent.AddReply(caller, body)
	}

	std.Emit(
		"ReplyCreate",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"replyID", reply.GetPostID().String(),
	)

	return reply.id
}

// CreateRepost reposts a thread into another board.
func CreateRepost(boardID BoardID, threadID PostID, title, body string, destinationBoardID BoardID) PostID {
	assertRealmIsNotLocked()

	title = strings.TrimSpace(title)
	assertTitleIsValid(title)

	caller := std.PreviousRealm().Address()
	dst := mustGetBoard(destinationBoardID)
	assertBoardIsNotFrozen(dst)
	assertHasBoardPermission(dst, caller, PermissionThreadRepost)

	board := mustGetBoard(boardID)
	thread := mustGetThread(board, threadID)
	assertThreadIsVisible(thread)

	if thread.IsRepost() {
		panic("reposting a thread that is a repost is not allowed")
	}

	repostId := dst.incGetPostID()
	body = strings.TrimSpace(body)
	repost := newPost(dst, repostId, caller, title, body, repostId, thread.GetPostID(), thread.GetBoard().GetID())
	thread.AddRepostTo(caller, repost, dst)

	std.Emit(
		"Repost",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"destinationBoardID", destinationBoardID.String(),
		"title", title,
	)

	return repostId
}

// DeleteThread deletes a thread from a board.
//
// Threads can be deleted by the users who created them or otherwise by users with special permissions.
func DeleteThread(boardID BoardID, threadID PostID) {
	// Council members should always be able to delete
	caller := std.PreviousRealm().Address()
	isRealmOwner := gPerms.HasRole(caller, RoleOwner) // TODO: Add DeleteThread filetest cases for realm owners
	if !isRealmOwner {
		assertRealmIsNotLocked()
	}

	board := mustGetBoard(boardID)
	thread := mustGetThread(board, threadID)

	if !isRealmOwner {
		assertBoardIsNotFrozen(board)
		assertThreadIsNotFrozen(thread)

		if caller != thread.GetCreator() {
			assertHasBoardPermission(board, caller, PermissionThreadDelete)
		}
	}

	// Hard delete thread and all its replies
	board.DeleteThread(threadID)

	std.Emit(
		"ThreadDeleted",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
	)
}

// DeleteReply deletes a reply from a thread.
//
// Replies can be deleted by the users who created them or otherwise by users with special permissions.
// Soft deletion is used when the deleted reply contains sub replies, in which case the reply content
// is replaced by a text informing that reply has been deleted to avoid deleting sub-replies.
func DeleteReply(boardID BoardID, threadID, replyID PostID) {
	// Council members should always be able to delete
	caller := std.PreviousRealm().Address()
	isRealmOwner := gPerms.HasRole(caller, RoleOwner) // TODO: Add DeleteReply filetest cases for realm owners
	if !isRealmOwner {
		assertRealmIsNotLocked()
	}

	board := mustGetBoard(boardID)
	thread := mustGetThread(board, threadID)
	reply := mustGetReply(thread, replyID)

	if !isRealmOwner {
		assertBoardIsNotFrozen(board)
		assertThreadIsNotFrozen(thread)
		assertReplyIsVisible(reply)
		assertReplyIsNotFrozen(reply)

		if caller != reply.GetCreator() {
			assertHasBoardPermission(board, caller, PermissionReplyDelete)
		}
	}

	// Soft delete reply by changing its body when it contains
	// sub-replies, otherwise hard delete it.
	if reply.HasReplies() {
		reply.Update(reply.GetTitle(), "This reply has been deleted")
	} else {
		thread.DeleteReply(replyID)
	}

	std.Emit(
		"ReplyDeleted",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"replyID", replyID.String(),
	)
}

// EditThread updates the title and body of thread.
//
// Threads can be updated by the users who created them or otherwise by users with special permissions.
func EditThread(boardID BoardID, threadID PostID, title, body string) {
	assertRealmIsNotLocked()

	title = strings.TrimSpace(title)
	assertTitleIsValid(title)

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	thread := mustGetThread(board, threadID)
	assertThreadIsNotFrozen(thread)

	body = strings.TrimSpace(body)
	if !thread.IsRepost() {
		assertBodyIsNotEmpty(body)
	}

	caller := std.PreviousRealm().Address()
	if caller != thread.GetCreator() {
		assertHasBoardPermission(board, caller, PermissionThreadEdit)
	}

	thread.Update(title, body)

	std.Emit(
		"ThreadEdited",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"title", title,
	)
}

// EditReply updates the body of comment or reply.
//
// Replies can be updated only by the users who created them.
func EditReply(boardID BoardID, threadID, replyID PostID, body string) {
	assertRealmIsNotLocked()

	body = strings.TrimSpace(body)
	assertReplyBodyIsValid(body)

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	thread := mustGetThread(board, threadID)
	assertThreadIsNotFrozen(thread)

	reply := mustGetReply(thread, replyID)
	assertReplyIsVisible(reply)
	assertReplyIsNotFrozen(reply)

	caller := std.PreviousRealm().Address()
	if caller != reply.GetCreator() {
		panic("only the reply creator is allowed to edit it")
	}

	reply.Update("", body)

	std.Emit(
		"ReplyEdited",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"replyID", replyID.String(),
		"body", body,
	)
}

// InviteMember adds a member to the realm or to a boards.
//
// A role can optionally be specified to be assigned to the new member.
// Board ID is only required when inviting a member to a board.
func InviteMember(boardID BoardID, user std.Address, role Role) {
	assertUserAddressIsValid(user)
	assertMembersUpdateIsEnabled(boardID)

	perms := mustGetPermissions(boardID)
	caller := std.PreviousRealm().Address()
	args := Args{user, role}
	perms.WithPermission(caller, PermissionMemberInvite, args, func(Args) {
		assertMembersUpdateIsEnabled(boardID)

		if err := perms.AddUser(user, role); err != nil {
			panic(err)
		}

		std.Emit(
			"MemberInvited",
			"caller", caller.String(),
			"boardID", boardID.String(),
			"member", user.String(),
			"role", string(role),
		)
	})
}

// RemoveMember removes a member from the realm or a boards.
//
// Board ID is only required when removing a member from board.
func RemoveMember(boardID BoardID, member std.Address) {
	assertMembersUpdateIsEnabled(boardID)
	assertMemberAddressIsValid(member)

	perms := mustGetPermissions(boardID)
	caller := std.PreviousRealm().Address()
	perms.WithPermission(caller, PermissionMemberRemove, Args{member}, func(Args) {
		assertMembersUpdateIsEnabled(boardID)

		if !perms.RemoveUser(member) {
			panic("member not found")
		}

		std.Emit(
			"MemberRemoved",
			"caller", caller.String(),
			"boardID", boardID.String(),
			"member", member.String(),
		)
	})
}

// IsMember checks if an user is a member of the realm or a board.
//
// Board ID is only required when checking if a user is a member of a board.
func IsMember(boardID BoardID, user std.Address) bool {
	assertUserAddressIsValid(user)

	if boardID != 0 {
		board := mustGetBoard(boardID)
		assertBoardIsNotFrozen(board)
	}

	perms := mustGetPermissions(boardID)
	return perms.HasUser(user)
}

// HasMemberRole checks if a realm or board member has a specific role assigned.
//
// Board ID is only required when checking a member of a board.
func HasMemberRole(boardID BoardID, member std.Address, role Role) bool {
	assertMemberAddressIsValid(member)

	if boardID != 0 {
		board := mustGetBoard(boardID)
		assertBoardIsNotFrozen(board)
	}

	perms := mustGetPermissions(boardID)
	return perms.HasRole(member, role)
}

// ChangeMemberRole changes the role of a realm or board member.
//
// Board ID is only required when changing the role for a member of a board.
func ChangeMemberRole(boardID BoardID, member std.Address, role Role) {
	assertMemberAddressIsValid(member)
	assertMembersUpdateIsEnabled(boardID)

	perms := mustGetPermissions(boardID)
	caller := std.PreviousRealm().Address()
	args := Args{boardID, member, role}
	perms.WithPermission(caller, PermissionRoleChange, args, func(Args) {
		assertMembersUpdateIsEnabled(boardID)

		if err := perms.SetUserRoles(member, role); err != nil {
			panic(err)
		}

		std.Emit(
			"RoleChanged",
			"caller", caller.String(),
			"boardID", boardID.String(),
			"member", member.String(),
			"newRole", string(role),
		)
	})
}

// IterateRealmMembers iterates realm members.
// The iteration is done only for realm members, board members are not iterated.
func IterateRealmMembers(offset int, fn UsersIterFn) (halted bool) {
	count := gPerms.UsersCount() - offset
	return gPerms.IterateUsers(offset, count, fn)
}

// IterateBoards iterates realm boards.
func IterateBoards(offset int, fn func(ReadonlyBoard) bool) (halted bool) {
	count := gBoardsByID.Size() - offset
	return gBoardsByID.IterateByOffset(offset, count, func(_ string, v any) bool {
		board, _ := v.(*Board)
		return fn(readonlyBoard{board})
	})
}

func assertMemberAddressIsValid(member std.Address) {
	if !member.IsValid() {
		panic("invalid member address")
	}
}

func assertUserAddressIsValid(user std.Address) {
	if !user.IsValid() {
		panic("invalid user address")
	}
}

func assertHasBoardPermission(b *Board, user std.Address, p Permission) {
	if !b.perms.HasPermission(user, p) {
		panic("unauthorized")
	}
}

func assertBoardExists(id BoardID) {
	if _, found := getBoard(id); !found {
		panic("board not found: " + id.String())
	}
}

func assertBoardIsNotFrozen(b *Board) {
	if b.IsReadonly() {
		panic("board is frozen")
	}
}

func assertIsValidBoardName(name string) {
	size := len(name)
	if size == 0 {
		panic("board name is empty")
	}

	if size > MaxBoardNameLength {
		n := strconv.Itoa(MaxBoardNameLength)
		panic("board name is too long, maximum allowed is " + n + " characters")
	}

	if !reBoardName.MatchString(name) {
		panic("board name contains invalid characters")
	}
}

func assertThreadIsNotFrozen(t *Post) {
	if t.IsReadonly() {
		panic("thread is frozen")
	}
}

func assertReplyIsNotFrozen(r *Post) {
	if r.IsReadonly() {
		panic("reply is frozen")
	}
}

func assertNameIsNotEmpty(name string) {
	if name == "" {
		panic("name is empty")
	}
}

func assertTitleIsValid(title string) {
	if title == "" {
		panic("title is empty")
	}

	if len(title) > MaxThreadTitleLength {
		n := strconv.Itoa(MaxThreadTitleLength)
		panic("thread title is too long, maximum allowed is " + n + " characters")
	}
}

func assertBodyIsNotEmpty(body string) {
	if body == "" {
		panic("body is empty")
	}
}

func assertBoardNameNotExists(name string) {
	if gBoardsByName.Has(name) {
		panic("board already exists")
	}
}

func assertThreadExists(b *Board, threadID PostID) {
	if _, found := b.GetThread(threadID); !found {
		panic("thread not found: " + threadID.String())
	}
}

func assertReplyExists(thread *Post, replyID PostID) {
	if _, found := thread.GetReply(replyID); !found {
		panic("reply not found: " + replyID.String())
	}
}

func assertThreadIsVisible(thread *Post) {
	if thread.IsHidden() {
		panic("thread is hidden")
	}
}

func assertReplyIsVisible(thread *Post) {
	if thread.IsHidden() {
		panic("reply is hidden")
	}
}

func assertReplyBodyIsValid(body string) {
	assertBodyIsNotEmpty(body)

	if len(body) > MaxReplyLength {
		n := strconv.Itoa(MaxReplyLength)
		panic("reply is too long, maximum allowed is " + n + " characters")
	}

	if reDeniedReplyLinePrefixes.MatchString(body) {
		panic("using Markdown headings, blockquotes or horizontal lines is not allowed in replies")
	}
}

func assertMembersUpdateIsEnabled(boardID BoardID) {
	if boardID != 0 {
		assertRealmIsNotLocked()
	} else {
		assertRealmMembersAreNotLocked()
	}
}

package boards2

import (
	"std"
	"strconv"
)

// SetFlaggingThreshold sets the number of flags required to hide a thread or comment.
//
// Threshold is only applicable within the board where it's setted.
func SetFlaggingThreshold(boardID BoardID, threshold int) {
	if threshold < 1 {
		panic("invalid flagging threshold")
	}

	assertRealmIsNotLocked()

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	caller := std.PreviousRealm().Address()
	args := Args{boardID, threshold}
	board.perms.WithPermission(caller, PermissionBoardFlaggingUpdate, args, func(Args) {
		assertRealmIsNotLocked()

		board := mustGetBoard(boardID)
		assertBoardIsNotFrozen(board)

		gFlaggingThresholds.Set(boardID.String(), threshold)
		std.Emit(
			"FlaggingThresholdUpdated",
			"caller", caller.String(),
			"boardID", boardID.String(),
			"threshold", strconv.Itoa(threshold),
		)
	})
}

// GetFlaggingThreshold returns the number of flags required to hide a thread or comment within a board.
func GetFlaggingThreshold(boardID BoardID) int {
	assertBoardExists(boardID)
	return getFlaggingThreshold(boardID)
}

// FlagThread adds a new flag to a thread.
//
// Flagging requires special permissions and hides the thread when
// the number of flags reaches a pre-defined flagging threshold.
func FlagThread(boardID BoardID, threadID PostID, reason string) {
	board := mustGetBoard(boardID)

	// Realm owners should be able to flag without permissions even when board is frozen
	caller := std.PreviousRealm().Address()
	isRealmOwner := gPerms.HasRole(caller, RoleOwner)
	if !isRealmOwner {
		assertRealmIsNotLocked()
		assertBoardIsNotFrozen(board)
		assertHasBoardPermission(board, caller, PermissionThreadFlag)
	}

	t, ok := board.GetThread(threadID)
	if !ok {
		panic("post doesn't exist")
	}
	assertThreadIsNotFrozen(t)

	f := Flag{
		User:   caller,
		Reason: reason,
	}

	// Realm owners can hide with a single flag
	hide := flagItem(t, f, getFlaggingThreshold(boardID))
	if hide || isRealmOwner {
		t.SetVisible(false)
	}

	std.Emit(
		"ThreadFlagged",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"reason", reason,
	)
}

// FlagReply adds a new flag to a comment or reply.
//
// Flagging requires special permissions and hides the comment or reply
// when the number of flags reaches a pre-defined flagging threshold.
func FlagReply(boardID BoardID, threadID, replyID PostID, reason string) {
	board := mustGetBoard(boardID)

	// Realm owners should be able to flag without permissions even when board is frozen
	caller := std.PreviousRealm().Address()
	isRealmOwner := gPerms.HasRole(caller, RoleOwner)
	if !isRealmOwner {
		assertRealmIsNotLocked()
		assertBoardIsNotFrozen(board)
		assertHasBoardPermission(board, caller, PermissionReplyFlag)
	}

	thread := mustGetThread(board, threadID)
	assertThreadIsNotFrozen(thread)

	reply := mustGetReply(thread, replyID)
	assertReplyIsNotFrozen(thread)

	f := Flag{
		User:   caller,
		Reason: reason,
	}

	// Realm owners can hide with a single flag
	hide := flagItem(reply, f, getFlaggingThreshold(boardID))
	if hide || isRealmOwner {
		reply.SetVisible(false)
	}

	std.Emit(
		"ReplyFlagged",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"replyID", replyID.String(),
		"reason", reason,
	)
}

package boards2

import (
	"std"
	"strconv"
)

// FreezeBoard freezes a board so no more threads and comments can be created or modified.
func FreezeBoard(boardID BoardID) {
	setBoardReadonly(boardID, true)
}

// UnfreezeBoard removes frozen status from a board.
func UnfreezeBoard(boardID BoardID) {
	setBoardReadonly(boardID, false)
}

// IsBoardFrozen checks if a board has been frozen.
func IsBoardFrozen(boardID BoardID) bool {
	board := mustGetBoard(boardID)
	return board.IsReadonly()
}

// FreezeThread freezes a thread so thread cannot be replied, modified or deleted.
//
// Fails if board is frozen.
func FreezeThread(boardID BoardID, threadID PostID) {
	setThreadReadonly(boardID, threadID, true)
}

// UnfreezeThread removes frozen status from a thread.
//
// Fails if board is frozen.
func UnfreezeThread(boardID BoardID, threadID PostID) {
	setThreadReadonly(boardID, threadID, false)
}

// IsThreadFrozen checks if a thread has been frozen.
//
// Returns true if board is frozen.
func IsThreadFrozen(boardID BoardID, threadID PostID) bool {
	board := mustGetBoard(boardID)
	thread := mustGetThread(board, threadID)
	assertThreadIsVisible(thread)

	return board.IsReadonly() || thread.IsReadonly()
}

// UnfreezeReply removes frozen status from a reply.
//
// Fails when parent thread or board are frozen.
func UnfreezeReply(boardID BoardID, threadID, replyID PostID) {
	// XXX: Is there a use case for also freezing replies?
	setReplyReadonly(boardID, threadID, replyID, false)
}

// FreezeReply freezes a thread reply so it cannot be modified or deleted.
//
// Fails when parent thread or board are frozen.
func FreezeReply(boardID BoardID, threadID, replyID PostID) {
	setReplyReadonly(boardID, threadID, replyID, true)
}

// IsReplyFrozen checks if a thread reply has been frozen.
//
// Returns true when board or a parent thread is frozen.
func IsReplyFrozen(boardID BoardID, threadID, replyID PostID) bool {
	board := mustGetBoard(boardID)
	thread := mustGetThread(board, threadID)
	assertThreadIsVisible(thread)

	reply := mustGetReply(thread, replyID)
	assertReplyIsVisible(reply)

	return board.IsReadonly() || thread.IsReadonly() || reply.IsReadonly()
}

func setReplyReadonly(boardID BoardID, threadID, replyID PostID, isReadonly bool) {
	assertRealmIsNotLocked()

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	caller := std.PreviousRealm().Address()
	assertHasBoardPermission(board, caller, PermissionReplyFreeze)

	thread := mustGetThread(board, threadID)
	assertThreadIsVisible(thread)
	assertThreadIsNotFrozen(thread)

	reply := mustGetReply(thread, replyID)
	assertReplyIsVisible(reply)

	if isReadonly {
		assertReplyIsNotFrozen(reply)
	}

	reply.SetReadonly(isReadonly)

	std.Emit(
		"ReplyFreeze",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"replyID", replyID.String(),
		"frozen", strconv.FormatBool(isReadonly),
	)
}

func setThreadReadonly(boardID BoardID, threadID PostID, isReadonly bool) {
	assertRealmIsNotLocked()

	board := mustGetBoard(boardID)
	assertBoardIsNotFrozen(board)

	caller := std.PreviousRealm().Address()
	assertHasBoardPermission(board, caller, PermissionThreadFreeze)

	thread := mustGetThread(board, threadID)
	if isReadonly {
		assertThreadIsNotFrozen(thread)
	}

	thread.SetReadonly(isReadonly)

	std.Emit(
		"ThreadFreeze",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"threadID", threadID.String(),
		"frozen", strconv.FormatBool(isReadonly),
	)
}

func setBoardReadonly(boardID BoardID, isReadonly bool) {
	assertRealmIsNotLocked()

	board := mustGetBoard(boardID)
	if isReadonly {
		assertBoardIsNotFrozen(board)
	}

	caller := std.PreviousRealm().Address()
	assertHasBoardPermission(board, caller, PermissionBoardFreeze)

	board.SetReadonly(isReadonly)

	std.Emit(
		"BoardFreeze",
		"caller", caller.String(),
		"boardID", boardID.String(),
		"frozen", strconv.FormatBool(isReadonly),
	)
}

package boards2

import (
	"std"
	"strconv"
)

// LockRealm locks the realm making it readonly.
//
// WARNING: Realm can't be unlocked once locked.
//
// Realm can also be locked without locking realm members.
// Realm members can be locked when locking the realm or afterwards.
// This is relevant for two reasons, one so members can be modified after lock.
// The other is for realm owners, which can delete threads and comments after lock.
func LockRealm(lockRealmMembers bool) {
	assertRealmMembersAreNotLocked()

	// If realm members are not being locked assert that realm is no locked.
	// Members can be locked after locking the realm, in a second `LockRealm` call.
	if !lockRealmMembers {
		assertRealmIsNotLocked()
	}

	caller := std.PreviousRealm().Address()
	gPerms.WithPermission(caller, PermissionRealmLock, nil, func(Args) {
		gLocked.realm = true
		gLocked.realmMembers = lockRealmMembers

		std.Emit(
			"RealmLocked",
			"caller", caller.String(),
			"lockRealmMembers", strconv.FormatBool(lockRealmMembers),
		)
	})
}

// IsRealmLocked checks if boards realm has been locked.
func IsRealmLocked() bool {
	return gLocked.realm
}

// AreRealmMembersLocked checks if realm members has been locked.
func AreRealmMembersLocked() bool {
	return gLocked.realmMembers
}

func assertRealmIsNotLocked() { // TODO: Add filtests for locked realm case to all public functions
	if gLocked.realm {
		panic("realm is locked")
	}
}

func assertRealmMembersAreNotLocked() { // TODO: Add filtests for locked members case to all public member functions
	if gLocked.realmMembers {
		panic("realm and members are locked")
	}
}

package boards2

import (
	"net/url"
	"std"
	"strconv"
	"strings"

	"gno.land/p/demo/mux"
	"gno.land/p/jeronimoalbi/pager"
	"gno.land/p/moul/realmpath"
	"gno.land/p/moul/txlink"
)

const (
	pageSizeBoards  = 6
	pageSizeThreads = 6
	pageSizeReplies = 10
)

const menuManageBoard = "manageBoard"

func Render(path string) string {
	router := mux.NewRouter()
	router.HandleFunc("", renderBoardsList)
	router.HandleFunc("help", renderHelp)
	router.HandleFunc("admin-users", renderMembers)
	router.HandleFunc("{board}", renderBoard)
	router.HandleFunc("{board}/members", renderMembers)
	router.HandleFunc("{board}/{thread}", renderThread)
	router.HandleFunc("{board}/{thread}/{reply}", renderReply)

	router.NotFoundHandler = func(res *mux.ResponseWriter, _ *mux.Request) {
		res.Write("Path not found")
	}

	return router.Render(path)
}

func renderHelp(res *mux.ResponseWriter, _ *mux.Request) {
	res.Write("# Boards Help\n")
	if gHelp != "" {
		res.Write(gHelp)
	} else {
		res.Write("### Help content has not been uploaded\n")
		res.Write("Do you want to ")
		res.Write(newLink("upload boards help", txlink.Call("SetHelp", "content", "")))
		res.Write(" ?")
	}
}

func renderBoardsList(res *mux.ResponseWriter, req *mux.Request) {
	renderNotice(res)

	res.Write("# Boards\n\n")
	renderBoardListMenu(res, req)
	res.Write("\n\n---\n")

	if gBoardsByID.Size() == 0 {
		link := txlink.Call("CreateBoard", "name", "")
		res.Write("### Currently there are no boards\n")
		res.Write("Be the first to ")
		res.Write(newLink("create a new board", link))
		res.Write(" !")

		return
	}

	p, err := pager.New(req.RawPath, gBoardsByID.Size(), pager.WithPageSize(pageSizeBoards))
	if err != nil {
		panic(err)
	}

	render := func(_ string, v any) bool {
		board := v.(*Board)

		res.Write("**" + newLink(board.GetName(), board.GetURL()) + "**  \n")
		res.Write("Created by " + newUserLink(board.creator) + " ")
		res.Write("on " + board.createdAt.Format(dateFormat))
		res.Write(", #" + board.id.String() + "  \n")
		res.Write("**" + strconv.Itoa(board.threads.Size()) + " threads")
		if board.IsReadonly() {
			res.Write(", read-only")
		}

		res.Write("**\n\n")
		return false
	}

	res.Write("Sort by: ")
	r := realmpath.Parse(req.RawPath)
	if r.Query.Get("order") == "desc" {
		r.Query.Set("order", "asc")
		res.Write(newLink("newest first", r.String()))
		res.Write("\n\n")
		gBoardsByID.ReverseIterateByOffset(p.Offset(), p.PageSize(), render)
	} else {
		r.Query.Set("order", "desc")
		res.Write(newLink("oldest first", r.String()))
		res.Write("\n\n")
		gBoardsByID.IterateByOffset(p.Offset(), p.PageSize(), render)
	}

	if p.HasPages() {
		res.Write("\n---\n")
		res.Write(pager.Picker(p))
	}
}

func renderBoardListMenu(res *mux.ResponseWriter, req *mux.Request) {
	path := strings.TrimPrefix(std.CurrentRealm().PkgPath(), "gno.land")

	res.Write(newLink("Create Board", txlink.Call("CreateBoard", "name", "")))
	res.Write("  ")
	res.Write(newLink("List Admin Users", path+":admin-users"))
	res.Write("  ")
	res.Write(newLink("Help", path+":help"))
	res.Write("\n\n")
}

func renderBoard(res *mux.ResponseWriter, req *mux.Request) {
	renderNotice(res)

	name := req.GetVar("board")
	v, found := gBoardsByName.Get(name)
	if !found {
		link := newLink("create a new board", txlink.Call("CreateBoard", "name", name))
		res.Write("### The board you are looking for does not exist\n")
		res.Write("Do you want to ")
		res.Write(link)
		res.Write(" ?")
		return
	}

	board := v.(*Board)
	menu := renderBoardMenu(board, req)

	res.Write(board.Render(req.RawPath, menu))
}

func renderBoardMenu(board *Board, req *mux.Request) string {
	var (
		b               strings.Builder
		boardMembersURL = board.GetPath() + "/members"
	)

	if board.IsReadonly() {
		b.WriteString(newLink("List Members", boardMembersURL))
		b.WriteString("  ")
		b.WriteString(newLink("Unfreeze Board", board.GetUnfreezeFormURL()))
		b.WriteString("\n")
	} else {
		b.WriteString(newLink("Create Thread", board.GetPostFormURL()))
		b.WriteString("  ")

		menu := getCurrentMenu(req.RawPath)
		if menu == menuManageBoard {
			b.WriteString("**Manage Board**")
		} else {
			b.WriteString(newLink("Manage Board", menuURL(menuManageBoard)))
		}

		b.WriteString("  \n")

		if menu == menuManageBoard {
			b.WriteString("")
			b.WriteString(newLink("Invite Member", board.GetInviteMemberFormURL()))
			b.WriteString("  ")
			b.WriteString(newLink("List Members", boardMembersURL))
			b.WriteString("  ")
			b.WriteString(newLink("Freeze Board", board.GetFreezeFormURL()))
			b.WriteString("\n\n")
		}
	}

	return b.String()
}

func renderThread(res *mux.ResponseWriter, req *mux.Request) {
	renderNotice(res)

	name := req.GetVar("board")
	v, found := gBoardsByName.Get(name)
	if !found {
		res.Write("Board does not exist: " + name)
		return
	}

	rawID := req.GetVar("thread")
	tID, err := strconv.Atoi(rawID)
	if err != nil {
		res.Write("Invalid thread ID: " + rawID)
		return
	}

	board := v.(*Board)
	thread, found := board.GetThread(PostID(tID))
	if !found {
		res.Write("Thread does not exist with ID: " + rawID)
	} else if thread.IsHidden() {
		res.Write("Thread with ID: " + rawID + " has been flagged as inappropriate")
	} else {
		res.Write(thread.Render(req.RawPath, "", 5))
	}
}

func renderReply(res *mux.ResponseWriter, req *mux.Request) {
	renderNotice(res)

	name := req.GetVar("board")
	v, found := gBoardsByName.Get(name)
	if !found {
		res.Write("Board does not exist: " + name)
		return
	}

	rawID := req.GetVar("thread")
	tID, err := strconv.Atoi(rawID)
	if err != nil {
		res.Write("Invalid thread ID: " + rawID)
		return
	}

	rawID = req.GetVar("reply")
	rID, err := strconv.Atoi(rawID)
	if err != nil {
		res.Write("Invalid reply ID: " + rawID)
		return
	}

	board := v.(*Board)
	thread, found := board.GetThread(PostID(tID))
	if !found {
		res.Write("Thread does not exist with ID: " + req.GetVar("thread"))
		return
	}

	reply, found := thread.GetReply(PostID(rID))
	if !found {
		res.Write("Reply does not exist with ID: " + rawID)
		return
	}

	// Call render even for hidden replies to display children.
	// Original comment content will be hidden under the hood.
	// See: #3480
	res.Write(reply.RenderInner())
}

func renderMembers(res *mux.ResponseWriter, req *mux.Request) {
	boardID := BoardID(0)
	perms := gPerms
	name := req.GetVar("board")
	if name != "" {
		v, found := gBoardsByName.Get(name)
		if !found {
			res.Write("### Board not found")
			return
		}

		board := v.(*Board)
		boardID = board.GetID()
		perms = board.perms

		res.Write("# " + board.GetName() + " Members\n")
		res.Write("### These are the board members\n\n")
	} else {
		res.Write("# Admin Users\n")
		res.Write("### These are the admin users of the realm\n\n")
	}

	// Create a pager with a small page size to reduce
	// the number of username lookups per page.
	p, err := pager.New(req.RawPath, perms.UsersCount(), pager.WithPageSize(5))
	if err != nil {
		res.Write(err.Error())
		return
	}

	res.Write("| Member | Role | Actions |\n| --- | --- | --- |\n")
	perms.IterateUsers(p.Offset(), p.PageSize(), func(u User) bool {
		res.Write("| ")
		res.Write(newUserLink(u.Address))
		res.Write(" | ")
		res.Write(rolesToString(u.Roles))
		res.Write(" | ")
		res.Write(newLink("remove", txlink.Call(
			"RemoveMember",
			"boardID", boardID.String(),
			"member", u.Address.String(),
		)))
		res.Write("  ")
		res.Write(newLink("change role", txlink.Call(
			"ChangeMemberRole",
			"boardID", boardID.String(),
			"member", u.Address.String(),
			"role", "",
		)))
		res.Write(" |\n")
		return false
	})

	if p.HasPages() {
		res.Write("\n\n" + pager.Picker(p))
	}
}

func renderNotice(res *mux.ResponseWriter) {
	if gNotice == "" {
		return
	}

	res.Write("> ")
	res.Write(gNotice)
	res.Write("\n\n")
}

func rolesToString(roles []Role) string {
	if len(roles) == 0 {
		return ""
	}

	names := make([]string, len(roles))
	for i, r := range roles {
		names[i] = string(r)
	}
	return strings.Join(names, ", ")
}

func menuURL(name string) string {
	// TODO: Menu URL works because no other GET arguments are being used
	return "?menu=" + name
}

func getCurrentMenu(rawURL string) string {
	_, rawQuery, found := strings.Cut(rawURL, "?")
	if !found {
		return ""
	}

	query, _ := url.ParseQuery(rawQuery)
	return query.Get("menu")
}

