// ReadonlyBoard defines an interface for readonly boards.
type ReadonlyBoard interface {
	// ID returns the unique identifier of the board.
	ID() BoardID

	// Name returns the name of the board.
	Name() string

	// Aliases returns a list with previous names assigned to the board.
	Aliases() []string

	// Creator returns board's creator address.
	Creator() std.Address

	// LastPostID returns the ID assigned to the last created thread.
	LastPostID() uint64

	// CreatedAt returns the time when board was created.
	CreatedAt() time.Time

	// IsReadonly returns true when board shouldn't allow the creation of threads and comments.
	IsReadonly() bool

	// MembersCount returns the number of invited board members.
	MembersCount() int

	// IterateMembers allows reading the list of invited board members.
	IterateMembers(offset, count int, fn UsersIterFn)
}

type readonlyBoard struct {
	board *Board
}

func (b readonlyBoard) ID() BoardID {
	return b.board.id
}

func (b readonlyBoard) Name() string {
	return b.board.name
}

func (b readonlyBoard) Aliases() []string {
	aliases := make([]string, len(b.board.aliases))
	for i, s := range b.board.aliases {
		aliases[i] = s
	}
	return aliases
}

func (b readonlyBoard) Creator() std.Address {
	return b.board.creator
}

func (b readonlyBoard) LastPostID() uint64 {
	return b.board.postsCtr
}

func (b readonlyBoard) CreatedAt() time.Time {
	return b.board.createdAt
}

func (b readonlyBoard) IsReadonly() bool {
	return b.board.readonly
}

func (b readonlyBoard) MembersCount() int {
	return b.board.perms.UsersCount()
}

func (b readonlyBoard) IterateMembers(offset, count int, fn UsersIterFn) {
	b.board.perms.IterateUsers(offset, count, fn)
}

package boards2

import (
	"std"

	"gno.land/p/demo/avl"
)

var (
	gPerms        Permissions
	gNotice       string
	gHelp         string
	gLastBoardID  BoardID
	gBoardsByID   avl.Tree // string(id) -> *Board
	gBoardsByName avl.Tree // string(name) -> *Board
	gLocked       struct {
		realm        bool
		realmMembers bool
	}
)

func init() {
	// TODO: Define and change the default realm owner (or owners)
	owner := std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5") // @test1

	// Initialize default realm permissions
	gPerms = createDefaultPermissions(owner)

	// Import relevant initial state from previous version
	initFromPrevVersion()
}

// incGetBoardID returns a new board ID.
func incGetBoardID() BoardID {
	gLastBoardID++
	return gLastBoardID
}

// getBoard returns a board for a specific ID.
func getBoard(id BoardID) (_ *Board, found bool) {
	v, exists := gBoardsByID.Get(id.Key())
	if !exists {
		return nil, false
	}
	return v.(*Board), true
}

// mustGetBoardByName returns a board or panics when it's not found.
func mustGetBoardByName(name string) *Board {
	v, found := gBoardsByName.Get(name)
	if !found {
		panic("board does not exist with name: " + name)
	}
	return v.(*Board)
}

// mustGetBoard returns a board or panics when it's not found.
func mustGetBoard(id BoardID) *Board {
	board, found := getBoard(id)
	if !found {
		panic("board does not exist with ID: " + id.String())
	}
	return board
}

// mustGetThread returns a thread or panics when it's not found.
func mustGetThread(board *Board, threadID PostID) *Post {
	thread, found := board.GetThread(threadID)
	if !found {
		panic("thread does not exist with ID: " + threadID.String())
	}
	return thread
}

// mustGetReply returns a reply or panics when it's not found.
func mustGetReply(thread *Post, replyID PostID) *Post {
	reply, found := thread.GetReply(replyID)
	if !found {
		panic("reply does not exist with ID: " + replyID.String())
	}
	return reply
}

func mustGetPermissions(bid BoardID) Permissions {
	if bid != 0 {
		board := mustGetBoard(bid)
		return board.perms
	}
	return gPerms
}

