		)
	}
	return txlink.Call(
		"CreateReply",
		"boardID", post.board.id.String(),
		"threadID", post.threadID.String(),
		"replyID", post.id.String(),
		"body", "",
	)
}

func (post *Post) GetRepostFormURL() string {
	return txlink.Call(
		"CreateRepost",
		"boardID", post.board.id.String(),
		"threadID", post.id.String(),
		"title", "",
		"body", "",
		"destinationBoardID", "",
	)
}

func (post *Post) GetDeleteFormURL() string {
	if post.IsThread() {
		return txlink.Call(
			"DeleteThread",
			"boardID", post.board.id.String(),
			"threadID", post.threadID.String(),
		)
	}
	return txlink.Call(
		"DeleteReply",
		"boardID", post.board.id.String(),
