func TestNewReply(t *testing.T) {
	creator := testutils.TestAddress("creator")
	body := strings.Repeat("A", 82)
	boardID := BoardID(1)
	threadID := PostID(42)
	parentID := PostID(1)
	replyID := PostID(2)
