	perms := createDefaultBoardPermissions(creator)
	board := newBoard(boardID, boardName, creator, perms)
	url := ufmt.Sprintf(
		"/r/devx000/gnoland/boards2/v1rc2:%s/%d/%d",
		boardName,
		uint(threadID),
		uint(replyID),
	)
	replyURL := ufmt.Sprintf(
		"/r/devx000/gnoland/boards2/v1rc2$help&func=CreateReply&boardID=%d&body=&replyID=%d&threadID=%d",
		uint(boardID),
		uint(replyID),
		uint(threadID),
	)
	deleteURL := ufmt.Sprintf(
		"/r/devx000/gnoland/boards2/v1rc2$help&func=DeleteReply&boardID=%d&replyID=%d&threadID=%d",
		uint(boardID),
		uint(replyID),
		uint(threadID),
	)

	reply := newPost(board, replyID, creator, "", body, threadID, parentID, 0)

	uassert.False(t, reply.IsThread())
