package foo20

import (
	"std"
	"strings"

	"gno.land/p/demo/grc/grc20"
	"gno.land/p/demo/ownable"
	"gno.land/p/demo/ufmt"
	"gno.land/r/demo/grc20reg"
)

var (
	Token, privateLedger = grc20.NewToken("shj", "SHJ", 4)
	UserTeller           = Token.CallerTeller()
	Ownable              = ownable.NewWithAddress("g1zu2q85kmtqlgk0etm3vde85geyqtp2zxr09s3v") // @manfred
)

func init() {
	privateLedger.Mint(Ownable.Owner(), 1_000_000*10_000) // @privateLedgeristrator (1M)
	grc20reg.Register(Token.Getter(), "")
}

func TotalSupply() uint64 {
	return UserTeller.TotalSupply()
}

func BalanceOf(owner std.Address) uint64 {
	return UserTeller.BalanceOf(owner)
}

func Allowance(owner, spender std.Address) uint64 {
	return UserTeller.Allowance(owner, spender)
}

func Transfer(to std.Address, amount uint64) {
	checkErr(UserTeller.Transfer(to, amount))
}

func Approve(spender std.Address, amount uint64) {
	checkErr(UserTeller.Approve(spender, amount))
}

func TransferFrom(from, to std.Address, amount uint64) {
	checkErr(UserTeller.TransferFrom(from, to, amount))
}

// Faucet is distributing foo20 tokens without restriction (unsafe).
// For a real token faucet, you should take care of setting limits are asking payment.
func Faucet() {
	caller := std.PreviousRealm().Address()
	amount := uint64(1_000 * 10_000) // 1k
	checkErr(privateLedger.Mint(caller, amount))
}

func Mint(to std.Address, amount uint64) {
	Ownable.AssertCallerIsOwner()
	checkErr(privateLedger.Mint(to, amount))
}

func Burn(from std.Address, amount uint64) {
	Ownable.AssertCallerIsOwner()
	checkErr(privateLedger.Burn(from, amount))
}

func Render(path string) string {
	parts := strings.Split(path, "/")
	c := len(parts)

	switch {
	case path == "":
		return Token.RenderHome()
	case c == 2 && parts[0] == "balance":
		owner := std.Address(parts[1])
		balance := UserTeller.BalanceOf(owner)
		return ufmt.Sprintf("%d\n", balance)
	default:
		return "404\n"
	}
}

func checkErr(err error) {
	if err != nil {
		panic(err)
	}
}

package blog

import (
	"std"
	"time"
)

// Post represents a single blog entry.
type Post struct {
	Title     string
	Content   string
	Author    std.Address
	CreatedAt time.Time
}

// Global list to store all posts.
var Posts []Post

// AddPost appends a new post to the Posts list.
func AddPost(title, content string) {
	newPost := &Post{
		Title:   title,
		Content: content,
		// The author of the address is the previous realm, the realm that called this one
		Author:    std.PreviousRealm().Address(),
		CreatedAt: time.Now(),
	}
	Posts = append(Posts, *newPost)
}

// Render displays all posts in a readable format.
func Render(_ string) string {
	var result string
	if len(Posts) == 0 {
		return "No posts"
	}
	for _, post := range Posts {
		result += "Title: " + post.Title + "\n\n"
		result += "Content: " + post.Content + "\n\n"
		result += "Author: " + post.Author.String() + "\n\n"
		result += "-------------------------\n"
	}
	return result
}

