func IntSlice(vals []int) int {
	sum := 0
	for _, v := range vals {
		sum += v
	}

	return sum / len(vals)
}

func Uint64Slice(vals []uint64) uint64 {
	sum := uint64(0)
	for _, v := range vals {
		sum += v
	}

	return sum / uint64(len(vals))
}

func StrBoolIntSlices(a []string, b []bool) []string {
	out := make([]string, 0, len(a))
	if len(a) != len(b) {
		panic("len of all args should be equal")
	}

	for i, s := range a {
		if !b[i] {
			continue
		}

		out = append(out, s)
	}

	return out
}

func Dot(a, b []float64) float64 {
	if len(a) != len(b) {
		panic("vectors must be the same length")
	}

	var sum float64
	for i := range a {
		sum += a[i] * b[i]
	}
	return sum
}

const encodeStd = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!#$%&()*+,./:;<=>?@[]^_`{|}~\""

func B91Encode(src []byte) string {
	dst := make([]byte, b91EncLen(len(src)))

	var queue, numBits uint
	encode := []byte(encodeStd)

	n := 0
	for i := 0; i < len(src); i++ {
		queue |= uint(src[i]) << numBits
		numBits += 8
		if numBits > 13 {
			var v uint = queue & 8191

			if v > 88 {
				queue >>= 13
				numBits -= 13
			} else {
				// We can take 14 bits.
				v = queue & 16383
				queue >>= 14
				numBits -= 14
			}
			dst[n] = encode[v%91]
			n++
			dst[n] = encode[v/91]
			n++
		}
	}

	if numBits > 0 {
		dst[n] = encode[queue%91]
		n++

		if numBits > 7 || queue > 90 {
			dst[n] = encode[queue/91]
			n++
		}
	}

	return string(dst[:n])
}

func b91EncLen(n int) int {
	return int(math.Ceil(float64(n) * 16.0 / 13.0))
}

