var (
	organizers []std.Address
	speakers   []std.Address
	attendees  []std.Address
)

func init() {
	organizers = []std.Address{
		"g1j39fhg29uehm7twwnhvnpz3ggrm6tprhq65t0t",
		"g1aa5umzchlwqxtdfh58exmydmzsgpzcg3u9egcp",
	}

	speakers = []std.Address{
		"g1j39fhg29uehm7twwnhvnpz3ggrm6tprhq65t0t",
		"g12m4ecssg9j0pvnhxct3sfky70dy9fk7xrvfwx2",
	}

	attendees = []std.Address{
		"g1j39fhg29uehm7twwnhvnpz3ggrm6tprhq65t0t",
		"g12m4ecssg9j0pvnhxct3sfky70dy9fk7xrvfwx2",
		"g1yfts8fy9jyfeca4p42em6mcttfwcypkpkfx0rv",
	}
}

func HasRole(role string, addr std.Address) bool {
	switch role {
	case "attendee":
		return isMember(addr, attendees)
	case "speaker":
		return isMember(addr, speakers)
	case "organizer":
		return isMember(addr, organizers)
	default:
		return false
	}
}

func isMember(addr std.Address, list []std.Address) bool {
	for _, listAddr := range list {
		if addr == listAddr {
			return true
		}
	}
	return false
}

func Render(_ string) string {
	return ufmt.Sprintf(`# Mock Event

This represents an Event organizer and attendee list.
This realm can check membership by Querying HasRole(role, addr), where
a role can be attendee, speaker, or organizer.

## Organizers

%v

## Speakers

%v

## Attendees

%v

`, toList(organizers), toList(speakers), toList(attendees))
}

func toList(addrs []std.Address) (result string) {
	for _, addr := range addrs {
		result += "- " + string(addr) + "\n"
	}
	return result
}

