	return previous
}

// Rotate takes a hex encoded ed25519 public key and rotates the current key to previous
// and the new key becomes the current key. The caller must be either the owner or backup
// address specified in the contract. The hexKey must be exactly 64 characters long, and
// the hexKey must be valid hex encoding, otherwise, rotate panics.
func Rotate(hexKey string) {
	assertIsAuthorized()
	if len(hexKey) != 64 {
		panic("rotation error: invalid key length")
	}
	key, err := hex.DecodeString(hexKey)
	if err != nil {
		panic("rotation error: invalid hex encoding")
	}
	previous = current
	current = key
}

// assertIsAuthorized checks that the caller is either the owner or the backup user.
func assertIsAuthorized() {
	caller := std.PreviousRealm().Address()
	if !(caller == owner.Owner() || caller == backup.Owner()) {
		panic("rotation error: not authorized")
	}
}

// Render displays basic information about the contract, including the hex encoded
// current and previous pubkey.
func Render(_ string) string {
	c := hex.EncodeToString(current)
	p := hex.EncodeToString(previous)

	return ufmt.Sprintf(`
# Discord Bot Keyring

Discord Bot maintains an ed25519 private key for signing claims. This contract keeps
track of the current active public key as well as the previous public key so that change
management processes on the discord bot can happen smoothly.

## Current Pubkey: 

%v

## Previous Pubkey: 

%v
`, c, p)

}

package user

import (
	"crypto/ed25519"
	"encoding/base64"
	"errors"
	"std"
	"strconv"
	"strings"
	"time"

	keyring "gno.land/r/linker000/discord/keyring/v0"
)

const (
	// claimTTL is the maximum duration allowed for a claim to be valid.
	claimTTL = 5 * time.Minute
	//maxClockSkew represents the maximum allowed time skew for claims to exist in the future.
	maxClockSkew = 30 * time.Second
)

// Claim is a struct that represents the contents of a signed claim from the discord bot.
type Claim struct {
	timestamp time.Time
	discordID string
	addr      std.Address
}

// validateEncodedClaim takes a base64 encoded ed25519 based nacl.Sign claim. If the claim is valid
// and all verifications pass, the discord ID and gno address are linked.
// A valid claim must:
// - have a valid signature
// - the payload must be in the format `timestamp,discord_id,gno_address`
// - timestamp must be within the TTL
// - timestamp must not exceed future time clock skew
// - address must match caller
func validateEncodedClaim(encodedClaim string) (*Claim, error) {
	// decode the base64 encoded claim
	decodedClaim, err := base64.StdEncoding.DecodeString(encodedClaim)
	if err != nil {
		return nil, err
	}

	// attempt to verify with primary public key, and failover to previous public key
	payload, ok := verifySignature(decodedClaim, keyring.PubKey())
	if !ok {
		payload, ok = verifySignature(decodedClaim, keyring.PreviousPubKey())
		if !ok {
			return nil, errors.New("claim: not a valid signature")
		}
	}

	// create claim from payload
	claim, err := claimFromPayload(string(payload))
	if err != nil {
		return nil, err
	}

	// verify timestamp
	if err := verifyTimestamp(claim.timestamp, time.Now(), claimTTL, maxClockSkew); err != nil {
		return nil, err
	}

	// verify address
	if err := verifyAddress(claim.addr); err != nil {
		return nil, err
	}

	return claim, nil
}

func claimFromPayload(payload string) (*Claim, error) {
	splitPayload := strings.Split(payload, ",")
	if len(splitPayload) != 3 {
		return nil, errors.New("claim: invalid arg length, expecting timestamp,discord_id,gno_addr")
	}
	unixSeconds, err := strconv.ParseInt(splitPayload[0], 10, 64)
	timestamp := time.Unix(unixSeconds, 0)
	if err != nil {
		return nil, errors.New("claim: failed to parse timestamp")
	}
	return &Claim{
		timestamp: timestamp,
		discordID: splitPayload[1],
		addr:      std.Address(splitPayload[2]),
	}, nil
}

func verifyAddress(addr std.Address) error {
	caller := std.PreviousRealm().Address()
	if addr != caller {
		return errors.New("claim: address does not match caller")
	}
	return nil
}

func verifyTimestamp(claim, evaluated time.Time, ttl, skew time.Duration) error {
	claimAge := evaluated.Sub(claim)
	if claimAge > ttl {
		return errors.New("claim: timestamp exceeds ttl window")
	}
	if claim.After(evaluated.Add(skew)) {
		return errors.New("claim: timestamp exceeds clock skew tolerance")
	}
	return nil
}

// verifySignature takes a signedMessage and a pubkey and attempts to verify.
// If it is able to verify, it returns the payload and true, otherwise it
// returns nil and false.
func verifySignature(signedMessage, pubkey []byte) ([]byte, bool) {
	overhead := 64
	if len(signedMessage) < overhead {
		return nil, false
	}
	if ed25519.Verify(pubkey, signedMessage[overhead:], signedMessage[:overhead]) {
		out := make([]byte, len(signedMessage)-overhead)
		copy(out, signedMessage[overhead:])
		return out, true
	}
	return nil, false
}

package user

import (
	"errors"
	"std"

	"gno.land/p/demo/avl"
)

type datastore struct {
	byAddr      *avl.Tree
	byDiscordID *avl.Tree
}

func (ds *datastore) set(claim *Claim) error {
	if ds.getByAddress(claim.addr) != nil {
		return errors.New("datastore: gno address is already linked")
	}
	if ds.getByDiscordID(claim.discordID) != nil {
		return errors.New("datastore: discord id is already linked")
	}
	ds.byAddr.Set(string(claim.addr), claim)
	ds.byDiscordID.Set(claim.discordID, claim)
	return nil
}

func (ds *datastore) removeByAddress(addr std.Address) {
	value, removed := ds.byAddr.Remove(string(addr))
	if removed {
		claim := value.(*Claim)
		ds.byDiscordID.Remove(claim.discordID)
	}
}

func (ds *datastore) removeByDiscordID(id string) {
	value, removed := ds.byDiscordID.Remove(id)
	if removed {
		claim := value.(*Claim)
		ds.byAddr.Remove(string(claim.addr))
	}
}

func (ds datastore) getByAddress(addr std.Address) *Claim {
	value, ok := ds.byAddr.Get(string(addr))
	if !ok {
		return nil
	}
	return value.(*Claim)
}

func (ds datastore) getByDiscordID(id string) *Claim {
	value, ok := ds.byDiscordID.Get(id)
	if !ok {
		return nil
	}
	return value.(*Claim)
}

