package user

import (
	"std"

	"gno.land/p/demo/avl"
)

var (
	// ds is the primary datastore for claims
	ds datastore
)

func init() {
	ds = datastore{
		byAddr:      avl.NewTree(),
		byDiscordID: avl.NewTree(),
	}
}

// Link takes a base64 encoded ed25519 signed claim from the gno discord linker bot.
// If the claim is valid, it will use the claim to link a gno address to a discord id.
// A gno address may only be linked to a single discord ID and a single discord ID may
// only be linked to a single gno address.
func Link(encodedClaim string) {
	claim, err := validateEncodedClaim(encodedClaim)
	if err != nil {
		panic(err)
	}
	if err := ds.set(claim); err != nil {
		panic(err)
	}
}

// UnlinkByCaller removes a discord link, based on the caller address
// This does not require a claim because the caller is the link owner
func UnlinkByCaller() {
	caller := std.PreviousRealm().Address()
	ds.removeByAddress(caller)
}

// UnlinkByClaim takes an encodedClaim from a valid discord bot
// and unlinks a discordID from a gno address. This is useful if you
// still have access to your discord account, but you have lost
// access to your old gno address.
func UnlinkByClaim(encodedClaim string) {
	claim, err := validateEncodedClaim(encodedClaim)
	if err != nil {
		panic(err)
	}
	ds.removeByDiscordID(claim.discordID)
}

// GetLinkedAddress takes a discord ID and returns a linked gno address
func GetLinkedAddress(discordID string) std.Address {
	claim := ds.getByDiscordID(discordID)
	if claim == nil {
		return std.Address("")
	}
	return claim.addr
}

// GetLinkedDiscordID takes an address and returns a linked discord ID
func GetLinkedDiscordID(address std.Address) string {
	claim := ds.getByAddress(address)
	if claim == nil {
		return ""
	}
	return claim.discordID
}

package role

import (
	"crypto/ed25519"
	"encoding/base64"
	"errors"
	"std"
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/ufmt"
	keyring "gno.land/r/linker000/discord/keyring/v0"
)

const (
	// claimTTL is the maximum duration allowed for a claim to be valid.
	claimTTL = 5 * time.Minute
	//maxClockSkew represents the maximum allowed time skew for claims to exist in the future.
	maxClockSkew = 30 * time.Second
)

type Claim struct {
	timestamp        time.Time
	discordAccountID string
	discordGuildID   string
	discordRoleID    string
	addr             std.Address
	roleName         string
	realmPath        string
}

func (c *Claim) PathRoleGuildKey() string {
	return fmtPathRoleGuildKey(c.realmPath, c.roleName, c.discordGuildID)
}

func (c *Claim) GuildRoleKey() string {
	return fmtGuildRoleKey(c.discordGuildID, c.discordRoleID)
}

func (c *Claim) RoleID() string {
	return c.discordRoleID
}

func (c *Claim) RealmRoleDetails() LinkedRealmRole {
	return LinkedRealmRole{
		RealmPath:      c.realmPath,
		RealmRoleName:  c.roleName,
		DiscordRoleID:  c.discordRoleID,
		DiscordGuildID: c.discordGuildID,
	}
}

func fmtPathGuildKey(realmPath, guildID string) string {
	return ufmt.Sprintf("%s|%s", realmPath, guildID)
}

func fmtPathRoleGuildKey(realmPath, roleName, guildID string) string {
	return ufmt.Sprintf("%s|%s|%s", realmPath, roleName, guildID)
}

func fmtGuildRoleKey(guildID, roleID string) string {
	return ufmt.Sprintf("%s|%s", guildID, roleID)
}

func validateEncodedClaim(encodedClaim string) (*Claim, error) {

	// decode the base64 encoded claim
	decodedClaim, err := base64.StdEncoding.DecodeString(encodedClaim)
	if err != nil {
		return nil, err
	}

	// attempt to verify with primary public key, and failover to previous public key
	payload, ok := verifySignature(decodedClaim, keyring.PubKey())
	if !ok {
		payload, ok = verifySignature(decodedClaim, keyring.PreviousPubKey())
		if !ok {
			return nil, errors.New("claim: not a valid signature")
		}
	}
	// create claim from payload
	claim, err := claimFromPayload(string(payload))
	if err != nil {
		return nil, err
	}

	// verify timestamp
	if err := verifyTimestamp(claim.timestamp, time.Now(), claimTTL, maxClockSkew); err != nil {
		return nil, err
	}

	// verify address
	if err := verifyAddress(claim.addr); err != nil {
		return nil, err
	}

	return claim, nil
}

func claimFromPayload(payload string) (*Claim, error) {
	splitPayload := strings.Split(payload, ",")
	if len(splitPayload) != 7 {
		return nil, errors.New("claim: invalid arg length, expecting 7 values")
	}
	unixSeconds, err := strconv.ParseInt(splitPayload[0], 10, 64)
	timestamp := time.Unix(unixSeconds, 0)
	if err != nil {
		return nil, errors.New("claim: failed to parse timestamp")
	}
	return &Claim{
		timestamp:        timestamp,
		discordAccountID: splitPayload[1],
		discordGuildID:   splitPayload[2],
		discordRoleID:    splitPayload[3],
		addr:             std.Address(splitPayload[4]),
		roleName:         splitPayload[5],
		realmPath:        splitPayload[6],
	}, nil
}

// verifySignature takes a signedMessage and attempts to verify it with any pubkey in the set of pubkeys
// if it is able to verify, it returns the payload and true, otherwise it returns nil and false.
func verifySignature(signedMessage, pubkey []byte) ([]byte, bool) {
	overhead := 64
	if len(signedMessage) < overhead {
		return nil, false
	}
	if ed25519.Verify(pubkey, signedMessage[overhead:], signedMessage[:overhead]) {
		out := make([]byte, len(signedMessage)-overhead)
		copy(out, signedMessage[overhead:])
		return out, true
	}
	return nil, false
}

func verifyTimestamp(claim, evaluated time.Time, ttl, skew time.Duration) error {
	claimAge := evaluated.Sub(claim)
	if claimAge > ttl {
		return errors.New("claim: timestamp exceeds ttl window")
	}
	if claim.After(evaluated.Add(skew)) {
		return errors.New("claim: timestamp exceeds clock skew tolerance")
	}
	return nil
}

func verifyAddress(addr std.Address) error {
	caller := std.PreviousRealm().Address()
	if addr != caller {
		return errors.New("claim: address does not match caller")
	}
	return nil
}

type LinkedRealmRole struct {
	RealmPath      string
	RealmRoleName  string
	DiscordRoleID  string
	DiscordGuildID string
}

// Method for a single LinkedRealmRole
func (lrr *LinkedRealmRole) ToJSON() string {
	var sb strings.Builder

	sb.WriteString("{")
	sb.WriteString(ufmt.Sprintf("\"RealmPath\":\"%s\",", escapeString(lrr.RealmPath)))
	sb.WriteString(ufmt.Sprintf("\"RealmRoleName\":\"%s\",", escapeString(lrr.RealmRoleName)))
	sb.WriteString(ufmt.Sprintf("\"DiscordRoleID\":\"%s\",", escapeString(lrr.DiscordRoleID)))
	sb.WriteString(ufmt.Sprintf("\"DiscordGuildID\":\"%s\"", escapeString(lrr.DiscordGuildID)))
	sb.WriteString("}")

	return sb.String()
}

// Function for a slice of LinkedRealmRole
func linkedRealmRolesToJSON(roles []LinkedRealmRole) string {
	var sb strings.Builder

	sb.WriteString("[")

	for i, role := range roles {
		// Use the individual ToJSON method for each role
		sb.WriteString(role.ToJSON())

		// Add comma for all elements except the last one
		if i < len(roles)-1 {
			sb.WriteString(",")
		}
	}

	sb.WriteString("]")
	return sb.String()
}

// Helper function to escape special characters in JSON strings
func escapeString(s string) string {
	s = strings.Replace(s, "\\", "\\\\", -1)
	s = strings.Replace(s, "\"", "\\\"", -1)
	s = strings.Replace(s, "\n", "\\n", -1)
	s = strings.Replace(s, "\r", "\\r", -1)
	s = strings.Replace(s, "\t", "\\t", -1)
	return s
}

package role

import (
	"errors"

	"gno.land/p/demo/avl"
)

// datastore keeps a *Claim database indexed by key/value store optimized for 3 types of queries
// byPathRoleGuild is keyed by realmPath|roleName|guildID, if you know these 3 pieces of information
// you can quickly find the discordRoleID linked to this realm and discord server
// byGuildRole is keyed by discordGuildID|discordRoleID, if you know the discord server and role information
// this will let you find the linked gno.land realm and role combination it is linked to
// byGuildRealm is a nested AVL tree keyed by RealmPath and Guild, contains a nested AVL tree that
// keeps track of all registered roles for that realm and discord server combination, making it easier
// to perform membership lookups
type datastore struct {
	byPathRoleGuild *avl.Tree
	byGuildRole     *avl.Tree
	byPathGuild     *avl.Tree
}

func newDatastore() *datastore {
	return &datastore{
		byPathRoleGuild: avl.NewTree(),
		byGuildRole:     avl.NewTree(),
		byPathGuild:     avl.NewTree(),
	}
}

func (ds *datastore) set(claim *Claim) error {
	if claim == nil {
		return errors.New("datastore: cannot set nil claim")
	}
	if ds.getByPathRoleGuild(claim.realmPath, claim.roleName, claim.discordGuildID) != nil {
		return errors.New("datastore: the unique path-role-guild key is already linked")
	}
	if ds.getByGuildRole(claim.discordGuildID, claim.discordRoleID) != nil {
		return errors.New("datastore: the unique guild-role key is already linked")
	}
	ds.byPathRoleGuild.Set(claim.PathRoleGuildKey(), claim)
	ds.byGuildRole.Set(claim.GuildRoleKey(), claim)
	ds.addRoleToPathGuild(claim.roleName, claim.realmPath, claim.discordGuildID)
	return nil
}

func (ds *datastore) removeByPathRoleGuild(realmPath, roleName, guildID string) {
	key := fmtPathRoleGuildKey(realmPath, roleName, guildID)
	value, removed := ds.byPathRoleGuild.Remove(key)
	if removed {
		claim := value.(*Claim)
		key := fmtGuildRoleKey(claim.discordGuildID, claim.discordRoleID)
		ds.byPathRoleGuild.Remove(key)
		ds.removeRoleFromPathGuild(claim.roleName, claim.realmPath, claim.discordGuildID)
	}
}

func (ds *datastore) removeByGuildRole(guildID, roleID string) {
	key := fmtGuildRoleKey(guildID, roleID)
	value, removed := ds.byGuildRole.Remove(key)
	if removed {
		claim := value.(*Claim)
		key := fmtPathRoleGuildKey(claim.realmPath, claim.roleName, claim.discordGuildID)
		ds.byPathRoleGuild.Remove(key)
		ds.removeRoleFromPathGuild(claim.roleName, claim.realmPath, claim.discordGuildID)
	}
}

func (ds *datastore) getByPathRoleGuild(realmPath, roleName, guildID string) *Claim {
	key := fmtPathRoleGuildKey(realmPath, roleName, guildID)
	value, ok := ds.byPathRoleGuild.Get(key)
	if !ok {
		return nil
	}
	return value.(*Claim)
}

func (ds *datastore) getByGuildRole(guildID, roleID string) *Claim {
	key := fmtGuildRoleKey(guildID, roleID)
	value, ok := ds.byGuildRole.Get(key)
	if !ok {
		return nil
	}
	return value.(*Claim)
}

func (ds *datastore) addRoleToPathGuild(roleName, realmPath, guildID string) {
	key := fmtPathGuildKey(realmPath, guildID)
	value, ok := ds.byPathGuild.Get(key)
	if !ok {
		ds.byPathGuild.Set(key, avl.NewTree())
		value, _ = ds.byPathGuild.Get(key)
	}
	roles := value.(*avl.Tree)
	roles.Set(roleName, struct{}{})
}

func (ds *datastore) removeRoleFromPathGuild(roleName, realmPath, guildID string) {
	key := fmtPathGuildKey(realmPath, guildID)
	value, ok := ds.byPathGuild.Get(key)
	if !ok {
		return
	}
	roles := value.(*avl.Tree)
	roles.Remove(roleName)
}

func (ds *datastore) listRolesByPathGuild(realmPath, guildID string) []string {
	key := fmtPathGuildKey(realmPath, guildID)
	value, ok := ds.byPathGuild.Get(key)
	if !ok {
		return []string{}
	}
	roles := value.(*avl.Tree)
	results := []string{}
	roles.Iterate("", "", func(key string, _ any) bool {
		results = append(results, key)
		return false
	})
	return results
}

