import (
    "gno.land/r/leon/config"
)

func main() {
    config.AddConfig(cross, "Indexer: Fetch what I voted for in GovDAO", `query whatDidIVoteFor {
  getTransactions(
    where: {
      success: {eq: true},
      messages: {value: {MsgCall: {
        pkg_path: { eq:"gno.land/r/gov/dao" },
        func: { eq: "MustVoteOnProposalSimple" },
        caller: { eq: "g125em6arxsnj49vx35f0n0z34putv5ty3376fg5" },
      }}},
    }
  ) {
    gas_wanted
    messages {
      value {
        ... on MsgCall {
          caller
          args
          pkg_path
          func
        }
      }
    }
  }
}`)
}

