// package simplepoll eases creation and management of polls with Gno.
package simplepoll

import (
	"errors"
	"std"
	"strconv"
	"strings"
)

// [Poll] represents a blueprint for a single poll.
type Poll struct {
	Creator  std.Address          // The address of the person who created the poll.
	Question string               // The poll's question.
	Options  []string             // A list of valid options for this poll.
	Votes    map[string]int64     // A map to store the vote count for each option.
	Voters   map[std.Address]bool // A map to track who has voted *in this specific poll*.
}

// polls is a list of [Poll] in the blockchain.
var polls []Poll

var (
	ErrQuestionMustBeProvided = errors.New("poll question must be provided")
	ErrMiniminalOptionsMustBeProvided   = errors.New("at least two comma-separated options must be provided")
	ErrPollNotFound = errors.New("poll not found")
	ErrAlreadyVoted = errors.New("you have already voted in this poll")
)

// CreatePoll allows anyone to create a new [Poll] and add it to our list.
func CreatePoll(_ realm, question string, optionsArg string) error {
  caller := std.PreviousRealm().Address()
  options := strings.Split(optionsArg, ",")

	isQuestionProvided := question != ""
	if !isQuestionProvided {
		return ErrQuestionMustBeProvided
	}

	areMinimalOptionsProvided := len(options) >= 2
	if !areMinimalOptionsProvided {
		return ErrMiniminalOptionsMustBeProvided
	}

	poll := Poll{
		Creator:  caller,
		Question: question,
		Options:  options,
		Votes:    make(map[string]int64),
		Voters:   make(map[std.Address]bool),
	}

	for _, option := range options {
		poll.Votes[option] = 0
	}

	polls = append(polls, poll)

	return nil
}

// Vote allows a user to vote on a *specific* poll, identified by its index.
func Vote(_ realm, pollNumber int64, choice string) error {
  pollIdx := pollNumber - 1
	caller := std.PreviousRealm().Address()

	if pollIdx >= int64(len(polls)) {
		return ErrPollNotFound
	}

	poll := &polls[pollIdx]

	_, hasAlreadyVoted := poll.Voters[caller]

	if hasAlreadyVoted {
		return ErrAlreadyVoted
	}

	_, isOptionAvailable := poll.Votes[choice]

	if !isOptionAvailable {
		return errors.New("the option you voted for does not exist in this poll")
	}

	poll.Votes[choice]++
	poll.Voters[caller] = true

	return nil
}

// Render provides a detailed view of all polls.
func Render(path string) string {
	result := instructions
	result += "# Latest polls created\n\n"

	if len(polls) == 0 {
		return result + "No polls have been created yet. Be the first!"
	}

	// Show the 5 most recent polls
	startIdx := len(polls) - 5
	if startIdx < 0 {
		startIdx = 0
	}

	for id, p := range polls[startIdx:] {
		pollNumber := 1 + startIdx + id
		result += "## " + strconv.Itoa(pollNumber) + ") " + p.Question + "\n\n"

		var totalVotes int64 = 0
		for _, count := range p.Votes {
			totalVotes += count
		}

		result += "| Option | Votes | Percentage |\n"
		result += "|:---|:---|:---|\n"

		// Display each option as a row in the new table format.
		for _, option := range p.Options {
			voteCount := p.Votes[option]

			var percentage int64 = 0
			if totalVotes > 0 {
				percentage = (voteCount * 100) / totalVotes
			}

			votes := strconv.FormatInt(voteCount, 10)
			distribution := strconv.FormatInt(percentage, 10) + "%"

			result += "| **" + option + "** | " + votes + " | " + distribution + " |\n"
		}

		result += "\n---\n\n"
	}

	return result
}

package simplepoll

import (
  "strings"
	"testing"
)

func TestCreatePoll(t *testing.T) {
	t.Run("CreatePollWithoutQuestion", func(t *testing.T) {
	  emptyQuestion := ""

		err := CreatePoll(cross, emptyQuestion, "Blue,Green")
		if err != ErrQuestionMustBeProvided {
      t.Errorf("expected error to be: %v, but got: %v", ErrQuestionMustBeProvided, err)
    }
	})

	t.Run("CreatePollWithLessThanTwoOptions", func(t *testing.T) {
	  question := "What is your favorite color?"
    insufficientOptions := "Blue"

    err := CreatePoll(cross, question, insufficientOptions)
    if err != ErrMiniminalOptionsMustBeProvided {
      t.Errorf("expected error to be: %v, but got: %v", ErrMiniminalOptionsMustBeProvided, err)
    }
	})

	t.Run("CreatePollSuccessfully", func(t *testing.T) {
	  question := "What is your favorite color?"
    options := []string{"Blue","Green"}

    err := CreatePoll(cross, question, strings.Join(options, ","))
    if err != nil {
      t.Errorf("expected no error, but got: %v", err)
    }

    lastPoll := polls[len(polls) - 1]

    if lastPoll.Question != question {
      t.Errorf("expected question to be: %v, but got: %v", question, lastPoll.Question)
    }

    if len(lastPoll.Options) != 2 {
      t.Errorf("expected options length to be: %v, but got: %v", len(options), len(lastPoll.Options))
    }

    for i, option := range options {
      if lastPoll.Options[i] != option {
        t.Errorf("expected option at index %d to be: %v, but got: %v", i, option, lastPoll.Options[i])
      }

      if lastPoll.Votes[option] != 0 {
        t.Errorf("expected votes for option %v to be 0, but got: %v", option, lastPoll.Votes[option])
      }
    }
	})
}

func TestVote(t *testing.T) {
  t.Run("PollNotFound", func(t *testing.T) {
    err := Vote(cross, 999, "Blue")
    if err != ErrPollNotFound {
      t.Errorf("expected error to be: %v, but got: %v", ErrPollNotFound, err)
    }
  })

  t.Run("AlreadyVoted", func(t *testing.T) {
    question := "What is your favorite color?"
    options := []string{"Blue", "Green"}

    err := CreatePoll(cross, question, strings.Join(options, ","))
    if err != nil {
      t.Fatalf("failed to create poll: %v", err)
    }

    pollNumber := int64(len(polls))

    err = Vote(cross, pollNumber, options[0])
    if err != nil {
      t.Fatalf("failed to vote: %v", err)
    }

    err = Vote(cross, pollNumber, options[1])
    if err != ErrAlreadyVoted {
      t.Errorf("expected error to be: %v, but got: %v", ErrAlreadyVoted, err)
    }
  })

  t.Run("Success", func(t *testing.T) {
    question := "What is your favorite color?"
    options := []string{"Blue", "Green"}

    err := CreatePoll(cross, question, strings.Join(options, ","))
    if err != nil {
      t.Fatalf("failed to create poll: %v", err)
    }

    pollNumber := len(polls)

    if pollNumber < 1 {
      t.Fatalf("no polls available to vote on")
    }

    err = Vote(cross, int64(pollNumber), options[0])
    if err != nil {
      t.Fatalf("failed to vote: %v", err)
    }

    poll := polls[pollNumber - 1]

    if poll.Votes[options[0]] != 1 {
      t.Errorf("expected votes for option %v to be 1, but got: %v", options[0], poll.Votes[options[0]])
    }
  })
}
