package alerts

import (
	"gno.land/p/demo/ufmt"
)

const (
	TypeError   Type = "alerts-error"
	TypeWarning Type = "alerts-warning"
)

const (
	StyleError = `
.alerts-error {
	padding: .75rem 1.25rem;
	border: 1px solid #f5c6cb;
	background-color: #f8d7da;
	color: #721c24;
	border-radius: .25rem;
}
`
	StyleWarning = `
.alerts-warning {
	padding: .75rem 1.25rem;
	border: 1px solid #ffeeba;
	background-color: #fff3cd;
	color: #856404;
	border-radius: .25rem;
}
`
)

// Type defines the type of alerts.
type Type string

// NewAlert returns HTML for an alert.
func NewAlert(t Type, content string) string {
	var css string
	switch t {
	case TypeWarning:
		css = StyleWarning
	case TypeError:
		css = StyleError
	default:
		panic("unknown alert type")
	}

	return "\n\n" + ufmt.Sprintf(`<p class="%s">%s</p><style>%s</style>`, string(t), content, css) + "\n\n"
}

// NewWarning returns HTML for a warning alert.
func NewWarning(content string) string {
	return NewAlert(TypeWarning, content)
}

// NewError returns HTML for an error alert.
func NewError(content string) string {
	return NewAlert(TypeError, content)
}

// NewLink returns an HTML link.
func NewLink(href, label string) string {
	return ufmt.Sprintf(`<a href="%s">%s</a>`, href, label)
}

package blog

import (
	"crypto/sha256"
	"encoding/hex"
	"net/url"
	"regexp"
	"strings"
)

var (
	hostnameRe = regexp.MustCompile(`^(?i)[a-z0-9-]+(\.[a-z0-9-]+)+\.?$`)
	sha256Re   = regexp.MustCompile(`^[a-f0-9]{64}$`)
	slugRe     = regexp.MustCompile(`^[a-z0-9\p{L}]+(?:-[a-z0-9\p{L}]+)*$`)
)

// AssertIsSlug asserts that a URL slug is valid.
func AssertIsSlug(slug string) {
	if !IsSlug(slug) {
		panic("URL slug is not valid")
	}
}

// AssertContentSha256Hash asserts that a hex hash is a valid SHA256 hash.
func AssertIsSha256Hash(hexHash string) {
	if !IsSha256Hash(hexHash) {
		panic("invalid sha256 hash")
	}
}

// AssertIsContentURL asserts that a URL is a valid link to a content.
// URL must have a path to ve valid. Website URLs will fail.
func AssertIsContentURL(url string) {
	if !IsURL(url, true) {
		panic("content URL is not valid, make sure path to content is specified")
	}
}

// AssertTitleIsNotEmpty asserts that a title is not an empty string.
func AssertTitleIsNotEmpty(title string) {
	if strings.TrimSpace(title) == "" {
		panic("title is empty")
	}
}

// AssertContentSha256Hash asserts that the SHA256 hash of a content matches a hash.
func AssertContentSha256Hash(content, hash string) {
	if hash != GetHexSha256Hash(content) {
		panic("content sha256 checksum is not valid")
	}
}

// IsSlug checks if a string is a valid URL slug.
func IsSlug(slug string) bool {
	return slugRe.MatchString(slug)
}

// IsSha256Hash checks is a hex hash is a valid SHA256 hash.
func IsSha256Hash(hexHash string) bool {
	return sha256Re.MatchString(strings.ToLower(hexHash))
}

// IsURL checks if a URL is valid.
// URL path availability can optionally be enforced.
func IsURL(rawURL string, requirePath bool) bool {
	u, err := url.ParseRequestURI(rawURL)
	if err != nil {
		return false
	}

	if requirePath && u.Path == "" || u.Path == "/" {
		return false
	}

	if u.Scheme != "https" && u.Scheme != "http" {
		return false
	}

	hostname := u.Hostname()
	return hostname != "" && hostnameRe.MatchString(hostname)
}

// GetHexSha256Hash returns the hexadecimal encoding of the string's SHA256 hash.
// An empty string is returned when the argument is an empty string.
func GetHexSha256Hash(s string) string {
	sum := sha256.Sum256([]byte(s))
	return hex.EncodeToString(sum[:])
}

package blog

import (
	"testing"

	"gno.land/p/g1lyzcpa7duh69lk04nahxup484xrz4k6k2nqdun/gnome/blog"
)

func TestIsSlug(t *testing.T) {
	cases := []struct {
		name, slug string
		want       bool
	}{
		{
			name: "empty",
			want: false,
		},
		{
			name: "one letter",
			slug: "a",
			want: true,
		},
		{
			name: "one unicode letter",
			slug: "",
			want: true,
		},
		{
			name: "one word",
			slug: "foo",
			want: true,
		},
		{
			name: "one unicode word",
			slug: "fo",
			want: true,
		},
		{
			name: "many words",
			slug: "foo-bar-baz",
			want: true,
		},
		{
			name: "many unicode words",
			slug: "fo-br-bz",
			want: true,
		},
		{
			name: "with spaces",
			slug: "foo bar",
			want: false,
		},
		{
			name: "with invalid chars",
			slug: "foo/bar",
			want: false,
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Act
			got := blog.IsSlug(tc.slug)

			// Assert
			if got != tc.want {
				t.Fatalf("expected slug check to return: %v", tc.want)
			}
		})
	}
}

func TestIsSha256Hash(t *testing.T) {
	cases := []struct {
		name, hash string
		want       bool
	}{
		{
			name: "empty",
			want: false,
		},
		{
			name: "ok",
			hash: "1a66cf828aea323fc58c653b0bc0d64061bb5c198e500a541a2c97f4f45b668d",
			want: true,
		},
		{
			name: "invalid size",
			hash: "1a66cf828aea323",
			want: false,
		},
		{
			name: "invalid characters",
			hash: "1a66#?",
			want: false,
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Act
			got := blog.IsSha256Hash(tc.hash)

			// Assert
			if got != tc.want {
				t.Fatalf("expected sha256 check check to return: %v", tc.want)
			}
		})
	}
}

func TestIsURL(t *testing.T) {
	cases := []struct {
		url  string
		want bool
	}{
		{url: "https", want: false},
		{url: "https/a", want: false},
		{url: "https/a/b", want: false},
		{url: "https/a/b/", want: false},
		{url: "https:", want: false},
		{url: "https:www.test.com", want: false},
		{url: "https:www.test.com/", want: false},
		{url: "https:www.test.com/a", want: false},
		{url: "https:www.test.com/a/b", want: false},
		{url: "https:www.test.com/a/b/", want: false},
		{url: "https:www.test.com:42/a/b/", want: false},
		{url: "https:/", want: false},
		{url: "https:/a", want: false},
		{url: "https:/a/b", want: false},
		{url: "https:/a/b/", want: false},
		{url: "https:/www.test.com/a/b", want: false},
		{url: "https://", want: false},
		{url: "https://a", want: false},
		{url: "https://a/b", want: false},
		{url: "https://a/b/", want: false},
		{url: "https://www.test.com", want: false},
		{url: "https://www.test.com/", want: false},
		{url: "https://www.test.com/a", want: true},
		{url: "https://www.test.com/a/b", want: true},
		{url: "https://www.test.com/a/b/", want: true},
		{url: "https://www.test.com:42/a/b/", want: true},
		{url: "https://foo.bar.test.com", want: false},
		{url: "https://foo.bar.test.com/", want: false},
		{url: "https://foo.bar.test.com/a", want: true},
		{url: "https://foo.bar.test.com/a/b", want: true},
		{url: "https://foo.bar.test.com/a/b/", want: true},
		{url: "https://foo.bar.test.com/a/b", want: true},
		{url: "https://foo.bar.test.com:42/a/b", want: true},
	}

	for _, tc := range cases {
		t.Run(tc.url, func(t *testing.T) {
			// Act
			got := blog.IsURL(tc.url, true)

			// Assert
			if got != tc.want {
				t.Fatalf("expected URL check to return: %v", tc.want)
			}
		})
	}
}

func TestGetHexSha256Hash(t *testing.T) {
	cases := []struct {
		name, content, want string
	}{
		{
			name: "empty",
			want: "e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855",
		},
		{
			name:    "ok",
			content: "foo",
			want:    "2c26b46b68ffc68ff99b453c1d30413413422d706483bfa0f98a5e886266e7ae",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Act
			got := blog.GetHexSha256Hash(tc.content)

			// Assert
			if got != tc.want {
				t.Fatalf("expected hash: '%s', got: '%s'", tc.want, got)
			}
		})
	}
}

package blog

import (
	"strings"

	"gno.land/p/demo/avl"
)

type (
	// Blog defines a blog.
	Blog struct {
		posts avl.Tree // string(slug) -> *Post

		// Title is blog's title.
		Title string

		// Description is the blog's description.
		Description string
	}

	// PostIterFn defines the a callback to iterate blog posts.
	PostIterFn func(*Post) bool
)

// HasPost checks if a post with a URL slug exists.
func (b Blog) HasPost(slug string) bool {
	return b.posts.Has(slug)
}

// GetPost returns a blog's post.
func (b Blog) GetPost(slug string) (_ *Post, found bool) {
	if v, found := b.posts.Get(slug); found {
		return v.(*Post), true
	}
	return nil, false
}

// AddPost adds a new post to the blog.
func (b *Blog) AddPost(p *Post) bool {
	slug := strings.TrimSpace(p.Slug)
	if slug == "" {
		panic("post has an empty slug")
	}

	return b.posts.Set(slug, p)
}

// RemovePost removes a post from the blog.
// The removed post is returned after being removed if it exists.
func (b *Blog) RemovePost(slug string) (_ *Post, removed bool) {
	if v, removed := b.posts.Remove(slug); removed {
		return v.(*Post), true
	}
	return nil, false
}

// IteratePosts iterates all posts by slug.
func (b Blog) IteratePosts(fn PostIterFn) bool {
	// TODO: Improve blog post iteration
	return b.posts.Iterate("", "", func(_ string, value interface{}) bool {
		return fn(value.(*Post))
	})
}

