package blog

import (
	"errors"
	"std"
	"strings"
	"time"
)

const (
	StatusDraft PostStatus = iota
	StatusApproved
	StatusPublished
	StatusRevised
	StatusArchived
)

type (
	// AddressList defines a list of addresses.
	AddressList []std.Address

	// PostStatus defines a type for blog post states.
	PostStatus uint8

	// Post defines a blog post.
	Post struct {
		// Slug contains the URL path slug for the post.
		Slug string

		// Title is the post's title.
		Title string

		// Summary is the post's summary.
		Summary string

		// Status is the current post's state.
		Status PostStatus

		// Content contains the post's content.
		Content string

		// ContentHash contains the hash of the post's content.
		ContentHash string

		// Authors contains the list of post authors.
		Authors AddressList

		// Editors contains the list of post editors.
		// Each account belongs to an editor that significantly improved the content.
		Editors AddressList

		// Contributors contains the list of post contributors.
		// Each account belongs to a contributor that submitted small content changes.
		Contributors AddressList

		// Publishers contains the accounts that published the content.
		Publishers AddressList

		// Tags contains a list of tags for the post.
		// These tags can be used to build the blog content taxonomy.
		Tags []string

		// CreatedAt is the block time when the post has been created.
		CreatedAt time.Time

		// UpdatedAt is the block time when the post has been updated for the last time.
		UpdatedAt time.Time

		// PublishAt is the block time when the post should be published.
		PublishAt time.Time

		// ExpireAt is the block time when the post should be archived.
		ExpireAt time.Time
	}
)

// String returns a comma separated string with the list of addresses.
func (x AddressList) String() string {
	var s []string
	for _, item := range x {
		s = append(s, item.String())
	}
	return strings.Join(s, ", ")
}

// HasAddress checks if an address is part of the address list.
func (x AddressList) HasAddress(addr std.Address) bool {
	for _, item := range x {
		if item == addr {
			return true
		}
	}
	return false
}

// String returns the post status name.
func (s PostStatus) String() string {
	switch s {
	case StatusDraft:
		return "draft"
	case StatusApproved:
		return "approved"
	case StatusPublished:
		return "published"
	case StatusRevised:
		return "revised"
	case StatusArchived:
		return "archived"
	default:
		return "unknown"
	}
}

// IsExpired checks if the expiration date was reached.
func (p Post) IsExpired() bool {
	return !p.ExpireAt.IsZero() && p.ExpireAt.Before(time.Now())
}

// ParseStringToAddresses parses a string addresses.
// String should have one or more lines where each line should contain an address.
// Addresses are validated after being parsed.
func ParseStringToAddresses(s string) (AddressList, error) {
	var addresses AddressList
	for _, line := range strings.Split(s, "\n") {
		line = strings.TrimSpace(line)
		if line == "" {
			// Skip empty lines
			continue
		}

		addr := std.Address(strings.TrimSpace(line))
		if !addr.IsValid() {
			return nil, errors.New("invalid address: " + EscapeHTML(addr.String()))
		}

		addresses = append(addresses, addr)
	}
	return addresses, nil
}

// MustParseStringToAddresses parses a string addresses.
// String should have one or more lines where each line should contain an address.
// Addresses are validated after being parsed.
func MustParseStringToAddresses(s string) AddressList {
	addresses, err := ParseStringToAddresses(s)
	if err != nil {
		panic(err.Error())
	}
	return addresses
}

// EscapeHTML escapes special characters like "<" to become "&lt;".
// It escapes only five such characters: <, >, &, ' and ".
func EscapeHTML(s string) string {
	s = strings.ReplaceAll(s, `&`, "&amp;")
	s = strings.ReplaceAll(s, `"`, "&#34;")
	s = strings.ReplaceAll(s, `'`, "&#39;")
	s = strings.ReplaceAll(s, `<`, "&lt;")
	return strings.ReplaceAll(s, `>`, "&gt;")
}

package dao

import (
	"errors"
	"std"
	"strings"
	"time"
)

// PathSeparator defines the DAO path separator.
const PathSeparator = "/"

type (
	// Role defines the type for DAO roles.
	Role string

	// Roles defines the type for a list of DAO roles.
	Roles []Role
)

// String returns the role as a string.
func (r Role) String() string {
	return string(r)
}

// NewMember creates a new DAO member.
func NewMember(addr std.Address, roles ...Role) Member {
	return Member{
		Address: addr,
		Roles:   roles,
	}
}

// Member defines a DAO member.
type Member struct {
	// Address is the member account address.
	Address std.Address

	// Roles contains the optional list of roles that the member belongs to.
	Roles Roles
}

// String returns a string representation of the member.
func (m Member) String() string {
	if len(m.Roles) == 0 {
		return m.Address.String()
	}

	var roles []string
	for _, r := range m.Roles {
		roles = append(roles, string(r))
	}
	return m.Address.String() + " " + strings.Join(roles, ", ")
}

// HasRole checks if the member belongs to a specific role.
func (m Member) HasRole(r Role) bool {
	for _, role := range m.Roles {
		if role == r {
			return true
		}
	}
	return false
}

// Option configures DAO.
type Option func(*DAO)

// AssignAsSuperCouncil makes the DAO a super council.
func AssignAsSuperCouncil() Option {
	return func(dao *DAO) {
		dao.isSuperCouncil = true
	}
}

// WithSubDAO assigns sub DAO to a DAO.
func WithSubDAO(sub *DAO) Option {
	return func(dao *DAO) {
		sub.parent = dao
		dao.children = append(dao.children, sub)
	}
}

// WithMembers assigns members to a DAO.
func WithMembers(members ...Member) Option {
	return func(dao *DAO) {
		dao.members = members
	}
}

// WithManifest assigns a manifest to a DAO.
// Manifest should describe the purpose of the DAO.
func WithManifest(manifest string) Option {
	return func(dao *DAO) {
		dao.manifest = manifest
	}
}

// New creates a new DAO.
func New(name, title string, options ...Option) (*DAO, error) {
	name = strings.TrimSpace(name)
	if name == "" {
		return nil, errors.New("DAO name is required")
	}

	if !IsSlug(name) {
		return nil, errors.New(`DAO name is not valid, only letters from "a" to "z", numbers, "-" and "_" are allowed`)
	}

	title = strings.TrimSpace(title)
	if title == "" {
		return nil, errors.New("DAO title is required")
	}

	dao := &DAO{
		name:      name,
		title:     title,
		createdAt: time.Now(),
	}

	for _, apply := range options {
		apply(dao)
	}

	return dao, nil
}

// MustNew creates a new DAO.
// The function panics if any of the arguments is not valid.
func MustNew(name, title string, options ...Option) *DAO {
	dao, err := New(name, title, options...)
	if err != nil {
		panic(err)
	}
	return dao
}

// DAO is a decentralized autonomous organization.
type DAO struct {
	name           string
	title          string
	manifest       string
	isSuperCouncil bool
	isLocked       bool
	lockReason     string
	parent         *DAO
	children       []*DAO
	members        []Member
	createdAt      time.Time
}

// Name returns the name of the DAO.
func (dao DAO) Name() string {
	return dao.name
}

// Title returns the title of the DAO.
func (dao DAO) Title() string {
	return dao.title
}

// Manifest returns the manifest of the DAO.
func (dao DAO) Manifest() string {
	return dao.manifest
}

// SetManifest sets the manifest of the DAO.
func (dao *DAO) SetManifest(s string) {
	dao.manifest = s
}

// CreatedAt returns the creation time of the DAO.
func (dao DAO) CreatedAt() time.Time {
	return dao.createdAt
}

// Parent returns the parent DAO of the sub DAO.
// The result is nil for the DAO at the root of the DAO tree.
func (dao DAO) Parent() *DAO {
	return dao.parent
}

// Path returns the path of the DAO.
func (dao DAO) Path() string {
	if dao.parent == nil {
		return dao.name
	}
	return dao.parent.Path() + PathSeparator + dao.name
}

// SubDAOs returns the first level sub DAOs.
func (dao DAO) SubDAOs() []*DAO {
	return dao.children
}

// Members returns the members of the DAOs.
func (dao DAO) Members() []Member {
	return dao.members
}

// LockReason returns a string with the reason the DAO is locked.
func (dao DAO) LockReason() string {
	return dao.lockReason
}

// IsSuperCouncil checks if the DAO is a super council.
func (dao DAO) IsSuperCouncil() bool {
	return dao.isSuperCouncil
}

// IsLocked checks if the DAO is locked.
func (dao DAO) IsLocked() bool {
	return dao.isLocked
}

// Lock locks the DAO.
func (dao *DAO) Lock(reason string) {
	dao.lockReason = reason
	dao.isLocked = true
}

// HasParent checks if a DAO is a parent of this DAO.
func (dao DAO) HasParent(parent *DAO) bool {
	if parent == nil {
		return false
	}
	return strings.HasPrefix(dao.Path(), parent.Path())
}

// HasMember checks if a member is part of the DAO.
func (dao DAO) HasMember(addr std.Address) bool {
	for _, m := range dao.members {
		if m.Address == addr {
			return true
		}
	}
	return false
}

// AddMember adds a member to the DAO.
// Caller must check the member before adding to avoid duplications.
func (dao *DAO) AddMember(m Member) {
	dao.members = append(dao.members, m)
}

// GetMember gets a member of the DAO.
func (dao DAO) GetMember(addr std.Address) (Member, bool) {
	for _, m := range dao.members {
		if m.Address == addr {
			return m, true
		}
	}
	return Member{}, false
}

// RemoveMember removes a member of the DAO.
func (dao *DAO) RemoveMember(addr std.Address) bool {
	for i, m := range dao.members {
		if m.Address == addr {
			dao.members = append(dao.members[:i], dao.members[i+1:]...)
			return true
		}
	}
	return false
}

// AddSubDAO adds a sub DAO to the DAO.
func (dao *DAO) AddSubDAO(sub *DAO) bool {
	if sub == nil {
		return false
	}

	for _, n := range dao.children {
		if n.name == sub.name {
			return false
		}
	}

	sub.parent = dao
	dao.children = append(dao.children, sub)
	return true
}

// GetDAO get a DAO by path.
func (dao *DAO) GetDAO(path string) (_ *DAO, found bool) {
	if path == "" {
		return nil, false
	}

	if path == dao.name {
		return dao, true
	}

	// Make sure that current node is not present at the beginning of the path
	path = strings.TrimPrefix(path, dao.name+PathSeparator)

	// Split DAO path in child name and relative sub path
	parts := strings.SplitN(path, PathSeparator, 2)
	childName := parts[0]

	for _, sub := range dao.children {
		if sub.name != childName {
			continue
		}

		if len(parts) > 1 {
			// Traverse node children when a sub node path is available
			return sub.GetDAO(parts[1])
		}
		return sub, true
	}

	return nil, false
}

// RemoveSubDAO removes a sub DAO.
// The sub DAO must be a first level children of the DAO.
func (dao *DAO) RemoveSubDAO(name string) bool {
	for i, sub := range dao.children {
		if sub.name == name {
			dao.children = append(dao.children[:i], dao.children[i+1:]...)
			return true
		}
	}
	return false
}

// IsRoot checks if the DAO is the main DAO.
// The main DAO is the root of the DAO tree.
func (dao DAO) IsRoot() bool {
	return dao.parent == nil
}

// ParseStringToMembers parses a string of member addresses and roles.
// String should have one or more lines where each line should contain an
// address optionally followed by one or more roles.
// Example multi line string:
//
//	g1hy6zry03hg5d8le9s2w4fxme6236hkgd928dun roleA
//	g1e5hud66rs7ye4zgeqmqvwfhurs2mnf408hdqcl
//	g1vh7krmmzfua5xjmkatvmx09z37w34lsvd2mxa5 roleB roleA
//
// Addresses are validated after being parsed.
// Roles must be validated by the caller to make sure the names are valid.
func ParseStringToMembers(s string) ([]Member, error) {
	var members []Member
	for _, line := range strings.Split(s, "\n") {
		line = strings.TrimSpace(line)
		if line == "" {
			// Skip empty lines
			continue
		}

		var (
			roles  []Role
			fields = strings.Fields(line)
			addr   = std.Address(strings.TrimSpace(fields[0]))
		)

		if !addr.IsValid() {
			return nil, errors.New("invalid member address: " + EscapeHTML(addr.String()))
		}

		for _, v := range fields[1:] {
			roles = appendRole(roles, strings.TrimSpace(v))
		}

		members = append(members, NewMember(addr, roles...))
	}
	return members, nil
}

// MustParseStringToMembers parses a string of member addresses and roles.
// String should have one or more lines where each line should contain an
// address optionally followed by one or more roles.
// Example multi line string:
//
//	g1hy6zry03hg5d8le9s2w4fxme6236hkgd928dun roleA
//	g1e5hud66rs7ye4zgeqmqvwfhurs2mnf408hdqcl
//	g1vh7krmmzfua5xjmkatvmx09z37w34lsvd2mxa5 roleB roleA
//
// Addresses are validated after being parsed.
// Roles must be validated by the caller to make sure the names are valid.
func MustParseStringToMembers(s string) []Member {
	members, err := ParseStringToMembers(s)
	if err != nil {
		panic(err.Error())
	}
	return members
}

// appendRole append a role if it doesn't exists within the list of roles.
func appendRole(roles []Role, name string) []Role {
	for _, r := range roles {
		if string(r) == name {
			return roles
		}
	}
	return append(roles, Role(name))
}

package dao

import (
	"std"
	"testing"

	"gno.land/p/demo/testutils"

	gnome "gno.land/p/gnome/dao"
)

func TestMember(t *testing.T) {
	cases := []struct {
		name    string
		address std.Address
		roles   []gnome.Role
		output  string
	}{
		{
			name:    "without roles",
			address: std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5"),
			output:  "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5",
		},
		{
			name:    "with one role",
			address: std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5"),
			roles:   []gnome.Role{"foo"},
			output:  "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5 foo",
		},
		{
			name:    "with two roles",
			address: std.Address("g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5"),
			roles:   []gnome.Role{"foo", "bar"},
			output:  "g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5 foo, bar",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Act
			m := gnome.NewMember(tc.address, tc.roles...)

			// Assert
			if got := m.Address; got != tc.address {
				t.Fatalf("expected address %s, got: %s", tc.address, got)
			}

			for i, r := range m.Roles {
				if r != tc.roles[i] {
					t.Fatalf("expected role %s, got: %s", tc.roles[i], r)
				}
			}

			if got := m.String(); got != tc.output {
				t.Fatalf("expected member string output '%s', got: '%s'", tc.output, got)
			}
		})
	}
}

// TODO: Add test cases to check different DAO options
func TestDAO(t *testing.T) {
	// Arrange
	name := "test"
	title := "Test DAO"
	manifest := "This is a test"
	addresses := []std.Address{
		testutils.TestAddress("member1"),
		testutils.TestAddress("member2"),
	}

	// Act
	dao := gnome.MustNew(name, title, gnome.WithManifest(manifest), gnome.WithMembers(
		gnome.NewMember(addresses[0]),
		gnome.NewMember(addresses[1]),
	))

	// Assert
	if got := dao.Name(); got != name {
		t.Fatalf("expected name: %d, got: %d", name, got)
	}

	if got := dao.CreatedAt(); got.IsZero() {
		t.Fatalf("expected a valid creation time, got: '%s'", got.String())
	}

	if got := dao.Title(); got != title {
		t.Fatalf("expected title: '%s', got: '%s'", title, got)
	}

	if got := dao.Manifest(); got != manifest {
		t.Fatalf("expected manifest: '%s', got: '%s'", manifest, got)
	}

	if got := dao.Parent(); got != nil {
		t.Fatalf("expected no parent DAO, got: '%s'", got.Name())
	}

	if c := len(dao.SubDAOs()); c != 0 {
		t.Fatalf("expected no sub DAO nodes, got %d node(s)", c)
	}

	if dao.IsSuperCouncil() {
		t.Fatal("expected DAO not to be a super council")
	}

	if c := len(dao.Members()); c != len(addresses) {
		t.Fatalf("expected %d DAO members, got %d", len(addresses), c)
	}

	for _, addr := range addresses {
		if !dao.HasMember(addr) {
			t.Fatalf("expected member %s to be a member of DAO", addr)
		}

		m, found := dao.GetMember(addr)
		if !found {
			t.Fatalf("expected member %s to be found", addr)
		}

		if m.Address != addr {
			t.Fatalf("expected member to have address %s, got: %s", addr, m.Address)
		}
	}
}

func TestDAOAddMember(t *testing.T) {
	cases := []struct {
		name         string
		member       gnome.Member
		membersCount int
		shouldExist  bool
		setup        func(*gnome.DAO)
	}{
		{
			name:         "ok",
			member:       newTestMember(t, "member"),
			membersCount: 1,
			shouldExist:  true,
		},
		{
			name:         "existing",
			member:       newTestMember(t, "member"),
			membersCount: 2,
			shouldExist:  true,
			setup: func(dao *gnome.DAO) {
				dao.AddMember(newTestMember(t, "member2"))
			},
		},
		{
			name:         "duplicate",
			member:       newTestMember(t, "member"),
			membersCount: 2,
			shouldExist:  true,
			setup: func(dao *gnome.DAO) {
				dao.AddMember(newTestMember(t, "member"))
			},
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			dao := gnome.MustNew("test", "Test")

			if tc.setup != nil {
				tc.setup(dao)
			}

			// Act
			dao.AddMember(tc.member)

			// Assert
			if got := dao.HasMember(tc.member.Address); got != tc.shouldExist {
				t.Fatalf("expected has member call to return %v, got: %v", tc.shouldExist, got)
			}

			m, found := dao.GetMember(tc.member.Address)
			if found != tc.shouldExist {
				t.Fatalf("expected member getter to return %v, got: %v", tc.shouldExist, found)
			}

			if tc.shouldExist && m.Address != tc.member.Address {
				t.Fatalf("expected added member to have adderss %s, got: %s", tc.member, m)
			}

			members := dao.Members()
			if c := len(members); c != tc.membersCount {
				t.Fatalf("expected %d member(s), got: %d", tc.membersCount, c)
			}

			if len(members) > 0 {
				m = members[len(members)-1]
				if m.Address != tc.member.Address {
					t.Fatalf("expected last added member address: %s, got: %s", tc.member.Address, m.Address)
				}
			}
		})
	}
}

func TestDAORemoveMember(t *testing.T) {
	cases := []struct {
		name   string
		member gnome.Member
		setup  func(*gnome.DAO)
		result bool
	}{
		{
			name:   "ok",
			member: newTestMember(t, "member"),
			result: true,
			setup: func(dao *gnome.DAO) {
				dao.AddMember(newTestMember(t, "member"))
			},
		},
		{
			name:   "missing",
			member: newTestMember(t, "member"),
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			dao := gnome.MustNew("test", "Test")

			if tc.setup != nil {
				tc.setup(dao)
			}

			// Act
			result := dao.RemoveMember(tc.member.Address)

			// Assert
			if result != tc.result {
				t.Fatalf("expected result to be %v, got: %v", tc.result, result)
			}

			if dao.HasMember(tc.member.Address) {
				t.Fatal("member shouldn't exist")
			}

			if _, found := dao.GetMember(tc.member.Address); found {
				t.Fatal("expected member getter to return false")
			}

			if c := len(dao.Members()); c != 0 {
				t.Fatalf("expected no DAO members, got: %d", c)
			}
		})
	}
}

func TestDAOAddSubDAO(t *testing.T) {
	cases := []struct {
		name, path  string
		children    int
		dao, subDAO *gnome.DAO
		result      bool
		setup       func(*gnome.DAO)
	}{
		{
			name:     "ok",
			dao:      gnome.MustNew("main", "Main"),
			subDAO:   gnome.MustNew("foo", "Foo"),
			children: 1,
			path:     "main/foo",
			result:   true,
		},
		{
			name: "with children",
			dao: gnome.MustNew(
				"main",
				"Main",
				gnome.WithSubDAO(gnome.MustNew("bar", "Bar")),
			),
			subDAO:   gnome.MustNew("foo", "Foo"),
			children: 2,
			path:     "main/foo",
			result:   true,
		},
		{
			name: "duplicate",
			dao: gnome.MustNew(
				"main",
				"Main",
				gnome.WithSubDAO(gnome.MustNew("foo", "Foo")),
			),
			subDAO: gnome.MustNew("foo", "Foo"),
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Act
			result := tc.dao.AddSubDAO(tc.subDAO)

			// Assert
			if result != tc.result {
				t.Fatalf("expected result to be %v, got: %v", tc.result, result)
			}

			if result {
				if got := tc.subDAO.Path(); got != tc.path {
					t.Fatalf("expected path to be '%s', got: '%s'", tc.path, got)
				}

				if c := len(tc.dao.SubDAOs()); c != tc.children {
					t.Fatalf("expected %d sub DAO node(s), got %d node(s)", tc.children, c)
				}
			}
		})
	}
}

