	return x.index.Set(dao.Path(), dao)
}

// GetByPath gets a DAO by its path.
func (x daoIndex) GetByPath(path string) (*gnome.DAO, bool) {
	if v, ok := x.index.Get(path); ok {
		return v.(*gnome.DAO), true
	}
	return nil, false
}

// HasPathKey checks if a key with a DAO path exists.
func (x daoIndex) HasPathKey(path string) bool {
	return x.index.Has(path)
}

type proposalIndex struct {
	index  avl.Tree // string(binary gnome.ID) -> *gnome.Proposal
	groups avl.Tree // string(DAO path) -> []*gnome.Proposal
}

// Index indexes a proposal by its ID and DAO.
func (x *proposalIndex) Index(p *gnome.Proposal) {
	x.IndexByID(p)
	x.IndexByDAO(p)
}

// IndexByID indexes a proposal by its ID.
func (x *proposalIndex) IndexByID(p *gnome.Proposal) bool {
	return x.index.Set(p.ID().Key(), p)
}

// IndexByDAO indexes a proposal for a DAO.
func (x *proposalIndex) IndexByDAO(p *gnome.Proposal) bool {
	daoPath := p.DAO().Path()
	proposals := x.GetAllByDAO(daoPath)
	proposals = append([]*gnome.Proposal{p}, proposals...) // reverse append
	return x.groups.Set(daoPath, proposals)
}

// GetByID gets a proposal by its ID.
func (x proposalIndex) GetByID(id gnome.ID) (*gnome.Proposal, bool) {
	if v, exists := x.index.Get(id.Key()); exists {
		return v.(*gnome.Proposal), true
	}
	return nil, false
}

// GetAllByDAO gets all proposals of a DAO.
func (x proposalIndex) GetAllByDAO(daoPath string) []*gnome.Proposal {
	if v, exists := x.groups.Get(daoPath); exists {
		return v.([]*gnome.Proposal)
	}
	return nil
}

// Iterate iterates all proposals starting from the oldest one.
func (x proposalIndex) Iterate(fn gnome.ProposalIterFn) bool {
	return x.index.Iterate("", "", func(_ string, value interface{}) bool {
		return fn(value.(*gnome.Proposal))
	})
}

// ReverseIterate iterates all proposals starting from the latest one.
func (x proposalIndex) ReverseIterate(fn gnome.ProposalIterFn) bool {
	return x.index.ReverseIterate("", "", func(_ string, value interface{}) bool {
		return fn(value.(*gnome.Proposal))
	})
}

package gnome

import (
	"time"

	gnome "gno.land/p/gnome/dao"
)

// Day defines the duration of a day.
const Day = time.Hour * 24

// Names for the different strategy types.
const (
	StrategyNameSubDAOCreation         = "sub-dao-creation"
	StrategyNameSubDAODismissal        = "sub-dao-dismissal"
	StrategyNameDAOMembersModification = "dao-members-modification"
	StrategyNameBudget                 = "budget"
	StrategyNameGeneral                = "general"
	StrategyNameLocking                = "locking"
	StrategyNameParamsUpdate           = "params-update"
)

var parameters struct {
	// VotingPeriods contains the current voting period for each proposal type.
	VotingPeriods gnome.DurationParams

	// ReviewDeadline defines the time after which a proposal can't be withdrawed by the proposer.
	// Proposal can only be voted on after this deadline but not before.
	// This greace period gives the proposer the chance to withdraw a proposal if there is a mistake.
	ReviewDeadline time.Duration
}

func init() {
	// Initial voting periods for each proposal type.
	// Periods can be changed by sumitting a params update proposal.
	parameters.VotingPeriods.Set(StrategyNameSubDAOCreation, time.Minute*10)
	parameters.VotingPeriods.Set(StrategyNameSubDAODismissal, Day*7)
	parameters.VotingPeriods.Set(StrategyNameDAOMembersModification, time.Minute*30)
	parameters.VotingPeriods.Set(StrategyNameBudget, Day*7)
	parameters.VotingPeriods.Set(StrategyNameGeneral, Day*2)
	parameters.VotingPeriods.Set(StrategyNameLocking, Day*2)
	parameters.VotingPeriods.Set(StrategyNameParamsUpdate, time.Minute*10)

	// Initial review deadline
	parameters.ReviewDeadline = time.Second
}

package gnome

import (
	"std"
	"strings"

	gnome "gno.land/p/gnome/dao"
	router "gno.land/p/gnome/router/v1"
)

// Render returns a Markdown string with DAO or proposal details.
// By default it renders the Council DAO details view.
//
// Paths:
//   - `dao/DAO_PATH` => Renders DAO or sub DAO details
//   - `proposal/PROPOSAL_ID` => Renders details for a proposal
//   - `proposals/DAO_PATH` => Renders the list of proposals for a DAO
func Render(path string) string {
	r := router.New()

	r.HandleFunc("", renderDAO)
	r.HandleFunc("dao", renderDAO)
	r.HandleFunc("proposal", renderProposal)
	r.HandleFunc("proposals", renderProposals)
	r.HandleFunc("params", renderParams)

	// Render global alerts before proposal states are updated within the handlers
	return renderAlerts() + r.Render(path)
}

// GetDAO returns an invariant reference to a DAO.
// Council DAO is returned when path is empty.
func GetDAO(path string) gnome.InvarDAO {
	if path == "" {
		path = nameCouncilDAO
	}

	dao := mustGetDAO(path)
	return gnome.NewInvarDAO(dao)
}

// IterateProposals iterates DAO proposals by ascending IDs.
func IterateProposals(fn func(gnome.InvarProposal) bool) {
	proposals.Iterate(func(p *gnome.Proposal) bool {
		return fn(gnome.NewInvarProposal(p))
	})
}

// WithdrawProposal withdraws a proposal.
// Proposals can only be withdrawed by the account that creates it when the state is "review".
// They can't be withdrawed once the review deadline of one hour after creation is met.
func WithdrawProposal(proposalID uint64) string {
	assertDAOIsNotLocked()

	p := mustGetProposal(proposalID)
	assertCallerCanWithdraw(p)

	if err := p.Withdraw(); err != nil {
		panic(err)
	}

	AdvanceProposals()

	return "Proposal withdrawed"
}

// Vote submits a vote for a proposal.
//
// Parameters:
// - proposalID: ID of the proposal to vote (required)
// - vote: Voting choice, true=Yes, false=No (required)
// - reason: Text with the reason for the vote
// - daoPath: Path of the DAO where the voting account belongs to
//
// Reason is in general optional but might be required for some proposals when voting No.
//
// DAO name is optional and by default is the one that the proposal belongs to.
// Only parents of the proposal's DAO are allowed as `daoPath` values.
// Child votes are not tallied when a member of a parent DAO votes on a child's proposal.
func Vote(proposalID uint64, vote bool, reason, daoPath string) string {
	assertDAOIsNotLocked()

	// Make sure proposal states are up to date before submitting the vote
	AdvanceProposals()

	// Get proposal and check that current status accepts votes
	p := mustGetProposal(proposalID)
	if s := p.Status(); s.IsFinal() {
		panic("proposal status doesn't allow new vote submissions: " + s.String())
	}

	// When a DAO name is availalable check that it matches one of the proposal's DAO parents
	// and if so promote the proposal to a parent DAO. Promoting a proposal invalidates the votes
	// submitted by current DAO's members and moves voting responsibility to the parent DAO members.
	daoPath = strings.TrimSpace(daoPath)
	if daoPath != "" && p.DAO().Path() != daoPath {
		// Check that the path belongs to a parent DAO.
		// Path separator is added to the prefix to make sure that similar prefixes don't match.
		if !strings.HasPrefix(p.DAO().Path(), daoPath+gnome.PathSeparator) {
			panic(`path "` + daoPath + `" is not a parent of the proposal's DAO path`)
		}

		// Promote the active proposal's DAO to a parent DAO
		parentDAO := mustGetDAO(daoPath)
		if err := p.Promote(parentDAO); err != nil {
			panic(err)
		}

		// Reindex the proposal so its available under the parent DAO proposals. Child DAO will also
		// keep the promoted proposal indexed so it can be listed within the child DAO's proposals.
		proposals.Index(p)
	}

	// When proposal has "review" status check if deadline is met and if so activate it
	if p.Status() == gnome.StatusReview {
		if !p.HasReviewDeadlinePassed() {
			panic("votes are not allowed until " + p.ReviewDeadline().UTC().Format("2006-01-02 15:04 MST"))
		}

		if err := p.Activate(); err != nil {
			panic(err)
		}
	}

	var choice gnome.VoteChoice
	if vote {
		choice = gnome.ChoiceYes
	} else {
		choice = gnome.ChoiceNo
	}

	// Submit vote
	caller := std.GetOrigCaller() // TODO: Check that caller is member of the DAO
	err := p.Vote(caller, gnome.VoteChoice(choice), reason)
	if err != nil {
		panic(err)
	}

	return "Vote submitted for proposal " + makeProposalURI(gnome.ID(proposalID), false)
}

// AdvanceProposals iterates review and active proposals and tallies proposals that met their deadlines.
// Proposals in review status are activated to allow voting.
// Active proposals are tallied which means the number of votes is counted and status changed accordingly.
// Active executable proposals are executed when the proposal status changes to "passed".
func AdvanceProposals() string {
	assertDAOIsNotLocked()

	advanceProposals()

	return "Proposals advanced for realm " + makeRealmURL("")
}

// IsProposalsAdvanceNeeded checks if a call to `AdvanceProposals()` is required to update proposals.
func IsProposalsAdvanceNeeded() bool {
	if gnomeDAO.IsLocked() {
		return false
	}

	return proposals.ReverseIterate(func(p *gnome.Proposal) bool {
		switch p.Status() {
		case gnome.StatusReview:
			if p.HasReviewDeadlinePassed() {
				return true
			}
		case gnome.StatusActive:
			if p.HasVotingDeadlinePassed() {
				return true
			}
		}
		return false
	})
}

func advanceProposals() {
	// TODO: Use unix timestamp as part of proposal IDs to avoid iterating older tallied proposals
	proposals.Iterate(func(p *gnome.Proposal) bool {
		status := p.Status()
		if status == gnome.StatusReview && p.HasReviewDeadlinePassed() {
			p.Activate()
			status = p.Status()
		}

		if p.Status() == gnome.StatusActive && p.HasVotingDeadlinePassed() {
			p.Tally()

			// Change proposal status to failed when execution fails
			if err := p.Execute(); gnome.IsExecutionError(err) {
				p.Fail("failed due to conflicts: " + err.Error())
			}
		}
		return false
	})
}

func mustGetProposal(id uint64) *gnome.Proposal {
	p, found := proposals.GetByID(gnome.ID(id))
	if !found {
		panic("proposal not found")
	}
	return p
}

func assertCallerCanWithdraw(p *gnome.Proposal) {
	if p.Proposer() != std.GetOrigCaller() {
		panic("proposals can only be withdrawed by the proposer")
	}

	if p.Status() != gnome.StatusReview {
		panic(`proposals can only be withdrawed when status is "review"`)
	} else if p.HasReviewDeadlinePassed() {
		panic("withdrawal not allowed, withdrawal deadline expired")
	}
}

package gnome

import (
	"std"
	"strings"
	"time"

	gnome "gno.land/p/gnome/dao"
)

// SubmitCustomProposal submits a new proposal of a custom type.
//
// This function allows other realms to submit custom proposal types.
//
// Parameters:
// - title: A title for the proposal (required)
// - description: A description of the proposal
// - strategy: A strategy for the new proposal (required)
// - daoPath: Path of the DAO where the proposal should be created (required)
func SubmitCustomProposal(title, description string, s gnome.ProposalStrategy, daoPath string) gnome.ID {
	assertDAOIsNotLocked()

	dao := mustGetDAO(daoPath)
	assertDAOIsNotDismissed(dao)

	caller := std.GetOrigCaller()
	assertCanCreateProposal(caller, dao)

	id := genProposalID()
	p, err := gnome.NewProposal(
		id,
		s,
		caller,
		dao,
		title,
		gnome.WithDescription(description),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	return p.ID()
}

// SubmitGeneralProposal submits a new general proposal.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal (required)
// - daoPath: Path of the DAO where the proposal should be created (required)
// - votingDeadline: Number of days until the voting period ends
//
// The name of the DAO where the proposal is created is a slug, where "council"
// is the Council DAO and "main" is the name of the Main DAO.
//
// The voting period deadline for the proposal must be between 2 and 10 days.
// It defaults to 2 days when `votingDeadline` value is 0.
func SubmitGeneralProposal(
	proposalTitle,
	proposalDescription,
	daoPath string,
	votingDeadline uint,
) uint64 {
	assertDAOIsNotLocked()

	opts := []gnome.ProposalOption{
		gnome.WithDescription(proposalDescription),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	}

	if votingDeadline != 0 {
		if votingDeadline < 2 || votingDeadline > 10 {
			panic("voting period deadline must be between 2 and 10 days")
		}

		deadline := time.Now().Add(time.Hour * 24 * time.Duration(votingDeadline))
		opts = append(opts, gnome.WithVotingDeadline(deadline))
	}

	dao := mustGetDAO(daoPath)
	assertDAOIsNotDismissed(dao)

	caller := std.GetOrigCaller()
	assertCanCreateProposal(caller, dao)

	p, err := gnome.NewProposal(genProposalID(), newGeneralStrategy(), caller, dao, proposalTitle, opts...)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

// SubmitSubDAOCreationProposal submits a new proposal to add a sub DAO to an existing DAO.
//
// Proposal requires the participation of all DAO members, otherwise the outcome will be low participation.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - parentDAOPath: Path of the sub DAO's parent (required)
// - subDAOName: Slug name of the new sub DAO (required)
// - subDAOTitle: A title for the new sub DAO (required)
// - subDAOManifest: Sub DAO manifest (required)
// - subDAOMembers: List of sub DAO member addresses (required)
//
// Sub DAO name must be a slug allows letters from "a" to "z", numbers, "-" and "_" as valid characters.
//
// The list of sub DAO members must be a newline separated list of addresses, with a minimum of 2 addresses.
// Each line must contain an address and optionally be followed by one or more DAO member roles:
// ```
// g187982000zsc493znqt828s90cmp6hcp2erhu6m foo
// g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5 bar foo
// ```
func SubmitSubDAOCreationProposal(
	proposalTitle,
	proposalDescription,
	parentDAOPath,
	subDAOName,
	subDAOTitle,
	subDAOManifest,
	subDAOMembers string,
) uint64 {
	assertDAOIsNotLocked()

	dao := mustGetDAO(parentDAOPath)
	assertDAOIsNotDismissed(dao)

	subDAOPath := dao.Path() + gnome.PathSeparator + subDAOName
	if daos.HasPathKey(subDAOPath) {
		panic("sub DAO name is already taken by another DAO")
	}

	caller := std.GetOrigCaller()
	assertCanCreateProposal(caller, dao)

	members := gnome.MustParseStringToMembers(subDAOMembers)
	strategy := newSubDAOCreationStrategy(daos, subDAOName, subDAOTitle, subDAOManifest, members)
	p, err := gnome.NewProposal(
		genProposalID(),
		strategy,
		caller,
		dao,
		proposalTitle,
		gnome.WithDescription(proposalDescription),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

// SubmitSubDAODismissalProposal submits a new proposal to dismiss a sub DAO.
//
// Dismissing a sub DAO also dismisses all active proposals and any sub DAO below the dismissed DAO tree.
// Only the direct parent of a DAO can create a proposal to dismiss any of its fist level sub DAOs.
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by plurality.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - daoPath: Path of the DAO where the proposal should be created (required)
// - subDAOName: Slug name of the sub DAO to dismiss (required)
func SubmitSubDAODismissalProposal(proposalTitle, daoPath, subDAOName string) uint64 {
	assertDAOIsNotLocked()

	dao := mustGetDAO(daoPath)
	assertDAOIsNotDismissed(dao)

	subDAOPath := dao.Path() + gnome.PathSeparator + subDAOName
	subDAO := mustGetDAO(subDAOPath)
	assertDAOIsNotDismissed(subDAO)

	caller := std.GetOrigCaller()
	strategy := newSubDAODismissalStrategy(subDAO, proposals)
	p, err := gnome.NewProposal(
		genProposalID(),
		strategy,
		caller,
		dao,
		proposalTitle,
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

// SubmitDAOMembersModificationProposal submits a new proposal to modify the members of a DAO.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by super majority with a 2/3s threshold. Abstentions are not considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - daoPath: Path of the DAO where the proposal should be created (required)
// - newMembers: List of member addresses to add to Main DAO
// - removeMembers: List of member addresses to remove from the Main DAO
//
// At leat one member address is required either to be added or removed from the DAO.
// Members can be added and removed within the same proposal.
//
// Each list of members must be newline separated list of addresses.
// Each line must contain an address and optionally be followed by one or more DAO member roles:
// ```
// g187982000zsc493znqt828s90cmp6hcp2erhu6m foo
// g1jg8mtutu9khhfwc4nxmuhcpftf0pajdhfvsqf5 bar foo
// ```
func SubmitDAOMembersModificationProposal(
	proposalTitle,
	proposalDescription,
	daoPath,
	newMembers,
	removeMembers string,
) uint64 {
	assertDAOIsNotLocked()

	dao := mustGetDAO(daoPath)
	assertDAOIsNotDismissed(dao)

	caller := std.GetOrigCaller()
	assertCanCreateProposal(caller, dao)

	strategy := newDAOMembersModificationStrategy(
		gnome.MustParseStringToMembers(newMembers),
		gnome.MustParseStringToMembers(removeMembers),
	)
	p, err := gnome.NewProposal(
		genProposalID(),
		strategy,
		caller,
		dao,
		proposalTitle,
		gnome.WithDescription(proposalDescription),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

// SubmitBudgetProposal submits a new budget proposal.
//
// Only membes of the Council or Main DAO can vote on this type of proposals.
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - daoPath: Path of the DAO where the proposal should be created (required)
// - budget: The proposal budget (required)
//
// Budget doesn't enforce any specific format right now but an example format that
// could be used is amount plus symbol, for example 100UGNOT, 100000USD, etc.
func SubmitBudgetProposal(
	proposalTitle,
	proposalDescription,
	daoPath,
	budget string,
) uint64 {
	assertDAOIsNotLocked()

	dao := mustGetDAO(daoPath)
	assertDAOIsNotDismissed(dao)

	caller := std.GetOrigCaller()
	assertCanCreateProposal(caller, dao)

	strategy := newBudgetStrategy(gnomeDAO, budget)
	p, err := gnome.NewProposal(
		genProposalID(),
		strategy,
		caller,
		dao,
		proposalTitle,
		gnome.WithDescription(proposalDescription),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

// SubmitDAOLockingProposal submits a new proposal to lock the DAO.
//
// Locking the DAO "freezes the state" by disallowing further modifications.
// State must be locked to migrate the realm to a newer version.
//
// Proposal requires a 33% quorum, otherwise the outcome will be low participation.
// This type of proposal can only be created by the Council or Main DAO members.
// Tally is done by plurality.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - daoPath: Path of the DAO where the proposal should be created (required)
// - reason: Text with the DAO locking reason
//
// The optional `reason` argument can contain HTML.
func SubmitDAOLockingProposal(
	proposalTitle,
	proposalDescription,
	daoPath,
	reason string,
) uint64 {
	assertDAOIsNotLocked()

	caller := std.GetOrigCaller()
	assertIsCouncilOrMainDAOMember(caller)

	dao := mustGetDAO(daoPath)
	assertIsCouncilOrMainDAO(dao)

	reason = strings.TrimSpace(reason)
	strategy := newLockingStrategy(gnomeDAO, reason, func() error {
		// Advance all proposals before locking the DAO
		advanceProposals()
		return nil
	})

	p, err := gnome.NewProposal(
		genProposalID(),
		strategy,
		caller,
		dao,
		proposalTitle,
		gnome.WithDescription(proposalDescription),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

// SubmitParamsUpdateProposal submits a new proposal to update one or more realm parameters.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - daoPath: Path of the DAO where the proposal should be created (required)
// - proposalReviewDeadline: Number of seconds where proposals can be withdrawed
// - votingPeriodSubDAOCreation: Voting period for sub DAO creation proposals
// - votingPeriodSubDAODismissal: Voting period for sub DAO dismissal proposals
// - votingPeriodDAOMembersModification: Voting period for DAO members modification proposals
// - votingPeriodBudget: Voting period for budget proposals
// - votingPeriodGeneral: Voting period for general proposals
// - votingPeriodLocking: Voting period for locking proposals
// - votingPeriodParamsUpdate: Voting period for parameters update proposals
//
// Voting period is the number of days that members can vote on a proposal
// At least one parameter value is required for creating a proposal.
func SubmitParamsUpdateProposal(
	proposalTitle,
	proposalDescription,
	daoPath string,
	proposalReviewDeadline,
	votingPeriodSubDAOCreation,
	votingPeriodSubDAODismissal,
	votingPeriodDAOMembersModification,
	votingPeriodBudget,
	votingPeriodGeneral,
	votingPeriodLocking,
	votingPeriodParamsUpdate int,
) uint64 {
	assertDAOIsNotLocked()

	dao := mustGetDAO(daoPath)
	assertDAOIsNotDismissed(dao)

	caller := std.GetOrigCaller()
	assertCanCreateProposal(caller, dao)

	strategy := paramsUpdateStrategy{
		reviewDeadline: time.Second * time.Duration(proposalReviewDeadline),
	}

	if votingPeriodSubDAOCreation > 0 {
		period := time.Duration(votingPeriodSubDAOCreation) * Day
		strategy.votingPeriods.Set(StrategyNameBudget, period)
	}

	if votingPeriodSubDAODismissal > 0 {
		period := time.Duration(votingPeriodSubDAODismissal) * Day
		strategy.votingPeriods.Set(StrategyNameBudget, period)
	}

	if votingPeriodDAOMembersModification > 0 {
		period := time.Duration(votingPeriodDAOMembersModification) * Day
		strategy.votingPeriods.Set(StrategyNameBudget, period)
	}

	if votingPeriodBudget > 0 {
		period := time.Duration(votingPeriodBudget) * Day
		strategy.votingPeriods.Set(StrategyNameBudget, period)
	}

	if votingPeriodGeneral > 0 {
		period := time.Duration(votingPeriodGeneral) * Day
		strategy.votingPeriods.Set(StrategyNameGeneral, period)
	}

	if votingPeriodLocking > 0 {
		period := time.Duration(votingPeriodLocking) * Day
		strategy.votingPeriods.Set(StrategyNameLocking, period)
	}

	if votingPeriodParamsUpdate > 0 {
		period := time.Duration(votingPeriodParamsUpdate) * Day
		strategy.votingPeriods.Set(StrategyNameParamsUpdate, period)
	}

	if strategy.votingPeriods.Size() == 0 && strategy.reviewDeadline == 0 {
		panic("at least one parameter value must be specified")
	}

	p, err := gnome.NewProposal(
		genProposalID(),
		strategy,
		caller,
		dao,
		proposalTitle,
		gnome.WithDescription(proposalDescription),
		gnome.WithReviewDeadline(time.Now().Add(parameters.ReviewDeadline)),
	)
	if err != nil {
		panic(err)
	}

	if err := p.Validate(); err != nil {
		panic(err)
	}

	proposals.Index(p)
	AdvanceProposals()

	return uint64(p.ID())
}

func assertCanCreateProposal(proposer std.Address, dao *gnome.DAO) {
	if !dao.HasMember(proposer) {
		panic("you must be a DAO member to create a proposal")
	}
}

func assertDAOIsNotDismissed(dao *gnome.DAO) {
	// DAOs are locked when they are dismissed
	if dao.IsLocked() {
		panic("DAO is dismissed: " + dao.Path())
	}
}

func assertDAOIsNotLocked() {
	if gnomeDAO.IsLocked() {
		panic("DAO is locked")
	}
}

func assertIsCouncilOrMainDAO(dao *gnome.DAO) {
	if !dao.IsSuperCouncil() {
		// Main DAO parent must be the super council
		parentDAO := dao.Parent()
		if !parentDAO.IsSuperCouncil() {
			panic("DAO is not the council or main DAO")
		}
	}
}

func assertIsCouncilOrMainDAOMember(addr std.Address) {
	if !gnomeDAO.HasMember(addr) {
		mainDAO := gnomeDAO.SubDAOs()[0]
		if !mainDAO.HasMember(addr) {
			panic("account is not a council or main DAO member")
		}
	}
}

