func assertError(t *testing.T, expected interface{}, actual error) {
	t.Helper()

	want, ok := expected.(string)
	if !ok {
		if err, ok := expected.(error); ok {
			want = err.Error()
		}
	}

	if actual == nil {
		t.Fatalf("expected error: '%s', got no error", want)
	}

	if want != actual.Error() {
		t.Fatalf("expected error: '%s', got: '%s'", want, actual.Error())
	}
}

func assertNoError(t *testing.T, err interface{}) {
	t.Helper()

	if err == nil {
		return
	}

	actual, ok := err.(string)
	if !ok {
		if e, ok := err.(error); ok {
			actual = e.Error()
		}
	}

	if actual != "" {
		t.Fatalf("expected no error, got: '%s'", actual)
	}
}

package gnome

import (
	"errors"
	"strings"
	"time"

	gnome "gno.land/p/gnome/dao"
)

// newLockingStrategy creates a new DAO locking proposal strategy.
func newLockingStrategy(council *gnome.DAO, reason string, preLockFn func() error) lockingStrategy {
	// Locking should only be done in the council DAO
	if !council.IsSuperCouncil() {
		panic("DAO is not the council")
	}

	return lockingStrategy{
		choices:   []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo},
		council:   council,
		reason:    reason,
		preLockFn: preLockFn,
	}
}

type lockingStrategy struct {
	choices   []gnome.VoteChoice
	council   *gnome.DAO
	reason    string
	preLockFn func() error
}

// Name returns the name of the strategy.
func (lockingStrategy) Name() string {
	return StrategyNameLocking
}

// Quorum returns the minimum required percentage of DAO member votes
// required for a proposal to pass.
func (lockingStrategy) Quorum() float64 {
	return 0.33
}

// VotingPeriod returns the period that a proposal should allow voting.
func (lockingStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameLocking)
	return period
}

// VoteChoices returns the valid voting choices for the strategy.
func (s lockingStrategy) VoteChoices() []gnome.VoteChoice {
	return s.choices
}

// Tally counts the votes and returns the winner voting choice.
func (lockingStrategy) Tally(_ *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	if choice, ok := gnome.SelectChoiceByPlurality(r); ok {
		return choice
	}
	return gnome.ChoiceNone
}

// Validate validates if a proposal is valid for the current state.
func (s lockingStrategy) Validate(*gnome.Proposal) error {
	if s.council.IsLocked() {
		return errors.New("council DAO is already locked")
	}
	return nil
}

// Execute locks the council DAO.
func (s lockingStrategy) Execute(*gnome.DAO) (err error) {
	if s.preLockFn != nil {
		if err := s.preLockFn(); err != nil {
			return err
		}
	}

	s.council.Lock(s.reason)
	return nil
}

// RenderParams returns a markdown with the rendered strategy parameters.
func (s lockingStrategy) RenderParams() string {
	var b strings.Builder

	b.WriteString("|||\n|---|---|\n")
	b.WriteString("| Reason: | " + gnome.EscapeHTML(s.reason) + " |\n")

	return b.String()
}

package gnome

import (
	"errors"
	"fmt"
	"testing"
	"time"

	gnome "gno.land/p/gnome/dao"
)

func TestLockingStrategy(t *testing.T) {
	cases := []struct {
		name, err string
		setup     func() *gnome.DAO
	}{
		{
			name: "ok",
			setup: func() *gnome.DAO {
				return gnome.MustNew("council", "Council", gnome.AssignAsSuperCouncil())
			},
		},
		{
			name: "dao is not council",
			setup: func() *gnome.DAO {
				return gnome.MustNew("council", "Council")
			},
			err: "DAO is not the council",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			name := StrategyNameLocking
			quorum := 0.33
			votingPeriod := time.Hour * 24 * 2
			choices := fmt.Sprintf("%v", []gnome.VoteChoice{
				gnome.ChoiceYes,
				gnome.ChoiceNo,
			})
			councilDAO := tc.setup()

			// Act
			var s lockingStrategy
			err := handlePanic(t, func() {
				s = newLockingStrategy(councilDAO, "", nil)
			})

			// Assert
			if tc.err != "" {
				assertError(t, tc.err, err)
				return
			}

			assertNoError(t, err)

			if got := s.Name(); got != name {
				t.Fatalf("expected strategy name: '%s', got: '%s'", name, got)
			}

			if got := s.Quorum(); got != quorum {
				t.Fatalf("expected strategy quorum: %.2f, got: %.2f", quorum, got)
			}

			if got := s.VotingPeriod(); got != votingPeriod {
				t.Fatalf("expected strategy voting period: %d, got: %d", votingPeriod, got)
			}

			if got := fmt.Sprintf("%v", s.VoteChoices()); got != choices {
				t.Fatalf("expected strategy vote choices: %s, got: %s", choices, got)
			}
		})
	}
}

func TestLockingStrategyTally(t *testing.T) {
	councilDAO := gnome.MustNew("council", "Council", gnome.AssignAsSuperCouncil())
	cases := []struct {
		name   string
		votes  []gnome.Vote
		choice gnome.VoteChoice
	}{
		{
			name: "yes with one vote",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
			},
			choice: gnome.ChoiceYes,
		},
		{
			name: "no with one vote",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceNo},
			},
			choice: gnome.ChoiceNo,
		},
		{
			name: "yes with multiple votes",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceYes},
			},
			choice: gnome.ChoiceYes,
		},
		{
			name: "no with multiple votes",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceYes},
			},
			choice: gnome.ChoiceNo,
		},
		{
			name: "tie",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceNo},
			},
			choice: gnome.ChoiceNone,
		},
		{
			name: "tie with multiple votes",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceNo},
			},
			choice: gnome.ChoiceNone,
		},
		{
			name:   "no votes",
			choice: gnome.ChoiceNone,
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			record := gnome.NewVotingRecord()
			for _, v := range tc.votes {
				record.Add(v)
			}

			s := newLockingStrategy(councilDAO, "", nil)

			// Act
			choice := s.Tally(nil, *record)

			// Assert
			if choice != tc.choice {
				t.Fatalf("expected tally result choice: '%v', got: '%v'", tc.choice, choice)
			}
		})
	}
}

func TestLockingStrategyValidate(t *testing.T) {
	cases := []struct {
		name  string
		setup func(*gnome.DAO)
		err   string
	}{
		{
			name: "ok",
		},
		{
			name: "locked council DAO",
			setup: func(councilDAO *gnome.DAO) {
				councilDAO.Lock("")
			},
			err: "council DAO is already locked",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			councilDAO := gnome.MustNew("council", "Council", gnome.AssignAsSuperCouncil())
			if tc.setup != nil {
				tc.setup(councilDAO)
			}

			s := newLockingStrategy(councilDAO, "", nil)

			// Act
			err := s.Validate(nil)

			// Assert
			if tc.err != "" {
				assertError(t, tc.err, err)
			} else {
				assertNoError(t, err)
			}
		})
	}
}

func TestLockingStrategyExecute(t *testing.T) {
	cases := []struct {
		name, reason, err string
		setup             func(*gnome.DAO)
		preLockErr        error
	}{
		{
			name:   "ok",
			reason: "Lock reason",
		},
		{
			name:       "pre lock function error",
			preLockErr: errors.New("test error"),
			err:        "test error",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			councilDAO := gnome.MustNew("council", "Council", gnome.AssignAsSuperCouncil())
			if tc.setup != nil {
				tc.setup(councilDAO)
			}

			var (
				preLockFnCalled bool

				s = newLockingStrategy(councilDAO, tc.reason, func() error {
					preLockFnCalled = true
					return tc.preLockErr
				})
			)

			// Act
			err := s.Execute(nil)

			// Assert
			if tc.err != "" {
				assertError(t, tc.err, err)
				return
			}

			assertNoError(t, err)

			if !preLockFnCalled {
				t.Fatal("expected pre-lock function to be called")
			}

			if !councilDAO.IsLocked() {
				t.Fatal("expected DAO to be locked")
			}

			if got := councilDAO.LockReason(); got != tc.reason {
				t.Fatalf("expected lock reason: '%s', got: '%s'", tc.reason, got)
			}
		})
	}
}

package gnome

import (
	"strings"
	"time"

	gnome "gno.land/p/gnome/dao"
)

type paramsUpdateStrategy struct {
	votingPeriods  gnome.DurationParams
	reviewDeadline time.Duration
}

func (paramsUpdateStrategy) Name() string {
	return StrategyNameParamsUpdate
}

func (paramsUpdateStrategy) Quorum() float64 {
	return 0.51
}

func (paramsUpdateStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameParamsUpdate)
	return period
}

func (paramsUpdateStrategy) VoteChoices() []gnome.VoteChoice {
	return []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo}
}

func (paramsUpdateStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	abstentions := len(dao.Members()) - r.VoteCount()
	if choice, ok := gnome.SelectChoiceByMajority(r, abstentions); ok {
		return choice
	}
	return gnome.ChoiceNone
}

func (s paramsUpdateStrategy) Execute(*gnome.DAO) error {
	if s.reviewDeadline > 0 {
		parameters.ReviewDeadline = s.reviewDeadline
	}

	s.votingPeriods.Iterate(func(name string, period time.Duration) bool {
		parameters.VotingPeriods.Set(name, period)
		return false
	})
	return nil
}

func (s paramsUpdateStrategy) RenderParams() string {
	var b strings.Builder

	b.WriteString("|||\n|---|---|\n")
	if s.reviewDeadline > 0 {
		b.WriteString("| Proposal Review Deadline: | " + gnome.HumanizeDuration(s.reviewDeadline) + " |\n")
	}

	s.votingPeriods.Iterate(func(name string, period time.Duration) bool {
		b.WriteString("| Voting Period for `" + name + "`: | " + gnome.HumanizeDuration(period) + " |\n")
		return false
	})

	return b.String()
}

package gnome

import (
