package tutorials

import (
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/gnome/blog"
)

const keyDateFmt = "2006-01-02T15:04:05"

var (
	tags      tagIndex
	tutorials tutorialIndex
)

type tagIndex struct {
	index avl.Tree // string(tag) -> *tutorialIndex
}

func (x *tagIndex) Index(p *blog.Post) (indexed bool) {
	if p == nil {
		return
	}

	for _, tag := range p.Tags {
		// Get the tutorials for the current tag
		var (
			idx      *tutorialIndex
			v, found = x.index.Get(tag)
		)

		if found {
			idx = v.(*tutorialIndex)
		} else {
			idx = &tutorialIndex{}
		}

		// Index the tutorial
		idx.Index(p)

		// Keep track of indexing success
		indexed = x.index.Set(tag, idx) || indexed
	}
	return
}

func (x *tagIndex) Remove(p *blog.Post) (removed bool) {
	if p == nil {
		return
	}

	for _, tag := range p.Tags {
		v, found := x.index.Get(tag)
		if !found {
			// Ignore tags that are not indexed
			continue
		}

		idx := v.(*tutorialIndex)
		if idx.Remove(p) && !removed {
			removed = true
		}

		if idx.Size() == 0 {
			// Remove the tag from the index when empty
			x.index.Remove(tag)
		}
	}
	return
}

func (x tagIndex) IterateTags(fn func(tag string) bool) bool {
	return x.index.Iterate("", "", func(tag string, _ interface{}) bool {
		return fn(tag)
	})
}

func (x tagIndex) IteratePosts(tag string, fn func(*blog.Post) bool) bool { // TODO: Support pagination
	v, found := x.index.Get(tag)
	if !found {
		return false
	}

	idx := v.(*tutorialIndex)
	return idx.Iterate("", "", func(p *blog.Post) bool {
		return fn(p)
	})
}

type tutorialIndex struct {
	index avl.Tree // string(post creation time + post slug) -> *blog.Post
}

func (x tutorialIndex) Size() int {
	return x.index.Size()
}

func (x *tutorialIndex) Index(p *blog.Post) bool {
	k := newTutorialKey(p)
	return x.index.Set(k, p)
}

func (x *tutorialIndex) Remove(p *blog.Post) bool {
	k := newTutorialKey(p)
	_, removed := x.index.Remove(k)
	return removed
}

func (x tutorialIndex) Iterate(start, end string, fn func(*blog.Post) bool) bool {
	return x.index.Iterate(start, end, func(_ string, v interface{}) bool {
		return fn(v.(*blog.Post))
	})
}

func newTutorialKey(p *blog.Post) string {
	if p != nil {
		return p.CreatedAt.UTC().Format(keyDateFmt) + p.Slug
	}

	// By default create a key for the current block time
	return time.Now().UTC().Format(keyDateFmt)
}

package tutorials

import (
	"time"

	gnome "gno.land/p/gnome/dao"
)

// Day defines the duration of a day.
const Day = time.Hour * 24

// Names for the different strategy types.
const (
	StrategyNameCreation     = "tutorial-creation"
	StrategyNameDeletion     = "tutorial-deletion"
	StrategyNameLocking      = "tutorial-realm-locking"
	StrategyNameModification = "tutorial-modification"
	StrategyNameParamsUpdate = "tutorial-params-update"
)

var parameters struct {
	VotingPeriods gnome.DurationParams
}

func init() {
	// Initial voting periods for each proposal type.
	// Periods can be changed by sumitting a params update proposal.
	parameters.VotingPeriods.Set(StrategyNameCreation, Day*3)
	parameters.VotingPeriods.Set(StrategyNameDeletion, Day*3)
	parameters.VotingPeriods.Set(StrategyNameLocking, Day*3)
	parameters.VotingPeriods.Set(StrategyNameModification, Day*3)
	parameters.VotingPeriods.Set(StrategyNameParamsUpdate, time.Minute*10)
}

package tutorials

import (
	"std"
	"time"

	"gno.land/p/demo/mux"
	"gno.land/p/gnome/blog"
)

func Render(path string) string {
	router := mux.NewRouter()
	router.NotFoundHandler = func(res *mux.ResponseWriter, _ *mux.Request) {
		res.Write("Path not found")
	}

	router.HandleFunc("", renderBlog)
	router.HandleFunc("posts", renderBlog)
	router.HandleFunc("posts/{slug}", renderPost)
	router.HandleFunc("drafts", renderDrafts)
	router.HandleFunc("revisions", renderRevisions)
	router.HandleFunc("tags", renderTags)
	router.HandleFunc("tags/{name}", renderPostsByTag)
	router.HandleFunc("params", renderParams)

	return renderAlerts() + router.Render(path)
}

// GetTutorialsBlog returns an invariant reference to the tutorials blog.
func GetTutorialsBlog() blog.InvarBlog {
	return blog.NewInvarBlog(&tutorialsBlog)
}

// Publish publishes content for a tutorial.
//
// The submited content must be previously approved by a creation or modification proposal.
//
// Parameters:
// - slug: Slug name of the tutorial (required)
// - content: The tutorial content to publish (required)
func Publish(slug, content string) {
	assertRealmIsNotLocked()

	// Check that content checksum matches the approved content for the tutorial post
	p := mustGetPost(slug)
	blog.AssertContentSha256Hash(content, p.ContentHash)

	// Add caller to the list of publishers
	caller := std.GetOrigCaller()
	if !p.Publishers.HasAddress(caller) {
		p.Publishers = append(p.Publishers, caller)
	}

	if p.Status == blog.StatusDraft {
		p.PublishAt = time.Now()
	}

	p.Status = blog.StatusPublished
	p.Content = content
	p.UpdatedAt = time.Now()
}

package tutorials

import (
	"std"
	"strings"
	"time"

	"gno.land/p/gnome/blog"
	gnome "gno.land/p/gnome/dao"
	gnomeDAO "gno.land/r/gnome/dao/pre1"
)

const tutorialsPath = "council/main/sections/tutorials"

// SubmitCreationProposal submits a new proposal to create a new tutorial.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - tutorialSlug: Slug name of the tutorial (required)
// - tutorialTitle: A title for the tutorial (required)
// - tutorialContentHash: A SHA256 hash of the tutorial's content (required)
// - tutorialContentURL: A URL where the tutorial's content is currently available (required)
// - tutorialAuthors: List of author addresses (required)
// - tutorialEditors:	List of editor addresses
// - tutorialTags: Space separated list of tutorial tags
//
// Tutorial slug name allows letters from "a" to "z", numbers and "-" as valid characters.
// Unicode letters are also allowed.
//
// The list of authors and editors must be a newline separated list of addresses.
func SubmitCreationProposal(
	proposalTitle,
	proposalDescription,
	tutorialSlug,
	tutorialTitle,
	tutorialContentHash,
	tutorialContentURL,
	tutorialAuthors,
	tutorialEditors,
	tutorialTags string,
) uint64 {
	assertRealmIsNotLocked()
	assertSlugIsAvailable(tutorialSlug)
	blog.AssertTitleIsNotEmpty(tutorialTitle)
	blog.AssertIsSlug(tutorialSlug)
	blog.AssertIsSha256Hash(tutorialContentHash)
	blog.AssertIsContentURL(tutorialContentURL)

	tags := strings.Fields(tutorialTags)
	assertValidTags(tags)

	authors := blog.MustParseStringToAddresses(tutorialAuthors)
	if len(authors) == 0 {
		panic("tutorial authors must have at least one author's address")
	}

	strategy := creationStrategy{
		slug:        tutorialSlug,
		title:       strings.TrimSpace(tutorialTitle),
		contentHash: tutorialContentHash,
		contentURL:  tutorialContentURL,
		authors:     authors,
		editors:     blog.MustParseStringToAddresses(tutorialEditors),
		tags:        tags,
	}
	id := gnomeDAO.SubmitCustomProposal(proposalTitle, proposalDescription, strategy, tutorialsPath)
	return uint64(id)
}

// SubmitModificationProposal submits a new proposal to modify a tutorial.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - tutorialSlug: Slug name of the tutorial (required)
// - tutorialTitle: A title for the tutorial
// - tutorialContentHash: A SHA256 hash of the new tutorial's content
// - tutorialCurrentContentHash: A SHA256 hash of the current tutorial's content
// - tutorialContentURL: A URL where the new tutorial's content is currently available
// - tutorialNewAuthors: List of author addresses
// - tutorialNewEditors:	List of editor addresses
// - tutorialTags: Space separated list of tutorial tags
//
// Tutorial slug name allows letters from "a" to "z", numbers and "-" as valid characters.
// Unicode letters are also allowed.
//
// The list of new authors and editors must be a newline separated list of addresses.
// If present, authors and editors are appended to the current list of authors and editors.
func SubmitModificationProposal(
	proposalTitle,
	proposalDescription,
	tutorialSlug,
	tutorialTitle,
	tutorialContentHash,
	tutorialCurrentContentHash,
	tutorialContentURL,
	tutorialNewAuthors,
	tutorialNewEditors,
	tutorialTags string,
) uint64 {
	assertRealmIsNotLocked()

	tutorialSlug = strings.TrimSpace(tutorialSlug)
	assertTutorialExists(tutorialSlug)

	tags := strings.Fields(tutorialTags)
	assertValidTags(tags)

	tutorialContentHash = strings.TrimSpace(tutorialContentHash)
	if tutorialContentHash != "" {
		tutorialCurrentContentHash = strings.TrimSpace(tutorialCurrentContentHash)
		if tutorialCurrentContentHash == "" {
			panic("the current content hash of the tutorial to modify is required")
		}

		blog.AssertIsSha256Hash(tutorialContentHash)
		blog.AssertIsSha256Hash(tutorialCurrentContentHash)
		blog.AssertIsContentURL(tutorialContentURL)
	}

	strategy := modificationStrategy{
		slug:               tutorialSlug,
		title:              strings.TrimSpace(tutorialTitle),
		contentHash:        tutorialContentHash,
		currentContentHash: tutorialCurrentContentHash,
		contentURL:         tutorialContentURL,
		authors:            blog.MustParseStringToAddresses(tutorialNewAuthors),
		editors:            blog.MustParseStringToAddresses(tutorialNewEditors),
		tags:               tags,
	}
	id := gnomeDAO.SubmitCustomProposal(proposalTitle, proposalDescription, strategy, tutorialsPath)
	return uint64(id)
}

// SubmitDeletionProposal submits a new proposal to delete a tutorial.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - tutorialSlug: Slug name of the tutorial (required)
func SubmitDeletionProposal(proposalTitle, proposalDescription, tutorialSlug string) uint64 {
	assertRealmIsNotLocked()

	tutorialSlug = strings.TrimSpace(tutorialSlug)
	assertTutorialExists(tutorialSlug)

	strategy := deletionStrategy{tutorialSlug}
	id := gnomeDAO.SubmitCustomProposal(proposalTitle, proposalDescription, strategy, tutorialsPath)
	return uint64(id)
}

// SubmitLockingProposal submits a new proposal to lock the realm.
//
// Locking the realm "freezes the state" by disallowing further modifications.
// State must be locked to migrate the realm to a newer version.
//
// Proposal requires a 33% quorum, otherwise the outcome will be low participation.
// This type of proposal can only be created by members with `admin` role.
// Tally is done by plurality.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - realmPath: Path of the realm that should be allowed to import state data
//
// The optional realm path authorizes a realm to import the state data once the realm is locked.
func SubmitLockingProposal(proposalTitle, proposalDescription, realmPath string) uint64 {
	assertHasAdminRole(std.GetOrigCaller())

	if realmPath != "" && !strings.HasPrefix(realmPath, "gno.land/r/") {
		panic(`realm path must start with "gno.land/r/"`)
	}

	strategy := lockingStrategy{realmPath}
	id := gnomeDAO.SubmitCustomProposal(proposalTitle, proposalDescription, strategy, tutorialsPath)
	return uint64(id)
}

// SubmitParamsUpdateProposal submits a new proposal to update one or more realm parameters.
//
// Proposal requires a 51% quorum, otherwise the outcome will be low participation.
// Tally is done by absolute majority, so all abstentions are considered.
//
// Parameters:
// - proposalTitle: A title for the proposal (required)
// - proposalDescription: A description of the proposal
// - votingPeriodCreation: Voting period for tutorial creation proposals
// - votingPeriodModification: Voting period for tutorial modification proposals
// - votingPeriodDeletion: Voting period for tutorial deletion proposals
// - votingPeriodLocking: Voting period for realm locking proposals
// - votingPeriodParamsUpdate: Voting period for parameters update proposals
//
// Voting period is the number of days that members can vote on a proposal
// At least one parameter value is required for creating a proposal.
func SubmitParamsUpdateProposal(
	proposalTitle,
	proposalDescription string,
	votingPeriodCreation,
	votingPeriodModification,
	votingPeriodDeletion,
	votingPeriodLocking,
	votingPeriodParamsUpdate int,
) uint64 {
	strategy := paramsUpdateStrategy{}
	if votingPeriodCreation > 0 {
		period := time.Duration(votingPeriodCreation) * Day
		strategy.votingPeriods.Set(StrategyNameCreation, period)
	}

	if votingPeriodModification > 0 {
		period := time.Duration(votingPeriodModification) * Day
		strategy.votingPeriods.Set(StrategyNameModification, period)
	}

	if votingPeriodDeletion > 0 {
		period := time.Duration(votingPeriodDeletion) * Day
		strategy.votingPeriods.Set(StrategyNameDeletion, period)
	}

	if votingPeriodLocking > 0 {
		period := time.Duration(votingPeriodLocking) * Day
		strategy.votingPeriods.Set(StrategyNameLocking, period)
	}

	if votingPeriodParamsUpdate > 0 {
		period := time.Duration(votingPeriodParamsUpdate) * Day
		strategy.votingPeriods.Set(StrategyNameParamsUpdate, period)
	}

	if strategy.votingPeriods.Size() == 0 {
		panic("at least one parameter value must be specified")
	}

	id := gnomeDAO.SubmitCustomProposal(proposalTitle, proposalDescription, strategy, tutorialsPath)
	return uint64(id)
}

func assertSlugIsAvailable(slug string) {
	if tutorialsBlog.HasPost(slug) {
		panic("tutorial URL slug already exists")
	}
}

func assertTutorialExists(slug string) {
	if !tutorialsBlog.HasPost(slug) {
		panic("tutorial not found")
	}
}

func assertValidTags(tags []string) {
	for _, t := range tags {
		if !blog.IsSlug(t) {
			panic("invalid tag: " + t)
		}
	}
}

func assertHasAdminRole(addr std.Address) {
	dao := gnomeDAO.GetDAO(tutorialsPath)
	m, found := getMember(dao, addr)
	if !found {
		panic("address is not a member of tutorials DAO: " + addr.String())
	}

	for _, r := range m.Roles {
		if r == gnomeDAO.RoleAdmin {
			// Member has admin role
			return
		}
	}

	panic("member doesn't have admin role: " + addr.String())
}

func getMember(dao gnome.InvarDAO, addr std.Address) (gnome.Member, bool) {
	for _, m := range dao.Members() {
		if m.Address == addr {
			return m, true
		}
	}
	return gnome.Member{}, false
}

package tutorials

import (
	"strings"
	"time"

	"gno.land/p/demo/mux"
	"gno.land/p/gnome/alerts"
	"gno.land/p/gnome/blog"
	gnome "gno.land/p/gnome/dao"
)

const (
	dateFormat      = "2006-01-02 15:04 MST"
	shortDateFormat = "Jan 2, 2006"
)

func renderBlog(res *mux.ResponseWriter, _ *mux.Request) {
	// Write header
	res.Write("# " + tutorialsBlog.Title + "\n")
	if tutorialsBlog.Description != "" {
		res.Write(tutorialsBlog.Description + "\n\n")
	}

	// Write tutorials menu
	res.Write(renderMenu() + "\n\n---\n")

	// Write list of published tutorials
	now := time.Now()
	tutorials.Iterate("", "", func(p *blog.Post) bool { // TODO: Add post pagination support
		// Skip posts that should be published at a future date
		if p.PublishAt.IsZero() || p.PublishAt.After(now) {
			return false
		}

		// Skip posts that are not published or being revised
		if p.Status != blog.StatusPublished && p.Status != blog.StatusRevised {
			return false
		}

		url := newRealmURL("posts/" + p.Slug)
		date := p.PublishAt.UTC().Format(shortDateFormat)
		res.Write("**[" + p.Title + "](" + url + ")**</br>")
		res.Write("_Published: " + date + "_\n\n")
		return false
	})
}

func renderPost(res *mux.ResponseWriter, req *mux.Request) {
	slug := req.GetVar("slug")
	p, found := tutorialsBlog.GetPost(slug)
	if !found {
		res.Write("Post not found")
		return
	}

	if p.Status == blog.StatusRevised {
		res.Write(alerts.NewWarning("Tutorial content is being revised"))
	}

	// TODO: Add post tags with links
	res.Write("# " + p.Title + "\n")
	res.Write("- Author(s): " + p.Authors.String() + "\n")

	if len(p.Editors) > 0 {
		res.Write("- Editors(s): " + p.Editors.String() + "\n")
	}

	res.Write("- Publisher(s): " + p.Publishers.String() + "\n")
	res.Write("- Status: " + p.Status.String() + "\n")
	res.Write("- Content Hash: " + p.ContentHash + "\n")
	res.Write("- Created: " + p.CreatedAt.UTC().Format(dateFormat) + "\n")
	if !p.UpdatedAt.IsZero() {
		res.Write("- Updated: " + p.UpdatedAt.UTC().Format(dateFormat) + "\n")
	}

	if len(p.Tags) > 0 {
		res.Write("- Tag(s): " + renderTagLinks(p.Tags) + "\n")
	}

	if p.Content != "" {
		res.Write("\n" + p.Content)
	}
}

func renderDrafts(res *mux.ResponseWriter, _ *mux.Request) {
	res.Write("# " + tutorialsBlog.Title + ": Drafts\n")
	tutorials.Iterate("", "", func(p *blog.Post) bool { // TODO: Add pagination support
		if p.Status != blog.StatusDraft {
			return false
		}

		url := newRealmURL("posts/" + p.Slug)
		date := p.CreatedAt.UTC().Format(shortDateFormat)
		res.Write("**[" + p.Title + "](" + url + ")**</br>")
		res.Write("_Created: " + date + "_\n\n")
		return false
	})
}

func renderRevisions(res *mux.ResponseWriter, _ *mux.Request) {
	res.Write("# " + tutorialsBlog.Title + ": Revisions\n")
	tutorials.Iterate("", "", func(p *blog.Post) bool { // TODO: Add pagination support
		if p.Status != blog.StatusRevised {
			return false
		}

		url := newRealmURL("posts/" + p.Slug)
		date := p.PublishAt.UTC().Format(shortDateFormat)
		res.Write("**[" + p.Title + "](" + url + ")**</br>")
		res.Write("_Published: " + date + "_\n\n")
		return false
	})
}

func renderTags(res *mux.ResponseWriter, req *mux.Request) {
	res.Write("# " + tutorialsBlog.Title + ": Tags\n")
	tags.IterateTags(func(tag string) bool {
		res.Write("- [" + tag + "](" + newRealmURL("tags/"+tag) + ")\n")
		return false
	})
}

func renderPostsByTag(res *mux.ResponseWriter, req *mux.Request) {
	tag := req.GetVar("name")
	res.Write("# " + tutorialsBlog.Title + ": Tag `" + tag + "`\n")

	if tag == "" {
		return
	}

	tags.IteratePosts(tag, func(p *blog.Post) bool {
		if p.Status != blog.StatusPublished && p.Status != blog.StatusRevised {
			return false
		}

		url := newRealmURL("posts/" + p.Slug)
		date := p.PublishAt.UTC().Format(shortDateFormat)
		res.Write("**[" + p.Title + "](" + url + ")**</br>")
		res.Write("_Published: " + date + "_\n\n")
		return false
	})
}

func renderParams(res *mux.ResponseWriter, _ *mux.Request) {
	res.Write("# " + tutorialsBlog.Title + ": Parameters\n")
	res.Write("## Proposal\n")
	res.Write("**Voting Periods**\n")
	parameters.VotingPeriods.Iterate(func(name string, period time.Duration) bool {
		res.Write("- `" + name + "`: " + gnome.HumanizeDuration(period) + "\n")
		return false
	})
}

func renderAlerts() string {
	if locked {
		msg := "Realm is locked"
		if nextVersionRealmPath != "" {
			link := alerts.NewLink("https://"+nextVersionRealmPath, nextVersionRealmPath)
			msg += "</br>This realm is deprecated in favor of a new version found at " + link
		}

		return alerts.NewError(msg)
	}
	return ""
}

func renderMenu() string {
	items := []string{
		"**[drafts](" + newRealmURL("drafts") + ")**",
		"**[revisions](" + newRealmURL("revisions") + ")**",
	}

	// Add taxonomy entries
	tags.IterateTags(func(tag string) bool {
		items = append(items, "**["+tag+"]("+newRealmURL("tags/"+tag)+")**")
		return false
	})

	return strings.Join(items, " ")
}

func renderTagLinks(tags []string) string {
	var links []string
	for _, t := range tags {
		links = append(links, "["+t+"]("+newRealmURL("tags/"+t)+")")
	}
	return strings.Join(links, ", ")
}

func newRealmURL(renderPath string) string {
	// TODO: Get the prefix for the current realm package path
	return "/r/gnome/tutorials/pre2:" + renderPath
}

package tutorials

import (
	"errors"
	"time"

	gnome "gno.land/p/gnome/dao"
)

type lockingStrategy struct {
	realmPath string
}

func (lockingStrategy) Name() string {
	return StrategyNameLocking
}

func (lockingStrategy) Quorum() float64 {
	return 0.33
}

func (lockingStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameLocking)
	return period
}

func (lockingStrategy) VoteChoices() []gnome.VoteChoice {
	return []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo}
}

func (s lockingStrategy) Validate(*gnome.Proposal) error {
	// Allow modification of the newxt version package path when realm is locked
	if locked && nextVersionRealmPath == "" && s.realmPath != "" {
		return nil
	}

	if locked {
		return errors.New("realm is already locked")
	}
	return nil
}

func (lockingStrategy) Tally(_ *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	if choice, ok := gnome.SelectChoiceByPlurality(r); ok {
		return choice
	}
	return gnome.ChoiceNone
}

func (s lockingStrategy) Execute(*gnome.DAO) error {
	locked = true
	if s.realmPath != "" {
		nextVersionRealmPath = s.realmPath
	}
	return nil
}

func (s lockingStrategy) RenderParams() string {
	if s.realmPath != "" {
		return "Next Realm Path: [" + s.realmPath + "](https://" + s.realmPath + ")"
	}
	return ""
}

package tutorials

import (
	"strings"
	"time"

	gnome "gno.land/p/gnome/dao"
)

type paramsUpdateStrategy struct {
	votingPeriods gnome.DurationParams
}

func (paramsUpdateStrategy) Name() string {
	return StrategyNameParamsUpdate
}

func (paramsUpdateStrategy) Quorum() float64 {
	return 0.51
}

func (paramsUpdateStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameParamsUpdate)
	return period
}

func (paramsUpdateStrategy) VoteChoices() []gnome.VoteChoice {
	return []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo}
}

func (paramsUpdateStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	abstentions := len(dao.Members()) - r.VoteCount()
	if choice, ok := gnome.SelectChoiceByMajority(r, abstentions); ok {
		return choice
	}
	return gnome.ChoiceNone
}

func (s paramsUpdateStrategy) Execute(*gnome.DAO) error {
	s.votingPeriods.Iterate(func(name string, period time.Duration) bool {
		parameters.VotingPeriods.Set(name, period)
		return false
	})
	return nil
}

func (s paramsUpdateStrategy) RenderParams() string {
	var b strings.Builder

	b.WriteString("|||\n|---|---|\n")
	s.votingPeriods.Iterate(func(name string, period time.Duration) bool {
		b.WriteString("| Voting Period for `" + name + "`: | " + gnome.HumanizeDuration(period) + " |\n")
		return false
	})

	return b.String()
}

package tutorials

import (
	"errors"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/gnome/blog"
	gnome "gno.land/p/gnome/dao"
)

type creationStrategy struct {
	slug, title, contentHash, contentURL string
	authors, editors                     blog.AddressList
	tags                                 []string
}

func (creationStrategy) Name() string {
	return StrategyNameCreation
}

func (creationStrategy) Quorum() float64 {
	return 0.51
}

func (creationStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameCreation)
	return period
}

func (creationStrategy) VoteChoices() []gnome.VoteChoice {
	return []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo}
}

func (s creationStrategy) Validate(*gnome.Proposal) error {
	if tutorialsBlog.HasPost(s.slug) {
		return errors.New("tutorial URL slug already exists")
	}
	return nil
}

func (creationStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	abstentions := len(dao.Members()) - r.VoteCount()
	if choice, ok := gnome.SelectChoiceByMajority(r, abstentions); ok {
		return choice
	}
	return gnome.ChoiceNone
}

func (s creationStrategy) Execute(*gnome.DAO) error {
	p := &blog.Post{ // TODO: Support other fields like summary and tags
		Slug:        s.slug,
		Title:       s.title,
		ContentHash: s.contentHash,
		Authors:     s.authors,
		Editors:     s.editors,
		Status:      blog.StatusDraft,
		Tags:        s.tags,
		CreatedAt:   time.Now(),
	}
	tutorialsBlog.AddPost(p)

	// Update realm indexes
	tutorials.Index(p)
	if len(p.Tags) > 0 {
		tags.Index(p)
	}

	return nil
}

func (s creationStrategy) RenderParams() string {
	var (
		b       strings.Builder
		authors = strings.ReplaceAll(s.authors.String(), ", ", "</br>")
	)

	// TODO: Implement using gno.land/p/demo/ui
	b.WriteString("|||\n|---|---|\n")
	b.WriteString("| Slug: | " + s.slug + " |\n")
	b.WriteString("| Title: | " + gnome.EscapeHTML(s.title) + " |\n")
	b.WriteString("| Content URL: | " + gnome.NewLinkURI(s.contentURL) + " |\n")
	b.WriteString("| Content Hash: | " + s.contentHash + " |\n")

	if len(s.tags) > 0 {
		b.WriteString("| Tag(s): | " + renderTagLinks(s.tags) + " |\n")
	}

	b.WriteString("| Author(s): | </br>" + authors + "</br></br> |\n")

	if len(s.editors) > 0 {
		editors := strings.ReplaceAll(s.editors.String(), ", ", "</br>")
		b.WriteString("| Editor(s): | </br>" + editors + "</br></br> |\n")
	}

	return b.String()
}

type modificationStrategy struct {
	slug, title, currentContentHash, contentHash, contentURL string
	authors, editors                                         blog.AddressList
	tags                                                     []string
}

func (modificationStrategy) Name() string {
	return StrategyNameModification
}

func (modificationStrategy) Quorum() float64 {
	return 0.51
}

func (modificationStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameModification)
	return period
}

func (modificationStrategy) VoteChoices() []gnome.VoteChoice {
	return []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo}
}

func (s modificationStrategy) Validate(*gnome.Proposal) error {
	p, found := tutorialsBlog.GetPost(s.slug)
	if !found {
		return errors.New("tutorial doesn't exists")
	}

	if s.currentContentHash != "" && s.currentContentHash != p.ContentHash {
		return errors.New("tutorial's content has been previously modified")
	}

	for _, addr := range s.authors {
		if p.Authors.HasAddress(addr) {
			return errors.New("author already exists: " + addr.String())
		}
	}

	for _, addr := range s.editors {
		if p.Authors.HasAddress(addr) {
			return errors.New("editor already exists: " + addr.String())
		}
	}

	if len(s.tags) > 0 {
		var seenTags avl.Tree
		for _, t := range s.tags {
			if seenTags.Has(t) {
				return errors.New("duplicated tag: " + t)
			}

			seenTags.Set(t, struct{}{})
		}
	}

	return nil
}

func (modificationStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	abstentions := len(dao.Members()) - r.VoteCount()
	if choice, ok := gnome.SelectChoiceByMajority(r, abstentions); ok {
		return choice
	}
	return gnome.ChoiceNone
}

func (s modificationStrategy) Execute(*gnome.DAO) error {
	p, _ := tutorialsBlog.GetPost(s.slug)

	if s.title != "" {
		p.Title = s.title
	}

	if len(s.authors) > 0 {
		p.Authors = append(p.Authors, s.authors...)
	}

	if len(s.editors) > 0 {
		p.Editors = append(p.Editors, s.editors...)
	}

	// Update tag index
	if len(s.tags) > 0 {
		tags.Remove(p)
		p.Tags = s.tags
		tags.Index(p)
	}

	// Changing content hash converts post to a revised until new content is setted
	if s.contentHash != "" {
		p.Status = blog.StatusRevised
		p.ContentHash = s.contentHash
	}

	p.UpdatedAt = time.Now()
	return nil
}

func (s modificationStrategy) RenderParams() string {
	var b strings.Builder

	// TODO: Implement using gno.land/p/demo/ui
	b.WriteString("|||\n|---|---|\n")
	b.WriteString("| Slug: | [" + s.slug + "](" + newRealmURL("posts/"+s.slug) + ") |\n")

	if s.title != "" {
		b.WriteString("| Title: | " + gnome.EscapeHTML(s.title) + " |\n")
	}

	if s.contentHash != "" {
		b.WriteString("| Content URL: | " + gnome.NewLinkURI(s.contentURL) + " |\n")
		b.WriteString("| Content Hash: | " + s.contentHash + " |\n")
		b.WriteString("| Modifies Content Hash: | " + s.currentContentHash + " |\n")
	}

	if len(s.tags) > 0 {
		b.WriteString("| Tag(s): | " + renderTagLinks(s.tags) + " |\n")
	}

	if len(s.authors) > 0 {
		authors := strings.ReplaceAll(s.authors.String(), ", ", "</br>")
		b.WriteString("| Author(s): | </br>" + authors + "</br></br> |\n")
	}

	if len(s.editors) > 0 {
		editors := strings.ReplaceAll(s.editors.String(), ", ", "</br>")
		b.WriteString("| Editor(s): | </br>" + editors + "</br></br> |\n")
	}

	return b.String()
}

type deletionStrategy struct {
	slug string
}

func (deletionStrategy) Name() string {
	return StrategyNameDeletion
}

func (deletionStrategy) Quorum() float64 {
	return 0.51
}

func (deletionStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameDeletion)
	return period
}

func (deletionStrategy) VoteChoices() []gnome.VoteChoice {
	return []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo}
}

func (deletionStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	abstentions := len(dao.Members()) - r.VoteCount()
	if choice, ok := gnome.SelectChoiceByMajority(r, abstentions); ok {
		return choice
	}
	return gnome.ChoiceNone
}

func (s deletionStrategy) Validate(*gnome.Proposal) error {
	if !tutorialsBlog.HasPost(s.slug) {
		return errors.New("tutorial doesn't exists")
	}
	return nil
}

func (s deletionStrategy) Execute(*gnome.DAO) error {
	p, found := tutorialsBlog.RemovePost(s.slug)
	if !found {
		return errors.New("tutorial not found")
	}

	// Update realm indexes
	tutorials.Remove(p)
	if len(p.Tags) > 0 {
		tags.Remove(p)
	}

	return nil
}

func (s deletionStrategy) RenderParams() string {
	return "Slug: [" + s.slug + "](" + newRealmURL("posts/"+s.slug) + ")"
}

