)

// gno.land/r/leon/demo/poll

type PollWithID struct {
	poll *ppkg.Poll
	id   uint
}

var (
	polls        []*PollWithID
	pollIdCunter uint
)

func NewPoll(title, description string, deadline int) string {
	pollInstanace, err := ppkg.NewPoll(title, description, int64(deadline))
	if err != nil {
		return err.Error()
	}

	p := &PollWithID{
		poll: pollInstanace,
		id:   pollIdCunter,
	}

	polls = append(polls, p)
	pollIdCunter++

