	primaryColor       = "#FEFEFE"
	borderColor        = "#D30000"
	fontSizeLarge      = "7rem"
	fontSizeMedium     = "4rem"
	fontSizeSmall      = "1.5rem"
	fontSizeExtraSmall = "1rem"
