type FormatKV struct {
	Key   string
	Value interface{}
	Raw   bool
}

// does not work for slices, use FormatSlice instead
func FormatAny(p interface{}) string {
	switch p.(type) {
	case std.Address:
		return FormatString(string(p.(std.Address)))
	case *avl.Tree:
		return FormatAVLTree(p.(*avl.Tree))
	case avl.Tree:
		t := p.(avl.Tree)
		return FormatAVLTree(&t)
	case JSONAble:
		return p.(JSONAble).ToJSON()
	case string:
		return FormatString(p.(string))
	case uint64:
		return FormatUint64(p.(uint64))
	case uint32:
		return FormatUint64(uint64(p.(uint32)))
	case uint:
		return FormatUint64(uint64(p.(uint)))
	case int64:
		return FormatInt64(p.(int64))
	case int32:
		return FormatInt64(int64(p.(int32)))
	case int:
		return FormatInt64(int64(p.(int)))
	case float32:
		panic("float32 not implemented")
	case float64:
		panic("float64 not implemented")
	case bool:
		return FormatBool(p.(bool))
	case time.Time:
		return FormatTime(p.(time.Time))
	case time.Duration:
		return FormatInt64(int64(p.(time.Duration)))
	case users.AddressOrName:
		return FormatString(string(p.(users.AddressOrName)))
	default:
		return "null"
	}
}

// loosely ported from https://cs.opensource.google/go/go/+/master:src/time/time.go;l=1357?q=appendStrictRFC3339&ss=go%2Fgo
func FormatTime(t time.Time) string {
	s := t.Format(time.RFC3339Nano)
	b := []byte(s)

	// Not all valid Go timestamps can be serialized as valid RFC 3339.
	// Explicitly check for these edge cases.
	// See https://go.dev/issue/4556 and https://go.dev/issue/54580.
	n0 := 0
	num2 := func(b []byte) byte { return 10*(b[0]-'0') + (b[1] - '0') }
	switch {
	case b[n0+len("9999")] != '-': // year must be exactly 4 digits wide
		panic(errors.New("year outside of range [0,9999]"))
	case b[len(b)-1] != 'Z':
		c := b[len(b)-len("Z07:00")]
		if ('0' <= c && c <= '9') || num2(b[len(b)-len("07:00"):]) >= 24 {
			panic(errors.New("timezone hour outside of range [0,23]"))
		}
	}
	return FormatString(string(b))
}

func FormatUint64(i uint64) string {
	return strconv.FormatUint(i, 10)
}

func FormatInt64(i int64) string {
	return strconv.FormatInt(i, 10)
}

func FormatSlice(s []interface{}) string {
	elems := make([]string, len(s))
	for i, elem := range s {
		elems[i] = FormatAny(elem)
	}
	return "[" + strings.Join(elems, ",") + "]"
}

func FormatObject(kv []FormatKV) string {
	elems := make([]string, len(kv))
	i := 0
	for _, elem := range kv {
		var val string
		if elem.Raw {
			val = elem.Value.(string)
		} else {
			val = FormatAny(elem.Value)
		}
		elems[i] = FormatString(elem.Key) + ":" + val
		i++
	}
	return "{" + strings.Join(elems, ",") + "}"
}

func FormatBool(b bool) string {
	if b {
		return "true"
	}
	return "false"
}

func FormatAVLTree(t *avl.Tree) string {
	if t == nil {
		return "{}"
	}
	kv := make([]FormatKV, 0, t.Size())
	t.Iterate("", "", func(key string, value interface{}) bool {
		kv = append(kv, FormatKV{key, value, false})
		return false
	})
	return FormatObject(kv)
}

func FormatUnionMember(name string, val interface{}, raw bool) string {
	return FormatObject([]FormatKV{
		{Key: name, Value: val, Raw: raw},
	})
}

package ujson

import (
	"std"
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/users"
)

// https://stackoverflow.com/a/4150626
const whitespaces = " \t\n\r"

type FromJSONAble interface {
	FromJSON(ast *JSONASTNode)
}

// does not work for slices, use ast exploration instead
func (ast *JSONASTNode) ParseAny(ptr interface{}) {
	switch ptr.(type) {
	case *std.Address:
		*ptr.(*std.Address) = std.Address(ParseString(ast.Value))
	case **avl.Tree:
		panic("*avl.Tree not implemented, there is no way to know the type of the tree values, use a custom parser instead")
	case *avl.Tree:
		panic("avl.Tree not implemented, there is no way to know the type of the tree values, use a custom parser instead")
	case *string:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindString {
			panic("not a string")
		}
		*ptr.(*string) = ParseString(ast.Value)
	case *uint64:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindNumber {
			panic("not a number")
		}
		*ptr.(*uint64) = ParseUint64(ast.Value)
	case *uint32:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindNumber {
			panic("not a number")
		}
		*ptr.(*uint32) = uint32(ParseUint64(ast.Value))
	case *uint:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindNumber {
			panic("not a number")
		}
		*ptr.(*uint) = uint(ParseUint64(ast.Value))
	case *int64:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindNumber {
			panic("not a number")
		}
		*ptr.(*int64) = ParseInt64(ast.Value)
	case *int32:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindNumber {
			panic("not a number")
		}
		*ptr.(*int32) = int32(ParseInt64(ast.Value))
	case *int:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindNumber {
			panic("not a number")
		}
		*ptr.(*int) = int(ParseInt64(ast.Value))
	case *float64:
		panic("float64 not implemented")
	case *float32:
		panic("float32 not implemented")
	case *bool:
		if ast.Kind != JSONKindValue {
			panic("not a value")
		}
		if ast.ValueKind != JSONTokenKindTrue && ast.ValueKind != JSONTokenKindFalse {
			panic("not a bool")
		}
		*ptr.(*bool) = ast.ValueKind == JSONTokenKindTrue
	case *FromJSONAble:
		(*(ptr.(*FromJSONAble))).FromJSON(ast)
	case FromJSONAble:
		ptr.(FromJSONAble).FromJSON(ast)
	case **JSONASTNode:
		*ptr.(**JSONASTNode) = ast
	case *time.Time:
		ast.ParseTime(ptr.(*time.Time))
	case *time.Duration:
		*ptr.(*time.Duration) = time.Duration(ParseInt64(ast.Value))
	case *users.AddressOrName:
		s := ParseString(ast.Value)
		*ptr.(*users.AddressOrName) = users.AddressOrName(s)
	default:
		if ast.Kind == JSONKindValue && ast.ValueKind == JSONTokenKindNull {
			// *ptr.(*interface{}) = nil // TODO: handle nil
			return
		}
		panic("type not defined for `" + ast.String() + "`")
	}
}

// loosely ported from https://cs.opensource.google/go/go/+/master:src/time/time.go;l=1370?q=appendStrictRFC3339&ss=go%2Fgo
// it's not a full port since it would require copying lot of utils
func (ast *JSONASTNode) ParseTime(t *time.Time) {
	if ast.Kind != JSONKindValue && ast.ValueKind != JSONTokenKindString {
		panic("time is not a string")
	}
	s := ParseString(ast.Value)
	var err error
	*t, err = time.Parse(time.RFC3339Nano, s)
	if err != nil {
		panic(err)
	}
}

func ParseUint64(s string) uint64 {
	val, err := strconv.Atoi(s)
	if err != nil {
		panic(err)
	}
	return uint64(val)
}

func ParseInt64(s string) int64 {
	val, err := strconv.Atoi(s)
	if err != nil {
		panic(err)
	}
	return int64(val)
}

type ParseKV struct {
	Key          string
	Value        interface{}
	ArrayParser  func(children []*JSONASTNode)
	ObjectParser func(children []*JSONASTKV)
	CustomParser func(node *JSONASTNode)
}

func ParseAny(s string, val interface{}) {
	tokens := tokenize(s)
	if len(tokens) == 0 {
		panic("empty json")
	}
	remainingTokens, ast := parseAST(tokens)
	if len(remainingTokens) > 0 {
		panic("invalid json")
	}
	ast.ParseAny(val)
}

func (ast *JSONASTNode) ParseObject(kv []*ParseKV) {
	if ast.Kind != JSONKindObject {
		panic("not an object")
	}
	for _, elem := range kv {
		for i, child := range ast.ObjectChildren {
			if child.Key == elem.Key {
				if elem.ArrayParser != nil {
					if child.Value.Kind != JSONKindArray {
						panic("not an array")
					}
					elem.ArrayParser(child.Value.ArrayChildren)
				} else if elem.ObjectParser != nil {
					if child.Value.Kind != JSONKindObject {
						panic("not an object")
					}
					elem.ObjectParser(child.Value.ObjectChildren)
				} else if elem.CustomParser != nil {
					elem.CustomParser(child.Value)
				} else {
					child.Value.ParseAny(elem.Value)
				}
				break
			}
			if i == (len(ast.ObjectChildren) - 1) {
				panic("invalid key `" + elem.Key + "` in object `" + ast.String() + "`")
			}
		}
	}
}

func (ast *JSONASTNode) ParseUnion(kv []*ParseKV) interface{} {
	if ast.Kind != JSONKindObject {
		panic("union is not an object")
	}
	if len(ast.ObjectChildren) != 1 {
		panic("union object does not have exactly one field")
	}
	k, node := ast.ObjectChildren[0].Key, ast.ObjectChildren[0].Value
	for _, kv := range kv {
		if kv.Key == k {
			node.ParseAny(kv.Value)
			return kv.Value
		}
	}
	panic("unknown union type") // TODO: expected one of ...
}

func ParseSlice(s string) []*JSONASTNode {
	ast := TokenizeAndParse(s)
	return ast.ParseSlice()
}

func (ast *JSONASTNode) ParseSlice() []*JSONASTNode {
	if ast.Kind != JSONKindArray {
		panic("not an array")
	}
	return ast.ArrayChildren
}

func countWhitespaces(s string) int {
	i := 0
	for i < len(s) {
		if strings.ContainsRune(whitespaces, int32(s[i])) {
			i++
		} else {
			break
		}
	}
	return i
}

func JSONTokensString(tokens []*JSONToken) string {
	s := ""
	for _, token := range tokens {
		s += token.Raw
	}
	return s
}

func (node *JSONASTNode) String() string {
	if node == nil {
		return "nil"
	}
	switch node.Kind {
	case JSONKindValue:
		return node.Value
	case JSONKindArray:
		s := "["
		for i, child := range node.ArrayChildren {
			if i > 0 {
				s += ","
			}
			s += child.String()
		}
		s += "]"
		return s
	case JSONKindObject:
		s := "{"
		for i, child := range node.ObjectChildren {
			if i > 0 {
				s += ","
			}
			s += `"` + child.Key + `":` + child.Value.String()
		}
		s += "}"
		return s
	default:
		panic("invalid json")
	}
}

func TokenizeAndParse(s string) *JSONASTNode {
	tokens := tokenize(s)
	if len(tokens) == 0 {
		panic("empty json")
	}
	remainingTokens, ast := parseAST(tokens)
	if len(remainingTokens) > 0 {
		panic("invalid json")
	}
	return ast
}

func parseAST(tokens []*JSONToken) (tkn []*JSONToken, tree *JSONASTNode) {
	if len(tokens) == 0 {
		panic("empty json")
	}

	switch tokens[0].Kind {

	case JSONTokenKindString:
		return tokens[1:], &JSONASTNode{Kind: JSONKindValue, ValueKind: tokens[0].Kind, Value: tokens[0].Raw}
	case JSONTokenKindNumber:
		return tokens[1:], &JSONASTNode{Kind: JSONKindValue, ValueKind: tokens[0].Kind, Value: tokens[0].Raw}
	case JSONTokenKindTrue:
		return tokens[1:], &JSONASTNode{Kind: JSONKindValue, ValueKind: tokens[0].Kind, Value: tokens[0].Raw}
	case JSONTokenKindFalse:
		return tokens[1:], &JSONASTNode{Kind: JSONKindValue, ValueKind: tokens[0].Kind, Value: tokens[0].Raw}
	case JSONTokenKindNull:
		return tokens[1:], &JSONASTNode{Kind: JSONKindValue, ValueKind: tokens[0].Kind, Value: tokens[0].Raw}

	case JSONTokenKindOpenArray:
		arrayChildren := []*JSONASTNode{}
		tokens = tokens[1:]
		for len(tokens) > 0 {
			if tokens[0].Kind == JSONTokenKindCloseArray {
				return tokens[1:], &JSONASTNode{Kind: JSONKindArray, ArrayChildren: arrayChildren}
			}
			var child *JSONASTNode
			tokens, child = parseAST(tokens)
			arrayChildren = append(arrayChildren, child)
			if len(tokens) == 0 {
				panic("exepected more tokens in array")
			}
			if tokens[0].Kind == JSONTokenKindComma {
				tokens = tokens[1:]
			} else if tokens[0].Kind == JSONTokenKindCloseArray {
				return tokens[1:], &JSONASTNode{Kind: JSONKindArray, ArrayChildren: arrayChildren}
			} else {
				panic("unexpected token in array after value `" + tokens[0].Raw + "`")
			}
		}

	case JSONTokenKindOpenObject:
		objectChildren := []*JSONASTKV{}
		if len(tokens) < 2 {
			panic("objects must have at least 2 tokens")
		}
		tokens = tokens[1:]
		for len(tokens) > 0 {
			if tokens[0].Kind == JSONTokenKindCloseObject {
				return tokens[1:], &JSONASTNode{Kind: JSONKindObject, ObjectChildren: objectChildren}
			}
			if tokens[0].Kind != JSONTokenKindString {
				panic("invalid json")
			}
			key := tokens[0].Raw
			tokens = tokens[1:]
			if len(tokens) == 0 {
				panic("exepected more tokens in object")
			}
			if tokens[0].Kind != JSONTokenKindColon {
				panic("expected :")
			}
			tokens = tokens[1:]
			if len(tokens) == 0 {
				panic("exepected more tokens in object after :")
			}
			var value *JSONASTNode
			tokens, value = parseAST(tokens)
			objectChildren = append(objectChildren, &JSONASTKV{Key: ParseString(key), Value: value})
			if len(tokens) == 0 {
				panic("exepected more tokens in object after value")
			}
			if tokens[0].Kind == JSONTokenKindComma {
				tokens = tokens[1:]
			} else if tokens[0].Kind == JSONTokenKindCloseObject {
				return tokens[1:], &JSONASTNode{Kind: JSONKindObject, ObjectChildren: objectChildren}
			} else {
				panic("unexpected token in object after value `" + tokens[0].Raw + "`")
			}
		}

	default:
		panic("unexpected token `" + tokens[0].Raw + "`")
	}

	return
}

func tokenize(s string) []*JSONToken {
	tokens := []*JSONToken{}
	for len(s) > 0 {
		var token *JSONToken
		s, token = tokenizeOne(s)
		if token.Kind != JSONTokenKindSpaces {
			tokens = append(tokens, token)
		}
	}
	return tokens
}

func tokenizeOne(s string) (string, *JSONToken) {
	if len(s) == 0 {
		panic("invalid token")
	}
	spacesCount := countWhitespaces(s)
	if spacesCount > 0 {
		spaces := s[:spacesCount]
		return s[spacesCount:], &JSONToken{Kind: JSONTokenKindSpaces, Raw: spaces}
	}
	switch s[0] {
	case '"':
		return parseStringToken(s)
	case 't':
		return parseKeyword(s, "true", JSONTokenKindTrue)
	case 'f':
		return parseKeyword(s, "false", JSONTokenKindFalse)
	case 'n':
		return parseKeyword(s, "null", JSONTokenKindNull)
	case '{':
		return s[1:], &JSONToken{Kind: JSONTokenKindOpenObject, Raw: "{"}
	case '[':
		return s[1:], &JSONToken{Kind: JSONTokenKindOpenArray, Raw: "["}
	case ':':
		return s[1:], &JSONToken{Kind: JSONTokenKindColon, Raw: ":"}
	case ',':
		return s[1:], &JSONToken{Kind: JSONTokenKindComma, Raw: ","}
	case ']':
		return s[1:], &JSONToken{Kind: JSONTokenKindCloseArray, Raw: "]"}
	case '}':
		return s[1:], &JSONToken{Kind: JSONTokenKindCloseObject, Raw: "}"}
	default:
		return parseNumber(s)
	}
}

func parseKeyword(s string, keyword string, kind JSONTokenKind) (string, *JSONToken) {
	if len(s) < len(keyword) {
		panic("invalid keyword")
	}
	if s[:len(keyword)] != keyword {
		panic("invalid keyword")
	}
	return s[len(keyword):], &JSONToken{Kind: kind, Raw: keyword}
}

func parseStringToken(s string) (string, *JSONToken) {
	if (len(s) < 2) || (s[0] != '"') {
		panic("invalid string")
	}
	quote := false
	for i := 1; i < len(s); i++ {
		if !quote && s[i] == '\\' {
			quote = true
			continue
		}
		if !quote && s[i] == '"' {
			return s[i+1:], &JSONToken{Kind: JSONTokenKindString, Raw: s[:i+1]}
		}
		quote = false
	}
	panic("invalid string")
}

// copiloted
func parseNumber(s string) (string, *JSONToken) {
	if len(s) == 0 {
		panic("invalid number")
	}
	i := 0
	if s[i] == '-' {
		i++
	}
	if i == len(s) {
		panic("invalid number")
	}
	if s[i] == '0' {
		i++
	} else if ('1' <= s[i]) && (s[i] <= '9') {
		i++
		for (i < len(s)) && ('0' <= s[i]) && (s[i] <= '9') {
			i++
		}
	} else {
		panic("invalid number")
	}
	if i == len(s) {
		return s[i:], &JSONToken{Kind: JSONTokenKindNumber, Raw: s}
	}
	if s[i] == '.' {
		i++
		if i == len(s) {
			panic("invalid number")
		}
		if ('0' <= s[i]) && (s[i] <= '9') {
			i++
			for (i < len(s)) && ('0' <= s[i]) && (s[i] <= '9') {
				i++
			}
		} else {
			panic("invalid number")
		}
	}
	if i == len(s) {
		return s[i:], &JSONToken{Kind: JSONTokenKindNumber, Raw: s}
	}
	if (s[i] == 'e') || (s[i] == 'E') {
		i++
		if i == len(s) {
			panic("invalid number")
		}
		if (s[i] == '+') || (s[i] == '-') {
			i++
		}
		if i == len(s) {
			panic("invalid number")
		}
		if ('0' <= s[i]) && (s[i] <= '9') {
			i++
			for (i < len(s)) && ('0' <= s[i]) && (s[i] <= '9') {
				i++
			}
		} else {
			panic("invalid number")
		}
	}
	return s[i:], &JSONToken{Kind: JSONTokenKindNumber, Raw: s[:i]}
}

type JSONTokenKind int

type JSONKind int

const (
	JSONKindUnknown JSONKind = iota
	JSONKindValue
	JSONKindObject
	JSONKindArray
)

type JSONASTNode struct {
	Kind           JSONKind
	ArrayChildren  []*JSONASTNode
	ObjectChildren []*JSONASTKV
	ValueKind      JSONTokenKind
	Value          string
}

type JSONASTKV struct {
	Key   string
	Value *JSONASTNode
}

const (
	JSONTokenKindUnknown JSONTokenKind = iota
	JSONTokenKindString
	JSONTokenKindNumber
	JSONTokenKindTrue
	JSONTokenKindFalse
	JSONTokenKindSpaces
	JSONTokenKindComma
	JSONTokenKindColon
	JSONTokenKindOpenArray
	JSONTokenKindCloseArray
	JSONTokenKindOpenObject
	JSONTokenKindCloseObject
	JSONTokenKindNull
)

func (k JSONTokenKind) String() string {
	switch k {
	case JSONTokenKindString:
		return "string"
	case JSONTokenKindNumber:
		return "number"
	case JSONTokenKindTrue:
		return "true"
	case JSONTokenKindFalse:
		return "false"
	case JSONTokenKindSpaces:
		return "spaces"
	case JSONTokenKindComma:
		return "comma"
	case JSONTokenKindColon:
		return "colon"
	case JSONTokenKindOpenArray:
		return "open-array"
	case JSONTokenKindCloseArray:
		return "close-array"
	case JSONTokenKindOpenObject:
		return "open-object"
	case JSONTokenKindCloseObject:
		return "close-object"
	case JSONTokenKindNull:
		return "null"
	default:
		return "unknown"
	}
}

type JSONToken struct {
	Kind JSONTokenKind
	Raw  string
}

package ujson

import (
	"unicode/utf8"

	"gno.land/p/teritori/utf16"
)

const (
	ReplacementChar = '\uFFFD' // Represents invalid code points.
)

// Ported from https://cs.opensource.google/go/go/+/refs/tags/go1.20.6:src/encoding/json/encode.go
func FormatString(s string) string {
	const escapeHTML = true
	e := `"` // e.WriteByte('"')
	start := 0
	for i := 0; i < len(s); {
		if b := s[i]; b < utf8.RuneSelf {
			if htmlSafeSet[b] || (!escapeHTML && safeSet[b]) {
				i++
				continue
			}
			if start < i {
				e += s[start:i] // e.WriteString(s[start:i])
			}
			e += "\\" // e.WriteByte('\\')
			switch b {
			case '\\', '"':
				e += string(b) // e.WriteByte(b)
			case '\n':
				e += "n" // e.WriteByte('n')
			case '\r':
				e += "r" // e.WriteByte('r')
			case '\t':
				e += "t" // e.WriteByte('t')
			default:
				// This encodes bytes < 0x20 except for \t, \n and \r.
				// If escapeHTML is set, it also escapes <, >, and &
				// because they can lead to security holes when
				// user-controlled strings are rendered into JSON
				// and served to some browsers.
				e += `u00`              // e.WriteString(`u00`)
				e += string(hex[b>>4])  // e.WriteByte(hex[b>>4])
				e += string(hex[b&0xF]) // e.WriteByte(hex[b&0xF])
			}
			i++
			start = i
			continue
		}
		c, size := utf8.DecodeRuneInString(s[i:])
		if c == utf8.RuneError && size == 1 {
			if start < i {
				e += s[start:i] // e.WriteString(s[start:i])
			}
			e += `\ufffd` // e.WriteString(`\ufffd`)
			i += size
			start = i
			continue
		}
		// U+2028 is LINE SEPARATOR.
		// U+2029 is PARAGRAPH SEPARATOR.
		// They are both technically valid characters in JSON strings,
		// but don't work in JSONP, which has to be evaluated as JavaScript,
		// and can lead to security holes there. It is valid JSON to
		// escape them, so we do so unconditionally.
		// See http://timelessrepo.com/json-isnt-a-javascript-subset for discussion.
		if c == '\u2028' || c == '\u2029' {
			if start < i {
				e += s[start:i] // e.WriteString(s[start:i])
			}
			e += `\u202`            // e.WriteString(`\u202`)
			e += string(hex[c&0xF]) // e.WriteByte(hex[c&0xF])
			i += size
			start = i
			continue
		}
		i += size
	}
	if start < len(s) {
		e += s[start:] // e.WriteString(s[start:])
	}
	e += `"` // e.WriteByte('"')
	return e
}

// Ported from https://cs.opensource.google/go/go/+/refs/tags/go1.20.6:src/encoding/json/decode.go
// unquote converts a quoted JSON string literal s into an actual string t.
// The rules are different than for Go, so cannot use strconv.Unquote.
func ParseString(s string) string {
	o, ok := unquoteBytes([]byte(s))
	if !ok {
		panic("invalid string")
	}
	return string(o)
}

func unquoteBytes(s []byte) (t []byte, ok bool) {
	if len(s) < 2 || s[0] != '"' || s[len(s)-1] != '"' {
		return
	}
	s = s[1 : len(s)-1]

	// Check for unusual characters. If there are none,
	// then no unquoting is needed, so return a slice of the
	// original bytes.
	r := 0
	for r < len(s) {
		c := s[r]
		if c == '\\' || c == '"' || c < ' ' {
			break
		}
		if c < utf8.RuneSelf {
			r++
			continue
		}
		rr, size := utf8.DecodeRune(s[r:])
		if rr == utf8.RuneError && size == 1 {
			break
		}
		r += size
	}
	if r == len(s) {
		return s, true
	}

	b := make([]byte, len(s)+2*utf8.UTFMax)
	w := copy(b, s[0:r])
	for r < len(s) {
		// Out of room? Can only happen if s is full of
		// malformed UTF-8 and we're replacing each
		// byte with RuneError.
		if w >= len(b)-2*utf8.UTFMax {
			nb := make([]byte, (len(b)+utf8.UTFMax)*2)
			copy(nb, b[0:w])
			b = nb
		}
		switch c := s[r]; {
		case c == '\\':
			r++
			if r >= len(s) {
				return
			}
			switch s[r] {
			default:
				return
			case '"', '\\', '/', '\'':
				b[w] = s[r]
				r++
				w++
			case 'b':
				b[w] = '\b'
				r++
				w++
			case 'f':
				b[w] = '\f'
				r++
				w++
			case 'n':
				b[w] = '\n'
				r++
				w++
			case 'r':
				b[w] = '\r'
				r++
				w++
			case 't':
				b[w] = '\t'
				r++
				w++
			case 'u':
				r--
				rr := getu4(s[r:])
				if rr < 0 {
					return
				}
				r += 6
				if utf16.IsSurrogate(rr) {
					rr1 := getu4(s[r:])
					if dec := utf16.DecodeRune(rr, rr1); dec != ReplacementChar {
						// A valid pair; consume.
						r += 6
						w += utf8.EncodeRune(b[w:], dec)
						break
					}
					// Invalid surrogate; fall back to replacement rune.
					rr = ReplacementChar
				}

			}

		// Quote, control characters are invalid.
		case c == '"', c < ' ':
			return

		// ASCII
		case c < utf8.RuneSelf:
			b[w] = c
			r++
			w++

		// Coerce to well-formed UTF-8.
		default:
			rr, size := utf8.DecodeRune(s[r:])
			r += size
			w += utf8.EncodeRune(b[w:], rr)
		}
	}
	return b[0:w], true
}

// getu4 decodes \uXXXX from the beginning of s, returning the hex value,
// or it returns -1.
func getu4(s []byte) rune {
	if len(s) < 6 || s[0] != '\\' || s[1] != 'u' {
		return -1
	}
	var r rune
	for _, c := range s[2:6] {
		switch {
		case '0' <= c && c <= '9':
			c = c - '0'
		case 'a' <= c && c <= 'f':
			c = c - 'a' + 10
		case 'A' <= c && c <= 'F':
			c = c - 'A' + 10
		default:
			return -1
		}
		r = r*16 + rune(c)
	}
	return r
}

package ujson

import (
	"strings"
	"testing"
	"time"

	"gno.land/p/demo/avl"
)

func TestAST(t *testing.T) {
	json := `{"a":[42, null, true, false, "hello\n\t\r"],"b":-3,"c":{"ia":{}, "ib":{     "foo"   :    "bar"}},"d":4,"e":5}`
	tokens := tokenize(json)
	expected := 44
	if len(tokens) != expected {
		t.Errorf("Expected %d tokens, got %d", expected, len(tokens))
	}
	remainingTokens, ast := parseAST(tokens)
	if len(remainingTokens) != 0 {
		t.Errorf("Expected 0 remaining tokens, got %d", len(remainingTokens))
	}
	if ast.Kind != JSONKindObject {
		t.Errorf("Expected root node to be an object, got %s", ast.Kind)
	}
	expectedTree := `{"a":[42,null,true,false,"hello\n\t\r"],"b":-3,"c":{"ia":{},"ib":{"foo":"bar"}},"d":4,"e":5}`
	if ast.String() != expectedTree {
		t.Errorf("Expected root node to be `%s`, got `%s`", expectedTree, ast.String())
	}
}

type TestType struct {
	A []string `json:"a"`
	B int      `json:"b"`
	C SubTestType
	D uint `json:"d"`
	E int  `json:"e"`
	F bool `json:"f"`
	// G       *EmptyType `json:"g"`
	AVLTree *avl.Tree `json:"avlTree"`
}

func (tt *TestType) FromJSON(ast *JSONASTNode) {
	ast.ParseObject([]*ParseKV{
		{Key: "a", ArrayParser: func(children []*JSONASTNode) {
			tt.A = make([]string, len(children))
			for i, child := range children {
				child.ParseAny(&tt.A[i])
			}
		}},
		{Key: "b", Value: &tt.B},
		{Key: "c", Value: &tt.C},
		{Key: "d", Value: &tt.D},
		{Key: "e", Value: &tt.E},
		{Key: "f", Value: &tt.F},
		// {Key: "g", Value: &tt.G},
		{Key: "avlTree", CustomParser: func(node *JSONASTNode) {
			if node.Kind != JSONKindObject {
				panic("Expected avlTree to be an object")
			}
			tt.AVLTree = avl.NewTree()
			for _, child := range node.ObjectChildren {
				var t string
				child.Value.ParseAny(&t)
				tt.AVLTree.Set(child.Key, t)
			}
		}},
	})
}

func (tt TestType) ToJSON() string {
	iSlice := make([]interface{}, len(tt.A))
	for i, v := range tt.A {
		iSlice[i] = v
	}
	return FormatObject([]FormatKV{
		{Key: "a", Value: FormatSlice(iSlice), Raw: true},
		{Key: "b", Value: tt.B},
		{Key: "c", Value: tt.C},
		{Key: "d", Value: tt.D},
		{Key: "e", Value: tt.E},
		{Key: "f", Value: tt.F},
		// {Key: "g", Value: tt.G},
		{Key: "avlTree", Value: tt.AVLTree},
	})
}

