package gnft

import (
	"std"

	"gno.land/p/demo/grc/grc721"
	"gno.land/r/demo/users"

	pusers "gno.land/p/demo/users"
)

var (
	admin std.Address = "g1y3uyaa63sjxvah2cx3c2usavwvx97kl8m2v7ye" // deployed position contract
	gnft              = grc721.NewBasicNFT("GNOSWAP NFT", "GNFT")
)

func init() {
}

// Getters
func TotalSupply() uint64 {
	return gnft.TokenCount()
}

func GetTokenURI(tid grc721.TokenID) string {
	uri, err := gnft.TokenURI(tid)
	if err != nil {
		panic(err.Error())
	}

	return string(uri)
}

func BalanceOf(user pusers.AddressOrName) uint64 {
	balance, err := gnft.BalanceOf(users.Resolve(user))
	if err != nil {
		panic(err.Error())
	}

	return balance
}

func OwnerOf(tid grc721.TokenID) std.Address {
	owner, err := gnft.OwnerOf(tid)
	if err != nil {
		panic(err.Error())
	}

	return owner
}

func IsApprovedForAll(owner, user pusers.AddressOrName) bool {
	return gnft.IsApprovedForAll(users.Resolve(owner), users.Resolve(user))
}

func GetApproved(tid grc721.TokenID) (std.Address, bool) {
	addr, err := gnft.GetApproved(tid)
	if err != nil {
		return "", false
	}

	return addr, true
}

// Setters

func Approve(user pusers.AddressOrName, tid grc721.TokenID) {
	err := gnft.Approve(users.Resolve(user), tid)
	if err != nil {
		panic(err.Error())
	}
}

func SetApprovalForAll(user pusers.AddressOrName, approved bool) {
	err := gnft.SetApprovalForAll(users.Resolve(user), approved)
	if err != nil {
		panic(err.Error())
	}
}

func TransferFrom(from, to pusers.AddressOrName, tid grc721.TokenID) {
	err := gnft.TransferFrom(users.Resolve(from), users.Resolve(to), tid)
	if err != nil {
		panic(err.Error())
	}
}

// Admin

func Mint(to pusers.AddressOrName, tid grc721.TokenID) {
	caller := std.PreviousRealm().Address()
	assertIsAdmin(caller)
	err := gnft.Mint(users.Resolve(to), tid)
	if err != nil {
		panic(err.Error())
	}

	tokenRaw := getImageBase64(tid)
	gnft.SetTokenURI(tid, grc721.TokenURI(tokenRaw))
}

func Burn(tid grc721.TokenID) {
	caller := std.PreviousRealm().Address()
	assertIsAdmin(caller)
	err := gnft.Burn(tid)
	if err != nil {
		panic(err.Error())
	}
}

func SetAdmin(newAdmin pusers.AddressOrName) {
	caller := std.PreviousRealm().Address()
	assertIsAdmin(caller)
	admin = users.Resolve(newAdmin)
}

// Render

func Render(path string) string {
	switch {
	case path == "":
		return gnft.RenderHome()
	default:
		return "404\n"
	}
}

// Util
func assertIsAdmin(address std.Address) {
	if address != admin {
		panic("restricted access")
	}
}

func Exists(tid grc721.TokenID) bool {
	_, err := gnft.OwnerOf(tid)
	if err != nil {
		return false
	}

	return true
}

package gnft

import (
	"std"
	"testing"

	pusers "gno.land/p/demo/users"
)

func TestGNFT(t *testing.T) {
	// admin := pusers.AddressOrName("g1y3uyaa63sjxvah2cx3c2usavwvx97kl8m2v7ye")
	adminRealm := std.NewUserRealm(admin)
	std.TestSetRealm(adminRealm)

	adminUser := pusers.AddressOrName(admin)

	// mint to admin
	Mint(adminUser, "1")
	Mint(adminUser, "2")
	Mint(adminUser, "3")
	Mint(adminUser, "4")
	Mint(adminUser, "5")
	Mint(adminUser, "6")
	Mint(adminUser, "7")
	Mint(adminUser, "8")
	Mint(adminUser, "9")
	Mint(adminUser, "10")
}

func TestGetTokenURI(t *testing.T) {
	url := GetTokenURI("1")
	if url != NFT_IMAGE_01 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_01, url)
	}

	url = GetTokenURI("2")
	if url != NFT_IMAGE_02 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_02, url)
	}

	url = GetTokenURI("3")
	if url != NFT_IMAGE_03 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_03, url)
	}

	url = GetTokenURI("4")
	if url != NFT_IMAGE_04 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_04, url)
	}

	url = GetTokenURI("5")
	if url != NFT_IMAGE_05 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_05, url)
	}

	url = GetTokenURI("6")
	if url != NFT_IMAGE_03 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_03, url)
	}

	url = GetTokenURI("7")
	if url != NFT_IMAGE_01 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_01, url)
	}

	url = GetTokenURI("7")
	if url != NFT_IMAGE_01 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_01, url)
	}

	url = GetTokenURI("8")
	if url != NFT_IMAGE_04 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_04, url)
	}

	url = GetTokenURI("9")
	if url != NFT_IMAGE_03 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_03, url)
	}

	url = GetTokenURI("10")
	if url != NFT_IMAGE_05 {
		t.Errorf("expected %s, got %s", NFT_IMAGE_05, url)
	}

	// PANIC
	shouldPanicWithMsg(
		t,
		func() {
			GetTokenURI("11")
		},
		"invalid token id",
	)

}

func shouldPanicWithMsg(t *testing.T, f func(), msg string) {
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("The code did not panic")
		} else {
			if r != msg {
				t.Errorf("excepted panic(%v), got(%v)", msg, r)
			}
		}
	}()
	f()
}

