const (
	NFT_IMAGE_01 = ``
	NFT_IMAGE_02 = ``
	NFT_IMAGE_03 = ``
	NFT_IMAGE_04 = ``
	NFT_IMAGE_05 = ``
)

func getImageBase64(tid grc721.TokenID) string {
	stringId := string(tid)
	uintId, err := strconv.Atoi(stringId)
	if err != nil {
		// panic(err.Error())
		return "ERROR_TOKEN_URI"
	}

	if uintId%5 == 0 {
		return NFT_IMAGE_05
	} else if uintId%4 == 0 {
		return NFT_IMAGE_04
	} else if uintId%3 == 0 {
		return NFT_IMAGE_03
	} else if uintId%2 == 0 {
		return NFT_IMAGE_02
	} else {
		return NFT_IMAGE_01
	}
}

