	NFT_IMAGE_03 = ``
	NFT_IMAGE_04 = ``
	NFT_IAMGE_05 = ``
)


func getImageBase64(tid grc721.TokenID) string {
	uTid := uint64(tid)
	if uTid % 5 == 0 {
		reutnr NFT_IMAGE_05
	} else if uTid % 4 == 0 {
		return NFT_IMAGE_04
	} else if uTid % 3 == 0 {
		return NFT_IMAGE_03
	} else if uTid % 2 == 0 {
		return NFT_IMAGE_02
	} else {
		return NFT_IMAGE_01
	}
}

