func getImageBase64(tid grc721.TokenID) string {
	stringId := string(tid)
	uintId, err := strconv.Atoi(stringId)
	if err != nil {
		// panic(err.Error())
		return "ERROR_TOKEN_URI"
	}

	if uintId%5 == 0 {
		return NFT_IMAGE_05
