func TestIsSlug(t *testing.T) {
	cases := []struct {
		name, slug string
		want       bool
	}{
		{
			name: "empty",
			want: false,
		},
		{
			name: "one letter",
			slug: "a",
			want: true,
		},
		{
			name: "one unicode letter",
			slug: "",
			want: true,
		},
		{
			name: "one word",
			slug: "foo",
			want: true,
		},
		{
			name: "one unicode word",
			slug: "fo",
			want: true,
		},
		{
			name: "many words",
			slug: "foo-bar-baz",
			want: true,
		},
		{
			name: "many unicode words",
			slug: "fo-br-bz",
			want: true,
		},
		{
			name: "with spaces",
			slug: "foo bar",
			want: false,
		},
		{
			name: "with invalid chars",
			slug: "foo/bar",
			want: false,
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Act
			got := blog.IsSlug(tc.slug)

			// Assert
			if got != tc.want {
				t.Fatalf("expected slug check to return: %v", tc.want)
			}
		})
	}
}

