package gnome

import (
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/ufmt"

	"gno.land/p/gnome/alerts"
	gnome "gno.land/p/gnome/dao"
	router "gno.land/p/gnome/router/v1"
)

const (
	dateFmt             = "2006-01-02 15:04 MST"
	proposalTakeoverMsg = "For the proposal outcome to change it has to be taken over by a parent DAO by voting on it"
)

const (
	customStyle = `
<style>
.custom ul { padding-left: 20px; }
.custom li { list-style-type: disc; }
.custom li.current { font-weight: 900; }
.custom li > p { margin: 0px; }
</style>
`
	paginatorStyle = `<style>
.paginator { text-align: center; }
.paginator a { text-decoration: none; }
.paginator a:hover { text-decoration: underline; }
.paginator .left { padding-right: 4px; }
.paginator .right { padding-left: 4px; }
</style>`
)

func renderAlerts() string {
	if gnomeDAO.IsLocked() {
		msg := "Realm is locked"
		if reason := gnomeDAO.LockReason(); reason != "" {
			msg += "</br>" + reason
		}

		return alerts.NewError(msg)
	}

	if IsProposalsAdvanceNeeded() {
		return alerts.NewWarning(
			newGnoStudioConnectLink("AdvanceProposals", "Proposals advance needed"),
		)
	}
	return ""
}

func renderDAO(res router.ResponseWriter, req router.Request) {
	var (
		dao     *gnome.DAO
		daoPath = req.Route
	)

	if daoPath == "" {
		dao = gnomeDAO
		daoPath = nameCouncilDAO
	} else {
		var found bool
		dao, found = daos.GetByPath(daoPath)
		if !found {
			res.Write("DAO Not Found")
			return
		}

		// TODO: Add lock dismissal reason when available
		if dao.IsLocked() {
			res.Write(alerts.NewError("DAO is dismissed"))
		}
	}

	res.Writef(
		"# Gno.me DAO\n"+
			"## %s\n"+
			"%s\n\n"+
			"[View Proposals of %s](%s)\n",
		dao.Title(),
		dao.Manifest(),
		dao.Title(),
		makeProposalsURI(daoPath, true),
	)

	res.Write("## " + dao.Title() + " Members\n")
	for _, m := range dao.Members() {
		res.Write("- " + m.String() + "\n")
	}

	res.Write("\n" + customStyle + "\n\n")

	res.Write("## Organization\n\n")
	res.Write(renderOrganizationTree(daoPath))
	res.Write("\n")
}

func renderProposals(res router.ResponseWriter, req router.Request) {
	daoPath := req.Route
	dao, found := daos.GetByPath(daoPath)
	if !found {
		res.Write("DAO Not Found")
		return
	}

	daoProposals := proposals.GetAllByDAO(dao.Path())
	count := len(daoProposals)
	if count == 0 {
		res.Write("DAO has no proposals")
		return
	}

	realmPath := makeRealmPath(req.Path)
	pages := gnome.NewPaginator(realmPath, gnome.WithItemCount(count))

	// TODO: Add links to toggle display of dismissed proposals (when DAO dismissal is implemented)

	res.Writef("# %s: Proposals\n", dao.Title())
	pages.Iterate(func(i int) bool {
		if i >= count {
			return true
		}

		p := daoProposals[i]
		_ = advanceProposal(p) // TODO: Handle errors when render notice support is implemented
		path := makeProposalURI(p.ID(), true)
		res.Writef("- [#%s %s](%s) (%s)\n", p.ID(), p.Title(), path, p.Status())
		return false
	})

	if pages.IsEnabled() {
		res.Write(renderPaginator(pages))
	}
}

// TODO: Improve renderProposal code
func renderProposal(res router.ResponseWriter, req router.Request) {
	rawID := req.Route
	id, err := strconv.Atoi(rawID)
	if err != nil {
		res.Write("Invalid proposal ID: " + gnome.EscapeHTML(rawID))
		return
	}

	proposal, found := proposals.GetByID(gnome.ID(id))
	if !found {
		res.Write("Proposal Not Found")
		return
	}

	var (
		outcome gnome.ProposalStatus
		status  = proposal.Status()
	)

	// When the status is not final advance the proposal to calculate the current outcome
	if !status.IsFinal() {
		_ = advanceProposal(proposal) // TODO: Implement generic alert support for render and use it to render errors
		outcome = proposal.Status()

		// Validate if proposal is valid for the current state
		if err := proposal.Validate(); err != nil {
			res.Write(alerts.NewError(err.Error()))
		}

		// Warn when the outcome could change if a member of a parent DAO votes on this proposal.
		// Proposal choice is only available when there is a majority, so there is voting concensus.
		if proposal.Choice() != gnome.ChoiceNone && !proposal.HasVotingDeadlinePassed() {
			res.Write(alerts.NewWarning(proposalTakeoverMsg))
		}
	} else if status == gnome.StatusDismissed {
		// Display an alert with the dismiss reason
		res.Write(alerts.NewWarning(proposal.StatusReason()))
	}

	dao := proposal.DAO()
	daoPath := dao.Path()
	if proposal.HasBeenPromoted() {
		uri := makeDAOURI(daoPath, true)
		link := alerts.NewLink(uri, dao.Title())
		res.Write(alerts.NewWarning("Proposal has been promoted to " + link + " DAO"))
	}

	res.Write("# #" + proposal.ID().String() + " " + proposal.Title() + "\n")
	res.Write("- Type: " + proposal.Strategy().Name() + "\n")
	res.Write("- Created: " + proposal.CreatedAt().UTC().Format(dateFmt) + "\n")
	res.Write("- Proposer: " + proposal.Proposer().String() + "\n")
	res.Write("- Status: " + getProposalStatusMarkdown(status, proposal.Choice(), proposal.StatusReason()) + "\n")

	if !status.IsFinal() {
		if outcome == gnome.StatusReview {
			res.Write("- Review Deadline: " + proposal.ReviewDeadline().UTC().Format(dateFmt) + "\n")
		} else {
			res.Write("- Voting Deadline: " + proposal.VotingDeadline().UTC().Format(dateFmt) + "\n")
			res.Write("- Expected Outcome: " + getProposalStatusMarkdown(outcome, proposal.Choice(), proposal.StatusReason()) + "\n")

			// Vote line should be render as long as voting deadline is not reached.
			// This is required for proposals that have to be advanced after deadline is reached.
			if !proposal.HasVotingDeadlinePassed() {
				res.Write("\n" + newGnoStudioConnectLink("Vote", "Vote on this proposal") + "\n")
			}
		}
	}

	if s := proposal.Description(); s != "" {
		res.Write("## Description\n" + s + "\n")
	}

	if r, ok := proposal.Strategy().(gnome.ParamsRenderer); ok {
		// TODO: Use custom HTML component to allow users to toggle params visibility
		if s := r.RenderParams(); s != "" {
			res.Write("## Parameters\n\n" + s + "\n")
		}
	}

	res.Write("## Votes\n")
	record := proposal.VotingRecord()
	if record.VoteCount() == 0 {
		res.Write("The proposal has no votes\n")
	} else {
		// TODO: Render percentages for each voting choice and abstentions?
		record.Iterate(func(c gnome.VoteChoice, count uint) bool {
			res.Writef("- %s: %d\n", string(c), count)
			return false
		})

		res.Write("## Participation\n")
		renderProposalParticipation(res, record.Votes())
	}

	// If proposal has been promoted to a parent DAO render participation in child DAOs
	if proposal.HasBeenPromoted() {
		res.Write("## Sub DAOs Participation\n")
		daos := proposal.Promotions()
		records := proposal.VotingRecords()
		for i := len(records) - 2; i >= 0; i-- { // reverse iteration excluding record for current DAO
			r := records[i]
			dao := daos[i]
			res.Write("### [" + dao.Title() + "](" + makeDAOURI(daoPath, true) + "]\n")
			renderProposalParticipation(res, r.Votes())
		}
	}
}

func renderParams(res router.ResponseWriter, req router.Request) {
	res.Write("# Gno.me DAO: Parameters\n")
	res.Write("## Proposal\n")
	res.Write("**General**\n")
	res.Write("- Review Deadline: " + gnome.HumanizeDuration(parameters.ReviewDeadline) + "\n")

	res.Write("\n**Voting Periods**\n")
	parameters.VotingPeriods.Iterate(func(name string, period time.Duration) bool {
		res.Write("- `" + name + "`: " + gnome.HumanizeDuration(period) + "\n")
		return false
	})
}

func renderProposalParticipation(res router.ResponseWriter, votes []gnome.Vote) {
	for _, v := range votes {
		choice := string(v.Choice)
		if v.Reason != "" {
			// TODO: Long reasons have to break lines to fit making web UI look bad
			choice += ` "` + gnome.EscapeHTML(v.Reason) + `"`
		}

		res.Writef("- %s: voted %s\n", v.Address.String(), choice)
	}
}

// TODO: Use the UI package for HTML elements because rendered Markdown styles break the tree
func renderOrganizationTree(currentPath string) string {
	var item string
	if gnomeDAO.Name() == currentPath {
		item = `<li class="current">` + gnomeDAO.Title() + `</li>`
	} else {
		uri := makeDAOURI(gnomeDAO.Path(), true)
		item = `<li>` + alerts.NewLink(uri, gnomeDAO.Title()) + `</li>`
	}
	return `<div class="custom"><ul>` + item + renderSubTree(gnomeDAO, currentPath) + `</ul></div>`
}

func renderSubTree(parentDAO *gnome.DAO, currentPath string) string {
	var (
		buf  strings.Builder
		item string
	)

	for _, dao := range parentDAO.SubDAOs() {
		if dao.IsLocked() {
			// Skip dismissed DAOs
			// TODO: Render filter option to toggle dismissed DAOs visibility
			continue
		}

		if dao.Path() == currentPath {
			item = `<li class="current">` + dao.Title() + `</li>`
		} else {
			uri := makeDAOURI(dao.Path(), true)
			item = `<li>` + alerts.NewLink(uri, dao.Title()) + `</li>`
		}

		buf.WriteString(item)

		if len(dao.SubDAOs()) > 0 {
			buf.WriteString(renderSubTree(dao, currentPath))
		}
	}
	return `<ul>` + buf.String() + `</ul>`
}

func renderPaginator(p gnome.Paginator) string {
	var out string
	if s := p.PrevPageURI(); s != "" {
		out = ufmt.Sprintf(`<a href="%s" class="left">&lt;-</a>`, s)
	} else {
		out += `<span class="left">--</span>`
	}

	// TODO: Add display links to other page numbers?
	out += ufmt.Sprintf("page %d", p.Page())

	if s := p.NextPageURI(); s != "" {
		out += ufmt.Sprintf(`<a href="%s" class="right">-&gt;</a>`, s)
	} else {
		out += `<span class="right">--</span>`
	}

	return "\n" + paginatorStyle + `<p class="paginator">` + out + `</p>`
}

func advanceProposal(p *gnome.Proposal) error {
	status := p.Status()
	if status == gnome.StatusReview && p.HasReviewDeadlinePassed() {
		if err := p.Activate(); err != nil {
			return err
		}

		status = p.Status()
	}

	if status == gnome.StatusActive {
		// Tally active proposals to always have an up to date state with the current proposal outcome
		if err := p.Tally(); err != nil {
			return err
		}
	}
	return nil
}

func getProposalStatusMarkdown(s gnome.ProposalStatus, c gnome.VoteChoice, reason string) string {
	switch s {
	case gnome.StatusPassed:
		return ufmt.Sprintf("**%s** (%s)", s, string(c))
	case gnome.StatusRejected:
		// Rejected proposal might have a reason
		if reason == "" {
			return ufmt.Sprintf("**%s**", s)
		} else {
			return ufmt.Sprintf("**%s** (%s)", s, reason)
		}
	case gnome.StatusDismissed, gnome.StatusFailed:
		return ufmt.Sprintf("**%s** (%s)", s, reason)
	default:
		return ufmt.Sprintf("**%s**", s)
	}
}

func newGnoStudioConnectLink(functionName, label string) string {
	href := makeGnoStudioConnectURL(functionName)
	return alerts.NewLink(href, label)
}

package gnome

import (
	"errors"
	"std"
	"strings"
	"time"

	gnome "gno.land/p/gnome/dao"
)

func newBudgetStrategy(council *gnome.DAO, budget string) budgetStrategy {
	if council == nil {
		panic("council DAO is requried")
	}

	if !council.IsSuperCouncil() {
		panic("budget strategy expects DAO to be a super council")
	}

	budget = strings.TrimSpace(budget)
	if budget == "" {
		panic("budget is required")
	}

	// The council DAO must have at least one sub DAO which should the main DAO.
	// The first sub DAO is some times used to check if a vote is valid.
	if len(council.SubDAOs()) == 0 {
		panic("budget strategy expects council DAO to have at least one sub DAO")
	}

	return budgetStrategy{
		choices: []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo},
		council: council,
		budget:  budget, // TODO: Validate/split budget format? (ex. AMOUNTSYMBOL: 10USD)
	}
}

type budgetStrategy struct {
	choices []gnome.VoteChoice
	council *gnome.DAO
	budget  string
}

// Name returns the name of the strategy.
func (budgetStrategy) Name() string {
	return StrategyNameBudget
}

// Quorum returns the minimum required percentage of DAO member votes
// required for a proposal to pass.
func (budgetStrategy) Quorum() float64 {
	return 0.51
}

// VotingPeriod returns the period that a proposal should allow voting.
func (budgetStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameBudget)
	return period
}

// VoteChoices returns the valid voting choices for the strategy.
func (s budgetStrategy) VoteChoices() []gnome.VoteChoice {
	return s.choices
}

// CheckVote checks that a vote is valid for the strategy.
func (s budgetStrategy) CheckVote(addr std.Address, _ gnome.VoteChoice, _ string) error {
	// Check that voter address belongs to a council DAO member
	if s.council.HasMember(addr) {
		return nil
	}

	// Make sure the main DAO was not dismissed and check that voter address belongs to a main DAO member
	// TODO: Check DAO status instead when DAO dismissal is implemented
	if sub := s.council.SubDAOs(); len(sub) > 0 {
		mainDAO := sub[0]
		if !mainDAO.HasMember(addr) {
			return errors.New("only members of the council DAO or main DAO can vote on budget proposals")
		}
	} else {
		return errors.New("main DAO not found")
	}
	return nil
}

// Tally counts the votes and returns the winner voting choice.
func (budgetStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	// Consider abstentions to make the majority absolute
	abstentions := len(dao.Members()) - r.VoteCount()
	if choice, ok := gnome.SelectChoiceByMajority(r, abstentions); ok {
		return choice
	}
	return gnome.ChoiceNone
}

// RenderParams returns a markdown with the rendered strategy parameters.
func (s budgetStrategy) RenderParams() string {
	return "Budget: " + gnome.EscapeHTML(s.budget)
}

package gnome

import (
	"errors"
	"fmt"
	"std"
	"testing"
	"time"

	"gno.land/p/demo/testutils"

	gnome "gno.land/p/gnome/dao"
)

func TestBudgetStrategy(t *testing.T) {
	cases := []struct {
		name    string
		council *gnome.DAO
		err     string
	}{
		{
			name: "ok",
			council: gnome.MustNew("council", "Council",
				gnome.AssignAsSuperCouncil(),
				gnome.WithSubDAO(
					gnome.MustNew("main", "Main"),
				),
			),
		},
		{
			name: "nil council",
			err:  "council DAO is requried",
		},
		{
			name:    "no super council",
			council: gnome.MustNew("council", "Council"),
			err:     "budget strategy expects DAO to be a super council",
		},
		{
			name:    "council without main DAO",
			council: gnome.MustNew("council", "Council", gnome.AssignAsSuperCouncil()),
			err:     "budget strategy expects council DAO to have at least one sub DAO",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			name := StrategyNameBudget
			quorum := 0.51
			votingPeriod := time.Hour * 24 * 7
			choices := fmt.Sprintf("%v", []gnome.VoteChoice{
				gnome.ChoiceYes,
				gnome.ChoiceNo,
			})

			// Act
			var s budgetStrategy
			err := handlePanic(t, func() {
				s = newBudgetStrategy(tc.council, "1USD")
			})

			// Assert
			if tc.err != "" {
				assertError(t, tc.err, err)
				return
			}

			assertNoError(t, err)

			if got := s.Name(); got != name {
				t.Fatalf("expected strategy name: '%s', got: '%s'", name, got)
			}

			if got := s.Quorum(); got != quorum {
				t.Fatalf("expected strategy quorum: %.2f, got: %.2f", quorum, got)
			}

			if got := s.VotingPeriod(); got != votingPeriod {
				t.Fatalf("expected strategy voting period: %d, got: %d", votingPeriod, got)
			}

			if got := fmt.Sprintf("%v", s.VoteChoices()); got != choices {
				t.Fatalf("expected strategy vote choices: %s, got: %s", choices, got)
			}
		})
	}
}

func TestBudgetStrategyCheckVote(t *testing.T) {
	councilMember := newTestMember(t, "council")
	mainMember := newTestMember(t, "main")
	council := gnome.MustNew(
		"council",
		"Council",
		gnome.AssignAsSuperCouncil(),
		gnome.WithMembers(councilMember),
		gnome.WithSubDAO(
			gnome.MustNew("main", "Main", gnome.WithMembers(mainMember)),
		),
	)

	cases := []struct {
		name    string
		address std.Address
		choice  gnome.VoteChoice
		council *gnome.DAO
		err     string
	}{
		{
			name:    "council DAO vote",
			choice:  gnome.ChoiceYes,
			address: councilMember.Address,
			council: council,
		},
		{
			name:    "main DAO vote",
			choice:  gnome.ChoiceYes,
			address: mainMember.Address,
			council: council,
		},
		{
			name:    "non member vote",
			choice:  gnome.ChoiceNo,
			address: testutils.TestAddress("foo"),
			council: council,
			err:     "only members of the council DAO or main DAO can vote on budget proposals",
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			s := newBudgetStrategy(tc.council, "1USD")

			// Act
			err := s.CheckVote(tc.address, tc.choice, "")

			// Assert
			if tc.err != "" {
				assertError(t, tc.err, err)
			} else {
				assertNoError(t, err)
			}
		})
	}
}

func TestBudgetStrategyTally(t *testing.T) {
	council := gnome.MustNew(
		"council",
		"Council",
		gnome.AssignAsSuperCouncil(),
		gnome.WithMembers(
			newTestMember(t, "member1"),
			newTestMember(t, "member2"),
			newTestMember(t, "member3"),
			newTestMember(t, "member4"),
		),
		gnome.WithSubDAO(
			gnome.MustNew("main", "Main"),
		),
	)
	cases := []struct {
		name   string
		votes  []gnome.Vote
		choice gnome.VoteChoice
	}{
		{
			name: "majority",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceNo},
			},
			choice: gnome.ChoiceYes,
		},
		{
			name: "majority with abstentions",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceNo},
			},
			choice: gnome.ChoiceNo,
		},
		{
			name: "no quorum",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceNo},
				{Choice: gnome.ChoiceNo},
			},
			choice: gnome.ChoiceNone,
		},
		{
			name: "no quorum with abstentions",
			votes: []gnome.Vote{
				{Choice: gnome.ChoiceYes},
				{Choice: gnome.ChoiceYes},
			},
			choice: gnome.ChoiceNone,
		},
	}

	for _, tc := range cases {
		t.Run(tc.name, func(t *testing.T) {
			// Arrange
			record := gnome.NewVotingRecord()
			for _, v := range tc.votes {
				record.Add(v)
			}

			s := newBudgetStrategy(council, "1USD")

			// Act
			choice := s.Tally(council, *record)

			// Assert
			if choice != tc.choice {
				t.Fatalf("expected tally result choice: '%v', got: '%v'", tc.choice, choice)
			}
		})
	}
}

func handlePanic(t *testing.T, fn func()) (reason error) {
	t.Helper()

	defer func() {
		if r := recover(); r != nil {
			if err, _ := r.(error); err != nil {
				reason = err
			} else {
				reason = errors.New(fmt.Sprint(r))
			}
		}
	}()

	fn()
	return
}

package gnome

import (
	"errors"
	"strconv"
	"strings"
	"time"

	gnome "gno.land/p/gnome/dao"
)

// Minimum number of members per DAO.
// This requirement is enforced because two members DAO can only use plurality to tally.
const minMembersCount = 3

func newSubDAOCreationStrategy(daos daoIndex, name, title, manifest string, members []gnome.Member) subDAOCreationStrategy {
	if strings.TrimSpace(name) == "" {
		panic("sub DAO name is required")
	}

	if !gnome.IsSlug(name) {
		panic(`invalid sub DAO name, only letters from "a" to "z", numbers, "-" and "_" are allowed`)
	}

	if strings.TrimSpace(title) == "" {
		panic("sub DAO title is required")
	}

	if strings.TrimSpace(manifest) == "" {
		panic("sub DAO manifest is required")
	}

	if len(members) < minMembersCount {
		panic("sub DAOs require at least " + strconv.Itoa(minMembersCount) + " members")
	}

	return subDAOCreationStrategy{
		choices:  []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo},
		daos:     daos,
		name:     name,
		title:    title,
		manifest: manifest,
		members:  members,
	}
}

type subDAOCreationStrategy struct {
	choices               []gnome.VoteChoice
	daos                  daoIndex
	name, title, manifest string
	members               []gnome.Member
}

// Name returns the name of the strategy.
func (subDAOCreationStrategy) Name() string {
	return StrategyNameSubDAOCreation
}

// Quorum returns the minimum required percentage of DAO member votes
// required for a proposal to pass.
func (subDAOCreationStrategy) Quorum() float64 {
	return 1.0
}

// VotingPeriod returns the period that a proposal should allow voting.
func (subDAOCreationStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameSubDAOCreation)
	return period
}

// VoteChoices returns the valid voting choices for the strategy.
func (s subDAOCreationStrategy) VoteChoices() []gnome.VoteChoice {
	return s.choices
}

// Tally counts the votes and returns the winner voting choice.
func (subDAOCreationStrategy) Tally(dao *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	// Strategy need 100% participation to decide on the outcome.
	// Normally quorum should make sure all members voted before
	// tallying but otherwise tally should not return a valid outcome.
	if len(dao.Members()) != r.VoteCount() {
		return gnome.ChoiceNone
	}

	// This type of proposals can pass only when 100% of members vote YES.
	for _, v := range r.Votes() {
		// If there is at least one NO vote then proposal must be rejected
		if v.Choice == gnome.ChoiceNo {
			return gnome.ChoiceNo
		}
	}
	// Proposal should pass when all votes are YES
	return gnome.ChoiceYes
}

// Validate validates if a proposal is valid for the current state.
func (s subDAOCreationStrategy) Validate(p *gnome.Proposal) error {
	dao := p.DAO()
	path := dao.Path()
	if dao.IsLocked() {
		return errors.New("parent DAO '" + path + "' is locked")
	}

	subDAOPath := path + gnome.PathSeparator + s.name
	if s.daos.HasPathKey(subDAOPath) {
		return errors.New("sub DAO path has been taken by another DAO")
	}
	return nil
}

// RenderParams returns a markdown with the rendered strategy parameters.
func (s subDAOCreationStrategy) RenderParams() string {
	var (
		b        strings.Builder
		members  []string
		manifest = gnome.EscapeHTML(s.manifest)
	)

	for _, addr := range s.members {
		members = append(members, addr.String())
	}

	// TODO: Use a custom HTML table and add styling (vertical alignment, padding, ...)
	//       This would allow to remove the markdown "hacks" to improve the output layout
	b.WriteString("|||\n|---|---|\n")
	b.WriteString("| Name: | " + gnome.EscapeHTML(s.name) + " |\n")
	b.WriteString("| Title: | " + gnome.EscapeHTML(s.title) + " |\n")
	b.WriteString("| Members: | </br>" + strings.Join(members, "</br>") + "</br></br> |\n")
	b.WriteString("| Manifest:&nbsp;&nbsp; | " + strings.ReplaceAll(manifest, "\n", "</br>") + " |\n")

	return b.String()
}

// Execute creates the new sub DAO.
func (s subDAOCreationStrategy) Execute(dao *gnome.DAO) error {
	subDAO, err := gnome.New(s.name, s.title, gnome.WithManifest(s.manifest), gnome.WithMembers(s.members...))
	if err != nil {
		return err
	}

	// Add the new sub DAO to its parent
	dao.AddSubDAO(subDAO)

	// Index the new sub DAO
	s.daos.IndexByPath(subDAO)

	return nil
}

func newDAOMembersModificationStrategy(newMembers, removeMembers []gnome.Member) daoMembersModificationStrategy {
	if len(newMembers) == 0 && len(removeMembers) == 0 {
		panic("members are required")
	}

	return daoMembersModificationStrategy{
		choices:       []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo},
		newMembers:    newMembers,
		removeMembers: removeMembers,
	}
}

type daoMembersModificationStrategy struct {
	choices                   []gnome.VoteChoice
	newMembers, removeMembers []gnome.Member
}

// Name returns the name of the strategy.
func (daoMembersModificationStrategy) Name() string {
	return StrategyNameDAOMembersModification
}

// Quorum returns the minimum required percentage of DAO member votes
// required for a proposal to pass.
func (daoMembersModificationStrategy) Quorum() float64 {
	return 0.51
}

// VotingPeriod returns the period that a proposal should allow voting.
func (daoMembersModificationStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameDAOMembersModification)
	return period
}

// VoteChoices returns the valid voting choices for the strategy.
func (s daoMembersModificationStrategy) VoteChoices() []gnome.VoteChoice {
	return s.choices
}

// Tally counts the votes and returns the winner voting choice.
func (daoMembersModificationStrategy) Tally(_ *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	// Tally requires at least three votes to be able to tally by 2/3s super majority
	if r.VoteCount() < 3 {
		return gnome.ChoiceNone
	}

	if choice, ok := gnome.SelectChoiceBySuperMajority(r); ok {
		return choice
	}
	return gnome.ChoiceNone
}

// Validate validates if a proposal is valid for the current DAO state.
func (s daoMembersModificationStrategy) Validate(p *gnome.Proposal) error {
	// At least three members are required to enforce 2/3s majority on proposals
	dao := p.DAO()
	memberCount := len(dao.Members()) + len(s.newMembers) - len(s.removeMembers)
	if memberCount < minMembersCount {
		return errors.New("DAO must always have a minimum of " + strconv.Itoa(minMembersCount) + " members")
	}

	// TODO: Should we allow re-adding members to only change assigned roles?
	for _, m := range s.newMembers {
		if dao.HasMember(m.Address) {
			return errors.New("address is already a DAO member: " + m.Address.String())
		}
	}

	for _, m := range s.removeMembers {
		if !dao.HasMember(m.Address) {
			return errors.New("address is not a DAO member: " + m.Address.String())
		}
	}

	return nil
}

// Execute modifies main DAO members.
func (s daoMembersModificationStrategy) Execute(dao *gnome.DAO) error {
	for _, m := range s.newMembers {
		dao.AddMember(m)
	}

	for _, m := range s.removeMembers {
		dao.RemoveMember(m.Address)
	}

	return nil
}

// RenderParams returns a markdown with the rendered strategy parameters.
func (s daoMembersModificationStrategy) RenderParams() string {
	var b strings.Builder

	b.WriteString("|||\n|---|---|\n")

	if len(s.newMembers) > 0 {
		var members []string
		for _, m := range s.newMembers {
			members = append(members, m.String())
		}

		b.WriteString("| New Members: | " + strings.Join(members, "</br>") + "</br></br> |\n")
	}

	if len(s.removeMembers) > 0 {
		var members []string
		for _, m := range s.removeMembers {
			members = append(members, m.String())
		}

		b.WriteString("| Members to Remove: | " + strings.Join(members, "</br>") + " |\n")
	}

	return b.String()
}

func newSubDAODismissalStrategy(dao *gnome.DAO, x proposalIndex) subDAODismissalStrategy {
	if dao == nil {
		panic("DAO is required")
	}

	return subDAODismissalStrategy{
		choices:   []gnome.VoteChoice{gnome.ChoiceYes, gnome.ChoiceNo},
		dao:       dao,
		proposals: x,
	}
}

type subDAODismissalStrategy struct {
	choices   []gnome.VoteChoice
	dao       *gnome.DAO
	proposals proposalIndex
}

// Name returns the name of the strategy.
func (subDAODismissalStrategy) Name() string {
	return StrategyNameSubDAODismissal
}

// Quorum returns the minimum required percentage of DAO member votes
// required for a proposal to pass.
func (subDAODismissalStrategy) Quorum() float64 {
	return 0.51
}

// VotingPeriod returns the period that a proposal should allow voting.
func (subDAODismissalStrategy) VotingPeriod() time.Duration {
	period, _ := parameters.VotingPeriods.Get(StrategyNameSubDAODismissal)
	return period
}

// VoteChoices returns the valid voting choices for the strategy.
func (s subDAODismissalStrategy) VoteChoices() []gnome.VoteChoice {
	return s.choices
}

// Tally counts the votes and returns the winner voting choice.
func (subDAODismissalStrategy) Tally(_ *gnome.DAO, r gnome.VotingRecord) gnome.VoteChoice {
	if choice, ok := gnome.SelectChoiceByPlurality(r); ok {
		return choice
	}
	return gnome.ChoiceNone
}

// Validate validates if a proposal is valid for the current DAO state.
func (s subDAODismissalStrategy) Validate(p *gnome.Proposal) error {
	parentDAO := s.dao.Parent()
	if parentDAO == nil {
		return errors.New("the DAO to dismiss has no parent DAO")
	}

	parentName := p.DAO().Name()
	if parentDAO.Name() != parentName {
		return errors.New(`the DAO to dismiss must be a first level sub DAO of "` + parentName + `"`)
	}
	return nil
}

// Execute modifies main DAO members.
func (s subDAODismissalStrategy) Execute(*gnome.DAO) error {
	// Get the list of all sub DAOs and the root DAO to dismiss
	daos := append(collectSubDAOs(s.dao), s.dao)
	// Proposal dismissal requires a reason
	// TODO: Send proposal to Execute and add dismissal proposal link?
	reason := "Dismissed because of DAO dismissal: " + s.dao.Path()

	for _, dao := range daos {
		// Dismiss all proposals for the current DAO
		for _, p := range s.proposals.GetAllByDAO(dao.Path()) {
			if !p.Status().IsFinal() {
				p.Dismiss(reason)
			}
		}

		// Lock the DAO to dismiss it
		dao.Lock("")
	}
	return nil
}

// RenderParams returns a markdown with the rendered strategy parameters.
func (s subDAODismissalStrategy) RenderParams() string {
	return "DAO: " + s.dao.Path()
}

func collectSubDAOs(dao *gnome.DAO) []*gnome.DAO {
	daos := dao.SubDAOs()
	for _, sub := range daos[:] {
		daos = append(daos, collectSubDAOs(sub)...)
	}
	return daos
}

package gnome

import (
	"fmt"
	"testing"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/testutils"

	gnome "gno.land/p/gnome/dao"
)

func TestSubDAOCreationStrategy(t *testing.T) {
	cases := []struct {
		name, daoName, title, manifest, err string
		members                             []gnome.Member
	}{
