package tictactoe

import (
	"errors"
	"std"

	"gno.land/p/demo/ufmt"
)

// this file is @moul's work in #613
// a few changes and bugfixes have been made

type Game struct {
	player1, player2 std.Address
	board            [9]rune // 0=empty, 1=player1, 2=player2
	turnCtr          int
	winnerIdx        int
}

func NewGame(player1, player2 std.Address) (*Game, error) {
	if player1 == player2 {
		return nil, errors.New("cannot fight against self")
	}

	g := Game{
		player1:   player1,
		player2:   player2,
		winnerIdx: -1,
		turnCtr:   -1,
	}
	return &g, nil
}

// Partially recover a game
// The game is guaranteed to be legit in terms of number of tiles 1 and 2
// No winning detection is implemented here however
func RecoverGame(player1, player2 std.Address, board string) (*Game, error) {
	g, e := NewGame(player1, player2)
	if e != nil {
		return nil, e
	}
	if len(board) != 9 {
		return nil, ufmt.Errorf("invalid board length: %d", len(board))
	}
	num1, num2 := 0, 0
	runes := [9]rune{}
	for i, c := range board {
		switch c {
		case rune(0), '_', '-':
			runes[i] = rune(0)
		case rune(1), 'O', 'o':
			num1 += 1
			runes[i] = rune(1)
		case rune(2), 'X', 'x':
			num2 += 1
			runes[i] = rune(2)
		default:
			return nil, errors.New("invalid rune")
		}
	}
	if num1 != num2 && num1 != num2+1 {
		return nil, errors.New("invalid number of x and o")
	}
	g.board = runes
	g.turnCtr = num1 + num2
	g.winnerIdx = -1
	return g, nil
}

// start sets turnCtr to 0.
func (g *Game) Start() {
	if g.turnCtr != -1 {
		panic("game already started")
	}
	g.turnCtr = 0
}

func (g *Game) Play(player std.Address, posX, posY int) error {
	if !g.Started() {
		return errors.New("game not started")
	}

	if g.Turn() != player {
		return errors.New("invalid turn")
	}

	if g.IsOver() {
		return errors.New("game over")
	}

	// are posX and posY valid
	if posX < 0 || posY < 0 || posX > 2 || posY > 2 {
		return errors.New("posX and posY should be 0, 1 or 2")
	}

	// is slot already used?
	idx := xyToIdx(posX, posY)
	if g.board[idx] != 0 {
		return ufmt.Errorf("slot already used (%d, %d)", posX, posY)
	}

	// play
	playerVal := rune(g.turnCtr%2) + 1 // player1=1, player2=2
	g.board[idx] = playerVal

	// check if win
	if g.checkLastMoveWon(posX, posY) {
		g.winnerIdx = g.turnCtr
	}

	// change turn
	g.turnCtr++
	return nil
}

func (g Game) WouldWin(side rune, x, y int) bool {
	idx := xyToIdx(x, y)
	if g.board[idx] != rune(0) {
		panic("tile should be empty")
	}
	// place rune temporarily
	g.board[idx] = side
	b := g.checkLastMoveWon(x, y)
	g.board[idx] = rune(0)
	return b
}

func (g Game) checkLastMoveWon(posX, posY int) bool {
	// assumes the game wasn't won yet, and that the move was already applied.

	// check vertical line
	{
		a := g.At(posX, 0)
		b := g.At(posX, 1)
		c := g.At(posX, 2)
		if a == b && b == c {
			return true
		}
	}

	// check horizontal line
	{
		a := g.At(0, posY)
		b := g.At(1, posY)
		c := g.At(2, posY)
		if a == b && b == c {
			return true
		}
	}

	// diagonals
	{
		tl := g.At(0, 0)
		tr := g.At(0, 2)
		bl := g.At(2, 0)
		br := g.At(2, 2)
		c := g.At(1, 1)
		if posX == posY && tl == c && c == br {
			return true
		}
		if posX+posY == 2 && tr == c && c == bl {
			return true
		}
	}
	return false
}

func (g Game) At(posX, posY int) rune { return g.board[xyToIdx(posX, posY)] }
func (g Game) Winner() std.Address    { return g.PlayerByIndex(g.winnerIdx) }
func (g Game) Turn() std.Address      { return g.PlayerByIndex(g.turnCtr) }
func (g Game) TurnNumber() int        { return g.turnCtr }
func (g Game) IsDraw() bool           { return g.turnCtr > 8 && g.winnerIdx == -1 }
func (g Game) Started() bool          { return g.turnCtr >= 0 }

func (g Game) IsOver() bool {
	// draw
	if g.turnCtr > 8 {
		return true
	}

	// winner
	return g.Winner() != std.Address("")
}

func (g Game) Output() string {
	output := ""

	for y := 2; y >= 0; y-- {
		for x := 0; x < 3; x++ {
			val := g.At(x, y)
			switch val {
			case 0:
				output += "-"
			case 1:
				output += "O"
			case 2:
				output += "X"
			}
		}
		output += "\n"
	}

	return output
}

func (g Game) PlayerByIndex(idx int) std.Address {
	switch idx % 2 {
	case 0:
		return g.player1
	case 1:
		return g.player2
	default:
		return std.Address("")
	}
}

func xyToIdx(x, y int) int { return y*3 + x }

package games

import (
	"strings"

	"gno.land/p/demo/ufmt"
)

func Render(path string) string {
	return "#### r/demo/games\n\n" +
		"A collection of small games written in the Gno language:\n\n" +
		renderGames() + "\n\n"
}

func renderGames() string {
	var b strings.Builder
	for _, o := range []struct {
		title, path string
		authors     []string
		desc        string
	}{
		{"shifumi", "games/shifumi", []string{"mvertes"}, "a very simple rock, paper, scissors game"},
		{"tictactoe", "games/tictactoe", []string{"grepsuzette", "moul"}, "CPU vs Human tictactoe, no need for a wallet"},
	} {
		b.WriteString(ufmt.Sprintf(
			"* [%s](%s): %s\n",
			o.title, o.path,
			o.desc,
			RenderAuthors(o.authors, "@"),
		))
	}
	return b.String()
}

// ["a", "b", "c"] -> "a, b and c"
// Typical prefix is "@": "a", "b", "c" -> "@a, @b and @c"
func RenderAuthors(authors []string, optionalPrefix ...string) string {
	a := []string{}
	prefix := ""
	if len(optionalPrefix) > 0 {
		prefix = optionalPrefix[0]
	}
	for _, author := range authors {
		a = append(a, prefix+author)
	}
	switch len(a) {
	case 0:
		return "*?*"
	case 1:
		return a[0]
	default:
		return strings.Join(a[0:len(a)-1], ", ") + " and " + a[len(a)-1]
	}
}

package games

import (
	"testing"
)

func TestRenderAuthors(t *testing.T) {
	assertEqual(t, RenderAuthors([]string{}), "*?*")
	assertEqual(t, RenderAuthors([]string{"tim"}), "tim")
	assertEqual(t, RenderAuthors([]string{"tim", "jim"}), "tim and jim")
	assertEqual(t, RenderAuthors([]string{"tim", "jim", "kim"}), "tim, jim and kim")
	assertEqual(t, RenderAuthors([]string{"tim", "jim", "kim", "nim"}), "tim, jim, kim and nim")
	assertEqual(t, RenderAuthors([]string{}, "@"), "*?*")
	assertEqual(t, RenderAuthors([]string{"tim"}, "@"), "@tim")
	assertEqual(t, RenderAuthors([]string{"tim", "jim"}, "@"), "@tim and @jim")
	assertEqual(t, RenderAuthors([]string{"tim", "jim", "kim"}, "@"), "@tim, @jim and @kim")
	assertEqual(t, RenderAuthors([]string{"tim", "jim", "kim", "nim"}, "@"), "@tim, @jim, @kim and @nim")
}

func assertEqual(t *testing.T, got, expected string) {
	t.Helper()
	if expected != got {
		t.Errorf("expected %s, got %s", expected, got)
	}
}

package tictactoe1p

// a 1P-vs-CPU tictactoe
// extending moul's tictactoe model

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/demo/tictactoe"
	"gno.land/p/demo/ufmt"
)

// 1P-VS-CPU is a tictactoe game
type Game struct {
	*tictactoe.Game
	cpu      std.Address
	Intn     func(n int) int
	pickMove func(g *tictactoe.Game, Intn func(int) int) (x, y int, e error) // AI function
}

// "" is a whole new game, whether CPU goes first is random.
// "---------"` OTOH indicates a game where cpu /declined/ to go first.
// fRand is a user-supplied func returning a random number in [0, n(
// fPickMove is nil (uses averageDifficulty) or a func used for CPU to pick moves
func GameFromRepr(
	text string,
	cpu, human std.Address,
	fRand func(int) int,
	fPickMove func(*tictactoe.Game, func(int) int,
	) (x, y int, e error),
) (*Game, error) {
	addr1, addr2 := decideOrder(cpu, human, text, fRand)

	var g *tictactoe.Game
	var e error
	switch len(text) {
	case 0:
		g, e = tictactoe.NewGame(addr1, addr2)
		g.Start()
	case 9:
		r := []rune(text)
		g, e = tictactoe.RecoverGame(
			addr1,
			addr2,
			string([]rune{
				r[6], r[7], r[8],
				r[3], r[4], r[5],
				r[0], r[1], r[2],
			}),
		)
	default:
		return nil, errors.New("invalid board length")
	}
	if fPickMove == nil {
		fPickMove = averageDifficulty
	}
	return &Game{g, cpu, fRand, fPickMove}, e
}

func (game Game) ToRepr() string {
	return strings.ReplaceAll(game.Output(), "\n", "")
}

func (game Game) IsCpuFirst() bool {
	return game.PlayerByIndex(0) == game.cpu
}

func (game *Game) PlayCPU() (x, y int, e error) {
	switch {
	case game.Turn() != game.cpu:
		return -1, -1, ufmt.Errorf(
			"not my turn (%s), turn is %s's",
			game.cpu.String(), game.Turn().String(),
		)
	case game.IsOver():
		return -1, -1, errors.New("game is over")
	default:
		x, y, _ = game.pickMove(game.Game, game.Intn)
		e = game.Play(game.cpu, x, y)
		return x, y, e
	}
}

// Decide who go first, based on the count of markers.
// No error. A special and important case is the empty board,
// meaning player to go first is random.
// fRand is a user-supplied func returning a random number in [0, n(
func decideOrder(cpu, human std.Address, board string, fRand func(int) int) (addr1, addr2 std.Address) {
	var cpuFirst bool
	if board == "" {
		cpuFirst = fRand(2) == 0
	} else {
		numO := strings.Count(board, "O") + strings.Count(board, "o")
		numX := strings.Count(board, "X") + strings.Count(board, "x")
		cpuFirst = numX != numO
	}
	if cpuFirst {
		return cpu, human
	} else {
		return human, cpu
	}
}

package tictactoe1p

import (
	"math/rand"
	"std"
	"testing"

	"gno.land/p/demo/uassert"
)

const (
	cpuAddress   = std.Address("CPU")
	humanAddress = std.Address("HUMAN")
	prng         = rand.New(rand.NewPCG(uint64(3), uint64(7)))
)

func TestRand(t *testing.T) {
	n0 := prng.IntN(10)
	for i := 0; i < 128; i++ {
		if prng.IntN(10) != n0 {
			return
		}
	}
	t.Errorf("no randomness")
}

func TestGameFromRepr(t *testing.T) {
	for _, o := range []struct{ repr, expect string }{
		{repr: "", expect: "---------"},
		{repr: "-X--OX-O-", expect: "-X--OX-O-"},
		{repr: "_x__ox_o_", expect: "-X--OX-O-"},
		{repr: "O--------", expect: "O--------"},
		{repr: "O---X----", expect: "O---X----"},
		{repr: "-X--O--O-", expect: "-X--O--O-"},
		{repr: "OXOXOXOXO", expect: "OXOXOXOXO"},
		{repr: "X--------", expect: "error"}, // O always begin
		{repr: "OOOOOX---", expect: "error"},
	} {
		game, e := GameFromRepr(o.repr, cpuAddress, humanAddress, prng.IntN, nil)
		if o.expect == "error" {
			uassert.Error(t, e)
		} else {
			uassert.NoError(t, e)
			uassert.Equal(t, o.expect, game.ToRepr())
		}
	}
}

func TestPlayer1Alternates(t *testing.T) {
	// ensure CPU goes first or second, in different games
	var isCpuFirst0 bool
	for i := 0; i < 128; i++ {
		g, e := GameFromRepr("", cpuAddress, humanAddress, prng.IntN, nil)
		uassert.NoError(t, e)
		if i == 0 {
			isCpuFirst0 = g.IsCpuFirst()
		} else if isCpuFirst0 != g.IsCpuFirst() {
			return // ok
		}
	}
	t.FailNow()
}

func TestPickMove(t *testing.T) {
	// study expected VS statistical result
	// CPU sees own winning move ~80% of times
	//     is otherwise blind to other side
	for _, o := range []struct {
		board     string
		expectedX int
		expectedY int
	}{
		{"O-OX-X---", 1, 2},
		{"O-OX-XO--", 1, 1},
		{"X-O-XOO--", 2, 0},
		{"OX-XO----", 2, 0},
	} {
		// play over and over, see most frequent moves
		h := map[struct{ x, y int }]int{}
		for i := 0; i < 64; i++ {
			game, e := GameFromRepr(o.board, cpuAddress, humanAddress, prng.IntN, nil)
			if !uassert.NoError(t, e, "GameFromRepr") {
				t.FailNow()
			}
			x, y, e := game.pickMove(game.Game, game.Intn)
			h[struct{ x, y int }{x, y}] += 1
		}
		// assert most frequent move
		var hiTemperature int
		var hotMove struct{ x, y int }
		for coord, v := range h {
			if v > hiTemperature {
				hiTemperature = v
				hotMove = coord
			}
		}
		uassert.Equal(t, o.expectedX, hotMove.x, "for x of repr", o.board)
		uassert.Equal(t, o.expectedY, hotMove.y, "for y of repr", o.board)
	}
}

package tictactoe1p

import (
	"errors"

	"gno.land/p/demo/tictactoe"
)

// the default move picker.
func averageDifficulty(game *tictactoe.Game, Intn func(int) int) (x, y int, e error) {
	// candidates
	a := make([]struct{ x, y int }, 0)
	side := rune(1 + game.TurnNumber()%2)
	for y := 0; y <= 2; y++ {
		for x := 0; x <= 2; x++ {
			if game.At(x, y) != rune(0) {
				continue
			}
			// if can wins (and can see it), then win
			if game.WouldWin(side, x, y) && Intn(5) > 0 {
				return x, y, nil
			}
			a = append(a, struct{ x, y int }{x, y})
		}
	}
	if len(a) == 0 {
		return -1, -1, errors.New("no free tile left")
	} else {
		// random pick among candidates
		c := a[Intn(len(a))]
		return c.x, c.y, nil
	}
}

