func compareNil(a, b []interface{}) bool {
	return a == nil && b == nil
}

func compareEmpty(a, b []interface{}) bool {
	return len(a) == 0 && len(b) == 0
}

func compareInterfaces(a, b []interface{}) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

func compareStrings(a, b []interface{}) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		as, ok1 := a[i].(string)
		bs, ok2 := b[i].(string)
		if !ok1 || !ok2 || as != bs {
			return false
		}
	}
	return true
}

