package payrolls

import (
	"net/url"
	"path"
	"std"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/mux"
	"gno.land/p/demo/ufmt"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

// TODO: pagination
const listMaxSize = 50

func Render(renderPath string) string {
	router := mux.NewRouter()

	router.HandleFunc("/u/*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Payrolls beneficiary\n")
		aon := users.AddressOrName(path.Base(r.Path))
		user := rusers.GetUserByAddressOrName(aon)
		addr := std.Address(aon)
		name := string(aon)
		if user != nil {
			addr = user.Address
			name = "@" + user.Name
			w.Write(ufmt.Sprintf("- 👤 User: @%s\n", user.Name))
		} else if !addr.IsValid() {
			w.Write(ufmt.Sprintf("❓ User %q not found\n", addr.String()))
			return
		}
		w.Write(ufmt.Sprintf("- 🆔 Address: %s\n", addr.String()))

		sb := strings.Builder{}
		tree, ok := payrollsByBeneficiary[addr]
		if !ok {
			tree = avl.NewTree()
		}
		estimatedDay := uint64(0)
		estimatedAvailable := uint64(0)
		estimatedClaimable := uint64(0)
		shouldEstimate := tree.Size() <= listMaxSize
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			sb.WriteString(p.render("beneficiary"))

			if shouldEstimate {
				available := p.available()
				estimatedAvailable += available
				vaultAmount := getVaultAmount(p.vaultID)
				if available > vaultAmount {
					available = vaultAmount
				}
				estimatedClaimable += available
				estimatedDay += p.estimateDay()
			}

			return false
		})

		if shouldEstimate {
			w.Write(renderEstimatedMonthly("", estimatedDay, denom))
			w.Write(renderClaimable("", estimatedClaimable, estimatedAvailable, denom, true))
		}

		actions := []string{}

		if !shouldEstimate || estimatedClaimable > 0 {
			claimAllButton := ufmt.Sprintf("[[Claim all]]($help&func=ClaimAll&destination=%s)", addr.String())
			actions = append(actions, claimAllButton)
		}

		label := url.QueryEscape(ufmt.Sprintf("%s Salary Monthly", name))
		newPayrollButton := ufmt.Sprintf("[[New payroll]]($help&func=CreateMonthly&namespace=&label=%s&beneficiary=%s&amount=10000&totalAmount=-1)", label, addr.String())
		actions = append(actions, newPayrollButton)

		w.Write(ufmt.Sprintf("- ✋ Actions: %s\n", strings.Join(actions, " ")))

		w.Write(sb.String())
	})

	router.HandleFunc("/v/*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Payrolls vault\n")

		name := path.Base(r.Path)
		vaultID, err := parseVaultID(name)
		if err != nil {
			w.Write(ufmt.Sprintf("❓ Vault %q not found", name))
			return
		}

		if name != vaultID {
			w.Write(ufmt.Sprintf("- 🎩 Name: %s\n", name))
		}
		w.Write(ufmt.Sprintf("- 🆔 ID: %s\n", vaultID))

		payrollsBuffer := strings.Builder{}
		estimatedDay := uint64(0)
		estimatedDebt := uint64(0)
		tree, ok := payrollsByVault[vaultID]
		if !ok {
			tree = avl.NewTree()
		}
		shouldEstimate := tree.Size() <= listMaxSize
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			if shouldEstimate {
				estimatedDebt += p.available()
				estimatedDay += p.estimateDay()
			}
			payrollsBuffer.WriteString(p.render("vault"))
			return false
		})

		funds := getVaultAmount(vaultID)

		if shouldEstimate {
			w.Write(renderEstimatedMonthly("", estimatedDay, denom))

			balance := int64(funds) - int64(estimatedDebt)
			prefix := "- 🏦 Funds: 🟢 "
			if balance < 0 {
				prefix = "- 🏦 Debt 🔴 "
			}
			w.Write(ufmt.Sprintf("%s%d%s\n", prefix, balance, denom))

			if balance > 0 {
				color := "🔴"
				runWayMonths := funds / (estimatedDay * 30)
				if runWayMonths >= 12 {
					color = "🟢"
				} else if runWayMonths >= 6 {
					color = "🟠"
				}
				w.Write(ufmt.Sprintf("- 🛬 Estimated runway: %s %d months\n", color, runWayMonths))
			}
		} else {
			w.Write(ufmt.Sprintf("- 🏦 Funds: %d%s (does not account for unclaimed amounts)\n", funds, denom))
		}

		label := url.QueryEscape("Salary Monthly")
		idParts := strings.Split(vaultID, ".")
		slug := ""
		if len(idParts) > 1 {
			slug = idParts[1]
		}
		depositFundsAction := ufmt.Sprintf("[[Deposit funds]]($help&func=Fund&addr=%s&slug=%s)", idParts[0], slug)
		newPayrollAction := ufmt.Sprintf("[[New payroll]]($help&func=CreateMonthly&namespace=&label=%s&beneficiary=&amount=10000&totalAmount=-1)", label)
		w.Write(ufmt.Sprintf("- ✋ Actions: %s %s\n", depositFundsAction, newPayrollAction))

		w.Write(payrollsBuffer.String())

	})

	router.HandleFunc("/d/*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Payroll details\n")
		idStr := path.Base(r.Path)
		p, ok := payrolls.Get(idStr)
		if !ok {
			w.Write(ufmt.Sprintf("❓ Payroll %q not found", idStr))
			return
		}
		w.Write(p.(*payroll).render(""))
	})

	router.HandleFunc("*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Latest payrolls\n")
		payrolls.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			w.Write(value.(*payroll).render(""))
			return false
		})

		w.Write("\n\nTODO\n")
		w.Write("- [ ] Stop / Archive\n")
		w.Write("- [ ] Home actions")
		w.Write("- [ ] Unit tests")
		w.Write("- [ ] Pause / Unpause\n")
		w.Write("- [ ] Details: Events history\n")
		w.Write("- [ ] PoV Tags\n")
		w.Write("- [ ] Edit label / PoV labels\n")
		w.Write("- [ ] Stop litigation\n")
		w.Write("- [ ] Fix anchors\n")
	})

	return router.Render(renderPath)
}

func (p *payroll) render(pov string) string {
	sb := strings.Builder{}

	sb.WriteString(ufmt.Sprintf("### [%s](%s:/d/%s)\n", p.id.String(), linkPrefix(), p.id.String()))

	sb.WriteString(ufmt.Sprintf("  - ✍️ Label: %s\n", p.label))

	if p.paused {
		sb.WriteString(ufmt.Sprintf("  - ⏸️ Paused since: %s\n", p.pausedAt.Format(time.DateTime)))
	}

	if pov != "vault" {
		pvid := prettyVaultID(p.vaultID)
		sb.WriteString(ufmt.Sprintf("  - 🎩 Vault: [%s](%s:/v/%s)\n", pvid, linkPrefix(), pvid))
	}

	if pov != "beneficiary" {
		beneficiaryUser := rusers.GetUserByAddress(p.beneficiary)
		var userStr string
		if beneficiaryUser == nil {
			userStr = p.beneficiary.String()
		} else {
			userStr = "@" + beneficiaryUser.Name
		}
		sb.WriteString(ufmt.Sprintf("  - 👤 Beneficiary: [%s](%s:/u/%s)\n", userStr, linkPrefix(), userStr))
	}

	available := p.available()
	vaultAmount := getVaultAmount(p.vaultID)
	claimable := available
	if available > vaultAmount {
		claimable = vaultAmount
	}
	if available > 0 {
		sb.WriteString(renderClaimable("  ", claimable, available, denom, false))
	}

	estimatedDay := p.estimateDay()
	sb.WriteString(renderEstimatedMonthly("  ", estimatedDay, denom))

	sb.WriteString("  - 🕙 Remaining: ")
	if p.amount >= 0 {
		amountRemaining := uint64(p.amount) - p.totalWithdrawn
		sb.WriteString(ufmt.Sprintf("%s (%d%s)", ((time.Duration(amountRemaining) * 24) / time.Duration(estimatedDay) / time.Hour).String(), amountRemaining, denom))
	} else {
		sb.WriteRune('∞')
	}
	sb.WriteRune('\n')

	actions := []string{}

	if pov != "vault" && claimable > 0 {
		claim := ufmt.Sprintf("[[Claim]]($help&func=Claim&id=%d&requestedAmount=-1&destination=%s)", uint64(p.id), p.beneficiary.String())
		actions = append(actions, claim)
	}

	if len(actions) > 0 {
		sb.WriteString(ufmt.Sprintf("- ✋ Actions: %s\n", strings.Join(actions, " ")))
	}

	return sb.String()
}

func renderClaimable(prefix string, claimable uint64, available uint64, denom string, plural bool) string {
	if available == 0 {
		return ""
	}
	sb := strings.Builder{}
	sb.WriteString(prefix)
	sb.WriteString("- 💵 Claimable: ")
	if claimable < available {
		pls := ""
		if plural {
			pls = "(s)"
		}
		color := "🟠"
		if claimable == 0 {
			color = "🔴"
		}
		sb.WriteString(ufmt.Sprintf("%s %d%s/%d%s (not enough funds in vault%s!)\n", color, claimable, denom, available, denom, pls))
	} else {
		sb.WriteString(ufmt.Sprintf("🟢 %d%s\n", available, denom))
	}
	return sb.String()
}

func renderEstimatedMonthly(prefix string, estimatedDay uint64, denom string) string {
	return ufmt.Sprintf("%s- 💸 Estimated monthly: %d%s\n", prefix, estimatedDay*30, denom)
}

func prettyVaultID(id string) string {
	parts := strings.Split(id, ".")
	if len(parts) == 0 {
		return id
	}

	user := rusers.GetUserByAddress(std.Address(parts[0]))
	if user == nil {
		return id
	}

	parts[0] = "@" + user.Name
	return strings.Join(parts, ".")
}

func linkPrefix() string {
	realmPath := std.CurrentRealm().PkgPath()
	slashIdx := strings.Index(realmPath, "/")
	if slashIdx == -1 {
		return realmPath
	}
	return realmPath[slashIdx:]
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

var vaults avl.Tree // std.Addresss -> uint64

func getVaultAmount(id string) uint64 {
	amount, ok := vaults.Get(id)
	if ok {
		return amount.(uint64)
	}
	return 0
}

var errInvalidInput = errors.New("invalid input")

func vaultID(input string, namespace string) (string, error) {
	if len(input) == 0 {
		return "", errInvalidInput
	}

	if id, ok := vaultIDFromPkgPath(input, namespace); ok {
		return id, nil
	}

	user := rusers.GetUserByAddressOrName(users.AddressOrName(input))
	if user != nil {
		return vaultIDUnsafe(user.Address.String(), namespace), nil
	}

	if id, ok := vaultIDFromAddr(std.Address(input), namespace); ok {
		return id, nil
	}

	return "", errInvalidInput
}

func parseVaultID(id string) (string, error) {
	parts := strings.Split(id, ".")
	if len(parts) == 0 {
		return "", errInvalidInput
	}

	if len(parts) == 1 {
		return vaultID(parts[0], "")
	}

	return vaultID(parts[0], parts[1])
}

func vaultIDUnsafe(entity string, namespace string) string {
	if namespace == "" {
		return entity
	}
	return entity + "." + namespace
}

func vaultIDFromAddr(addr std.Address, namespace string) (string, bool) {
	if !addr.IsValid() {
		return "", false
	}
	return vaultIDUnsafe(addr.String(), namespace), true
}

func vaultIDFromPkgPath(entity string, namespace string) (string, bool) {
	if !strings.HasPrefix(entity, std.GetChainDomain()+"/r/") {
		return "", false
	}
	return vaultIDUnsafe(entity, namespace), true
}

func vaultIDFromRealm(realm std.Realm, namespace string) string {
	if realm.IsUser() {
		return vaultIDUnsafe(realm.Addr().String(), namespace)
	} else {
		return vaultIDUnsafe(realm.PkgPath(), namespace)
	}
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/r/demo/grc20reg"
)

// Coins transforms coins into payrolls coins, prefixing them with their type. Examples: "ugnot" -> "/native/ugnot", "gno.land/r/demo/foo20" -> "/grc20/gno.land/r/demo/foo20"
func Coins(native std.Coins, grc20 std.Coins) (std.Coins, error) {
	out := make(std.Coins, len(native)+len(grc20))
	for i, coin := range native {
		out[i].Amount = coin.Amount
		out[i].Denom = "/native/" + coin.Denom
	}
	offset := len(native)
	for i, coin := range grc20 {
		j := offset + i
		out[j].Amount = coin.Amount
		out[j].Denom = "/grc20/" + coin.Denom
	}
	return out, nil
}

func addCoins(a std.Coins, b std.Coins) std.Coins {
	out := make(std.Coins, len(a))
	copy(out, a)
	for _, coin := range b {
		out = addCoinAmount(out, coin)
	}
	return out
}

func subCoins(a std.Coins, b std.Coins) std.Coins {
	out := make(std.Coins, len(a))
	copy(out, a)
	for _, coin := range b {
		out = addCoinAmount(out, std.NewCoin(coin.Denom, -coin.Amount))
	}
	return out
}

func coinsHasPositive(coins std.Coins) bool {
	for _, coin := range coins {
		if coin.Amount > 0 {
			return true
		}
	}
	return false
}

func clampCoins(a std.Coins, max std.Coins) std.Coins {
	out := std.Coins{}
	for _, coin := range a {
		maxAmount := max.AmountOf(coin.Denom)
		if coin.Amount > maxAmount {
			out = append(out, std.NewCoin(coin.Denom, maxAmount))
			continue
		}
		out = append(out, coin)
	}
	return out
}

func addCoinAmount(coins std.Coins, value std.Coin) std.Coins {
	for i, coin := range coins {
		if coin.Denom != value.Denom {
			continue
		}

		out := make(std.Coins, len(coins))
		copy(out, coins)
		out[i].Amount += value.Amount
		return out
	}

	return append(coins, value)
}

func coinsEmpty(coins std.Coins) bool {
	if len(coins) == 0 {
		return true
	}
	for _, coin := range coins {
		if coin.Amount != 0 {
			return false
		}
	}
	return true
}

func sendCoins(dst std.Address, coins std.Coins) {
	if len(coins) == 0 {
		return
	}

	natives := std.Coins{}
	grc20s := std.Coins{}

	for _, coin := range coins {
		if coin.Amount == 0 {
			continue
		}
		if coin.Amount < 0 {
			panic(errors.New("negative send amount"))
		}

		var (
			target *std.Coins
			denom  string
		)

		if strings.HasPrefix(coin.Denom, "/native/") {
			target = &natives
			denom = strings.TrimPrefix(coin.Denom, "/native/")
		} else if strings.HasPrefix(coin.Denom, "/grc20/") {
			target = &grc20s
			denom = strings.TrimPrefix(coin.Denom, "/grc20/")
		} else {
			panic(errors.New("unknown coin kind"))
		}
		*target = addCoins(*target, std.NewCoins(std.NewCoin(denom, coin.Amount)))
	}

	if len(natives) != 0 {
		banker := std.GetBanker(std.BankerTypeRealmSend)
		banker.SendCoins(std.CurrentRealm().Addr(), dst, natives)
	}

	for _, coin := range grc20s {
		err := grc20reg.MustGet(coin.Denom)().CallerTeller().Transfer(dst, uint64(coin.Amount))
		if err != nil {
			panic(err)
		}
	}
}

package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/seqid"
)

// DistribFn is a distribution function used to compute and amount of released coins at a particular point in time.
//
// It must return payrolls [Coins]
type DistribFn func(elapsed time.Duration) std.Coins

// BreakupFn is a function used to compute payroll termination bonuses.
//
// It must return payrolls [Coins]
type BreakupFn func(elapsed time.Duration, pauseDuration time.Duration, source BreakupSource) std.Coins

type BreakupSource uint

const (
	BreakupSourceCreator BreakupSource = iota
	BreakupSourceBeneficiary
)

// DistribStep returns a distribution function that will release coins every `per` duration.
func DistribStep(coins std.Coins, per time.Duration) DistribFn {
	return func(elapsed time.Duration) std.Coins {
		blocksCount := elapsed / per
		out := make(std.Coins, 0, len(coins))
		for _, coin := range coins {
			out = append(out, std.NewCoin(coin.Denom, coin.Amount*int64(blocksCount)))
		}
		return out
	}
}

// DistribLinear returns a distribution function that will continuously release coins every seconds and amount to a rate of `coins` every `per` duration.
func DistribLinear(coins std.Coins, per time.Duration) DistribFn {
	return func(elapsed time.Duration) std.Coins {
		out := make(std.Coins, 0, len(coins))
		for _, coin := range coins {
			newAmount := (int64(elapsed.Seconds()) * coin.Amount) / int64(per.Seconds())
			out = append(out, std.NewCoin(coin.Denom, newAmount))
		}
		return out
	}
}

// DistribMonthlyStep returns a distribution function that will release `coins` every months
func DistribMonthlyStep(amountPerMonth std.Coins) DistribFn {
	return DistribStep(amountPerMonth, time.Hour*24*30)
}

// DistribMonthlyStep returns a distribution function that will continuously release coins every seconds and amount to a rate of `coins` every months
func DistribMonthlyContinuous(amountPerMonth std.Coins) DistribFn {
	return DistribLinear(amountPerMonth, time.Hour*24*30)
}

// CreateMonthlyContinuous creates a payroll with a continuous release at a fixed rate per months of a token identified by denom.
//
// denom must be a payrolls [Coins] denom
func CreateMonthlyContinuous(namespace string, label string, beneficiary std.Address, amountPerMonth int64, denom string) seqid.ID {
	if amountPerMonth <= 0 || denom == "" {
		panic(errors.New("invalid input"))
	}
	coinsPerMonth := std.NewCoins(std.NewCoin(denom, amountPerMonth))
	return Create(namespace, label, beneficiary, DistribMonthlyContinuous(coinsPerMonth), nil)
}

// CreateCDI creates a payroll that tries to match the French CDI contract
//
// Denom must be a payrolls [Coins] denom
//
// See: https://travail-emploi.gouv.fr/le-contrat-de-travail-duree-indeterminee-cdi
func CreateCDI(namespace string, label string, beneficiary std.Address, amountPerMonth int64, denom string) seqid.ID {
	if amountPerMonth <= 0 || denom == "" {
		panic(errors.New("invalid input"))
	}
	coinsPerMonth := std.NewCoins(std.NewCoin(denom, amountPerMonth))
	return Create(namespace, label, beneficiary, DistribMonthlyStep(coinsPerMonth), BreakupCDI(coinsPerMonth))
}

// BreakupCDI is a breakup function that will release the an amount of coins
// based on a simplified "Rupture Conventionelle" (conventional breakup) model based on the French CDI employment contract
func BreakupCDI(coinsPerMonth std.Coins) BreakupFn {
	const hoursPerYear = float64(8_670)
	return func(elapsed time.Duration, pauseDuration time.Duration, source BreakupSource) std.Coins {
		if source != BreakupSourceCreator {
			return nil
		}

		// conventional breakup
		out := make(std.Coins, 0, len(coinsPerMonth))
		worked := elapsed - pauseDuration
		numYears := worked.Hours() / hoursPerYear
		for _, coin := range coinsPerMonth {
			newAmount := int64(0.25 * float64(coin.Amount) * numYears)
			out = append(out, std.NewCoin(coin.Denom, newAmount))
		}
		return out
	}
}

package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/seqid"
)

type payroll struct {
	id             seqid.ID
	creatorAddr    std.Address
	vaultID        string
	createdAt      time.Time
	beneficiary    std.Address
	totalWithdrawn std.Coins
	distrib        DistribFn
	breakup        BreakupFn
	paused         bool
	pausedAt       time.Time
	pauseDuration  time.Duration
	label          string
	breakupCoins   std.Coins
	stopped        bool
	stoppedAt      time.Time
}

func (p *payroll) freeAt(when time.Time, estimate bool) std.Coins {
	if p.stopped && p.stoppedAt.Before(when) {
		when = p.stoppedAt
	}

	var elapsed time.Duration
	if p.paused && !estimate {
		elapsed = p.pausedAt.Sub(p.createdAt)
	} else {
		elapsed = when.Sub(p.createdAt)
	}
	elapsed -= p.pauseDuration
	res := p.distrib(elapsed)

	return res
}

func (p *payroll) available() std.Coins {
	return subCoins(addCoins(p.freeAt(time.Now(), false), p.breakupCoins), p.totalWithdrawn)
}

func (p *payroll) estimateDay() std.Coins {
	return subCoins(p.freeAt(time.Now().Add(time.Hour*24), true), p.freeAt(time.Now(), true))
}

func (p *payroll) claim(vaultAmount std.Coins) std.Coins {
	available := p.available()
	claimable := clampCoins(available, vaultAmount)
	p.totalWithdrawn = addCoins(p.totalWithdrawn, claimable)
	return claimable
}

func (p *payroll) pause(source std.Address) {
	p.assertNotStopped()
	p.assertBeneficiary(source)

	if p.paused {
		panic(errors.New("already paused"))
	}
	p.paused = true
	p.pausedAt = time.Now()
}

func (p *payroll) resume(source std.Address) {
	p.assertNotStopped()
	p.assertBeneficiary(source)

	if !p.paused {
		panic(errors.New("not paused"))
	}
	p.resumeUnsafe()
}

func (p *payroll) resumeUnsafe() {
	p.paused = false
	p.pauseDuration += time.Now().Sub(p.pausedAt)
}

func (p *payroll) stop(source std.Address) {
	p.assertNotStopped()

	if p.paused {
		p.resumeUnsafe()
	}

	p.breakupCoins = p.getBreakupCoins(source)
	p.stopped = true
	p.stoppedAt = time.Now()
}

func (p *payroll) getBreakupCoins(source std.Address) std.Coins {
	if p.breakup == nil {
		return nil
	}

	var breakupSource BreakupSource
	switch source {
	case p.beneficiary:
		breakupSource = BreakupSourceBeneficiary
	case p.creatorAddr:
		breakupSource = BreakupSourceCreator
	default:
		panic(errors.New("unknown source"))
	}

	return p.breakup(time.Now().Sub(p.createdAt), p.pauseDuration, breakupSource)
}

func (p *payroll) assertNotStopped() {
	if p.stopped {
		panic(errors.New("stopped"))
	}
}

func (p *payroll) assertBeneficiary(addr std.Address) {
	if addr != p.beneficiary {
		panic(errors.New("only beneficiary allowed"))
	}
}

// package payrolls is a payroll management example
package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/grc/grc20"
	"gno.land/p/demo/seqid"
	"gno.land/r/demo/grc20reg"
)

var (
	id                    seqid.ID
	payrolls              avl.Tree                          // seqid.ID -> *payroll
	payrollsByVault       = make(map[string]*avl.Tree)      // vaultID -> seqid.ID -> *payroll
	payrollsByBeneficiary = make(map[std.Address]*avl.Tree) // addr -> seqid.ID -> *payroll
)

// Create creates a new payrolls using the vault identified by the caller and namespace.
//
// - distrib is the function used to compute the amount of coins release at a specific point it time. It must not be nil Example: [MonthlyContinuous]
//
// - breakup is the function used to manage bonuses when a payroll is terminated. It can be nil. Example: [BreakupCDI]
func Create(namespace string, label string, beneficiary std.Address, distrib DistribFn, breakup BreakupFn) seqid.ID {
	vaultID := vaultIDFromRealm(std.PrevRealm(), namespace)

	if distrib == nil {
		panic(errors.New("nil distrib"))
	}
	if !beneficiary.IsValid() {
		panic(errors.New("invalid beneficiary"))
	}

	p := payroll{
		id:          id.Next(),
		vaultID:     vaultID,
		createdAt:   time.Now(),
		distrib:     distrib,
		breakup:     breakup,
		beneficiary: beneficiary,
		label:       label,
		creatorAddr: std.PrevRealm().Addr(),
	}

	payrolls.Set(id.String(), &p)

	if _, ok := payrollsByBeneficiary[p.beneficiary]; !ok {
		payrollsByBeneficiary[p.beneficiary] = avl.NewTree()
	}
	payrollsByBeneficiary[p.beneficiary].Set(p.id.String(), &p)

	if _, ok := payrollsByVault[p.vaultID]; !ok {
		payrollsByVault[p.vaultID] = avl.NewTree()
	}
	payrollsByVault[p.vaultID].Set(p.id.String(), &p)

	return p.id
}

// ClaimAll claims the coins available in the payroll identified by id
//
// - The caller must be the beneficiary
//
// - If destination is empty, it will send coins to the caller address
func Claim(id seqid.ID, destination std.Address) {
	claimInternal(id, destination, true)
}

func claimInternal(id seqid.ID, destination std.Address, mustClaim bool) bool {
	p := mustGetPayroll(id)
	caller := std.PrevRealm().Addr()
	if caller != p.beneficiary {
		panic(errors.New("only beneficiary can withdraw"))
	}

	vaultAmount := getVaultCoins(p.vaultID)
	claimed := p.claim(vaultAmount)
	if coinsEmpty(claimed) {
		if mustClaim {
			panic(errors.New("nothing to claim"))
		} else {
			return false
		}
	}

	if len(destination) == 0 {
		destination = caller
	}

	sendCoins(destination, claimed)

	vaults.Set(p.vaultID, subCoins(vaultAmount, claimed))

	return true
}

// ClaimAll claims the coins available in all payrolls where the caller is the beneficiary
//
// - The caller must be the beneficiary
//
// - If destination is empty, it will send coins to the caller address
func ClaimAll(destination std.Address) {
	beneficiary := std.PrevRealm().Addr()
	claimed := false
	payrollsByBeneficiary[beneficiary].Iterate("", "", func(key string, value interface{}) bool {
		p := value.(*payroll)
		if ok := claimInternal(p.id, destination, false); ok {
			claimed = true
		}
		return false
	})
	if !claimed {
		panic(errors.New("nothing to claim"))
	}
}

// FundNative funds a vault identified by base and namespace using the OrigSend coins
//
// - Anyone can fund any vault
func FundNative(base string, namespace string) {
	std.AssertOriginCall()

	var (
		id  string
		err error
	)

	if base == "" {
		id = vaultIDFromRealm(std.PrevRealm(), namespace)
	} else {
		id, err = vaultID(base, namespace)
		if err != nil {
			panic(err)
		}
	}

	send := std.GetOrigSend()
	if coinsEmpty(send) {
		panic(errors.New("invalid amount"))
	}
	for i := range send {
		send[i].Denom = "/native/" + send[i].Denom
	}
	vaults.Set(id, addCoins(getVaultCoins(id), send))
}

// FundGRC20Reg funds a vault identified by base and namespace using the fqname of a registered coin
//
// - If amount is empty, it will use the full allowance
//
// - Anyone can fund any vault
func FundGRC20Reg(base string, namespace string, fqname string, amount int64) {
	tokenGetter := grc20reg.MustGet(fqname)
	token := tokenGetter()
	if token == nil {
		panic(errors.New("token getter returned nil"))
	}

	fundGRC20(base, namespace, token, "/grc20/"+fqname, amount)
}

func fundGRC20(base string, namespace string, token *grc20.Token, tokenKey string, amount int64) {
	anyAmount := amount == -1
	if !anyAmount && amount <= 0 {
		panic(errors.New("invalid amount"))
	}

	var (
		id  string
		err error
	)

	if base == "" {
		id = vaultIDFromRealm(std.PrevRealm(), namespace)
	} else {
		id, err = vaultID(base, namespace)
		if err != nil {
			panic(err)
		}
	}

	if anyAmount {
		amount = int64(token.Allowance(std.PrevRealm().Addr(), std.CurrentRealm().Addr()))
	}

	teller := token.CallerTeller()
	if err := teller.TransferFrom(std.PrevRealm().Addr(), std.CurrentRealm().Addr(), uint64(amount)); err != nil {
		panic(err)
	}

	send := std.NewCoins(std.NewCoin(tokenKey, int64(amount)))
	vaults.Set(id, addCoins(getVaultCoins(id), send))
}

// WithdrawFunds withdraws from a vault identified by the caller and namespace. It uses the coins identified by a payrolls denom (see [Coins])
//
// - If amount is empty, it will withdraw all available funds
//
// - If destination is empty, it will send to the caller's address
func WithdrawFunds(namespace string, denom string, amount int64, destination std.Address) {
	if amount < -1 {
		panic(errors.New("invalid amount"))
	}
	anyAmount := amount == -1
	if amount == 0 {
		panic(errors.New("invalid amount"))
	}

	prevRealm := std.PrevRealm()
	if len(destination) == 0 {
		destination = prevRealm.Addr()
	}

	id := vaultIDFromRealm(prevRealm, namespace)

	vaultAmount := getVaultCoins(id)
	if vaultAmount.AmountOf(denom) == 0 {
		panic(errors.New("vault empty"))
	}

	available := vaultAmount
	payrollsByVault[id].Iterate("", "", func(_ string, value interface{}) bool {
		p := value.(*payroll)
		available = subCoins(available, p.available())
		return false
	})

	availableAmount := available.AmountOf(denom)
	if anyAmount {
		amount = availableAmount
	} else if availableAmount < amount {
		panic(errors.New("not enough in vault"))
	}

	toSend := std.NewCoins(std.Coin{Denom: denom, Amount: amount})
	sendCoins(destination, toSend)

	vaults.Set(id, subCoins(vaultAmount, toSend))
}

func Stop(id seqid.ID) {
	p := mustGetPayroll(id)
	p.stop(std.PrevRealm().Addr())
}

func Pause(id seqid.ID) {
	p := mustGetPayroll(id)
	p.pause(std.PrevRealm().Addr())
}

func Resume(id seqid.ID) {
	p := mustGetPayroll(id)
	p.resume(std.PrevRealm().Addr())
}

func mustGetPayroll(id seqid.ID) *payroll {
	ip, ok := payrolls.Get(id.String())
	if !ok {
		panic(errors.New("no such payroll"))
	}
	return ip.(*payroll)
}

package payrolls

import (
	"math"
	"net/url"
	"std"
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/seqid"
	"gno.land/p/demo/ufmt"
	"gno.land/p/demo/users"
	"gno.land/r/demo/grc20reg"
	rusers "gno.land/r/demo/users"
)

const (
	listMaxSize    = 50
	defaultFqdenom = "gno.land/r/demo/foo20"
	defaultDenom   = "/grc20/" + defaultFqdenom
)

func Render(renderPath string) string {
	w := strings.Builder{}

	switch {
	case strings.HasPrefix(renderPath, "/u/"):
		w.WriteString("# Payrolls beneficiary\n")

		path := strings.TrimPrefix(renderPath, "/u/")
		aon := users.AddressOrName(path)
		user := rusers.GetUserByAddressOrName(aon)
		addr := std.Address(aon)
		if user != nil {
			addr = user.Address
			w.WriteString(ufmt.Sprintf("- 👤 User: @%s\n", user.Name))
		} else if !addr.IsValid() {
			w.WriteString(ufmt.Sprintf("❓ User %q not found\n", addr.String()))
			break
		}

		w.WriteString(ufmt.Sprintf("- 🆔 Address: %s\n", addr.String()))

		sb := strings.Builder{}
		tree, ok := payrollsByBeneficiary[addr]
		if !ok {
			tree = avl.NewTree()
		}
		estimatedDay := std.Coins{}
		estimatedAvailable := std.Coins{}
		estimatedClaimable := std.Coins{}
		shouldEstimate := tree.Size() <= listMaxSize
		if shouldEstimate {
			sb.WriteString("## All payrolls\n")
		} else {
			sb.WriteString("## Latest payrolls\n")
		}
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			sb.WriteString(renderPayroll(p, "beneficiary"))

			if shouldEstimate {
				available := p.available()
				estimatedAvailable = addCoins(estimatedAvailable, available)

				claimable := clampCoins(available, getVaultCoins(p.vaultID))
				estimatedClaimable = addCoins(estimatedClaimable, claimable)

				estimatedDay = addCoins(estimatedDay, p.estimateDay())
			}

			return false
		})

		actions := []string{}

		if !shouldEstimate || !coinsEmpty(estimatedClaimable) {
			actions = append(actions, renderButton("Claim all", "ClaimAll", map[string]string{
				"destination": addr.String(),
			}))
		}

		actions = append(actions, renderButton("New payroll", "CreateMonthlyContinuous", map[string]string{
			"label":          "Salary Monthly",
			"beneficiary":    addr.String(),
			"amountPerMonth": "10000",
			"denom":          defaultDenom,
		}))

		w.WriteString(renderActions("", actions))

		if shouldEstimate {
			for _, coin := range addCoins(estimatedAvailable, estimatedDay) {
				w.WriteString(ufmt.Sprintf("- %s:\n", renderCoinDenom(coin.Denom)))
				w.WriteString(renderEstimatedMonthly("  ", estimatedDay.AmountOf(coin.Denom), coin.Denom))
				w.WriteString(renderClaimable("  ", estimatedClaimable.AmountOf(coin.Denom), estimatedAvailable.AmountOf(coin.Denom), coin.Denom, true))
			}
		}

		w.WriteString(sb.String())

	case strings.HasPrefix(renderPath, "/v/"):
		w.WriteString("# Payrolls vault\n")

		input := strings.TrimPrefix(renderPath, "/v/")
		inputBase, namespace := splitVaultInput(input)
		base, vaultUser, ok := resolveVaultBase(inputBase)
		if !ok {
			w.WriteString(ufmt.Sprintf("❓ Vault %q not found", input))
			break
		}

		vaultID := vaultIDUnsafe(base, namespace)

		name := vaultID
		if vaultUser != nil {
			name = vaultIDUnsafe("@"+vaultUser.Name, namespace)
		}
		w.WriteString(ufmt.Sprintf("- 🎩 Name: %s\n", name))

		w.WriteString(ufmt.Sprintf("- 🆔 ID: %s\n", vaultID))

		payrollsBuffer := strings.Builder{}
		estimatedDay := std.Coins{}
		estimatedDebt := std.Coins{}
		tree, ok := payrollsByVault[vaultID]
		if !ok {
			tree = avl.NewTree()
		}
		shouldEstimate := tree.Size() <= listMaxSize
		if shouldEstimate {
			payrollsBuffer.WriteString("## All payrolls\n")
		} else {
			payrollsBuffer.WriteString("## Latest payrolls\n")
		}
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			if shouldEstimate {
				estimatedDebt = addCoins(estimatedDebt, p.available())
				estimatedDay = addCoins(estimatedDay, p.estimateDay())
			}
			payrollsBuffer.WriteString(renderPayroll(p, "vault"))
			return false
		})

		funds := getVaultCoins(vaultID)
		balance := subCoins(funds, estimatedDebt)

		actions := []string{}
		actions = append(actions, renderButton("Deposit native", "FundNative", map[string]string{
			"base":      base,
			"namespace": namespace,
		}))
		actions = append(actions, renderButton("Deposit GRC20", "FundGRC20Reg", map[string]string{
			"base":      base,
			"namespace": namespace,
			"fqdenom":   defaultFqdenom,
			"amount":    "-1",
		}))
		if !shouldEstimate || coinsHasPositive(balance) {
			actions = append(actions, renderButton("Withdraw funds", "WithdrawFunds", map[string]string{
				"namespace":   namespace,
				"amount":      "-1",
				"denom":       defaultDenom,
				"destination": base,
			}))
		}
		actions = append(actions, renderButton("New payroll", "CreateMonthlyContinuous", map[string]string{
			"namespace":      namespace,
			"label":          "Salary Monthly",
			"amountPerMonth": "10000",
			"denom":          defaultDenom,
		}))
		w.WriteString(renderActions("", actions))

		if shouldEstimate {
			for _, coin := range addCoins(balance, estimatedDay) {
				w.WriteString(ufmt.Sprintf("- %s:\n", renderCoinDenom(coin.Denom)))

				color := "🟢"
				coinBalance := balance.AmountOf(coin.Denom)
				if coinBalance < 0 {
					color = "🔴"
				}
				w.WriteString(ufmt.Sprintf("  - 🏦 Funds: %s %s\n", color, renderCoin(std.NewCoin(coin.Denom, coinBalance))))

				w.WriteString(renderEstimatedMonthly("  ", estimatedDay.AmountOf(coin.Denom), coin.Denom))

				if coinBalance > 0 && estimatedDay.AmountOf(coin.Denom) > 0 {
					color := "🔴"
					runWayMonths := coinBalance / (estimatedDay.AmountOf(coin.Denom) * 30)
					if runWayMonths >= 12 {
						color = "🟢"
					} else if runWayMonths >= 6 {
						color = "🟠"
					}
					w.WriteString(ufmt.Sprintf("  - 🛬 Estimated runway: %s %d months\n", color, runWayMonths))
				}
			}
		} else {
			for _, coin := range funds {
				w.WriteString(ufmt.Sprintf("- 🏦 Funds: %d %s (does not account for unclaimed amounts)\n", renderCoin(coin)))
			}
		}

		w.WriteString(payrollsBuffer.String())

	case strings.HasPrefix(renderPath, "/d/"):
		idStr := strings.TrimPrefix(renderPath, "/d/")

		w.WriteString(ufmt.Sprintf("# Payroll #%s\n", idStr))

		p, ok := payrolls.Get(idStr)
		if !ok {
			return ufmt.Sprintf("❓ Payroll %q not found", idStr)
		}

		w.WriteString(renderPayroll(p.(*payroll), "details"))

	case renderPath == "":
		w.WriteString("# Payrolls\n")

		actions := []string{}
		actions = append(actions, renderButton("New payroll", "CreateMonthlyContinuous", map[string]string{
			"label":          "Salary Monthly",
			"amountPerMonth": "10000",
			"denom":          defaultDenom,
		}))
		w.WriteString(renderActions("", actions))

		if payrolls.Size() <= listMaxSize {
			w.WriteString("## All payrolls\n")
		} else {
			w.WriteString("## Latest payrolls\n")
		}
		payrolls.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			w.WriteString(renderPayroll(value.(*payroll), "home"))
			return false
		})

		/*
			w.WriteString("## v2 ideas\n")
			w.WriteString("- Better test coverage\n")
			w.WriteString("- Gnoweb pagination\n")
			w.WriteString("- Show payroll running duration\n")
			w.WriteString("- Archive\n")
			w.WriteString("- Details: Events history\n")
			w.WriteString("- PoV Tags\n")
			w.WriteString("- Edit label / PoV labels\n")
			w.WriteString("- Stop litigation\n")
		*/

	default:
		w.WriteString("# Payrolls\n")
		w.WriteString("❓ 404 Not found")
	}

	return w.String()
}

func renderPayroll(p *payroll, pov string) string {
	sb := strings.Builder{}

	if pov != "details" {
		sb.WriteString(ufmt.Sprintf("### %s\n", p.id.String()))
	}

	sb.WriteString(ufmt.Sprintf("  - ✍️ Label: %s\n", p.label))

	if p.paused {
		sb.WriteString(ufmt.Sprintf("  - ⏸️ Paused since: %s\n", p.pausedAt.Format(time.DateTime)))
	}

	if pov != "vault" {
		base, namespace := splitVaultInput(p.vaultID)
		_, usr, _ := resolveVaultBase(base)
		if usr != nil {
			base = "@" + usr.Name
		}
		pvid := vaultIDUnsafe(base, namespace)
		sb.WriteString(ufmt.Sprintf("  - 🎩 Vault: [%s](%s:/v/%s)\n", pvid, linkPrefix(), pvid))
	}

	if pov != "beneficiary" {
		beneficiaryUser := rusers.GetUserByAddress(p.beneficiary)
		var userStr string
		if beneficiaryUser == nil {
			userStr = p.beneficiary.String()
		} else {
			userStr = "@" + beneficiaryUser.Name
		}
		sb.WriteString(ufmt.Sprintf("  - 👤 Beneficiary: [%s](%s:/u/%s)\n", userStr, linkPrefix(), userStr))
	}

	available := p.available()
	vaultAmount := getVaultCoins(p.vaultID)
	claimable := clampCoins(available, vaultAmount)

	sb.WriteString(ufmt.Sprintf("  - 🏁 Created at: %s\n", p.createdAt.Format(time.DateTime)))

	if p.stopped {
		sb.WriteString(ufmt.Sprintf("  - 🛑 Stopped since: %s\n", p.stoppedAt.Format(time.DateTime)))
	}

	if pov != "details" {
		sb.WriteString(ufmt.Sprintf("  - 🔍 [Details](%s:/d/%s)\n", linkPrefix(), p.id.String()))
	}

	actions := []string{}

	if pov != "vault" && coinsHasPositive(claimable) {
		actions = append(actions, renderButton("Claim", "Claim", map[string]string{
			"id":          strconv.FormatUint(uint64(p.id), 10),
			"destination": p.beneficiary.String(),
		}))
	}

	if pov != "vault" && !p.paused && !p.stopped {
		actions = append(actions, renderButton("Pause", "Pause", map[string]string{
			"id": strconv.FormatUint(uint64(p.id), 10),
		}))
	}

	if pov != "vault" && p.paused {
		actions = append(actions, renderButton("Resume", "Resume", map[string]string{
			"id": strconv.FormatUint(uint64(p.id), 10),
		}))
	}

	if !p.stopped {
		actions = append(actions, renderStopButton(p.id))
	}

	sb.WriteString(renderActions("  ", actions))

	estimatedDay := p.estimateDay()
	allCoins := addCoins(available, estimatedDay)

	var beneficiaryBreakupCoins std.Coins
	var creatorBreakupCoins std.Coins
	if pov == "details" {
		allCoins = addCoins(allCoins, beneficiaryBreakupCoins)
		allCoins = addCoins(allCoins, creatorBreakupCoins)
		beneficiaryBreakupCoins = p.getBreakupCoins(p.beneficiary)
		creatorBreakupCoins = p.getBreakupCoins(p.creatorAddr)
	}
	for _, coin := range allCoins {
		sb.WriteString(ufmt.Sprintf("  - %s:\n", renderCoinDenom(coin.Denom)))
		sb.WriteString(renderEstimatedMonthly("    ", estimatedDay.AmountOf(coin.Denom), coin.Denom))
		sb.WriteString(renderClaimable("    ", claimable.AmountOf(coin.Denom), available.AmountOf(coin.Denom), coin.Denom, true))
		if pov == "details" {
			if bb := beneficiaryBreakupCoins.AmountOf(coin.Denom); bb > 0 {
				sb.WriteString(ufmt.Sprintf("    - Breakup by beneficiary bonus: %s\n", renderCoin(std.NewCoin(coin.Denom, bb))))
			}
			if cb := creatorBreakupCoins.AmountOf(coin.Denom); cb > 0 {
				sb.WriteString(ufmt.Sprintf("    - Breakup by creator bonus: %s\n", renderCoin(std.NewCoin(coin.Denom, cb))))
			}
		}
	}

	return sb.String()
}

func renderClaimable(prefix string, claimable int64, available int64, denom string, plural bool) string {
	if available == 0 {
		return ""
	}
	sb := strings.Builder{}
	sb.WriteString(prefix)
	sb.WriteString("- 💵 Claimable: ")
	if claimable < available {
		pls := ""
		if plural {
			pls = "(s)"
		}
		color := "🟠"
		if claimable == 0 {
			color = "🔴"
		}
		sb.WriteString(ufmt.Sprintf("%s %s/%s %s (not enough funds in vault%s!)\n",
			color,
			renderCoinAmount(std.NewCoin(denom, claimable)),
			renderCoinAmount(std.NewCoin(denom, available)),
			renderCoinDenom(denom),
			pls,
		))
	} else {
		sb.WriteString(ufmt.Sprintf("🟢 %s\n", renderCoin(std.NewCoin(denom, available))))
	}
	return sb.String()
}

func renderEstimatedMonthly(prefix string, estimatedDay int64, denom string) string {
	if estimatedDay == 0 {
		return ""
	}
	return ufmt.Sprintf("%s- 💸 Estimated monthly: %s\n", prefix, renderCoin(std.NewCoin(denom, estimatedDay*30)))
}

func renderStopButton(id seqid.ID) string {
	return renderButton("Stop", "Stop", map[string]string{
		"id": strconv.FormatUint(uint64(id), 10),
	})
}

func renderButton(text, funcName string, args map[string]string) string {
	argsBuf := strings.Builder{}
	for key, arg := range args {
		argsBuf.WriteString("&" + url.QueryEscape(key) + "=" + url.QueryEscape(arg))
	}
	return ufmt.Sprintf("[[%s]](%s$help&func=%s%s)", text, linkPrefix(), funcName, argsBuf.String())
}

func renderActions(prefix string, actions []string) string {
	if len(actions) == 0 {
		return ""
	}
	return prefix + "- ✋ Actions: " + strings.Join(actions, " ") + "\n"
}

func resolveVaultBase(base string) (string, *users.User, bool) {
	if len(base) == 0 {
		return "", nil, false
	}

	if strings.HasPrefix(base, std.GetChainDomain()+"/r/") {
		return base, nil, true
	}

	usr := rusers.GetUserByAddressOrName(users.AddressOrName(base))
	if usr != nil {
		return usr.Address.String(), usr, true
	}

	if std.Address(base).IsValid() {
		return base, nil, true
	}

	return "", nil, false
}

func linkPrefix() string {
	realmPath := std.CurrentRealm().PkgPath()
	slashIdx := strings.Index(realmPath, "/")
	if slashIdx == -1 {
		return realmPath
	}
	return realmPath[slashIdx:]
}

func renderCoin(coin std.Coin) string {
	return ufmt.Sprintf("%s %s", renderCoinAmount(coin), renderCoinDenom(coin.Denom))
}

func renderCoinAmount(coin std.Coin) string {
	switch {
	case strings.HasPrefix(coin.Denom, "/grc20/"):
		fqdenom := strings.TrimPrefix(coin.Denom, "/grc20/")
		tokenGetter := grc20reg.Get(fqdenom)
		if tokenGetter == nil {
			break
		}
		token := tokenGetter()
		if token == nil {
			break
		}
		amount := float64(coin.Amount) / float64(math.Pow10(int(token.GetDecimals())))
		return ufmt.Sprintf("%g", amount)
	}

	return ufmt.Sprintf("%d", coin.Amount)
}

func renderCoinDenom(denom string) string {
	switch {
	case strings.HasPrefix(denom, "/native/"):
		nativeDenom := strings.TrimPrefix(denom, "/native/")
		return nativeDenom

	case strings.HasPrefix(denom, "/grc20/"):
		fqdenom := strings.TrimPrefix(denom, "/grc20/")
		tokenGetter := grc20reg.Get(fqdenom)
		if tokenGetter == nil {
			break
		}
		token := tokenGetter()
		if token == nil {
			break
		}
		return ufmt.Sprintf("[$%s](%s)", tokenGetter().GetSymbol(), strings.TrimPrefix(fqdenom, std.GetChainDomain()))
	}

	return denom
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

var vaults avl.Tree // std.Addresss -> std.Coins

var errInvalidInput = errors.New("invalid input")

func getVaultCoins(id string) std.Coins {
	amount, ok := vaults.Get(id)
	if ok {
		return amount.(std.Coins)
	}
	return nil
}

func vaultID(base string, namespace string) (string, error) {
	if len(base) == 0 {
		return "", errInvalidInput
	}

	if id, ok := vaultIDFromPkgPath(base, namespace); ok {
		return id, nil
	}

	user := rusers.GetUserByAddressOrName(users.AddressOrName(base))
	if user != nil {
		return vaultIDUnsafe(user.Address.String(), namespace), nil
	}

	if id, ok := vaultIDFromAddr(std.Address(base), namespace); ok {
		return id, nil
	}

	return "", errInvalidInput
}

func splitVaultInput(input string) (string, string) {
	lastDot := strings.LastIndex(input, ".")
	if lastDot == -1 {
		return input, ""
	}

	base := input[:lastDot]
	namespace := input[lastDot+1:]
	return base, namespace
}

func vaultIDUnsafe(base string, namespace string) string {
	if namespace == "" {
		return base
	}
	return base + "." + namespace
}

func vaultIDFromAddr(addr std.Address, namespace string) (string, bool) {
	if !addr.IsValid() {
		return "", false
	}
	return vaultIDUnsafe(addr.String(), namespace), true
}

func vaultIDFromPkgPath(base string, namespace string) (string, bool) {
	if !strings.HasPrefix(base, std.GetChainDomain()+"/r/") {
		return "", false
	}
	return vaultIDUnsafe(base, namespace), true
}

func vaultIDFromRealm(base std.Realm, namespace string) string {
	if base.IsUser() {
		return vaultIDUnsafe(base.Addr().String(), namespace)
	} else {
		return vaultIDUnsafe(base.PkgPath(), namespace)
	}
}

