		out += posts[i].String()
		out += "---\n\n"
	}

	return out
}

func (p Post) String() string {
	out := p.text
	out += "\n\n"
	out += ufmt.Sprintf("_%s, by %s_", p.createdAt.Format("02 Jan 2006, 15:04"), p.author)
	out += "\n\n"

	return out
}

