	return nil
}

func Render(_ string) string {
	out := md.H1("Posts")

	if len(posts) == 0 {
		out += "No posts."
		return out
	}

	for i := len(posts) - 1; i >= 0; i-- {
		out += md.H3(ufmt.Sprintf("Post #%d\n\n", i))
		out += posts[i].String()
		out += "---\n\n"
	}

	return out
}

func (p Post) String() string {
	out := p.text
	out += "\n\n"
	out += ufmt.Sprintf("_%s, by %s_", p.createdAt.Format("02 Jan 2006, 15:04"), p.author)
	out += "\n\n"

	return out
}

package urlshortenerdemo

import (
	"crypto/sha256"
	"encoding/base64"

	"gno.land/p/demo/btree"
)

// We'll store code->URL mappings in a BTree.
var store = btree.New()

// Shorten a URL and return a short code, or `#` if there is an error.
func Shorten(url string) string {
	hash := sha256.Sum256([]byte(url))
	hashBytes := hash[:]
	buffer := make([]byte, base64.RawURLEncoding.EncodedLen(len(hashBytes)))
	base64.RawURLEncoding.Encode(buffer, hashBytes)
	code := findShortestNonExistingKey(store, buffer, url)

	store.Insert(Content{Key: code, Value: url})
	return code
}

// Render returns the full URL based on the shortcode, or `#` if the code doesn't match a URL.
func Render(code string) string {
	data := store.Get(Content{Key: code})
	if data != nil {
		return data.(Content).Value
	}

	return "#"
}

// Less compares two Content records by their Keys.
// The Key must be either an int or a string.
func (c Content) Less(than btree.Record) bool {
	other, ok := than.(Content)
	if !ok {
		panic("cannot compare: incompatible types")
	}
	return c.Key < other.Key
}

var _ btree.Record = Content{}

func findShortestNonExistingKey(ds *btree.BTree, bytes []byte, to_store string) string {
	var key string
	for i := 0; i < len(bytes); i++ {
		key += string(bytes[i])
		if ds.Has(Content{Key: key}) {
			value := ds.Get(Content{Key: key})

			if value.(Content).Value == to_store {
				return key
			}
		} else {
			return key
		}
	}
	return key
}

// We'll store code->URL mappings in a BTree.
var store = btree.New()

// Shorten a URL and return a short code, or `#` if there is an error.
func Shorten(url string) string {
	hash := sha256.Sum256([]byte(url))
	hashBytes := hash[:]
	buffer := make([]byte, base64.RawURLEncoding.EncodedLen(len(hashBytes)))
	base64.RawURLEncoding.Encode(buffer, hashBytes)
	code := findShortestNonExistingKey(store, buffer, url)

	store.Insert(Content{Key: code, Value: url})
	return code
}

// Render returns the full URL based on the shortcode, or `#` if the code doesn't match a URL.
func Render(code string) string {
	data := store.Get(Content{Key: code})
	if data != nil {
		return data.(Content).Value
	}

	return "#"
}

