func TestManagedGroup(t *testing.T) {
	t.Parallel()

	u1 := testutils.TestAddress("u1")
	u2 := testutils.TestAddress("u2")
	u3 := testutils.TestAddress("u3")

	t.Run("AddBackupOwner", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		// happy path
		{
			std.TestSetOrigCaller(u1)
			err := g.AddBackupOwner(u2)
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
		}
		// ensure checking for authorized caller
		{
			std.TestSetOrigCaller(u2)
			err := g.AddBackupOwner(u3)
			if err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ErrNotBackupOwner.Error(), err.Error())
			}
		}
		// ensure invalid address is caught
		{
			std.TestSetOrigCaller(u1)
			var badAddr std.Address
			err := g.AddBackupOwner(badAddr)
			if err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
		}
	})
	t.Run("RemoveBackupOwner", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		// happy path
		{
			std.TestSetOrigCaller(u1)
			g.AddBackupOwner(u2)
			err := g.RemoveBackupOwner(u2)
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
		}
		// running this twice should not error.
		{
			std.TestSetOrigCaller(u1)
			err := g.RemoveBackupOwner(u2)
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
		}
		// ensure checking for authorized caller
		{
			std.TestSetOrigCaller(u2)
			err := g.RemoveBackupOwner(u3)
			if err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ErrNotBackupOwner.Error(), err.Error())
			}
		}
		{
			std.TestSetOrigCaller(u1)
			var badAddr std.Address
			err := g.RemoveBackupOwner(badAddr)
			if err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
		}
		{
			std.TestSetOrigCaller(u1)
			err := g.RemoveBackupOwner(u1)
			if err != ErrCannotRemoveOwner {
				t.Errorf("expected %v, got %v", ErrCannotRemoveOwner.Error(), err.Error())
			}
		}
	})
	t.Run("ClaimOwnership", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		g.AddBackupOwner(u2)
		// happy path
		{
			std.TestSetOrigCaller(u2)
			err := g.ClaimOwnership()
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			if g.Owner() != u2 {
				t.Errorf("expected %v, got %v", u2, g.Owner())
			}
			if !g.IsMember(u2) {
				t.Errorf("expected %v to be a member", u2)
			}
		}
		// running this twice should not error.
		{
			std.TestSetOrigCaller(u2)
			err := g.ClaimOwnership()
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
		}
		// ensure checking for authorized caller
		{
			std.TestSetOrigCaller(u3)
			err := g.ClaimOwnership()
			if err != ErrNotMember {
				t.Errorf("expected %v, got %v", ErrNotMember.Error(), err.Error())
			}
		}
	})
	t.Run("AddMember", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		// happy path
		{
			std.TestSetOrigCaller(u1)
			err := g.AddMember(u2)
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			if !g.IsMember(u2) {
				t.Errorf("expected %v to be a member", u2)
			}
		}
		// ensure checking for authorized caller
		{
			std.TestSetOrigCaller(u2)
			err := g.AddMember(u3)
			if err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ownable.ErrUnauthorized.Error(), err.Error())
			}
		}
		// ensure invalid address is caught
		{
			std.TestSetOrigCaller(u1)
			var badAddr std.Address
			err := g.AddMember(badAddr)
			if err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
		}
	})
	t.Run("RemoveMember", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		// happy path
		{
			std.TestSetOrigCaller(u1)
			g.AddMember(u2)
			err := g.RemoveMember(u2)
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
			if g.IsMember(u2) {
				t.Errorf("expected %v to not be a member", u2)
			}
		}
		// running this twice should not error.
		{
			std.TestSetOrigCaller(u1)
			err := g.RemoveMember(u2)
			if err != nil {
				t.Errorf("expected nil, got %v", err.Error())
			}
		}
		// ensure checking for authorized caller
		{
			std.TestSetOrigCaller(u2)
			err := g.RemoveMember(u3)
			if err != ownable.ErrUnauthorized {
				t.Errorf("expected %v, got %v", ownable.ErrUnauthorized.Error(), err.Error())
			}
		}
		// ensure invalid address is caught
		{
			std.TestSetOrigCaller(u1)
			var badAddr std.Address
			err := g.RemoveMember(badAddr)
			if err != ErrInvalidAddress {
				t.Errorf("expected %v, got %v", ErrInvalidAddress.Error(), err.Error())
			}
		}
		// ensure owner cannot be removed
		{
			std.TestSetOrigCaller(u1)
			err := g.RemoveMember(u1)
			if err != ErrCannotRemoveOwner {
				t.Errorf("expected %v, got %v", ErrCannotRemoveOwner.Error(), err.Error())
			}
		}
	})
	t.Run("MemberCount", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		if g.MemberCount() != 1 {
			t.Errorf("expected 0, got %v", g.MemberCount())
		}
		g.AddMember(u2)
		if g.MemberCount() != 2 {
			t.Errorf("expected 1, got %v", g.MemberCount())
		}
		g.AddMember(u3)
		if g.MemberCount() != 3 {
			t.Errorf("expected 2, got %v", g.MemberCount())
		}
		g.RemoveMember(u2)
		if g.MemberCount() != 2 {
			t.Errorf("expected 1, got %v", g.MemberCount())
		}
	})
	t.Run("BackupOwnerCount", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		if g.BackupOwnerCount() != 1 {
			t.Errorf("expected 0, got %v", g.BackupOwnerCount())
		}
		g.AddBackupOwner(u2)
		if g.BackupOwnerCount() != 2 {
			t.Errorf("expected 1, got %v", g.BackupOwnerCount())
		}
		g.AddBackupOwner(u3)
		if g.BackupOwnerCount() != 3 {
			t.Errorf("expected 2, got %v", g.BackupOwnerCount())
		}
		g.RemoveBackupOwner(u2)
		if g.BackupOwnerCount() != 2 {
			t.Errorf("expected 1, got %v", g.BackupOwnerCount())
		}
	})
	t.Run("IsMember", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		if !g.IsMember(u1) {
			t.Errorf("expected %v to be a member", u1)
		}
		if g.IsMember(u2) {
			t.Errorf("expected %v to not be a member", u2)
		}
		g.AddMember(u2)
		if !g.IsMember(u2) {
			t.Errorf("expected %v to be a member", u2)
		}
	})
	t.Run("IsBackupOwner", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		if !g.IsBackupOwner(u1) {
			t.Errorf("expected %v to be a backup owner", u1)
		}
		if g.IsBackupOwner(u2) {
			t.Errorf("expected %v to not be a backup owner", u2)
		}
		g.AddBackupOwner(u2)
		if !g.IsBackupOwner(u2) {
			t.Errorf("expected %v to be a backup owner", u2)
		}
	})
	t.Run("Owner", func(t *testing.T) {
		t.Parallel()
		g := New(u1)
		if g.Owner() != u1 {
			t.Errorf("expected %v, got %v", u1, g.Owner())
		}
		g.AddBackupOwner(u2)
		if g.Owner() != u1 {
			t.Errorf("expected %v, got %v", u1, g.Owner())
		}
		std.TestSetOrigCaller(u2)
		g.ClaimOwnership()
		if g.Owner() != u2 {
			t.Errorf("expected %v, got %v", u2, g.Owner())
		}
	})
	t.Run("BackupOwners", func(t *testing.T) {
		t.Parallel()
		std.TestSetOrigCaller(u1)
		g := New(u1)
		g.AddBackupOwner(u2)
		g.AddBackupOwner(u3)
		owners := g.BackupOwners()
		if len(owners) != 3 {
			t.Errorf("expected 2, got %v", len(owners))
		}
		if owners[0] != u1.String() {
			t.Errorf("expected %v, got %v", u2, owners[0])
		}
		if owners[1] != u3.String() {
			t.Errorf("expected %v, got %v", u3, owners[1])
		}
		if owners[2] != u2.String() {
			t.Errorf("expected %v, got %v", u3, owners[1])
		}
	})
	t.Run("Members", func(t *testing.T) {
		t.Parallel()
		std.TestSetOrigCaller(u1)
		g := New(u1)
		g.AddMember(u2)
		g.AddMember(u3)
		members := g.Members()
		if len(members) != 3 {
			t.Errorf("expected 2, got %v", len(members))
		}
		if members[0] != u1.String() {
			t.Errorf("expected %v, got %v", u2, members[0])
		}
		if members[1] != u3.String() {
			t.Errorf("expected %v, got %v", u3, members[1])
		}
		if members[2] != u2.String() {
			t.Errorf("expected %v, got %v", u3, members[1])
		}
	})
}

func TestSliceWithOffset(t *testing.T) {
	t.Parallel()
	testTable := []struct {
		name          string
		slice         []string
		offset        int
		count         int
		expected      []string
		expectedCount int
	}{
		{
			name:          "empty",
			slice:         []string{},
			offset:        0,
			count:         0,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "single",
			slice:         []string{"a"},
			offset:        0,
			count:         1,
			expected:      []string{"a"},
			expectedCount: 1,
		},
		{
			name:          "single offset",
			slice:         []string{"a"},
			offset:        1,
			count:         1,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "multiple",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        0,
			count:         10,
			expected:      []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			expectedCount: 10,
		},
		{
			name:          "multiple offset",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        5,
			count:         5,
			expected:      []string{"f", "g", "h", "i", "j"},
			expectedCount: 5,
		},
		{
			name:          "multiple offset end",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        10,
			count:         5,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "multiple offset past end",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        11,
			count:         5,
			expected:      []string{},
			expectedCount: 0,
		},
		{
			name:          "multiple offset count past end",
			slice:         []string{"a", "b", "c", "d", "e", "f", "g", "h", "i", "j"},
			offset:        5,
			count:         20,
			expected:      []string{"f", "g", "h", "i", "j"},
			expectedCount: 5,
		},
	}
	for _, test := range testTable {
		t.Run(test.name, func(t *testing.T) {
			t.Parallel()
			tree := avl.NewTree()
			for _, s := range test.slice {
				tree.Set(s, struct{}{})
			}
			slice := sliceWithOffset(tree, test.offset, test.count)
			if len(slice) != test.expectedCount {
				t.Errorf("expected %v, got %v", test.expectedCount, len(slice))
			}
		})
	}
}

package pager

import (
	"math"
	"net/url"
	"strconv"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/ufmt"
)

// Pager is a struct that holds the AVL tree and pagination parameters.
type Pager struct {
	Tree            avl.ITree
	PageQueryParam  string
	SizeQueryParam  string
	DefaultPageSize int
	Reversed        bool
}

// Page represents a single page of results.
type Page struct {
	Items      []Item
	PageNumber int
	PageSize   int
	TotalItems int
	TotalPages int
	HasPrev    bool
	HasNext    bool
	Pager      *Pager // Reference to the parent Pager
}

// Item represents a key-value pair in the AVL tree.
type Item struct {
	Key   string
	Value interface{}
}

// NewPager creates a new Pager with default values.
func NewPager(tree avl.ITree, defaultPageSize int, reversed bool) *Pager {
	return &Pager{
		Tree:            tree,
		PageQueryParam:  "page",
		SizeQueryParam:  "size",
		DefaultPageSize: defaultPageSize,
		Reversed:        reversed,
	}
}

// GetPage retrieves a page of results from the AVL tree.
func (p *Pager) GetPage(pageNumber int) *Page {
	return p.GetPageWithSize(pageNumber, p.DefaultPageSize)
}

func (p *Pager) GetPageWithSize(pageNumber, pageSize int) *Page {
	totalItems := p.Tree.Size()
	totalPages := int(math.Ceil(float64(totalItems) / float64(pageSize)))

	page := &Page{
		TotalItems: totalItems,
		TotalPages: totalPages,
		PageSize:   pageSize,
		Pager:      p,
	}

	// pages without content
	if pageSize < 1 {
		return page
	}

	// page number provided is not available
	if pageNumber < 1 {
		page.HasNext = totalPages > 0
		return page
	}

	// page number provided is outside the range of total pages
	if pageNumber > totalPages {
		page.PageNumber = pageNumber
		page.HasPrev = pageNumber > 0
		return page
	}

	startIndex := (pageNumber - 1) * pageSize
	endIndex := startIndex + pageSize
	if endIndex > totalItems {
		endIndex = totalItems
	}

	items := []Item{}

	if p.Reversed {
		p.Tree.ReverseIterateByOffset(startIndex, endIndex-startIndex, func(key string, value interface{}) bool {
			items = append(items, Item{Key: key, Value: value})
			return false
		})
	} else {
		p.Tree.IterateByOffset(startIndex, endIndex-startIndex, func(key string, value interface{}) bool {
			items = append(items, Item{Key: key, Value: value})
			return false
		})
	}

	page.Items = items
	page.PageNumber = pageNumber
	page.HasPrev = pageNumber > 1
	page.HasNext = pageNumber < totalPages
	return page
}

func (p *Pager) MustGetPageByPath(rawURL string) *Page {
	page, err := p.GetPageByPath(rawURL)
	if err != nil {
		panic("invalid path")
	}
	return page
}

// GetPageByPath retrieves a page of results based on the query parameters in the URL path.
func (p *Pager) GetPageByPath(rawURL string) (*Page, error) {
	pageNumber, pageSize, err := p.ParseQuery(rawURL)
	if err != nil {
		return nil, err
	}
	return p.GetPageWithSize(pageNumber, pageSize), nil
}

// Picker generates the Markdown UI for the page Picker
func (p *Page) Picker() string {
	pageNumber := p.PageNumber
	pageNumber = max(pageNumber, 1)

	if p.TotalPages <= 1 {
		return ""
	}

	md := ""

	if p.HasPrev {
		// Always show the first page link
		md += ufmt.Sprintf("[%d](?%s=%d) | ", 1, p.Pager.PageQueryParam, 1)

		// Before
		if p.PageNumber > 4 {
			md += "… | "
		}

		if p.PageNumber > 3 {
			md += ufmt.Sprintf("[%d](?%s=%d) | ", p.PageNumber-2, p.Pager.PageQueryParam, p.PageNumber-2)
		}

		if p.PageNumber > 2 {
			md += ufmt.Sprintf("[%d](?%s=%d) | ", p.PageNumber-1, p.Pager.PageQueryParam, p.PageNumber-1)
		}
	}

	if p.PageNumber > 0 && p.PageNumber <= p.TotalPages {
		// Current page
		md += ufmt.Sprintf("**%d**", p.PageNumber)
	} else {
		md += ufmt.Sprintf("_%d_", p.PageNumber)
	}

	if p.HasNext {
		md += " | "

		if p.PageNumber < p.TotalPages-1 {
			md += ufmt.Sprintf("[%d](?%s=%d) | ", p.PageNumber+1, p.Pager.PageQueryParam, p.PageNumber+1)
		}

		if p.PageNumber < p.TotalPages-2 {
			md += ufmt.Sprintf("[%d](?%s=%d) | ", p.PageNumber+2, p.Pager.PageQueryParam, p.PageNumber+2)
		}

		if p.PageNumber < p.TotalPages-3 {
			md += "… | "
		}

		// Always show the last page link
		md += ufmt.Sprintf("[%d](?%s=%d)", p.TotalPages, p.Pager.PageQueryParam, p.TotalPages)
	}

	return md
}

// ParseQuery parses the URL to extract the page number and page size.
func (p *Pager) ParseQuery(rawURL string) (int, int, error) {
	u, err := url.Parse(rawURL)
	if err != nil {
		return 1, p.DefaultPageSize, err
	}

	query := u.Query()
	pageNumber := 1
	pageSize := p.DefaultPageSize

	if p.PageQueryParam != "" {
		if pageStr := query.Get(p.PageQueryParam); pageStr != "" {
			pageNumber, err = strconv.Atoi(pageStr)
			if err != nil || pageNumber < 1 {
				pageNumber = 1
			}
		}
	}

	if p.SizeQueryParam != "" {
		if sizeStr := query.Get(p.SizeQueryParam); sizeStr != "" {
			pageSize, err = strconv.Atoi(sizeStr)
			if err != nil || pageSize < 1 {
				pageSize = p.DefaultPageSize
			}
		}
	}

	return pageNumber, pageSize, nil
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

package pager

import (
	"testing"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/uassert"
	"gno.land/p/demo/ufmt"
	"gno.land/p/demo/urequire"
)

func TestPager_GetPage(t *testing.T) {
	// Create a new AVL tree and populate it with some key-value pairs.
	tree := avl.NewTree()
	tree.Set("a", 1)
	tree.Set("b", 2)
	tree.Set("c", 3)
	tree.Set("d", 4)
	tree.Set("e", 5)

	t.Run("normal ordering", func(t *testing.T) {
		// Create a new pager.
		pager := NewPager(tree, 10, false)

		// Define test cases.
		tests := []struct {
			pageNumber int
			pageSize   int
			expected   []Item
		}{
			{1, 2, []Item{{Key: "a", Value: 1}, {Key: "b", Value: 2}}},
			{2, 2, []Item{{Key: "c", Value: 3}, {Key: "d", Value: 4}}},
			{3, 2, []Item{{Key: "e", Value: 5}}},
			{1, 3, []Item{{Key: "a", Value: 1}, {Key: "b", Value: 2}, {Key: "c", Value: 3}}},
			{2, 3, []Item{{Key: "d", Value: 4}, {Key: "e", Value: 5}}},
			{1, 5, []Item{{Key: "a", Value: 1}, {Key: "b", Value: 2}, {Key: "c", Value: 3}, {Key: "d", Value: 4}, {Key: "e", Value: 5}}},
			{2, 5, []Item{}},
		}

		for _, tt := range tests {
			page := pager.GetPageWithSize(tt.pageNumber, tt.pageSize)

			uassert.Equal(t, len(tt.expected), len(page.Items))

			for i, item := range page.Items {
				uassert.Equal(t, tt.expected[i].Key, item.Key)
				uassert.Equal(t, tt.expected[i].Value, item.Value)
			}
		}
	})

	t.Run("reversed ordering", func(t *testing.T) {
		// Create a new pager.
		pager := NewPager(tree, 10, true)

		// Define test cases.
		tests := []struct {
			pageNumber int
			pageSize   int
			expected   []Item
		}{
			{1, 2, []Item{{Key: "e", Value: 5}, {Key: "d", Value: 4}}},
			{2, 2, []Item{{Key: "c", Value: 3}, {Key: "b", Value: 2}}},
			{3, 2, []Item{{Key: "a", Value: 1}}},
			{1, 3, []Item{{Key: "e", Value: 5}, {Key: "d", Value: 4}, {Key: "c", Value: 3}}},
			{2, 3, []Item{{Key: "b", Value: 2}, {Key: "a", Value: 1}}},
			{1, 5, []Item{{Key: "e", Value: 5}, {Key: "d", Value: 4}, {Key: "c", Value: 3}, {Key: "b", Value: 2}, {Key: "a", Value: 1}}},
			{2, 5, []Item{}},
		}

		for _, tt := range tests {
			page := pager.GetPageWithSize(tt.pageNumber, tt.pageSize)

			uassert.Equal(t, len(tt.expected), len(page.Items))

			for i, item := range page.Items {
				uassert.Equal(t, tt.expected[i].Key, item.Key)
				uassert.Equal(t, tt.expected[i].Value, item.Value)
			}
		}
	})
}

