func TestSet(t *testing.T) {
	tests := []struct {
		name        string
		setup       func() *List
		index       int
		value       interface{}
		expectedErr error
		verify      func(t *testing.T, l *List)
	}{
		{
			name: "set value in empty list",
			setup: func() *List {
				return New()
			},
			index:       0,
			value:       42,
			expectedErr: ErrOutOfBounds,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 0, l.Size())
			},
		},
		{
			name: "set value at valid index",
			setup: func() *List {
				l := New()
				l.Append(1)
				return l
			},
			index: 0,
			value: 42,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 42, l.Get(0))
				uassert.Equal(t, 1, l.Size())
				uassert.Equal(t, 1, l.TotalSize())
			},
		},
		{
			name: "set value at negative index",
			setup: func() *List {
				l := New()
				l.Append(1)
				return l
			},
			index:       -1,
			value:       42,
			expectedErr: ErrOutOfBounds,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 1, l.Get(0))
			},
		},
		{
			name: "set value beyond size",
			setup: func() *List {
				l := New()
				l.Append(1)
				return l
			},
			index:       1,
			value:       42,
			expectedErr: ErrOutOfBounds,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 1, l.Get(0))
				uassert.Equal(t, 1, l.Size())
			},
		},
		{
			name: "set nil value",
			setup: func() *List {
				l := New()
				l.Append(1)
				return l
			},
			index: 0,
			value: nil,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, nil, l.Get(0))
				uassert.Equal(t, 0, l.Size())
			},
		},
		{
			name: "set value at deleted index",
			setup: func() *List {
				l := New()
				l.Append(1, 2, 3)
				l.Delete(1)
				return l
			},
			index: 1,
			value: 42,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 42, l.Get(1))
				uassert.Equal(t, 3, l.Size())
				uassert.Equal(t, 3, l.TotalSize())
			},
		},
		{
			name: "set value in nil list",
			setup: func() *List {
				return nil
			},
			index:       0,
			value:       42,
			expectedErr: ErrOutOfBounds,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 0, l.Size())
			},
		},
		{
			name: "set multiple values at same index",
			setup: func() *List {
				l := New()
				l.Append(1)
				return l
			},
			index: 0,
			value: 42,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 42, l.Get(0))
				err := l.Set(0, 99)
				uassert.Equal(t, nil, err)
				uassert.Equal(t, 99, l.Get(0))
				uassert.Equal(t, 1, l.Size())
			},
		},
		{
			name: "set value at last index",
			setup: func() *List {
				l := New()
				l.Append(1, 2, 3)
				return l
			},
			index: 2,
			value: 42,
			verify: func(t *testing.T, l *List) {
				uassert.Equal(t, 42, l.Get(2))
				uassert.Equal(t, 3, l.Size())
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			l := tt.setup()
			err := l.Set(tt.index, tt.value)

			if tt.expectedErr != nil {
				uassert.Equal(t, tt.expectedErr.Error(), err.Error())
			} else {
				uassert.Equal(t, nil, err)
			}

			tt.verify(t, l)
		})
	}
}

package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/seqid"
	"gno.land/p/demo/ufmt"
)

// TODO: support grc20

type DistribFn func(elapsed time.Duration) uint64

const denom = "ugnot"

type payroll struct {
	id             seqid.ID
	vaultID        string
	createdAt      time.Time
	amount         int64
	beneficiary    std.Address
	totalWithdrawn uint64
	distrib        DistribFn
	paused         bool
	pausedAt       time.Time
	pauseDuration  time.Duration
	label          string
}

func Create(namespace string, label string, beneficiary std.Address, amount int64, distrib DistribFn) seqid.ID {
	vaultID := vaultIDFromRealm(std.PrevRealm(), namespace)

	p := payroll{
		id:          id.Next(),
		vaultID:     vaultID,
		createdAt:   time.Now(),
		amount:      amount,
		distrib:     distrib,
		beneficiary: beneficiary,
		label:       label,
	}

	payrolls.Set(id.String(), &p)

	if _, ok := payrollsByBeneficiary[p.beneficiary]; !ok {
		payrollsByBeneficiary[p.beneficiary] = avl.NewTree()
	}
	payrollsByBeneficiary[p.beneficiary].Set(p.id.String(), &p)

	if _, ok := payrollsByVault[p.vaultID]; !ok {
		payrollsByVault[p.vaultID] = avl.NewTree()
	}
	payrollsByVault[p.vaultID].Set(p.id.String(), &p)

	return p.id
}

func CreateMonthly(namespace string, label string, beneficiary std.Address, amount uint64, totalAmount int64) seqid.ID {
	return Create(namespace, label, beneficiary, totalAmount, MonthlyContinuous(amount))
}

func Claim(id seqid.ID, requestedAmount int64, destination std.Address) {
	if requestedAmount == 0 {
		panic(errors.New("invalid amount"))
	}

	p := mustGetPayroll(id)
	if std.PrevRealm().Addr() != p.beneficiary {
		panic(errors.New("only beneficiary can withdraw"))
	}

	vaultAmount := getVaultAmount(p.vaultID)
	if vaultAmount == 0 && requestedAmount != -1 {
		panic(errors.New("nothing to claim: vault empty"))
	}
	if requestedAmount > int64(vaultAmount) {
		requestedAmount = int64(vaultAmount)
	}

	withdrawn := p.withdraw(requestedAmount)
	if withdrawn == 0 && requestedAmount != -1 {
		panic(errors.New("nothing to claim"))
	}

	if withdrawn == 0 {
		return
	}

	banker := std.GetBanker(std.BankerTypeRealmSend)
	banker.SendCoins(std.CurrentRealm().Addr(), destination, std.NewCoins(std.NewCoin(denom, int64(withdrawn))))

	vaults.Set(p.vaultID, vaultAmount-withdrawn)
}

func ClaimAll(destination std.Address) {
	// TODO: optimize
	beneficiary := std.PrevRealm().Addr()
	claimed := false
	payrollsByBeneficiary[beneficiary].Iterate("", "", func(key string, value interface{}) bool {
		p := value.(*payroll)
		Claim(p.id, -1, destination)
		claimed = true
		return false
	})
	if !claimed {
		panic(errors.New("nothing to claim"))
	}
}

func Fund(input string, namespace string) {
	std.AssertOriginCall()

	var (
		id  string
		err error
	)

	if input == "" {
		id = vaultIDFromRealm(std.PrevRealm(), namespace)
	} else {
		id, err = vaultID(input, namespace)
		if err != nil {
			panic(err)
		}
	}

	send := std.GetOrigSend()
	amount := send.AmountOf(denom)
	if amount <= 0 {
		panic(errors.New("invalid amount"))
	}
	for _, coin := range send {
		if coin.Denom != denom {
			panic(ufmt.Errorf("only %q supported, got %q", denom, coin.Denom))
		}
	}

	vaults.Set(id, getVaultAmount(id)+uint64(amount))
}

var id seqid.ID

var payrolls avl.Tree                                       // seqid.ID -> *payroll
var payrollsByVault = make(map[string]*avl.Tree)            // seqid.ID -> *payroll
var payrollsByBeneficiary = make(map[std.Address]*avl.Tree) // seqid.ID -> *payroll

func (p *payroll) freeAt(when time.Time) uint64 {
	var elapsed time.Duration
	if p.paused {
		elapsed = p.pausedAt.Sub(p.createdAt)
	} else {
		elapsed = when.Sub(p.createdAt)
	}
	elapsed -= p.pauseDuration
	res := p.distrib(elapsed)
	if p.amount > 0 && res > uint64(p.amount) {
		return uint64(p.amount)
	}
	return res
}

func (p *payroll) available() uint64 {
	return p.freeAt(time.Now()) - p.totalWithdrawn
}

func (p *payroll) estimateDay() uint64 {
	return p.freeAt(time.Now().Add(time.Hour*24)) - p.freeAt(time.Now())
}

func (p *payroll) withdraw(amount int64) uint64 {
	available := int64(p.available())
	if amount > available || amount < 0 {
		amount = available
	}
	p.totalWithdrawn += uint64(amount)
	return uint64(amount)
}

func (p *payroll) pause() {
	if p.paused {
		panic(errors.New("already paused"))
	}
	p.paused = true
	p.pausedAt = time.Now()
}

func (p *payroll) resume() {
	if !p.paused {
		panic(errors.New("not paused"))
	}
	p.paused = false
	p.pauseDuration += time.Now().Sub(p.pausedAt)
}

func mustGetPayroll(id seqid.ID) *payroll {
	ip, ok := payrolls.Get(id.String())
	if !ok {
		panic(errors.New("no such payroll"))
	}
	return ip.(*payroll)
}

func StepDistrib(blockAmount uint64, per time.Duration) DistribFn {
	return func(elapsed time.Duration) uint64 {
		blocksCount := elapsed / per
		return blockAmount * uint64(blocksCount)
	}
}

func LinearDistrib(blockAmount uint64, per time.Duration) DistribFn {
	return func(elapsed time.Duration) uint64 {
		return (uint64(elapsed.Seconds()) * blockAmount) / uint64(per.Seconds())
	}
}

func Monthly(amount uint64) DistribFn {
	return StepDistrib(amount, time.Hour*24*30)
}

func MonthlyContinuous(amount uint64) DistribFn {
	return LinearDistrib(amount, time.Hour*24*30)
}

// Usage Create(std.Address("foo"), MonthlyContinuous(1000))

package payrolls

import (
	"net/url"
	"path"
	"std"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/mux"
	"gno.land/p/demo/ufmt"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

// TODO: pagination
const listMaxSize = 50

func Render(renderPath string) string {
	router := mux.NewRouter()

	router.HandleFunc("/u/*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Payrolls beneficiary\n")
		aon := users.AddressOrName(path.Base(r.Path))
		user := rusers.GetUserByAddressOrName(aon)
		addr := std.Address(aon)
		name := string(aon)
		if user != nil {
			addr = user.Address
			name = "@" + user.Name
			w.Write(ufmt.Sprintf("- 👤 User: @%s\n", user.Name))
		} else if !addr.IsValid() {
			w.Write(ufmt.Sprintf("❓ User %q not found\n", addr.String()))
			return
		}
		w.Write(ufmt.Sprintf("- 🆔 Address: %s\n", addr.String()))

		sb := strings.Builder{}
		tree, ok := payrollsByBeneficiary[addr]
		if !ok {
			tree = avl.NewTree()
		}
		estimatedDay := uint64(0)
		estimatedAvailable := uint64(0)
		estimatedClaimable := uint64(0)
		shouldEstimate := tree.Size() <= listMaxSize
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			sb.WriteString(p.render("beneficiary"))

			if shouldEstimate {
				available := p.available()
				estimatedAvailable += available
				vaultAmount := getVaultAmount(p.vaultID)
				if available > vaultAmount {
					available = vaultAmount
				}
				estimatedClaimable += available
				estimatedDay += p.estimateDay()
			}

			return false
		})

		if shouldEstimate {
			w.Write(renderEstimatedMonthly("", estimatedDay, denom))
			w.Write(renderClaimable("", estimatedClaimable, estimatedAvailable, denom, true))
		}

		actions := []string{}

		if !shouldEstimate || estimatedClaimable > 0 {
			claimAllButton := ufmt.Sprintf("[[Claim all]]($help&func=ClaimAll&destination=%s)", addr.String())
			actions = append(actions, claimAllButton)
		}

		label := url.QueryEscape(ufmt.Sprintf("%s Salary Monthly", name))
		newPayrollButton := ufmt.Sprintf("[[New payroll]]($help&func=CreateMonthly&namespace=&label=%s&beneficiary=%s&amount=10000&totalAmount=-1)", label, addr.String())
		actions = append(actions, newPayrollButton)

		w.Write(ufmt.Sprintf("- ✋ Actions: %s\n", strings.Join(actions, " ")))

		w.Write(sb.String())
	})

	router.HandleFunc("/v/*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Payrolls vault\n")

		name := path.Base(r.Path)
		vaultID, err := parseVaultID(name)
		if err != nil {
			w.Write(ufmt.Sprintf("❓ Vault %q not found", name))
			return
		}

		if name != vaultID {
			w.Write(ufmt.Sprintf("- 🎩 Name: %s\n", name))
		}
		w.Write(ufmt.Sprintf("- 🆔 ID: %s\n", vaultID))

		payrollsBuffer := strings.Builder{}
		estimatedDay := uint64(0)
		estimatedDebt := uint64(0)
		tree, ok := payrollsByVault[vaultID]
		if !ok {
			tree = avl.NewTree()
		}
		shouldEstimate := tree.Size() <= listMaxSize
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			if shouldEstimate {
				estimatedDebt += p.available()
				estimatedDay += p.estimateDay()
			}
			payrollsBuffer.WriteString(p.render("vault"))
			return false
		})

		funds := getVaultAmount(vaultID)

		if shouldEstimate {
			w.Write(renderEstimatedMonthly("", estimatedDay, denom))

			balance := int64(funds) - int64(estimatedDebt)
			prefix := "- 🏦 Funds: 🟢 "
			if balance < 0 {
				prefix = "- 🏦 Debt 🔴 "
			}
			w.Write(ufmt.Sprintf("%s%d%s\n", prefix, balance, denom))

			if balance > 0 {
				color := "🔴"
				runWayMonths := funds / (estimatedDay * 30)
				if runWayMonths >= 12 {
					color = "🟢"
				} else if runWayMonths >= 6 {
					color = "🟠"
				}
				w.Write(ufmt.Sprintf("- 🛬 Estimated runway: %s %d months\n", color, runWayMonths))
			}
		} else {
			w.Write(ufmt.Sprintf("- 🏦 Funds: %d%s (does not account for unclaimed amounts)\n", funds, denom))
		}

		label := url.QueryEscape("Salary Monthly")
		idParts := strings.Split(vaultID, ".")
		slug := ""
		if len(idParts) > 1 {
			slug = idParts[1]
		}
		depositFundsAction := ufmt.Sprintf("[[Deposit funds]]($help&func=Fund&addr=%s&slug=%s)", idParts[0], slug)
		newPayrollAction := ufmt.Sprintf("[[New payroll]]($help&func=CreateMonthly&namespace=&label=%s&beneficiary=&amount=10000&totalAmount=-1)", label)
		w.Write(ufmt.Sprintf("- ✋ Actions: %s %s\n", depositFundsAction, newPayrollAction))

		w.Write(payrollsBuffer.String())

	})

	router.HandleFunc("/d/*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Payroll details\n")
		idStr := path.Base(r.Path)
		p, ok := payrolls.Get(idStr)
		if !ok {
			w.Write(ufmt.Sprintf("❓ Payroll %q not found", idStr))
			return
		}
		w.Write(p.(*payroll).render(""))
	})

	router.HandleFunc("*", func(w *mux.ResponseWriter, r *mux.Request) {
		w.Write("# Latest payrolls\n")
		payrolls.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			w.Write(value.(*payroll).render(""))
			return false
		})

		w.Write("\n\nTODO\n")
		w.Write("- [ ] Stop / Archive\n")
		w.Write("- [ ] Home actions")
		w.Write("- [ ] Unit tests")
		w.Write("- [ ] Pause / Unpause\n")
		w.Write("- [ ] Details: Events history\n")
		w.Write("- [ ] PoV Tags\n")
		w.Write("- [ ] Edit label / PoV labels\n")
		w.Write("- [ ] Stop litigation\n")
		w.Write("- [ ] Fix anchors\n")
	})

	return router.Render(renderPath)
}

func (p *payroll) render(pov string) string {
	sb := strings.Builder{}

	sb.WriteString(ufmt.Sprintf("### [%s](/r/demo/payrolls:/d/%s)\n", p.id.String(), p.id.String()))

	sb.WriteString(ufmt.Sprintf("  - ✍️ Label: %s\n", p.label))

	if p.paused {
		sb.WriteString(ufmt.Sprintf("  - ⏸️ Paused since: %s\n", p.pausedAt.Format(time.DateTime)))
	}

	if pov != "vault" {
		pvid := prettyVaultID(p.vaultID)
		sb.WriteString(ufmt.Sprintf("  - 🎩 Vault: [%s](/r/demo/payrolls:/v/%s)\n", pvid, pvid))
	}

	if pov != "beneficiary" {
		beneficiaryUser := rusers.GetUserByAddress(p.beneficiary)
		var userStr string
		if beneficiaryUser == nil {
			userStr = p.beneficiary.String()
		} else {
			userStr = "@" + beneficiaryUser.Name
		}
		sb.WriteString(ufmt.Sprintf("  - 👤 Beneficiary: [%s](/r/demo/payrolls:/u/%s)\n", userStr, userStr))
	}

	available := p.available()
	vaultAmount := getVaultAmount(p.vaultID)
	claimable := available
	if available > vaultAmount {
		claimable = vaultAmount
	}
	if available > 0 {
		sb.WriteString(renderClaimable("  ", claimable, available, denom, false))
	}

	estimatedDay := p.estimateDay()
	sb.WriteString(renderEstimatedMonthly("  ", estimatedDay, denom))

	sb.WriteString("  - 🕙 Remaining: ")
	if p.amount >= 0 {
		amountRemaining := uint64(p.amount) - p.totalWithdrawn
		sb.WriteString(ufmt.Sprintf("%s (%d%s)", ((time.Duration(amountRemaining) * 24) / time.Duration(estimatedDay) / time.Hour).String(), amountRemaining, denom))
	} else {
		sb.WriteRune('∞')
	}
	sb.WriteRune('\n')

	actions := []string{}

	if pov != "vault" && claimable > 0 {
		claim := ufmt.Sprintf("[[Claim]]($help&func=Claim&id=%d&requestedAmount=-1&destination=%s)", uint64(p.id), p.beneficiary.String())
		actions = append(actions, claim)
	}

	if len(actions) > 0 {
		sb.WriteString(ufmt.Sprintf("- ✋ Actions: %s\n", strings.Join(actions, " ")))
	}

	return sb.String()
}

func renderClaimable(prefix string, claimable uint64, available uint64, denom string, plural bool) string {
	if available == 0 {
		return ""
	}
	sb := strings.Builder{}
	sb.WriteString(prefix)
	sb.WriteString("- 💵 Claimable: ")
	if claimable < available {
		pls := ""
		if plural {
			pls = "(s)"
		}
		color := "🟠"
		if claimable == 0 {
			color = "🔴"
		}
		sb.WriteString(ufmt.Sprintf("%s %d%s/%d%s (not enough funds in vault%s!)\n", color, claimable, denom, available, denom, pls))
	} else {
		sb.WriteString(ufmt.Sprintf("🟢 %d%s\n", available, denom))
	}
	return sb.String()
}

func renderEstimatedMonthly(prefix string, estimatedDay uint64, denom string) string {
	return ufmt.Sprintf("%s- 💸 Estimated monthly: %d%s\n", prefix, estimatedDay*30, denom)
}

func prettyVaultID(id string) string {
	parts := strings.Split(id, ".")
	if len(parts) == 0 {
		return id
	}

	user := rusers.GetUserByAddress(std.Address(parts[0]))
	if user == nil {
		return id
	}

	parts[0] = "@" + user.Name
	return strings.Join(parts, ".")
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

var vaults avl.Tree // std.Addresss -> uint64

func getVaultAmount(id string) uint64 {
	amount, ok := vaults.Get(id)
	if ok {
		return amount.(uint64)
	}
	return 0
}

var errInvalidInput = errors.New("invalid input")

func vaultID(input string, namespace string) (string, error) {
	if len(input) == 0 {
		return "", errInvalidInput
	}

	if id, ok := vaultIDFromPkgPath(input, namespace); ok {
		return id, nil
	}

	user := rusers.GetUserByAddressOrName(users.AddressOrName(input))
	if user != nil {
		return vaultIDUnsafe(user.Address.String(), namespace), nil
	}

	if id, ok := vaultIDFromAddr(std.Address(input), namespace); ok {
		return id, nil
	}

	return "", errInvalidInput
}

func parseVaultID(id string) (string, error) {
	parts := strings.Split(id, ".")
	if len(parts) == 0 {
		return "", errInvalidInput
	}

	if len(parts) == 1 {
		return vaultID(parts[0], "")
	}

	return vaultID(parts[0], parts[1])
}

func vaultIDUnsafe(entity string, namespace string) string {
	if namespace == "" {
		return entity
	}
	return entity + "." + namespace
}

func vaultIDFromAddr(addr std.Address, namespace string) (string, bool) {
	if !addr.IsValid() {
		return "", false
	}
	return vaultIDUnsafe(addr.String(), namespace), true
}

func vaultIDFromPkgPath(entity string, namespace string) (string, bool) {
	if !strings.HasPrefix(entity, std.GetChainDomain()+"/r/") {
		return "", false
	}
	return vaultIDUnsafe(entity, namespace), true
}

func vaultIDFromRealm(realm std.Realm, namespace string) string {
	if realm.IsUser() {
		return vaultIDUnsafe(realm.Addr().String(), namespace)
	} else {
		return vaultIDUnsafe(realm.PkgPath(), namespace)
	}
}

package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/seqid"
	"gno.land/p/demo/ufmt"
)

// TODO: support grc20

type DistribFn func(elapsed time.Duration) uint64

const denom = "ugnot"

type payroll struct {
	id             seqid.ID
	vaultID        string
	createdAt      time.Time
	amount         int64
	beneficiary    std.Address
	totalWithdrawn uint64
	distrib        DistribFn
	paused         bool
	pausedAt       time.Time
	pauseDuration  time.Duration
	label          string
}

func Create(namespace string, label string, beneficiary std.Address, amount int64, distrib DistribFn) seqid.ID {
	vaultID := vaultIDFromRealm(std.PrevRealm(), namespace)

	p := payroll{
		id:          id.Next(),
		vaultID:     vaultID,
		createdAt:   time.Now(),
		amount:      amount,
		distrib:     distrib,
		beneficiary: beneficiary,
		label:       label,
	}

	payrolls.Set(id.String(), &p)

	if _, ok := payrollsByBeneficiary[p.beneficiary]; !ok {
		payrollsByBeneficiary[p.beneficiary] = avl.NewTree()
	}
	payrollsByBeneficiary[p.beneficiary].Set(p.id.String(), &p)

	if _, ok := payrollsByVault[p.vaultID]; !ok {
		payrollsByVault[p.vaultID] = avl.NewTree()
	}
	payrollsByVault[p.vaultID].Set(p.id.String(), &p)

	return p.id
}

func CreateMonthly(namespace string, label string, beneficiary std.Address, amount uint64, totalAmount int64) seqid.ID {
	return Create(namespace, label, beneficiary, totalAmount, MonthlyContinuous(amount))
}

func Claim(id seqid.ID, requestedAmount int64, destination std.Address) {
	if requestedAmount == 0 {
		panic(errors.New("invalid amount"))
	}

	p := mustGetPayroll(id)
	if std.PrevRealm().Addr() != p.beneficiary {
		panic(errors.New("only beneficiary can withdraw"))
	}

	vaultAmount := getVaultAmount(p.vaultID)
	if vaultAmount == 0 && requestedAmount != -1 {
		panic(errors.New("nothing to claim: vault empty"))
	}
	if requestedAmount > int64(vaultAmount) {
		requestedAmount = int64(vaultAmount)
	}

	withdrawn := p.withdraw(requestedAmount)
	if withdrawn == 0 && requestedAmount != -1 {
		panic(errors.New("nothing to claim"))
	}

	if withdrawn == 0 {
		return
	}

	banker := std.GetBanker(std.BankerTypeRealmSend)
	banker.SendCoins(std.CurrentRealm().Addr(), destination, std.NewCoins(std.NewCoin(denom, int64(withdrawn))))

	vaults.Set(p.vaultID, vaultAmount-withdrawn)
}

func ClaimAll(destination std.Address) {
	// TODO: optimize
	beneficiary := std.PrevRealm().Addr()
	claimed := false
	payrollsByBeneficiary[beneficiary].Iterate("", "", func(key string, value interface{}) bool {
		p := value.(*payroll)
		Claim(p.id, -1, destination)
		claimed = true
		return false
	})
	if !claimed {
		panic(errors.New("nothing to claim"))
	}
}

func Fund(input string, namespace string) {
	std.AssertOriginCall()

	var (
		id  string
		err error
	)

	if input == "" {
		id = vaultIDFromRealm(std.PrevRealm(), namespace)
	} else {
		id, err = vaultID(input, namespace)
		if err != nil {
			panic(err)
		}
	}

	send := std.GetOrigSend()
	amount := send.AmountOf(denom)
	if amount <= 0 {
		panic(errors.New("invalid amount"))
	}
	for _, coin := range send {
		if coin.Denom != denom {
			panic(ufmt.Errorf("only %q supported, got %q", denom, coin.Denom))
		}
	}

	vaults.Set(id, getVaultAmount(id)+uint64(amount))
}

var id seqid.ID

var payrolls avl.Tree                                       // seqid.ID -> *payroll
var payrollsByVault = make(map[string]*avl.Tree)            // seqid.ID -> *payroll
var payrollsByBeneficiary = make(map[std.Address]*avl.Tree) // seqid.ID -> *payroll

func (p *payroll) freeAt(when time.Time) uint64 {
	var elapsed time.Duration
	if p.paused {
		elapsed = p.pausedAt.Sub(p.createdAt)
	} else {
		elapsed = when.Sub(p.createdAt)
	}
	elapsed -= p.pauseDuration
	res := p.distrib(elapsed)
	if p.amount > 0 && res > uint64(p.amount) {
		return uint64(p.amount)
	}
	return res
}

func (p *payroll) available() uint64 {
	return p.freeAt(time.Now()) - p.totalWithdrawn
}

func (p *payroll) estimateDay() uint64 {
	return p.freeAt(time.Now().Add(time.Hour*24)) - p.freeAt(time.Now())
}

func (p *payroll) withdraw(amount int64) uint64 {
	available := int64(p.available())
	if amount > available || amount < 0 {
		amount = available
	}
	p.totalWithdrawn += uint64(amount)
	return uint64(amount)
}

func (p *payroll) pause() {
	if p.paused {
		panic(errors.New("already paused"))
	}
	p.paused = true
	p.pausedAt = time.Now()
}

func (p *payroll) resume() {
	if !p.paused {
		panic(errors.New("not paused"))
	}
	p.paused = false
	p.pauseDuration += time.Now().Sub(p.pausedAt)
}

func mustGetPayroll(id seqid.ID) *payroll {
	ip, ok := payrolls.Get(id.String())
	if !ok {
		panic(errors.New("no such payroll"))
	}
	return ip.(*payroll)
}

func StepDistrib(blockAmount uint64, per time.Duration) DistribFn {
	return func(elapsed time.Duration) uint64 {
		blocksCount := elapsed / per
		return blockAmount * uint64(blocksCount)
	}
}

func LinearDistrib(blockAmount uint64, per time.Duration) DistribFn {
	return func(elapsed time.Duration) uint64 {
		return (uint64(elapsed.Seconds()) * blockAmount) / uint64(per.Seconds())
	}
}

func Monthly(amount uint64) DistribFn {
	return StepDistrib(amount, time.Hour*24*30)
}

func MonthlyContinuous(amount uint64) DistribFn {
	return LinearDistrib(amount, time.Hour*24*30)
}

