	sb := strings.Builder{}

	sb.WriteString(ufmt.Sprintf("### [%s](%s:/d/%s)\n", p.id.String(), linkPrefix(), p.id.String()))

	sb.WriteString(ufmt.Sprintf("  - ✍️ Label: %s\n", p.label))

	if p.paused {
		sb.WriteString(ufmt.Sprintf("  - ⏸️ Paused since: %s\n", p.pausedAt.Format(time.DateTime)))
	}

	if pov != "vault" {
		pvid := prettyVaultID(p.vaultID)
		sb.WriteString(ufmt.Sprintf("  - 🎩 Vault: [%s](%s:/v/%s)\n", pvid, linkPrefix(), pvid))
	}

	if pov != "beneficiary" {
		beneficiaryUser := rusers.GetUserByAddress(p.beneficiary)
		var userStr string
		if beneficiaryUser == nil {
			userStr = p.beneficiary.String()
		} else {
			userStr = "@" + beneficiaryUser.Name
		}
		sb.WriteString(ufmt.Sprintf("  - 👤 Beneficiary: [%s](%s:/u/%s)\n", userStr, linkPrefix(), userStr))
	}

	available := p.available()
	vaultAmount := getVaultAmount(p.vaultID)
	claimable := available
	if available > vaultAmount {
		claimable = vaultAmount
	}
	if available > 0 {
		sb.WriteString(renderClaimable("  ", claimable, available, denom, false))
	}

	estimatedDay := p.estimateDay()
	sb.WriteString(renderEstimatedMonthly("  ", estimatedDay, denom))

	sb.WriteString("  - 🕙 Remaining: ")
	if p.amount >= 0 {
		amountRemaining := uint64(p.amount) - p.totalWithdrawn
		sb.WriteString(ufmt.Sprintf("%s (%d%s)", ((time.Duration(amountRemaining) * 24) / time.Duration(estimatedDay) / time.Hour).String(), amountRemaining, denom))
	} else {
		sb.WriteRune('∞')
	}
	sb.WriteRune('\n')

	actions := []string{}

	if pov != "vault" && claimable > 0 {
		claim := ufmt.Sprintf("[[Claim]]($help&func=Claim&id=%d&requestedAmount=-1&destination=%s)", uint64(p.id), p.beneficiary.String())
		actions = append(actions, claim)
	}

	if len(actions) > 0 {
		sb.WriteString(ufmt.Sprintf("- ✋ Actions: %s\n", strings.Join(actions, " ")))
	}

	return sb.String()
}

func renderClaimable(prefix string, claimable uint64, available uint64, denom string, plural bool) string {
	if available == 0 {
		return ""
	}
	sb := strings.Builder{}
	sb.WriteString(prefix)
	sb.WriteString("- 💵 Claimable: ")
	if claimable < available {
		pls := ""
		if plural {
			pls = "(s)"
		}
		color := "🟠"
		if claimable == 0 {
			color = "🔴"
		}
		sb.WriteString(ufmt.Sprintf("%s %d%s/%d%s (not enough funds in vault%s!)\n", color, claimable, denom, available, denom, pls))
	} else {
		sb.WriteString(ufmt.Sprintf("🟢 %d%s\n", available, denom))
	}
	return sb.String()
}

func renderEstimatedMonthly(prefix string, estimatedDay uint64, denom string) string {
	return ufmt.Sprintf("%s- 💸 Estimated monthly: %d%s\n", prefix, estimatedDay*30, denom)
}

func prettyVaultID(id string) string {
	parts := strings.Split(id, ".")
	if len(parts) == 0 {
		return id
	}

	user := rusers.GetUserByAddress(std.Address(parts[0]))
	if user == nil {
		return id
	}

	parts[0] = "@" + user.Name
	return strings.Join(parts, ".")
}

func linkPrefix() string {
	realmPath := std.CurrentRealm().PkgPath()
	slashIdx := strings.Index(realmPath, "/")
	if slashIdx == -1 {
		return realmPath
	}
	return realmPath[slashIdx:]
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

var vaults avl.Tree // std.Addresss -> uint64

func getVaultAmount(id string) uint64 {
	amount, ok := vaults.Get(id)
	if ok {
		return amount.(uint64)
	}
	return 0
}

var errInvalidInput = errors.New("invalid input")

func vaultID(input string, namespace string) (string, error) {
	if len(input) == 0 {
		return "", errInvalidInput
	}

	if id, ok := vaultIDFromPkgPath(input, namespace); ok {
		return id, nil
	}

	user := rusers.GetUserByAddressOrName(users.AddressOrName(input))
	if user != nil {
		return vaultIDUnsafe(user.Address.String(), namespace), nil
	}

	if id, ok := vaultIDFromAddr(std.Address(input), namespace); ok {
		return id, nil
	}

	return "", errInvalidInput
}

func parseVaultID(id string) (string, error) {
	parts := strings.Split(id, ".")
	if len(parts) == 0 {
		return "", errInvalidInput
	}

	if len(parts) == 1 {
		return vaultID(parts[0], "")
	}

	return vaultID(parts[0], parts[1])
}

func vaultIDUnsafe(entity string, namespace string) string {
	if namespace == "" {
		return entity
	}
	return entity + "." + namespace
}

func vaultIDFromAddr(addr std.Address, namespace string) (string, bool) {
	if !addr.IsValid() {
		return "", false
	}
	return vaultIDUnsafe(addr.String(), namespace), true
}

func vaultIDFromPkgPath(entity string, namespace string) (string, bool) {
	if !strings.HasPrefix(entity, std.GetChainDomain()+"/r/") {
		return "", false
	}
	return vaultIDUnsafe(entity, namespace), true
}

func vaultIDFromRealm(realm std.Realm, namespace string) string {
	if realm.IsUser() {
		return vaultIDUnsafe(realm.Addr().String(), namespace)
	} else {
		return vaultIDUnsafe(realm.PkgPath(), namespace)
	}
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/r/demo/grc20reg"
)

// Coins transforms coins into payrolls coins, prefixing them with their type. Examples: "ugnot" -> "/native/ugnot", "gno.land/r/demo/foo20" -> "/grc20/gno.land/r/demo/foo20"
func Coins(native std.Coins, grc20 std.Coins) (std.Coins, error) {
	out := make(std.Coins, len(native)+len(grc20))
	for i, coin := range native {
		out[i].Amount = coin.Amount
		out[i].Denom = "/native/" + coin.Denom
	}
	offset := len(native)
	for i, coin := range grc20 {
		j := offset + i
		out[j].Amount = coin.Amount
		out[j].Denom = "/grc20/" + coin.Denom
	}
	return out, nil
}

func addCoins(a std.Coins, b std.Coins) std.Coins {
	out := make(std.Coins, len(a))
	copy(out, a)
	for _, coin := range b {
		out = addCoinAmount(out, coin)
	}
	return out
}

func subCoins(a std.Coins, b std.Coins) std.Coins {
	out := make(std.Coins, len(a))
	copy(out, a)
	for _, coin := range b {
		out = addCoinAmount(out, std.NewCoin(coin.Denom, -coin.Amount))
	}
	return out
}

func coinsHasPositive(coins std.Coins) bool {
	for _, coin := range coins {
		if coin.Amount > 0 {
			return true
		}
	}
	return false
}

func clampCoins(a std.Coins, max std.Coins) std.Coins {
	out := std.Coins{}
	for _, coin := range a {
		maxAmount := max.AmountOf(coin.Denom)
		if coin.Amount > maxAmount {
			out = append(out, std.NewCoin(coin.Denom, maxAmount))
			continue
		}
		out = append(out, coin)
	}
	return out
}

func addCoinAmount(coins std.Coins, value std.Coin) std.Coins {
	for i, coin := range coins {
		if coin.Denom != value.Denom {
			continue
		}

		out := make(std.Coins, len(coins))
		copy(out, coins)
		out[i].Amount += value.Amount
		return out
	}

	return append(coins, value)
}

func coinsEmpty(coins std.Coins) bool {
	if len(coins) == 0 {
		return true
	}
	for _, coin := range coins {
		if coin.Amount != 0 {
			return false
		}
	}
	return true
}

func sendCoins(dst std.Address, coins std.Coins) {
	if len(coins) == 0 {
		return
	}

	natives := std.Coins{}
	grc20s := std.Coins{}

	for _, coin := range coins {
		if coin.Amount == 0 {
			continue
		}
		if coin.Amount < 0 {
			panic(errors.New("negative send amount"))
		}

		var (
			target *std.Coins
			denom  string
		)

		if strings.HasPrefix(coin.Denom, "/native/") {
			target = &natives
			denom = strings.TrimPrefix(coin.Denom, "/native/")
		} else if strings.HasPrefix(coin.Denom, "/grc20/") {
			target = &grc20s
			denom = strings.TrimPrefix(coin.Denom, "/grc20/")
		} else {
			panic(errors.New("unknown coin kind"))
		}
		*target = addCoins(*target, std.NewCoins(std.NewCoin(denom, coin.Amount)))
	}

	if len(natives) != 0 {
		banker := std.GetBanker(std.BankerTypeRealmSend)
		banker.SendCoins(std.CurrentRealm().Addr(), dst, natives)
	}

	for _, coin := range grc20s {
		err := grc20reg.MustGet(coin.Denom)().CallerTeller().Transfer(dst, uint64(coin.Amount))
		if err != nil {
			panic(err)
		}
	}
}

package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/seqid"
)

// DistribFn is a distribution function used to compute and amount of released coins at a particular point in time.
//
// It must return payrolls [Coins]
type DistribFn func(elapsed time.Duration) std.Coins

// BreakupFn is a function used to compute payroll termination bonuses.
//
// It must return payrolls [Coins]
type BreakupFn func(elapsed time.Duration, pauseDuration time.Duration, source BreakupSource) std.Coins

type BreakupSource uint

const (
	BreakupSourceCreator BreakupSource = iota
	BreakupSourceBeneficiary
)

// DistribStep returns a distribution function that will release coins every `per` duration.
func DistribStep(coins std.Coins, per time.Duration) DistribFn {
	return func(elapsed time.Duration) std.Coins {
		blocksCount := elapsed / per
		out := make(std.Coins, 0, len(coins))
		for _, coin := range coins {
			out = append(out, std.NewCoin(coin.Denom, coin.Amount*int64(blocksCount)))
		}
		return out
	}
}

// DistribLinear returns a distribution function that will continuously release coins every seconds and amount to a rate of `coins` every `per` duration.
func DistribLinear(coins std.Coins, per time.Duration) DistribFn {
	return func(elapsed time.Duration) std.Coins {
		out := make(std.Coins, 0, len(coins))
		for _, coin := range coins {
			newAmount := (int64(elapsed.Seconds()) * coin.Amount) / int64(per.Seconds())
			out = append(out, std.NewCoin(coin.Denom, newAmount))
		}
		return out
	}
}

// DistribMonthlyStep returns a distribution function that will release `coins` every months
func DistribMonthlyStep(amountPerMonth std.Coins) DistribFn {
	return DistribStep(amountPerMonth, time.Hour*24*30)
}

// DistribMonthlyStep returns a distribution function that will continuously release coins every seconds and amount to a rate of `coins` every months
func DistribMonthlyContinuous(amountPerMonth std.Coins) DistribFn {
	return DistribLinear(amountPerMonth, time.Hour*24*30)
}

// CreateMonthlyContinuous creates a payroll with a continuous release at a fixed rate per months of a token identified by denom.
//
// denom must be a payrolls [Coins] denom
func CreateMonthlyContinuous(namespace string, label string, beneficiary std.Address, amountPerMonth int64, denom string) seqid.ID {
	if amountPerMonth <= 0 || denom == "" {
		panic(errors.New("invalid input"))
	}
	coinsPerMonth := std.NewCoins(std.NewCoin(denom, amountPerMonth))
	return Create(namespace, label, beneficiary, DistribMonthlyContinuous(coinsPerMonth), nil)
}

// CreateCDI creates a payroll that tries to match the French CDI contract
//
// Denom must be a payrolls [Coins] denom
//
// See: https://travail-emploi.gouv.fr/le-contrat-de-travail-duree-indeterminee-cdi
func CreateCDI(namespace string, label string, beneficiary std.Address, amountPerMonth int64, denom string) seqid.ID {
	if amountPerMonth <= 0 || denom == "" {
		panic(errors.New("invalid input"))
	}
	coinsPerMonth := std.NewCoins(std.NewCoin(denom, amountPerMonth))
	return Create(namespace, label, beneficiary, DistribMonthlyStep(coinsPerMonth), BreakupCDI(coinsPerMonth))
}

// BreakupCDI is a breakup function that will release the an amount of coins
// based on a simplified "Rupture Conventionelle" (conventional breakup) model based on the French CDI employment contract
func BreakupCDI(coinsPerMonth std.Coins) BreakupFn {
	const hoursPerYear = float64(8_670)
	return func(elapsed time.Duration, pauseDuration time.Duration, source BreakupSource) std.Coins {
		if source != BreakupSourceCreator {
			return nil
		}

		// conventional breakup
		out := make(std.Coins, 0, len(coinsPerMonth))
		worked := elapsed - pauseDuration
		numYears := worked.Hours() / hoursPerYear
		for _, coin := range coinsPerMonth {
			newAmount := int64(0.25 * float64(coin.Amount) * numYears)
			out = append(out, std.NewCoin(coin.Denom, newAmount))
		}
		return out
	}
}

package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/seqid"
)

type payroll struct {
	id             seqid.ID
	creatorAddr    std.Address
	vaultID        string
	createdAt      time.Time
	beneficiary    std.Address
	totalWithdrawn std.Coins
	distrib        DistribFn
	breakup        BreakupFn
	paused         bool
	pausedAt       time.Time
	pauseDuration  time.Duration
	label          string
	breakupCoins   std.Coins
	stopped        bool
	stoppedAt      time.Time
}

func (p *payroll) freeAt(when time.Time, estimate bool) std.Coins {
	if p.stopped && p.stoppedAt.Before(when) {
		when = p.stoppedAt
	}

	var elapsed time.Duration
	if p.paused && !estimate {
		elapsed = p.pausedAt.Sub(p.createdAt)
	} else {
		elapsed = when.Sub(p.createdAt)
	}
	elapsed -= p.pauseDuration
	res := p.distrib(elapsed)

	return res
}

func (p *payroll) available() std.Coins {
	return subCoins(addCoins(p.freeAt(time.Now(), false), p.breakupCoins), p.totalWithdrawn)
}

func (p *payroll) estimateDay() std.Coins {
	return subCoins(p.freeAt(time.Now().Add(time.Hour*24), true), p.freeAt(time.Now(), true))
}

func (p *payroll) claim(vaultAmount std.Coins) std.Coins {
	available := p.available()
	claimable := clampCoins(available, vaultAmount)
	p.totalWithdrawn = addCoins(p.totalWithdrawn, claimable)
	return claimable
}

func (p *payroll) pause(source std.Address) {
	p.assertNotStopped()
	p.assertBeneficiary(source)

	if p.paused {
		panic(errors.New("already paused"))
	}
	p.paused = true
	p.pausedAt = time.Now()
}

func (p *payroll) resume(source std.Address) {
	p.assertNotStopped()
	p.assertBeneficiary(source)

	if !p.paused {
		panic(errors.New("not paused"))
	}
	p.resumeUnsafe()
}

func (p *payroll) resumeUnsafe() {
	p.paused = false
	p.pauseDuration += time.Now().Sub(p.pausedAt)
}

func (p *payroll) stop(source std.Address) {
	p.assertNotStopped()

	if p.paused {
		p.resumeUnsafe()
	}

	p.breakupCoins = p.getBreakupCoins(source)
	p.stopped = true
	p.stoppedAt = time.Now()
}

func (p *payroll) getBreakupCoins(source std.Address) std.Coins {
	if p.breakup == nil {
		return nil
	}

	var breakupSource BreakupSource
	switch source {
	case p.beneficiary:
		breakupSource = BreakupSourceBeneficiary
	case p.creatorAddr:
		breakupSource = BreakupSourceCreator
	default:
		panic(errors.New("unknown source"))
	}

	return p.breakup(time.Now().Sub(p.createdAt), p.pauseDuration, breakupSource)
}

func (p *payroll) assertNotStopped() {
	if p.stopped {
		panic(errors.New("stopped"))
	}
}

func (p *payroll) assertBeneficiary(addr std.Address) {
	if addr != p.beneficiary {
		panic(errors.New("only beneficiary allowed"))
	}
}

// package payrolls is a payroll management example
package payrolls

import (
	"errors"
	"std"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/grc/grc20"
	"gno.land/p/demo/seqid"
	"gno.land/r/demo/grc20reg"
)

var (
	id                    seqid.ID
	payrolls              avl.Tree                          // seqid.ID -> *payroll
	payrollsByVault       = make(map[string]*avl.Tree)      // vaultID -> seqid.ID -> *payroll
	payrollsByBeneficiary = make(map[std.Address]*avl.Tree) // addr -> seqid.ID -> *payroll
)

// Create creates a new payrolls using the vault identified by the caller and namespace.
//
// - distrib is the function used to compute the amount of coins release at a specific point it time. It must not be nil Example: [MonthlyContinuous]
//
// - breakup is the function used to manage bonuses when a payroll is terminated. It can be nil. Example: [BreakupCDI]
func Create(namespace string, label string, beneficiary std.Address, distrib DistribFn, breakup BreakupFn) seqid.ID {
	vaultID := vaultIDFromRealm(std.PrevRealm(), namespace)

	if distrib == nil {
		panic(errors.New("nil distrib"))
	}
	if !beneficiary.IsValid() {
		panic(errors.New("invalid beneficiary"))
	}

	p := payroll{
		id:          id.Next(),
		vaultID:     vaultID,
		createdAt:   time.Now(),
		distrib:     distrib,
		breakup:     breakup,
		beneficiary: beneficiary,
		label:       label,
		creatorAddr: std.PrevRealm().Addr(),
	}

	payrolls.Set(id.String(), &p)

	if _, ok := payrollsByBeneficiary[p.beneficiary]; !ok {
		payrollsByBeneficiary[p.beneficiary] = avl.NewTree()
	}
	payrollsByBeneficiary[p.beneficiary].Set(p.id.String(), &p)

	if _, ok := payrollsByVault[p.vaultID]; !ok {
		payrollsByVault[p.vaultID] = avl.NewTree()
	}
	payrollsByVault[p.vaultID].Set(p.id.String(), &p)

	return p.id
}

// ClaimAll claims the coins available in the payroll identified by id
//
// - The caller must be the beneficiary
//
// - If destination is empty, it will send coins to the caller address
func Claim(id seqid.ID, destination std.Address) {
	claimInternal(id, destination, true)
}

func claimInternal(id seqid.ID, destination std.Address, mustClaim bool) bool {
	p := mustGetPayroll(id)
	caller := std.PrevRealm().Addr()
	if caller != p.beneficiary {
		panic(errors.New("only beneficiary can withdraw"))
	}

	vaultAmount := getVaultCoins(p.vaultID)
	claimed := p.claim(vaultAmount)
	if coinsEmpty(claimed) {
		if mustClaim {
			panic(errors.New("nothing to claim"))
		} else {
			return false
		}
	}

	if len(destination) == 0 {
		destination = caller
	}

	sendCoins(destination, claimed)

	vaults.Set(p.vaultID, subCoins(vaultAmount, claimed))

	return true
}

// ClaimAll claims the coins available in all payrolls where the caller is the beneficiary
//
// - The caller must be the beneficiary
//
// - If destination is empty, it will send coins to the caller address
func ClaimAll(destination std.Address) {
	beneficiary := std.PrevRealm().Addr()
	claimed := false
	payrollsByBeneficiary[beneficiary].Iterate("", "", func(key string, value interface{}) bool {
		p := value.(*payroll)
		if ok := claimInternal(p.id, destination, false); ok {
			claimed = true
		}
		return false
	})
	if !claimed {
		panic(errors.New("nothing to claim"))
	}
}

// FundNative funds a vault identified by base and namespace using the OrigSend coins
//
// - Anyone can fund any vault
func FundNative(base string, namespace string) {
	std.AssertOriginCall()

	var (
		id  string
		err error
	)

	if base == "" {
		id = vaultIDFromRealm(std.PrevRealm(), namespace)
	} else {
		id, err = vaultID(base, namespace)
		if err != nil {
			panic(err)
		}
	}

	send := std.GetOrigSend()
	if coinsEmpty(send) {
		panic(errors.New("invalid amount"))
	}
	for i := range send {
		send[i].Denom = "/native/" + send[i].Denom
	}
	vaults.Set(id, addCoins(getVaultCoins(id), send))
}

// FundGRC20Reg funds a vault identified by base and namespace using the fqname of a registered coin
//
// - If amount is empty, it will use the full allowance
//
// - Anyone can fund any vault
func FundGRC20Reg(base string, namespace string, fqname string, amount int64) {
	tokenGetter := grc20reg.MustGet(fqname)
	token := tokenGetter()
	if token == nil {
		panic(errors.New("token getter returned nil"))
	}

	fundGRC20(base, namespace, token, "/grc20/"+fqname, amount)
}

func fundGRC20(base string, namespace string, token *grc20.Token, tokenKey string, amount int64) {
	anyAmount := amount == -1
	if !anyAmount && amount <= 0 {
		panic(errors.New("invalid amount"))
	}

	var (
		id  string
		err error
	)

	if base == "" {
		id = vaultIDFromRealm(std.PrevRealm(), namespace)
	} else {
		id, err = vaultID(base, namespace)
		if err != nil {
			panic(err)
		}
	}

	if anyAmount {
		amount = int64(token.Allowance(std.PrevRealm().Addr(), std.CurrentRealm().Addr()))
	}

	teller := token.CallerTeller()
	if err := teller.TransferFrom(std.PrevRealm().Addr(), std.CurrentRealm().Addr(), uint64(amount)); err != nil {
		panic(err)
	}

	send := std.NewCoins(std.NewCoin(tokenKey, int64(amount)))
	vaults.Set(id, addCoins(getVaultCoins(id), send))
}

// WithdrawFunds withdraws from a vault identified by the caller and namespace. It uses the coins identified by a payrolls denom (see [Coins])
//
// - If amount is empty, it will withdraw all available funds
//
// - If destination is empty, it will send to the caller's address
func WithdrawFunds(namespace string, denom string, amount int64, destination std.Address) {
	if amount < -1 {
		panic(errors.New("invalid amount"))
	}
	anyAmount := amount == -1
	if amount == 0 {
		panic(errors.New("invalid amount"))
	}

	prevRealm := std.PrevRealm()
	if len(destination) == 0 {
		destination = prevRealm.Addr()
	}

	id := vaultIDFromRealm(prevRealm, namespace)

	vaultAmount := getVaultCoins(id)
	if vaultAmount.AmountOf(denom) == 0 {
		panic(errors.New("vault empty"))
	}

	available := vaultAmount
	payrollsByVault[id].Iterate("", "", func(_ string, value interface{}) bool {
		p := value.(*payroll)
		available = subCoins(available, p.available())
		return false
	})

	availableAmount := available.AmountOf(denom)
	if anyAmount {
		amount = availableAmount
	} else if availableAmount < amount {
		panic(errors.New("not enough in vault"))
	}

	toSend := std.NewCoins(std.Coin{Denom: denom, Amount: amount})
	sendCoins(destination, toSend)

	vaults.Set(id, subCoins(vaultAmount, toSend))
}

func Stop(id seqid.ID) {
	p := mustGetPayroll(id)
	p.stop(std.PrevRealm().Addr())
}

func Pause(id seqid.ID) {
	p := mustGetPayroll(id)
	p.pause(std.PrevRealm().Addr())
}

func Resume(id seqid.ID) {
	p := mustGetPayroll(id)
	p.resume(std.PrevRealm().Addr())
}

func mustGetPayroll(id seqid.ID) *payroll {
	ip, ok := payrolls.Get(id.String())
	if !ok {
		panic(errors.New("no such payroll"))
	}
	return ip.(*payroll)
}

package payrolls

import (
	"math"
	"net/url"
	"std"
	"strconv"
	"strings"
	"time"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/seqid"
