	rusers "gno.land/r/demo/users"
)

const (
	listMaxSize    = 50
	defaultFqdenom = "gno.land/r/demo/foo20"
	defaultDenom   = "/grc20/" + defaultFqdenom
)

func Render(renderPath string) string {
	w := strings.Builder{}

	switch {
	case strings.HasPrefix(renderPath, "/u/"):
		w.WriteString("# Payrolls beneficiary\n")

		path := strings.TrimPrefix(renderPath, "/u/")
		aon := users.AddressOrName(path)
		user := rusers.GetUserByAddressOrName(aon)
		addr := std.Address(aon)
		if user != nil {
			addr = user.Address
			w.WriteString(ufmt.Sprintf("- 👤 User: @%s\n", user.Name))
		} else if !addr.IsValid() {
			w.WriteString(ufmt.Sprintf("❓ User %q not found\n", addr.String()))
			break
		}

		w.WriteString(ufmt.Sprintf("- 🆔 Address: %s\n", addr.String()))

		sb := strings.Builder{}
		tree, ok := payrollsByBeneficiary[addr]
		if !ok {
			tree = avl.NewTree()
		}
		estimatedDay := std.Coins{}
		estimatedAvailable := std.Coins{}
		estimatedClaimable := std.Coins{}
		shouldEstimate := tree.Size() <= listMaxSize
		if shouldEstimate {
			sb.WriteString("## All payrolls\n")
		} else {
			sb.WriteString("## Latest payrolls\n")
		}
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			sb.WriteString(renderPayroll(p, "beneficiary"))

			if shouldEstimate {
				available := p.available()
				estimatedAvailable = addCoins(estimatedAvailable, available)

				claimable := clampCoins(available, getVaultCoins(p.vaultID))
				estimatedClaimable = addCoins(estimatedClaimable, claimable)

				estimatedDay = addCoins(estimatedDay, p.estimateDay())
			}

			return false
		})

		actions := []string{}

		if !shouldEstimate || !coinsEmpty(estimatedClaimable) {
			actions = append(actions, renderButton("Claim all", "ClaimAll", map[string]string{
				"destination": addr.String(),
			}))
		}

		actions = append(actions, renderButton("New payroll", "CreateMonthlyContinuous", map[string]string{
			"label":          "Salary Monthly",
			"beneficiary":    addr.String(),
			"amountPerMonth": "10000",
			"denom":          defaultDenom,
		}))

		w.WriteString(renderActions("", actions))

		if shouldEstimate {
			for _, coin := range addCoins(estimatedAvailable, estimatedDay) {
				w.WriteString(ufmt.Sprintf("- %s:\n", renderCoinDenom(coin.Denom)))
				w.WriteString(renderEstimatedMonthly("  ", estimatedDay.AmountOf(coin.Denom), coin.Denom))
				w.WriteString(renderClaimable("  ", estimatedClaimable.AmountOf(coin.Denom), estimatedAvailable.AmountOf(coin.Denom), coin.Denom, true))
			}
		}

		w.WriteString(sb.String())

	case strings.HasPrefix(renderPath, "/v/"):
		w.WriteString("# Payrolls vault\n")

		input := strings.TrimPrefix(renderPath, "/v/")
		inputBase, namespace := splitVaultInput(input)
		base, vaultUser, ok := resolveVaultBase(inputBase)
		if !ok {
			w.WriteString(ufmt.Sprintf("❓ Vault %q not found", input))
			break
		}

		vaultID := vaultIDUnsafe(base, namespace)

		name := vaultID
		if vaultUser != nil {
			name = vaultIDUnsafe("@"+vaultUser.Name, namespace)
		}
		w.WriteString(ufmt.Sprintf("- 🎩 Name: %s\n", name))

		w.WriteString(ufmt.Sprintf("- 🆔 ID: %s\n", vaultID))

		payrollsBuffer := strings.Builder{}
		estimatedDay := std.Coins{}
		estimatedDebt := std.Coins{}
		tree, ok := payrollsByVault[vaultID]
		if !ok {
			tree = avl.NewTree()
		}
		shouldEstimate := tree.Size() <= listMaxSize
		if shouldEstimate {
			payrollsBuffer.WriteString("## All payrolls\n")
		} else {
			payrollsBuffer.WriteString("## Latest payrolls\n")
		}
		tree.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			p := value.(*payroll)
			if shouldEstimate {
				estimatedDebt = addCoins(estimatedDebt, p.available())
				estimatedDay = addCoins(estimatedDay, p.estimateDay())
			}
			payrollsBuffer.WriteString(renderPayroll(p, "vault"))
			return false
		})

		funds := getVaultCoins(vaultID)
		balance := subCoins(funds, estimatedDebt)

		actions := []string{}
		actions = append(actions, renderButton("Deposit native", "FundNative", map[string]string{
			"base":      base,
			"namespace": namespace,
		}))
		actions = append(actions, renderButton("Deposit GRC20", "FundGRC20Reg", map[string]string{
			"base":      base,
			"namespace": namespace,
			"fqdenom":   defaultFqdenom,
			"amount":    "-1",
		}))
		if !shouldEstimate || coinsHasPositive(balance) {
			actions = append(actions, renderButton("Withdraw funds", "WithdrawFunds", map[string]string{
				"namespace":   namespace,
				"amount":      "-1",
				"denom":       defaultDenom,
				"destination": base,
			}))
		}
		actions = append(actions, renderButton("New payroll", "CreateMonthlyContinuous", map[string]string{
			"namespace":      namespace,
			"label":          "Salary Monthly",
			"amountPerMonth": "10000",
			"denom":          defaultDenom,
		}))
		w.WriteString(renderActions("", actions))

		if shouldEstimate {
			for _, coin := range addCoins(balance, estimatedDay) {
				w.WriteString(ufmt.Sprintf("- %s:\n", renderCoinDenom(coin.Denom)))

				color := "🟢"
				coinBalance := balance.AmountOf(coin.Denom)
				if coinBalance < 0 {
					color = "🔴"
				}
				w.WriteString(ufmt.Sprintf("  - 🏦 Funds: %s %s\n", color, renderCoin(std.NewCoin(coin.Denom, coinBalance))))

				w.WriteString(renderEstimatedMonthly("  ", estimatedDay.AmountOf(coin.Denom), coin.Denom))

				if coinBalance > 0 && estimatedDay.AmountOf(coin.Denom) > 0 {
					color := "🔴"
					runWayMonths := coinBalance / (estimatedDay.AmountOf(coin.Denom) * 30)
					if runWayMonths >= 12 {
						color = "🟢"
					} else if runWayMonths >= 6 {
						color = "🟠"
					}
					w.WriteString(ufmt.Sprintf("  - 🛬 Estimated runway: %s %d months\n", color, runWayMonths))
				}
			}
		} else {
			for _, coin := range funds {
				w.WriteString(ufmt.Sprintf("- 🏦 Funds: %d %s (does not account for unclaimed amounts)\n", renderCoin(coin)))
			}
		}

		w.WriteString(payrollsBuffer.String())

	case strings.HasPrefix(renderPath, "/d/"):
		idStr := strings.TrimPrefix(renderPath, "/d/")

		w.WriteString(ufmt.Sprintf("# Payroll #%s\n", idStr))

		p, ok := payrolls.Get(idStr)
		if !ok {
			return ufmt.Sprintf("❓ Payroll %q not found", idStr)
		}

		w.WriteString(renderPayroll(p.(*payroll), "details"))

	case renderPath == "":
		w.WriteString("# Payrolls\n")

		actions := []string{}
		actions = append(actions, renderButton("New payroll", "CreateMonthlyContinuous", map[string]string{
			"label":          "Salary Monthly",
			"amountPerMonth": "10000",
			"denom":          defaultDenom,
		}))
		w.WriteString(renderActions("", actions))

		if payrolls.Size() <= listMaxSize {
			w.WriteString("## All payrolls\n")
		} else {
			w.WriteString("## Latest payrolls\n")
		}
		payrolls.ReverseIterateByOffset(0, listMaxSize, func(key string, value interface{}) bool {
			w.WriteString(renderPayroll(value.(*payroll), "home"))
			return false
		})

		/*
			w.WriteString("## v2 ideas\n")
			w.WriteString("- Better test coverage\n")
			w.WriteString("- Gnoweb pagination\n")
			w.WriteString("- Show payroll running duration\n")
			w.WriteString("- Archive\n")
			w.WriteString("- Details: Events history\n")
			w.WriteString("- PoV Tags\n")
			w.WriteString("- Edit label / PoV labels\n")
			w.WriteString("- Stop litigation\n")
		*/

	default:
		w.WriteString("# Payrolls\n")
		w.WriteString("❓ 404 Not found")
	}

	return w.String()
}

func renderPayroll(p *payroll, pov string) string {
	sb := strings.Builder{}

	if pov != "details" {
		sb.WriteString(ufmt.Sprintf("### %s\n", p.id.String()))
	}

	sb.WriteString(ufmt.Sprintf("  - ✍️ Label: %s\n", p.label))

	if p.paused {
		sb.WriteString(ufmt.Sprintf("  - ⏸️ Paused since: %s\n", p.pausedAt.Format(time.DateTime)))
	}

	if pov != "vault" {
		base, namespace := splitVaultInput(p.vaultID)
		_, usr, _ := resolveVaultBase(base)
		if usr != nil {
			base = "@" + usr.Name
		}
		pvid := vaultIDUnsafe(base, namespace)
		sb.WriteString(ufmt.Sprintf("  - 🎩 Vault: [%s](%s:/v/%s)\n", pvid, linkPrefix(), pvid))
	}

	if pov != "beneficiary" {
		beneficiaryUser := rusers.GetUserByAddress(p.beneficiary)
		var userStr string
		if beneficiaryUser == nil {
			userStr = p.beneficiary.String()
		} else {
			userStr = "@" + beneficiaryUser.Name
		}
		sb.WriteString(ufmt.Sprintf("  - 👤 Beneficiary: [%s](%s:/u/%s)\n", userStr, linkPrefix(), userStr))
	}

	available := p.available()
	vaultAmount := getVaultCoins(p.vaultID)
	claimable := clampCoins(available, vaultAmount)

	sb.WriteString(ufmt.Sprintf("  - 🏁 Created at: %s\n", p.createdAt.Format(time.DateTime)))

	if p.stopped {
		sb.WriteString(ufmt.Sprintf("  - 🛑 Stopped since: %s\n", p.stoppedAt.Format(time.DateTime)))
	}

	if pov != "details" {
		sb.WriteString(ufmt.Sprintf("  - 🔍 [Details](%s:/d/%s)\n", linkPrefix(), p.id.String()))
	}

	actions := []string{}

	if pov != "vault" && coinsHasPositive(claimable) {
		actions = append(actions, renderButton("Claim", "Claim", map[string]string{
			"id":          strconv.FormatUint(uint64(p.id), 10),
			"destination": p.beneficiary.String(),
		}))
	}

	if pov != "vault" && !p.paused && !p.stopped {
		actions = append(actions, renderButton("Pause", "Pause", map[string]string{
			"id": strconv.FormatUint(uint64(p.id), 10),
		}))
	}

	if pov != "vault" && p.paused {
		actions = append(actions, renderButton("Resume", "Resume", map[string]string{
			"id": strconv.FormatUint(uint64(p.id), 10),
		}))
	}

	if !p.stopped {
		actions = append(actions, renderStopButton(p.id))
	}

	sb.WriteString(renderActions("  ", actions))

	estimatedDay := p.estimateDay()
	allCoins := addCoins(available, estimatedDay)

	var beneficiaryBreakupCoins std.Coins
	var creatorBreakupCoins std.Coins
	if pov == "details" {
		allCoins = addCoins(allCoins, beneficiaryBreakupCoins)
		allCoins = addCoins(allCoins, creatorBreakupCoins)
		beneficiaryBreakupCoins = p.getBreakupCoins(p.beneficiary)
		creatorBreakupCoins = p.getBreakupCoins(p.creatorAddr)
	}
	for _, coin := range allCoins {
		sb.WriteString(ufmt.Sprintf("  - %s:\n", renderCoinDenom(coin.Denom)))
		sb.WriteString(renderEstimatedMonthly("    ", estimatedDay.AmountOf(coin.Denom), coin.Denom))
		sb.WriteString(renderClaimable("    ", claimable.AmountOf(coin.Denom), available.AmountOf(coin.Denom), coin.Denom, true))
		if pov == "details" {
			if bb := beneficiaryBreakupCoins.AmountOf(coin.Denom); bb > 0 {
				sb.WriteString(ufmt.Sprintf("    - Breakup by beneficiary bonus: %s\n", renderCoin(std.NewCoin(coin.Denom, bb))))
			}
			if cb := creatorBreakupCoins.AmountOf(coin.Denom); cb > 0 {
				sb.WriteString(ufmt.Sprintf("    - Breakup by creator bonus: %s\n", renderCoin(std.NewCoin(coin.Denom, cb))))
			}
		}
	}

	return sb.String()
}

func renderClaimable(prefix string, claimable int64, available int64, denom string, plural bool) string {
	if available == 0 {
		return ""
	}
	sb := strings.Builder{}
	sb.WriteString(prefix)
	sb.WriteString("- 💵 Claimable: ")
	if claimable < available {
		pls := ""
		if plural {
			pls = "(s)"
		}
		color := "🟠"
		if claimable == 0 {
			color = "🔴"
		}
		sb.WriteString(ufmt.Sprintf("%s %s/%s %s (not enough funds in vault%s!)\n",
			color,
			renderCoinAmount(std.NewCoin(denom, claimable)),
			renderCoinAmount(std.NewCoin(denom, available)),
			renderCoinDenom(denom),
			pls,
		))
	} else {
		sb.WriteString(ufmt.Sprintf("🟢 %s\n", renderCoin(std.NewCoin(denom, available))))
	}
	return sb.String()
}

func renderEstimatedMonthly(prefix string, estimatedDay int64, denom string) string {
	if estimatedDay == 0 {
		return ""
	}
	return ufmt.Sprintf("%s- 💸 Estimated monthly: %s\n", prefix, renderCoin(std.NewCoin(denom, estimatedDay*30)))
}

func renderStopButton(id seqid.ID) string {
	return renderButton("Stop", "Stop", map[string]string{
		"id": strconv.FormatUint(uint64(id), 10),
	})
}

func renderButton(text, funcName string, args map[string]string) string {
	argsBuf := strings.Builder{}
	for key, arg := range args {
		argsBuf.WriteString("&" + url.QueryEscape(key) + "=" + url.QueryEscape(arg))
	}
	return ufmt.Sprintf("[[%s]](%s$help&func=%s%s)", text, linkPrefix(), funcName, argsBuf.String())
}

func renderActions(prefix string, actions []string) string {
	if len(actions) == 0 {
		return ""
	}
	return prefix + "- ✋ Actions: " + strings.Join(actions, " ") + "\n"
}

func resolveVaultBase(base string) (string, *users.User, bool) {
	if len(base) == 0 {
		return "", nil, false
	}

	if strings.HasPrefix(base, std.GetChainDomain()+"/r/") {
		return base, nil, true
	}

	usr := rusers.GetUserByAddressOrName(users.AddressOrName(base))
	if usr != nil {
		return usr.Address.String(), usr, true
	}

	if std.Address(base).IsValid() {
		return base, nil, true
	}

	return "", nil, false
}

func linkPrefix() string {
	realmPath := std.CurrentRealm().PkgPath()
	slashIdx := strings.Index(realmPath, "/")
	if slashIdx == -1 {
		return realmPath
	}
	return realmPath[slashIdx:]
}

func renderCoin(coin std.Coin) string {
	return ufmt.Sprintf("%s %s", renderCoinAmount(coin), renderCoinDenom(coin.Denom))
}

func renderCoinAmount(coin std.Coin) string {
	switch {
	case strings.HasPrefix(coin.Denom, "/grc20/"):
		fqdenom := strings.TrimPrefix(coin.Denom, "/grc20/")
		tokenGetter := grc20reg.Get(fqdenom)
		if tokenGetter == nil {
			break
		}
		token := tokenGetter()
		if token == nil {
			break
		}
		amount := float64(coin.Amount) / float64(math.Pow10(int(token.GetDecimals())))
		return ufmt.Sprintf("%g", amount)
	}

	return ufmt.Sprintf("%d", coin.Amount)
}

func renderCoinDenom(denom string) string {
	switch {
	case strings.HasPrefix(denom, "/native/"):
		nativeDenom := strings.TrimPrefix(denom, "/native/")
		return nativeDenom

	case strings.HasPrefix(denom, "/grc20/"):
		fqdenom := strings.TrimPrefix(denom, "/grc20/")
		tokenGetter := grc20reg.Get(fqdenom)
		if tokenGetter == nil {
			break
		}
		token := tokenGetter()
		if token == nil {
			break
		}
		return ufmt.Sprintf("[$%s](%s)", tokenGetter().GetSymbol(), strings.TrimPrefix(fqdenom, std.GetChainDomain()))
	}

	return denom
}

package payrolls

import (
	"errors"
	"std"
	"strings"

	"gno.land/p/demo/avl"
	"gno.land/p/demo/users"
	rusers "gno.land/r/demo/users"
)

var vaults avl.Tree // std.Addresss -> std.Coins

var errInvalidInput = errors.New("invalid input")

func getVaultCoins(id string) std.Coins {
	amount, ok := vaults.Get(id)
	if ok {
		return amount.(std.Coins)
	}
	return nil
}

func vaultID(base string, namespace string) (string, error) {
	if len(base) == 0 {
		return "", errInvalidInput
	}

	if id, ok := vaultIDFromPkgPath(base, namespace); ok {
		return id, nil
	}

	user := rusers.GetUserByAddressOrName(users.AddressOrName(base))
	if user != nil {
		return vaultIDUnsafe(user.Address.String(), namespace), nil
	}

	if id, ok := vaultIDFromAddr(std.Address(base), namespace); ok {
		return id, nil
	}

	return "", errInvalidInput
}

func splitVaultInput(input string) (string, string) {
	lastDot := strings.LastIndex(input, ".")
	if lastDot == -1 {
		return input, ""
	}

	base := input[:lastDot]
	namespace := input[lastDot+1:]
	return base, namespace
}

func vaultIDUnsafe(base string, namespace string) string {
	if namespace == "" {
		return base
	}
	return base + "." + namespace
}

func vaultIDFromAddr(addr std.Address, namespace string) (string, bool) {
	if !addr.IsValid() {
		return "", false
	}
	return vaultIDUnsafe(addr.String(), namespace), true
}

func vaultIDFromPkgPath(base string, namespace string) (string, bool) {
	if !strings.HasPrefix(base, std.GetChainDomain()+"/r/") {
		return "", false
	}
	return vaultIDUnsafe(base, namespace), true
}

func vaultIDFromRealm(base std.Realm, namespace string) string {
	if base.IsUser() {
		return vaultIDUnsafe(base.Addr().String(), namespace)
	} else {
		return vaultIDUnsafe(base.PkgPath(), namespace)
	}
}

// Package fp provides functional programming utilities for Gno, enabling
// transformations, filtering, and other operations on slices of interface{}.
//
// Example of chaining operations:
//
//	numbers := []interface{}{1, 2, 3, 4, 5, 6}
//
//	// Define predicates, mappers and reducers
//	isEven := func(v interface{}) bool { return v.(int)%2 == 0 }
//	double := func(v interface{}) interface{} { return v.(int) * 2 }
//	sum := func(a, b interface{}) interface{} { return a.(int) + b.(int) }
//
//	// Chain operations: filter even numbers, double them, then sum
//	evenNums := Filter(numbers, isEven)        // [2, 4, 6]
//	doubled := Map(evenNums, double)           // [4, 8, 12]
//	result := Reduce(doubled, sum, 0)          // 24
//
//	// Alternative: group by even/odd, then get even numbers
//	byMod2 := func(v interface{}) interface{} { return v.(int) % 2 }
//	grouped := GroupBy(numbers, byMod2)        // {0: [2,4,6], 1: [1,3,5]}
//	evens := grouped[0]                        // [2,4,6]
package fp

// Mapper is a function type that maps an element to another element.
type Mapper func(interface{}) interface{}

// Predicate is a function type that evaluates a condition on an element.
type Predicate func(interface{}) bool

// Reducer is a function type that reduces two elements to a single value.
type Reducer func(interface{}, interface{}) interface{}

// Filter filters elements from the slice that satisfy the given predicate.
//
// Example:
//
//	numbers := []interface{}{-1, 0, 1, 2}
//	isPositive := func(v interface{}) bool { return v.(int) > 0 }
//	result := Filter(numbers, isPositive) // [1, 2]
func Filter(values []interface{}, fn Predicate) []interface{} {
	result := []interface{}{}
	for _, v := range values {
		if fn(v) {
			result = append(result, v)
		}
	}
	return result
}

// Map applies a function to each element in the slice.
//
// Example:
//
//	numbers := []interface{}{1, 2, 3}
//	toString := func(v interface{}) interface{} { return fmt.Sprintf("%d", v) }
//	result := Map(numbers, toString) // ["1", "2", "3"]
func Map(values []interface{}, fn Mapper) []interface{} {
	result := make([]interface{}, len(values))
	for i, v := range values {
		result[i] = fn(v)
	}
	return result
}

// Reduce reduces a slice to a single value by applying a function.
//
// Example:
//
//	numbers := []interface{}{1, 2, 3, 4}
//	sum := func(a, b interface{}) interface{} { return a.(int) + b.(int) }
//	result := Reduce(numbers, sum, 0) // 10
func Reduce(values []interface{}, fn Reducer, initial interface{}) interface{} {
	acc := initial
	for _, v := range values {
		acc = fn(acc, v)
	}
	return acc
}

// FlatMap maps each element to a collection and flattens the results.
//
// Example:
//
//	words := []interface{}{"hello", "world"}
//	split := func(v interface{}) interface{} {
//	    chars := []interface{}{}
//	    for _, c := range v.(string) {
//	        chars = append(chars, string(c))
//	    }
//	    return chars
//	}
//	result := FlatMap(words, split) // ["h","e","l","l","o","w","o","r","l","d"]
func FlatMap(values []interface{}, fn Mapper) []interface{} {
	result := []interface{}{}
	for _, v := range values {
		inner := fn(v).([]interface{})
		result = append(result, inner...)
	}
	return result
}

// All returns true if all elements satisfy the predicate.
//
// Example:
//
//	numbers := []interface{}{2, 4, 6, 8}
//	isEven := func(v interface{}) bool { return v.(int)%2 == 0 }
//	result := All(numbers, isEven) // true
func All(values []interface{}, fn Predicate) bool {
	for _, v := range values {
		if !fn(v) {
			return false
		}
	}
	return true
}

// Any returns true if at least one element satisfies the predicate.
//
// Example:
//
//	numbers := []interface{}{1, 3, 4, 7}
//	isEven := func(v interface{}) bool { return v.(int)%2 == 0 }
//	result := Any(numbers, isEven) // true (4 is even)
func Any(values []interface{}, fn Predicate) bool {
	for _, v := range values {
		if fn(v) {
			return true
		}
	}
	return false
}

// None returns true if no elements satisfy the predicate.
//
// Example:
//
//	numbers := []interface{}{1, 3, 5, 7}
//	isEven := func(v interface{}) bool { return v.(int)%2 == 0 }
//	result := None(numbers, isEven) // true (no even numbers)
func None(values []interface{}, fn Predicate) bool {
	for _, v := range values {
		if fn(v) {
			return false
		}
	}
	return true
}

// Chunk splits a slice into chunks of the given size.
//
// Example:
//
//	numbers := []interface{}{1, 2, 3, 4, 5}
//	result := Chunk(numbers, 2) // [[1,2], [3,4], [5]]
func Chunk(values []interface{}, size int) [][]interface{} {
	if size <= 0 {
		return nil
	}
	var chunks [][]interface{}
	for i := 0; i < len(values); i += size {
		end := i + size
		if end > len(values) {
			end = len(values)
		}
		chunks = append(chunks, values[i:end])
	}
	return chunks
}

// Find returns the first element that satisfies the predicate and a boolean indicating if an element was found.
//
// Example:
//
//	numbers := []interface{}{1, 2, 3, 4}
//	isEven := func(v interface{}) bool { return v.(int)%2 == 0 }
//	result, found := Find(numbers, isEven) // 2, true
func Find(values []interface{}, fn Predicate) (interface{}, bool) {
	for _, v := range values {
		if fn(v) {
			return v, true
		}
	}
	return nil, false
}

// Reverse reverses the order of elements in a slice.
//
// Example:
//
//	numbers := []interface{}{1, 2, 3}
//	result := Reverse(numbers) // [3, 2, 1]
func Reverse(values []interface{}) []interface{} {
	result := make([]interface{}, len(values))
	for i, v := range values {
		result[len(values)-1-i] = v
	}
	return result
}

// Zip combines two slices into a slice of pairs. If the slices have different lengths,
// extra elements from the longer slice are ignored.
//
// Example:
//
//	a := []interface{}{1, 2, 3}
//	b := []interface{}{"a", "b", "c"}
//	result := Zip(a, b) // [[1,"a"], [2,"b"], [3,"c"]]
func Zip(a, b []interface{}) [][2]interface{} {
	length := min(len(a), len(b))
	result := make([][2]interface{}, length)
	for i := 0; i < length; i++ {
		result[i] = [2]interface{}{a[i], b[i]}
	}
	return result
}

// Unzip splits a slice of pairs into two separate slices.
//
// Example:
//
//	pairs := [][2]interface{}{{1,"a"}, {2,"b"}, {3,"c"}}
//	numbers, letters := Unzip(pairs) // [1,2,3], ["a","b","c"]
func Unzip(pairs [][2]interface{}) ([]interface{}, []interface{}) {
	a := make([]interface{}, len(pairs))
	b := make([]interface{}, len(pairs))
	for i, pair := range pairs {
		a[i] = pair[0]
		b[i] = pair[1]
	}
	return a, b
}

// GroupBy groups elements based on a key returned by a Mapper.
//
// Example:
//
//	numbers := []interface{}{1, 2, 3, 4, 5, 6}
//	byMod3 := func(v interface{}) interface{} { return v.(int) % 3 }
//	result := GroupBy(numbers, byMod3) // {0: [3,6], 1: [1,4], 2: [2,5]}
func GroupBy(values []interface{}, fn Mapper) map[interface{}][]interface{} {
	result := make(map[interface{}][]interface{})
	for _, v := range values {
		key := fn(v)
		result[key] = append(result[key], v)
	}
	return result
}

// Flatten flattens a slice of slices into a single slice.
//
// Example:
//
//	nested := [][]interface{}{{1,2}, {3,4}, {5}}
//	result := Flatten(nested) // [1,2,3,4,5]
func Flatten(values [][]interface{}) []interface{} {
	result := []interface{}{}
	for _, v := range values {
		result = append(result, v...)
	}
	return result
}

// Helper functions
func min(a, b int) int {
	if a < b {
		return a
	}
	return b
}

