// Helper function for testing
func contains(slice []interface{}, item interface{}) bool {
	for _, v := range slice {
		if v == item {
			return true
		}
	}
	return false
}

// Helper functions for comparing slices
func equalSlices(a, b []interface{}) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

func equalNestedSlices(a, b [][]interface{}) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !equalSlices(a[i], b[i]) {
			return false
		}
	}
	return true
}

// Package memo provides a simple memoization utility to cache function results.
//
// The package offers a Memoizer type that can cache function results based on keys,
// with optional validation of cached values. This is useful for expensive computations
// that need to be cached and potentially invalidated based on custom conditions.
//
// /!\ Important Warning for Gno Usage:
// In Gno, storage updates only persist during transactions. This means:
//   - Cache entries created during queries will NOT persist
//   - Creating cache entries during queries will actually decrease performance
//     as it wastes resources trying to save data that won't be saved
//
// Best Practices:
// - Use this pattern in transaction-driven contexts rather than query/render scenarios
// - Consider controlled cache updates, e.g., by specific accounts (like oracles)
// - Ideal for cases where cache updates happen every N blocks or on specific events
// - Carefully evaluate if caching will actually improve performance in your use case
//
// Basic usage example:
//
//	m := memo.New()
//
//	// Cache expensive computation
//	result := m.Memoize("key", func() interface{} {
//	    // expensive operation
//	    return "computed-value"
//	})
//
//	// Subsequent calls with same key return cached result
//	result = m.Memoize("key", func() interface{} {
//	    // function won't be called, cached value is returned
//	    return "computed-value"
//	})
//
// Example with validation:
//
//	type TimestampedValue struct {
//	    Value     string
//	    Timestamp time.Time
//	}
//
//	m := memo.New()
//
//	// Cache value with timestamp
//	result := m.MemoizeWithValidator(
//	    "key",
//	    func() interface{} {
//	        return TimestampedValue{
//	            Value:     "data",
//	            Timestamp: time.Now(),
//	        }
//	    },
//	    func(cached interface{}) bool {
//	        // Validate that the cached value is not older than 1 hour
//	        if tv, ok := cached.(TimestampedValue); ok {
//	            return time.Since(tv.Timestamp) < time.Hour
//	        }
//	        return false
//	    },
//	)
package memo

import (
	"gno.land/p/demo/btree"
	"gno.land/p/demo/ufmt"
)

// Record implements the btree.Record interface for our cache entries
type cacheEntry struct {
	key   interface{}
	value interface{}
}

// Less implements btree.Record interface
func (e cacheEntry) Less(than btree.Record) bool {
	// Convert the other record to cacheEntry
	other := than.(cacheEntry)
	// Compare string representations of keys for consistent ordering
	return ufmt.Sprintf("%v", e.key) < ufmt.Sprintf("%v", other.key)
}

// Memoizer is a structure to handle memoization of function results.
type Memoizer struct {
	cache *btree.BTree
}

// New creates a new Memoizer instance.
func New() *Memoizer {
	return &Memoizer{
		cache: btree.New(),
	}
}

// Memoize ensures the result of the given function is cached for the specified key.
func (m *Memoizer) Memoize(key interface{}, fn func() interface{}) interface{} {
	entry := cacheEntry{key: key}
	if found := m.cache.Get(entry); found != nil {
		return found.(cacheEntry).value
	}

	value := fn()
	m.cache.Insert(cacheEntry{key: key, value: value})
	return value
}

// MemoizeWithValidator ensures the result is cached and valid according to the validator function.
func (m *Memoizer) MemoizeWithValidator(key interface{}, fn func() interface{}, isValid func(interface{}) bool) interface{} {
	entry := cacheEntry{key: key}
	if found := m.cache.Get(entry); found != nil {
		cachedEntry := found.(cacheEntry)
		if isValid(cachedEntry.value) {
			return cachedEntry.value
		}
	}

	value := fn()
	m.cache.Insert(cacheEntry{key: key, value: value})
	return value
}

// Invalidate removes the cached value for the specified key.
func (m *Memoizer) Invalidate(key interface{}) {
	m.cache.Delete(cacheEntry{key: key})
}

// Clear clears all cached values.
func (m *Memoizer) Clear() {
	m.cache.Clear(true)
}

// Size returns the number of items currently in the cache.
func (m *Memoizer) Size() int {
	return m.cache.Len()
}

package memo

import (
	"std"
	"testing"
	"time"
)

type timestampedValue struct {
	value     interface{}
	timestamp time.Time
}

// complexKey is used to test struct keys
type complexKey struct {
	ID   int
	Name string
}

func TestMemoize(t *testing.T) {
	tests := []struct {
		name      string
		key       interface{}
		value     interface{}
		callCount *int
	}{
		{
			name:      "string key and value",
			key:       "test-key",
			value:     "test-value",
			callCount: new(int),
		},
		{
			name:      "int key and value",
			key:       42,
			value:     123,
			callCount: new(int),
		},
		{
			name:      "mixed types",
			key:       "number",
			value:     42,
			callCount: new(int),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			m := New()
			if m.Size() != 0 {
				t.Errorf("Initial size = %d, want 0", m.Size())
			}

			fn := func() interface{} {
				*tt.callCount++
				return tt.value
			}

			// First call should compute
			result := m.Memoize(tt.key, fn)
			if result != tt.value {
				t.Errorf("Memoize() = %v, want %v", result, tt.value)
			}
			if *tt.callCount != 1 {
				t.Errorf("Function called %d times, want 1", *tt.callCount)
			}
			if m.Size() != 1 {
				t.Errorf("Size after first call = %d, want 1", m.Size())
			}

			// Second call should use cache
			result = m.Memoize(tt.key, fn)
			if result != tt.value {
				t.Errorf("Memoize() second call = %v, want %v", result, tt.value)
			}
			if *tt.callCount != 1 {
				t.Errorf("Function called %d times, want 1", *tt.callCount)
			}
			if m.Size() != 1 {
				t.Errorf("Size after second call = %d, want 1", m.Size())
			}
		})
	}
}

func TestMemoizeWithValidator(t *testing.T) {
	tests := []struct {
		name            string
		key             interface{}
		value           interface{}
		validDuration   time.Duration
		waitDuration    time.Duration
		expectedCalls   int
		shouldRecompute bool
	}{
		{
			name:            "valid cache",
			key:             "key1",
			value:           "value1",
			validDuration:   time.Hour,
			waitDuration:    time.Millisecond,
			expectedCalls:   1,
			shouldRecompute: false,
		},
		{
			name:            "expired cache",
			key:             "key2",
			value:           "value2",
			validDuration:   time.Millisecond,
			waitDuration:    time.Millisecond * 2,
			expectedCalls:   2,
			shouldRecompute: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			m := New()
			callCount := 0

			fn := func() interface{} {
				callCount++
				return timestampedValue{
					value:     tt.value,
					timestamp: time.Now(),
				}
			}

			isValid := func(cached interface{}) bool {
				if tv, ok := cached.(timestampedValue); ok {
					return time.Since(tv.timestamp) < tt.validDuration
				}
				return false
			}

			// First call
			result := m.MemoizeWithValidator(tt.key, fn, isValid)
			if tv, ok := result.(timestampedValue); !ok || tv.value != tt.value {
				t.Errorf("MemoizeWithValidator() = %v, want value %v", result, tt.value)
			}

			// Wait
			std.TestSkipHeights(10)

			// Second call
			result = m.MemoizeWithValidator(tt.key, fn, isValid)
			if tv, ok := result.(timestampedValue); !ok || tv.value != tt.value {
				t.Errorf("MemoizeWithValidator() second call = %v, want value %v", result, tt.value)
			}

			if callCount != tt.expectedCalls {
				t.Errorf("Function called %d times, want %d", callCount, tt.expectedCalls)
			}
		})
	}
}

func TestInvalidate(t *testing.T) {
	tests := []struct {
		name      string
		key       interface{}
		value     interface{}
		callCount *int
	}{
		{
			name:      "invalidate existing key",
			key:       "test-key",
			value:     "test-value",
			callCount: new(int),
		},
		{
			name:      "invalidate non-existing key",
			key:       "missing-key",
			value:     "test-value",
			callCount: new(int),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			m := New()
			fn := func() interface{} {
				*tt.callCount++
				return tt.value
			}

			// First call
			m.Memoize(tt.key, fn)
			if m.Size() != 1 {
				t.Errorf("Size after first call = %d, want 1", m.Size())
			}

			// Invalidate
			m.Invalidate(tt.key)
			if m.Size() != 0 {
				t.Errorf("Size after invalidate = %d, want 0", m.Size())
			}

			// Call again should recompute
			result := m.Memoize(tt.key, fn)
			if result != tt.value {
				t.Errorf("Memoize() after invalidate = %v, want %v", result, tt.value)
			}
			if *tt.callCount != 2 {
				t.Errorf("Function called %d times, want 2", *tt.callCount)
			}
			if m.Size() != 1 {
				t.Errorf("Size after recompute = %d, want 1", m.Size())
			}
		})
	}
}

func TestClear(t *testing.T) {
	m := New()
	callCount := 0

	fn := func() interface{} {
		callCount++
		return "value"
	}

	// Cache some values
	m.Memoize("key1", fn)
	m.Memoize("key2", fn)

	if callCount != 2 {
		t.Errorf("Initial calls = %d, want 2", callCount)
	}
	if m.Size() != 2 {
		t.Errorf("Size after initial calls = %d, want 2", m.Size())
	}

	// Clear cache
	m.Clear()
	if m.Size() != 0 {
		t.Errorf("Size after clear = %d, want 0", m.Size())
	}

	// Recompute values
	m.Memoize("key1", fn)
	m.Memoize("key2", fn)

	if callCount != 4 {
		t.Errorf("Calls after clear = %d, want 4", callCount)
	}
	if m.Size() != 2 {
		t.Errorf("Size after recompute = %d, want 2", m.Size())
	}
}

func TestSize(t *testing.T) {
	m := New()

	if m.Size() != 0 {
		t.Errorf("Initial size = %d, want 0", m.Size())
	}

	callCount := 0
	fn := func() interface{} {
		callCount++
		return "value"
	}

	// Add items
	m.Memoize("key1", fn)
	if m.Size() != 1 {
		t.Errorf("Size after first insert = %d, want 1", m.Size())
	}

	m.Memoize("key2", fn)
	if m.Size() != 2 {
		t.Errorf("Size after second insert = %d, want 2", m.Size())
	}

	// Duplicate key should not increase size
	m.Memoize("key1", fn)
	if m.Size() != 2 {
		t.Errorf("Size after duplicate insert = %d, want 2", m.Size())
	}

	// Remove item
	m.Invalidate("key1")
	if m.Size() != 1 {
		t.Errorf("Size after invalidate = %d, want 1", m.Size())
	}

	// Clear all
	m.Clear()
	if m.Size() != 0 {
		t.Errorf("Size after clear = %d, want 0", m.Size())
	}
}

func TestMemoizeWithDifferentKeyTypes(t *testing.T) {
	tests := []struct {
		name      string
		keys      []interface{} // Now an array of keys
		values    []string      // Corresponding values
		callCount *int
	}{
		{
			name:      "integer keys",
			keys:      []interface{}{42, 43},
			values:    []string{"value-for-42", "value-for-43"},
			callCount: new(int),
		},
		{
			name:      "float keys",
			keys:      []interface{}{3.14, 2.718},
			values:    []string{"value-for-pi", "value-for-e"},
			callCount: new(int),
		},
		{
			name:      "bool keys",
			keys:      []interface{}{true, false},
			values:    []string{"value-for-true", "value-for-false"},
			callCount: new(int),
		},
		/*
			{
				name: "struct keys",
				keys: []interface{}{
					complexKey{ID: 1, Name: "test1"},
					complexKey{ID: 2, Name: "test2"},
				},
				values:    []string{"value-for-struct1", "value-for-struct2"},
				callCount: new(int),
			},
			{
				name:      "nil and empty interface keys",
				keys:      []interface{}{nil, interface{}(nil)},
				values:    []string{"value-for-nil", "value-for-empty-interface"},
				callCount: new(int),
			},
		*/
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			m := New()

			// Test both keys
			for i, key := range tt.keys {
				value := tt.values[i]
				fn := func() interface{} {
					*tt.callCount++
					return value
				}

				// First call should compute
				result := m.Memoize(key, fn)
				if result != value {
					t.Errorf("Memoize() for key %v = %v, want %v", key, result, value)
				}
				if *tt.callCount != i+1 {
					t.Errorf("Function called %d times, want %d", *tt.callCount, i+1)
				}
			}

			// Verify size includes both entries
			if m.Size() != 2 {
				t.Errorf("Size after both inserts = %d, want 2", m.Size())
			}

			// Second call for each key should use cache
			for i, key := range tt.keys {
				initialCount := *tt.callCount
				result := m.Memoize(key, func() interface{} {
					*tt.callCount++
					return "should-not-be-called"
				})

				if result != tt.values[i] {
					t.Errorf("Memoize() second call for key %v = %v, want %v", key, result, tt.values[i])
				}
				if *tt.callCount != initialCount {
					t.Errorf("Cache miss for key %v", key)
				}
			}

			// Test invalidate for each key
			for i, key := range tt.keys {
				m.Invalidate(key)
				if m.Size() != 1-i {
					t.Errorf("Size after invalidate %d = %d, want %d", i+1, m.Size(), 1-i)
				}
			}
		})
	}
}

func TestMultipleKeyTypes(t *testing.T) {
	m := New()
	callCount := 0

	// Insert different key types simultaneously (two of each type)
	keys := []interface{}{
		42, 43, // ints
		"string-key1", "string-key2", // strings
		3.14, 2.718, // floats
		true, false, // bools
	}

	for i, key := range keys {
		value := i
		m.Memoize(key, func() interface{} {
			callCount++
			return value
		})
	}

	// Verify size includes all entries
	if m.Size() != len(keys) {
		t.Errorf("Size = %d, want %d", m.Size(), len(keys))
	}

	// Verify all values are cached correctly
	for i, key := range keys {
		initialCount := callCount
		result := m.Memoize(key, func() interface{} {
			callCount++
			return -1 // Should never be returned if cache works
		})

		if result != i {
			t.Errorf("Memoize(%v) = %v, want %v", key, result, i)
		}
		if callCount != initialCount {
			t.Errorf("Cache miss for key %v", key)
		}
	}

	// Test invalidation of pairs
	for i := 0; i < len(keys); i += 2 {
		m.Invalidate(keys[i])
		m.Invalidate(keys[i+1])
		expectedSize := len(keys) - (i + 2)
		if m.Size() != expectedSize {
			t.Errorf("Size after invalidating pair %d = %d, want %d", i/2, m.Size(), expectedSize)
		}
	}

	// Clear and verify
	m.Clear()
	if m.Size() != 0 {
		t.Errorf("Size after clear = %d, want 0", m.Size())
	}
}

