// How-to: Write a simple Gno Smart Contract (Realm)

package coin

import "std"

func init() {
	Mint()
}

func Mint() {
    bank := std.GetBanker(std.BankerTypeRealmIssue)
    bank.IssueCoin("g1ej0qca5ptsw9kfr64ey8jvfy9eacga6mpj2z0y", "tony", 100_000_000)
}

func Render(_ string) string {
	bank := std.GetBanker(std.BankerTypeReadonly)
	balance := bank.GetCoins("g1ej0qca5ptsw9kfr64ey8jvfy9eacga6mpj2z0y")

    return balance.String()
}

package coin

import "std"

func init() {
	Mint()
}

func Mint() {
    bank := std.GetBanker(std.BankerTypeRealmIssue)
    bank.IssueCoin("g1ej0qca5ptsw9kfr64ey8jvfy9eacga6mpj2z0y", std.CoinDenom(std.CurrentRealm().PkgPath(), "tony"), 100_000_000)
}

func Render(_ string) string {
	bank := std.GetBanker(std.BankerTypeReadonly)
	balance := bank.GetCoins("g1ej0qca5ptsw9kfr64ey8jvfy9eacga6mpj2z0y")

    return balance.String()
}

