	}
}

// Function to convert an integer to a string (since Gno does not have fmt)
func IntToString(n int) string {
	if n == 0 {
		return "0"
	}
	digits := []byte{}
	for n > 0 {
		digits = append([]byte{byte('0' + (n % 10))}, digits...)
		n /= 10
	}
	return string(digits)
}

// Function to check if an address is eligible
func GetAirdropBalance(address string) string {
	if balance, exists := airdropList[address]; exists {
		return "✅ Your airdrop balance: " + IntToString(balance) + " tokens"
	}
	return "❌ No airdrop found for this address"
}

// Entry point function for users to query their airdrop
func QueryAirdrop(args []string) string {
	if len(args) == 0 {
		return "⚠️ Please provide a Cosmos address"
	}
	return GetAirdropBalance(args[0])
}

package airdroprealm

import "std"

// Global map to store eligible airdrop recipients and amounts
var airdropList map[string]int

// Initialize airdrop data
func init() {
	airdropList = map[string]int{
		"cosmos1xyz...": 1000,
		"cosmos1abc...": 500,
		"cosmos1def...": 750,
	}
}

// Simple integer-to-string conversion for Gno (since fmt is not available)
func IntToString(n int) string {
	if n == 0 {
		return "0"
	}
	digits := []byte{}
	for n > 0 {
		digits = append([]byte{byte('0' + (n % 10))}, digits...)
		n /= 10
	}
	return string(digits)
}

// Function to check if an address is eligible for an airdrop
func GetAirdropBalance(address string) string {
	if balance, exists := airdropList[address]; exists {
		return "✅ Your airdrop balance: " + IntToString(balance) + " tokens"
	}
	return "❌ No airdrop found for this address"
}

// Function to check the actual token balance using std.NewBanker
func GetTokenBalance(denom string, address string) string {
	banker := std.NewBanker(std.BankerTypeReadonly)
	balance := banker.GetBalance(address, denom)
	return "💰 Your " + denom + " balance: " + IntToString(balance)
}

// Render function for deployment in play.gno.land
func Render(path string) string {
	if path == "" {
		return "🔹 Welcome to the Airdrop & Token Balance Checker!\n" +
			"Use /r/airdroprealm/Render:<denom>/<your_address> to check your token balance.\n" +
			"Use /r/airdroprealm/Render:airdrop/<your_address> to check airdrop eligibility."
	}

	parts := std.Split(path, "/")

	if len(parts) == 2 && parts[0] == "airdrop" {
		return GetAirdropBalance(parts[1])
	} else if len(parts) == 2 {
		return GetTokenBalance(parts[0], parts[1])
	}

	return "⚠️ Invalid path format. Use /airdrop/<address> or /<denom>/<address>."
}
