	type test struct {
		n    int
		want int64
	}
	tests := []test{
		{0, 0},
		{1, 2},
		{2, 4},
		{3, 5},
		{4, 7},
		{5, 8},
		{6, 10},
		{7, 12},
		{10, 16},
		{11, 18},
