package mux

import (
	"net/url"
	"strings"
)

// Router handles the routing and rendering logic.
type Router struct {
	routes          []Handler
	NotFoundHandler NotFoundHandler
}

// NewRouter creates a new Router instance.
func NewRouter() *Router {
	return &Router{
		routes:          make([]Handler, 0),
		NotFoundHandler: defaultNotFoundHandler,
	}
}

// Render renders the output for the given path using the registered route handler.
func (r *Router) Render(reqPath string) string {
	clearPath, rawQuery, _ := strings.Cut(reqPath, "?")
	query, _ := url.ParseQuery(rawQuery)
	reqParts := strings.Split(clearPath, "/")

	for _, route := range r.routes {
		patParts := strings.Split(route.Pattern, "/")
		wildcard := false
		for _, part := range patParts {
			if part == "*" {
				wildcard = true
				break
			}
		}
		if !wildcard && len(patParts) != len(reqParts) {
			continue
		}

		match := true
		for i := 0; i < len(patParts); i++ {
			patPart := patParts[i]
			reqPart := reqParts[i]

			if patPart == "*" {
				break
			}
			if strings.HasPrefix(patPart, "{") && strings.HasSuffix(patPart, "}") {
				continue
			}
			if patPart != reqPart {
				match = false
				break
			}
		}
		if match {
			req := &Request{
				Path:        clearPath,
				RawPath:     reqPath,
				HandlerPath: route.Pattern,
				Query:       query,
			}
			res := &ResponseWriter{}
			route.Fn(res, req)
			return res.Output()
		}
	}

	// not found
	req := &Request{Path: reqPath, Query: query}
	res := &ResponseWriter{}
	r.NotFoundHandler(res, req)
	return res.Output()
}

// HandleFunc registers a route and its handler function.
func (r *Router) HandleFunc(pattern string, fn HandlerFunc) {
	route := Handler{Pattern: pattern, Fn: fn}
	r.routes = append(r.routes, route)
}

// HandleErrFunc registers a route and its error handler function.
func (r *Router) HandleErrFunc(pattern string, fn ErrHandlerFunc) {
	// Convert ErrHandlerFunc to regular HandlerFunc
	handler := func(res *ResponseWriter, req *Request) {
		if err := fn(res, req); err != nil {
			res.Write("Error: " + err.Error())
		}
	}

	r.HandleFunc(pattern, handler)
}

// SetNotFoundHandler sets custom message for 404 defaultNotFoundHandler.
func (r *Router) SetNotFoundHandler(handler NotFoundHandler) {
	r.NotFoundHandler = handler
}

package mux

import (
	"testing"

	"gno.land/p/nt/uassert"
)

func TestRouter_Render(t *testing.T) {
	cases := []struct {
		label          string
		path           string
		expectedOutput string
		setupHandler   func(t *testing.T, r *Router)
	}{
		{
			label:          "route with named parameter",
			path:           "hello/Alice",
			expectedOutput: "Hello, Alice!",
			setupHandler: func(t *testing.T, r *Router) {
				r.HandleFunc("hello/{name}", func(rw *ResponseWriter, req *Request) {
					name := req.GetVar("name")
					uassert.Equal(t, "Alice", name)
					rw.Write("Hello, " + name + "!")
				})
			},
		},
		{
			label:          "static route",
			path:           "hi",
			expectedOutput: "Hi, earth!",
			setupHandler: func(t *testing.T, r *Router) {
				r.HandleFunc("hi", func(rw *ResponseWriter, req *Request) {
					uassert.Equal(t, req.Path, "hi")
					rw.Write("Hi, earth!")
				})
			},
		},
		{
			label:          "route with named parameter and query string",
			path:           "hello/foo/bar?foo=bar&baz",
			expectedOutput: "foo bar",
			setupHandler: func(t *testing.T, r *Router) {
				r.HandleFunc("hello/{key}/{val}", func(rw *ResponseWriter, req *Request) {
					key := req.GetVar("key")
					val := req.GetVar("val")
					uassert.Equal(t, "foo", key)
					uassert.Equal(t, "bar", val)
					uassert.Equal(t, "hello/foo/bar?foo=bar&baz", req.RawPath)
					uassert.Equal(t, "hello/foo/bar", req.Path)
					uassert.Equal(t, "bar", req.Query.Get("foo"))
					uassert.Empty(t, req.Query.Get("baz"))
					rw.Write(key + " " + val)
				})
			},
		},
		{
			// TODO: finalize how router should behave with double slash in path.
			label:          "double slash in nested route",
			path:           "a/foo//",
			expectedOutput: "test foo",
			setupHandler: func(t *testing.T, r *Router) {
				r.HandleFunc("a/{key}", func(rw *ResponseWriter, req *Request) {
					// Assert not called
					uassert.False(t, true, "unexpected handler called")
				})

				r.HandleFunc("a/{key}/{val}/", func(rw *ResponseWriter, req *Request) {
					key := req.GetVar("key")
					val := req.GetVar("val")
					uassert.Equal(t, key, "foo")
					uassert.Empty(t, val)
					rw.Write("test " + key)
				})
			},
		},
		{
			label:          "wildcard in route",
			path:           "hello/Alice/Bob",
			expectedOutput: "Matched: Alice/Bob",
			setupHandler: func(t *testing.T, r *Router) {
				r.HandleFunc("hello/*", func(rw *ResponseWriter, req *Request) {
					path := req.GetVar("*")
					uassert.Equal(t, "Alice/Bob", path)
					uassert.Equal(t, "hello/Alice/Bob", req.Path)
					rw.Write("Matched: " + path)
				})
			},
		},
		{
			label:          "wildcard in route with query string",
			path:           "hello/Alice/Bob?foo=bar",
			expectedOutput: "Matched: Alice/Bob",
			setupHandler: func(t *testing.T, r *Router) {
				r.HandleFunc("hello/*", func(rw *ResponseWriter, req *Request) {
					path := req.GetVar("*")
					uassert.Equal(t, "Alice/Bob", path)
					uassert.Equal(t, "hello/Alice/Bob?foo=bar", req.RawPath)
					uassert.Equal(t, "hello/Alice/Bob", req.Path)
					uassert.Equal(t, "bar", req.Query.Get("foo"))
					rw.Write("Matched: " + path)
				})
			},
		},
		// TODO: {"hello", "Hello, world!"},
		// TODO: hello/, /hello, hello//Alice, hello/Alice/, hello/Alice/Bob, etc
	}
	for _, tt := range cases {
		t.Run(tt.label, func(t *testing.T) {
			router := NewRouter()
			tt.setupHandler(t, router)
			output := router.Render(tt.path)
			if output != tt.expectedOutput {
				t.Errorf("Expected output %q, but got %q", tt.expectedOutput, output)
			}
		})
	}
}

package ownable

import "errors"

var (
	ErrUnauthorized   = errors.New("ownable: caller is not owner")
	ErrInvalidAddress = errors.New("ownable: new owner address is invalid")
)

package ownable

import (
	"std"
)

const OwnershipTransferEvent = "OwnershipTransfer"

// Ownable is meant to be used as a top-level object to make your contract ownable OR
// being embedded in a Gno object to manage per-object ownership.
// Ownable is safe to export as a top-level object
type Ownable struct {
	owner std.Address
}

func New() *Ownable {
	return &Ownable{
		owner: std.CurrentRealm().Address(),
	}
}

func NewWithOrigin() *Ownable {
	origin := std.OriginCaller()
	previous := std.PreviousRealm()
	if origin != previous.Address() {
		panic("NewWithOrigin() should be called from init() where std.PreviousRealm() is origin")
	}
	return &Ownable{
		owner: origin,
	}
}

func NewWithAddress(addr std.Address) *Ownable {
	return &Ownable{
		owner: addr,
	}
}

// TransferOwnership transfers ownership of the Ownable struct to a new address
func (o *Ownable) TransferOwnership(newOwner std.Address) error {
	if !o.OwnedByCurrent() {
		return ErrUnauthorized
	}

	if !newOwner.IsValid() {
		return ErrInvalidAddress
	}

	prevOwner := o.owner
	o.owner = newOwner
	std.Emit(
		OwnershipTransferEvent,
		"from", prevOwner.String(),
		"to", newOwner.String(),
	)

	return nil
}

// DropOwnershipByCurrent removes the owner, effectively disabling any owner-related actions
// Top-level usage: disables all only-owner actions/functions,
// Embedded usage: behaves like a burn functionality, removing the owner from the struct
func (o *Ownable) DropOwnershipByCurrent() error {
	if !o.OwnedByCurrent() {
		return ErrUnauthorized
	}
	o.dropOwnership(o.owner)
	return nil
}

// DropOwnershipByPrevious removes the owner, effectively disabling any owner-related actions
// Top-level usage: disables all only-owner actions/functions,
// Embedded usage: behaves like a burn functionality, removing the owner from the struct
func (o *Ownable) DropOwnershipByPrevious() error {
	if !o.OwnedByPrevious() {
		return ErrUnauthorized
	}
	o.dropOwnership(o.owner)
	return nil
}

func (o *Ownable) dropOwnership(prevOwner std.Address) {
	o.owner = ""
	std.Emit(
		OwnershipTransferEvent,
		"from", prevOwner.String(),
		"to", "",
	)
}

// Owner returns the owner address from Ownable
func (o *Ownable) Owner() std.Address {
	if o == nil {
		return std.Address("")
	}
	return o.owner
}

// OwnedByCurrent checks if the caller of the function is the Realm's owner
func (o *Ownable) OwnedByCurrent() bool {
	if o == nil {
		return false
	}
	return std.CurrentRealm().Address() == o.owner
}

// AssertOwnedByCurrent panics if the caller is not the owner
func (o *Ownable) AssertOwnedByCurrent() {
	if !o.OwnedByCurrent() {
		panic(ErrUnauthorized)
	}
}

// OwnedByPrevious checks if the caller of the function is the Realm's owner
func (o *Ownable) OwnedByPrevious() bool {
	if o == nil {
		return false
	}
	return std.PreviousRealm().Address() == o.owner
}

// AssertOwnedByPrevious panics if the caller is not the owner
func (o *Ownable) AssertOwnedByPrevious() {
	if !o.OwnedByPrevious() {
		panic(ErrUnauthorized)
	}
}

