		o.AssertOwnedByPrevious()

		// Should panic when previous is not owner
		testing.SetRealm(std.NewUserRealm(bob))
		uassert.PanicsWithMessage(t, ErrUnauthorized.Error(), func() {
			o.AssertOwnedByCurrent()
		})
