package grc20reg

import (
	"std"

	"gno.land/p/demo/tokens/grc20"
	"gno.land/p/nt/avl"
	"gno.land/p/nt/avl/rotree"
	"gno.land/p/nt/fqname"
	"gno.land/p/nt/ufmt"
)

var registry = avl.NewTree() // rlmPath[.slug] -> *Token (slug is optional)
func Register(cur realm, token *grc20.Token, slug string) {
	rlmPath := std.PreviousRealm().PkgPath()
	key := fqname.Construct(rlmPath, slug)
	registry.Set(key, token)
	std.Emit(
		registerEvent,
		"pkgpath", rlmPath,
		"slug", slug,
	)
}

func Get(key string) *grc20.Token {
	token, ok := registry.Get(key)
	if !ok {
		return nil
	}
	return token.(*grc20.Token)
}

func MustGet(key string) *grc20.Token {
	token := Get(key)
	if token == nil {
		panic("unknown token: " + key)
	}
	return token
}

func Render(path string) string {
	switch {
	case path == "": // home
		// TODO: add pagination
		s := ""
		count := 0
		registry.Iterate("", "", func(key string, tokenI any) bool {
			count++
			token := tokenI.(*grc20.Token)
			rlmPath, slug := fqname.Parse(key)
			rlmLink := fqname.RenderLink(rlmPath, slug)
			infoLink := "/r/demo/grc20reg:" + key
			s += ufmt.Sprintf("- **%s** - %s - [info](%s)\n", token.GetName(), rlmLink, infoLink)
			return false
		})
		if count == 0 {
			return "No registered token."
		}
		return s
	default: // specific token
		key := path
		token := MustGet(key)
		rlmPath, slug := fqname.Parse(key)
		rlmLink := fqname.RenderLink(rlmPath, slug)
		s := ufmt.Sprintf("# %s\n", token.GetName())
		s += ufmt.Sprintf("- symbol: **%s**\n", token.GetSymbol())
		s += ufmt.Sprintf("- realm: %s\n", rlmLink)
		s += ufmt.Sprintf("- decimals: %d\n", token.GetDecimals())
		s += ufmt.Sprintf("- total supply: %d\n", token.TotalSupply())
		return s
	}
}

const registerEvent = "register"

func GetRegistry() *rotree.ReadOnlyTree {
	return rotree.Wrap(registry, nil)
}

package foo20

import (
	"std"

	"gno.land/p/demo/tokens/grc20"
	"gno.land/p/moul/md"
	"gno.land/p/nt/avl"
	p "gno.land/p/nt/avl/pager"
	"gno.land/p/nt/avl/rotree"
	"gno.land/p/nt/mux"
	"gno.land/p/nt/ownable"
	"gno.land/p/nt/ufmt"
	"gno.land/r/demo/defi/grc20reg"
)

var (
	instances avl.Tree // symbol -> *instance
	pager     = p.NewPager(rotree.Wrap(&instances, nil), 20, false)
)

type instance struct {
	token  *grc20.Token
	ledger *grc20.PrivateLedger
	admin  *ownable.Ownable
	faucet int64 // per-request amount. disabled if 0.
}

func New(cur realm, name, symbol string, decimals int, initialMint, faucet int64) {
	caller := std.PreviousRealm().Address()
	NewWithAdmin(cur, name, symbol, decimals, initialMint, faucet, caller)
}

func NewWithAdmin(cur realm, name, symbol string, decimals int, initialMint, faucet int64, admin std.Address) {
	exists := instances.Has(symbol)
	if exists {
		panic("token already exists")
	}

	token, ledger := grc20.NewToken(name, symbol, decimals)
	if initialMint > 0 {
		ledger.Mint(admin, initialMint)
	}

	inst := instance{
		token:  token,
		ledger: ledger,
		admin:  ownable.NewWithAddress(admin),
		faucet: faucet,
	}
	instances.Set(symbol, &inst)

	grc20reg.Register(cross, token, symbol)
}

func Bank(symbol string) *grc20.Token {
	inst := mustGetInstance(symbol)
	return inst.token
}

func TotalSupply(symbol string) int64 {
	inst := mustGetInstance(symbol)
	return inst.token.ReadonlyTeller().TotalSupply()
}

func HasAddr(symbol string, owner std.Address) bool {
	inst := mustGetInstance(symbol)
	return inst.token.HasAddr(owner)
}

func BalanceOf(symbol string, owner std.Address) int64 {
	inst := mustGetInstance(symbol)
	return inst.token.ReadonlyTeller().BalanceOf(owner)
}

func Allowance(symbol string, owner, spender std.Address) int64 {
	inst := mustGetInstance(symbol)
	return inst.token.ReadonlyTeller().Allowance(owner, spender)
}

func Transfer(cur realm, symbol string, to std.Address, amount int64) {
	inst := mustGetInstance(symbol)
	caller := std.PreviousRealm().Address()
	teller := inst.ledger.ImpersonateTeller(caller)
	checkErr(teller.Transfer(to, amount))
}

func Approve(cur realm, symbol string, spender std.Address, amount int64) {
	inst := mustGetInstance(symbol)
	caller := std.PreviousRealm().Address()
	teller := inst.ledger.ImpersonateTeller(caller)
	checkErr(teller.Approve(spender, amount))
}

func TransferFrom(cur realm, symbol string, from, to std.Address, amount int64) {
	inst := mustGetInstance(symbol)
	caller := std.PreviousRealm().Address()
	teller := inst.ledger.ImpersonateTeller(caller)
	checkErr(teller.TransferFrom(from, to, amount))
}

// faucet.
func Faucet(cur realm, symbol string) {
	inst := mustGetInstance(symbol)
	if inst.faucet == 0 {
		panic("faucet disabled for this token")
	}
	// FIXME: add limits?
	// FIXME: add payment in gnot?
	caller := std.PreviousRealm().Address()
	checkErr(inst.ledger.Mint(caller, inst.faucet))
}

func Mint(cur realm, symbol string, to std.Address, amount int64) {
	inst := mustGetInstance(symbol)
	inst.admin.AssertOwnedByPrevious()
	checkErr(inst.ledger.Mint(to, amount))
}

func Burn(cur realm, symbol string, from std.Address, amount int64) {
	inst := mustGetInstance(symbol)
	inst.admin.AssertOwnedByPrevious()
	checkErr(inst.ledger.Burn(from, amount))
}

// instance admin functionality
func DropInstanceOwnership(cur realm, symbol string) {
	inst := mustGetInstance(symbol)
	checkErr(inst.admin.DropOwnershipByCurrent())
}

func TransferInstanceOwnership(cur realm, symbol string, newOwner std.Address) {
	inst := mustGetInstance(symbol)
	checkErr(inst.admin.TransferOwnership(newOwner))
}

func ListTokens(pageNumber, pageSize int) []*grc20.Token {
	page := pager.GetPageWithSize(pageNumber, pageSize)

	tokens := make([]*grc20.Token, len(page.Items))
	for i := range page.Items {
		tokens[i] = page.Items[i].Value.(*instance).token
	}

	return tokens
}

func Render(path string) string {
	router := mux.NewRouter()
	router.HandleFunc("", renderHome)
	router.HandleFunc("{symbol}", renderToken)
	router.HandleFunc("{symbol}/balance/{address}", renderBalance)
	return router.Render(path)
}

func renderHome(res *mux.ResponseWriter, req *mux.Request) {
	out := md.H1(ufmt.Sprintf("GRC20 Tokens (%d)", instances.Size()))

	// Get the current page of tokens based on the request path.
	page := pager.MustGetPageByPath(req.RawPath)

	// Render the list of tokens.
	for _, item := range page.Items {
		token := item.Value.(*instance).token
		out += md.BulletItem(
			md.Link(
				ufmt.Sprintf("%s ($%s)", token.GetName(), token.GetSymbol()),
				ufmt.Sprintf("/r/demo/grc20factory:%s", token.GetSymbol()),
			),
		)
	}
	out += "\n"

	// Add the page picker.
	out += md.Paragraph(page.Picker(req.Path))

	res.Write(out)
}

func renderToken(res *mux.ResponseWriter, req *mux.Request) {
	// Get the token symbol from the request.
	symbol := req.GetVar("symbol")
	inst := mustGetInstance(symbol)

	// Render the token details.
	out := inst.token.RenderHome()
	out += md.BulletItem(
		ufmt.Sprintf("%s: %s", md.Bold("Admin"), inst.admin.Owner()),
	)

	res.Write(out)
}

func renderBalance(res *mux.ResponseWriter, req *mux.Request) {
	var (
		symbol = req.GetVar("symbol")
		addr   = req.GetVar("address")
	)

	// Get the balance of the specified address for the token.
	inst := mustGetInstance(symbol)
	balance := inst.token.CallerTeller().BalanceOf(std.Address(addr))

	// Render the balance information.
	out := md.Paragraph(
		ufmt.Sprintf("%s balance: %d", md.Bold(addr), balance),
	)

	res.Write(out)
}

func mustGetInstance(symbol string) *instance {
	t, exists := instances.Get(symbol)
	if !exists {
		panic("token instance does not exist")
	}
	return t.(*instance)
}

func checkErr(err error) {
	if err != nil {
		panic(err.Error())
	}
}

package foo20

import (
	"testing"

	"gno.land/p/nt/testutils"
	"gno.land/p/nt/uassert"
	"gno.land/p/nt/ufmt"
)

func TestReadOnlyPublicMethods(t *testing.T) {
	admin := testutils.TestAddress("admin")
	bob := testutils.TestAddress("bob")
	carl := testutils.TestAddress("carl")

	type test struct {
		name    string
		balance int64
		fn      func() int64
	}

	checkBalances := func(step string, totSup, balAdm, balBob, allowAdmBob, balCarl int64) {
		tests := []test{
			{"TotalSupply", totSup, func() int64 { return TotalSupply("FOO") }},
			{"BalanceOf(admin)", balAdm, func() int64 { return BalanceOf("FOO", admin) }},
			{"BalanceOf(bob)", balBob, func() int64 { return BalanceOf("FOO", bob) }},
			{"Allowance(admin, bob)", allowAdmBob, func() int64 { return Allowance("FOO", admin, bob) }},
			{"BalanceOf(carl)", balCarl, func() int64 { return BalanceOf("FOO", carl) }},
		}
		for _, tc := range tests {
			reason := ufmt.Sprintf("%s.%s - %s", step, tc.name, "balances do not match")
			uassert.Equal(t, tc.balance, tc.fn(), reason)
		}
	}

	// admin creates FOO and BAR.
	testing.SetOriginCaller(admin)
	NewWithAdmin(cross, "Foo", "FOO", 3, 1_111_111_000, 5_555, admin)
	NewWithAdmin(cross, "Bar", "BAR", 3, 2_222_000, 6_666, admin)
	checkBalances("step1", 1_111_111_000, 1_111_111_000, 0, 0, 0)

	// admin mints to bob.
	mustGetInstance("FOO").ledger.Mint(bob, 333_333_000)
	checkBalances("step2", 1_444_444_000, 1_111_111_000, 333_333_000, 0, 0)

	// carl uses the faucet.
	testing.SetOriginCaller(carl)
	Faucet(cross, "FOO")
	checkBalances("step3", 1_444_449_555, 1_111_111_000, 333_333_000, 0, 5_555)

	// admin gives to bob some allowance.
	testing.SetOriginCaller(admin)
	Approve(cross, "FOO", bob, 1_000_000)
	checkBalances("step4", 1_444_449_555, 1_111_111_000, 333_333_000, 1_000_000, 5_555)

	// bob uses a part of the allowance.
	testing.SetOriginCaller(bob)
	TransferFrom(cross, "FOO", admin, carl, 400_000)
	checkBalances("step5", 1_444_449_555, 1_110_711_000, 333_333_000, 600_000, 405_555)

	// bob uses a part of the allowance.
	testing.SetOriginCaller(bob)
	TransferFrom(cross, "FOO", admin, carl, 600_000)
	checkBalances("step6", 1_444_449_555, 1_110_111_000, 333_333_000, 0, 1_005_555)
}

package panictoerr

import (
	"errors"

	"gno.land/p/nt/ufmt"
)

// PanicToError executes a function that might panic and, if it does,
// recovers the panic and converts it to an error.
func PanicToError(mightPanic func()) (err error) {
	// Catch any panic that might occur and convert it to an error.
	defer func() {
		if r := recover(); r != nil {
			err = anyToError(r)
		}
	}()

	// Execute the function that might panic.
	mightPanic()

	return nil
}

// AbortToError executes a function that might abort, and if it does,
// revives the abort and converts it to an error.
func AbortToError(mightAbort func()) error {
	// Catch any abort that might occur and convert it to an error.
	if r := revive(mightAbort); r != nil {
		return anyToError(r)
	}

	return nil
}

// PanicAbortToError executes a function that might either panic or abort,
// and if it does, it recovers the panic or revives the abort and converts
// it to an error.
func PanicAbortToError(mightPanicOrAbort func()) error {
	var panicErr error

	// Catch any panic or abort that might occur and convert it to an error.
	if abortErr := AbortToError(func() {
		panicErr = PanicToError(mightPanicOrAbort)
	}); abortErr != nil {
		return abortErr
	}

	return panicErr
}

// anyToError converts any value to an error.
func anyToError(v any) error {
	switch v := v.(type) {
	case string:
		return errors.New(v)
	case error:
		return v
	default:
		return errors.New(ufmt.Sprint(v))
	}
}

package panictoerr_test

import (
	"errors"
	"std"
	"testing"

	pte "gno.land/p/aeddi/panictoerr"
	"gno.land/p/nt/uassert"
	grc20 "gno.land/r/demo/defi/grc20factory"
)

// Test PanicToError with different types as panic value.
func TestSimplePanicToError(t *testing.T) {
	err := pte.PanicToError(func() {
		panic("string")
	})
	uassert.Equal(t, err.Error(), "string")

	err = pte.PanicToError(func() {
		panic(errors.New("error"))
	})
	uassert.Equal(t, err.Error(), "error")

	err = pte.PanicToError(func() {
		panic(42)
	})
	uassert.Equal(t, err.Error(), "42")
}

func TestRealmPanicToError(t *testing.T) {
	// Set a test realm to be able to call a realm.
	testRealm := std.NewCodeRealm("gno.land/r/aeddi/panictoerr/test")
	testing.SetRealm(testRealm)

	const message = "token instance does not exist"
	var err error

	// Define a panicking function (not crossing).
	panicking := func() {
		grc20.Bank("unknown")
	}

	// panicking function should panic.
	uassert.PanicsWithMessage(t, message, panicking)

	// panicking function should panic when wrapped in AbortToError.
	uassert.PanicsWithMessage(t, message, func() { pte.AbortToError(panicking) })

	// panicking function should not panic when wrapped in PanicToError.
	uassert.NotPanics(
		t,
		func() { err = pte.PanicToError(panicking) },
		"panicking function should not panic when wrapped in PanicToError",
	)
	uassert.Equal(t, err.Error(), message)

	// panicking function should not panic when wrapped in PanicAbortToError.
	uassert.NotPanics(
		t,
		func() { err = pte.PanicAbortToError(panicking) },
		"panicking function should not panic when wrapped in PanicAbortToError",
	)
	uassert.Equal(t, err.Error(), message)

	// Define an aborting function (crossing).
	aborting := func() {
		grc20.Faucet(cross, "unknown")
	}

	// aborting function should abort.
	uassert.AbortsWithMessage(t, message, aborting)

	// aborting function should abort when wrapped in PanicToError.
	uassert.AbortsWithMessage(t, message, func() { pte.PanicToError(aborting) })

	// aborting function should not abort when wrapped in AbortToError.
	uassert.NotAborts(
		t,
		func() { err = pte.AbortToError(aborting) },
		"aborting function should not abort when wrapped in AbortToError",
	)
	uassert.Equal(t, err.Error(), message)

	// aborting function should not abort when wrapped in PanicAbortToError.
	uassert.NotAborts(
		t,
		func() { err = pte.PanicAbortToError(aborting) },
		"aborting function should not abort when wrapped in PanicAbortToError",
	)
	uassert.Equal(t, err.Error(), message)
}

package json

import (
	"errors"
	"io"

	"gno.land/p/nt/ufmt"
)

type buffer struct {
	data   []byte
	length int
	index  int

	last  States
	state States
	class Classes
}

// newBuffer creates a new buffer with the given data
func newBuffer(data []byte) *buffer {
	return &buffer{
		data:   data,
		length: len(data),
		last:   GO,
		state:  GO,
	}
}

// first retrieves the first non-whitespace (or other escaped) character in the buffer.
func (b *buffer) first() (byte, error) {
	for ; b.index < b.length; b.index++ {
		c := b.data[b.index]

		if !(c == whiteSpace || c == carriageReturn || c == newLine || c == tab) {
			return c, nil
		}
	}

	return 0, io.EOF
}

// current returns the byte of the current index.
func (b *buffer) current() (byte, error) {
	if b.index >= b.length {
		return 0, io.EOF
	}

	return b.data[b.index], nil
}

// next moves to the next byte and returns it.
func (b *buffer) next() (byte, error) {
	b.index++
	return b.current()
}

// step just moves to the next position.
func (b *buffer) step() error {
	_, err := b.next()
	return err
}

// move moves the index by the given position.
func (b *buffer) move(pos int) error {
	newIndex := b.index + pos

	if newIndex > b.length {
		return io.EOF
	}

	b.index = newIndex

	return nil
}

// slice returns the slice from the current index to the given position.
func (b *buffer) slice(pos int) ([]byte, error) {
	end := b.index + pos

	if end > b.length {
		return nil, io.EOF
	}

	return b.data[b.index:end], nil
}

// sliceFromIndices returns a slice of the buffer's data starting from 'start' up to (but not including) 'stop'.
func (b *buffer) sliceFromIndices(start, stop int) []byte {
	if start > b.length {
		start = b.length
	}

	if stop > b.length {
		stop = b.length
	}

	return b.data[start:stop]
}

// skip moves the index to skip the given byte.
func (b *buffer) skip(bs byte) error {
	for b.index < b.length {
		if b.data[b.index] == bs && !b.backslash() {
			return nil
		}

		b.index++
	}

	return io.EOF
}

// skipAndReturnIndex moves the buffer index forward by one and returns the new index.
func (b *buffer) skipAndReturnIndex() (int, error) {
	err := b.step()
	if err != nil {
		return 0, err
	}

	return b.index, nil
}

// skipUntil moves the buffer index forward until it encounters a byte contained in the endTokens set.
func (b *buffer) skipUntil(endTokens map[byte]bool) (int, error) {
	for b.index < b.length {
		currentByte, err := b.current()
		if err != nil {
			return b.index, err
		}

		// Check if the current byte is in the set of end tokens.
		if _, exists := endTokens[currentByte]; exists {
			return b.index, nil
		}

		b.index++
	}

	return b.index, io.EOF
}

// significantTokens is a map where the keys are the significant characters in a JSON path.
// The values in the map are all true, which allows us to use the map as a set for quick lookups.
var significantTokens = [256]bool{
	dot:          true, // access properties of an object
	dollarSign:   true, // root object
	atSign:       true, // current object
	bracketOpen:  true, // start of an array index or filter expression
	bracketClose: true, // end of an array index or filter expression
}

// filterTokens stores the filter expression tokens.
var filterTokens = [256]bool{
	aesterisk: true, // wildcard
	andSign:   true,
	orSign:    true,
}

// skipToNextSignificantToken advances the buffer index to the next significant character.
// Significant characters are defined based on the JSON path syntax.
func (b *buffer) skipToNextSignificantToken() {
	for b.index < b.length {
		current := b.data[b.index]

		if significantTokens[current] {
			break
		}

		b.index++
	}
}

// backslash checks to see if the number of backslashes before the current index is odd.
//
// This is used to check if the current character is escaped. However, unlike the "unescape" function,
// "backslash" only serves to check the number of backslashes.
func (b *buffer) backslash() bool {
	if b.index == 0 {
		return false
	}

	count := 0
	for i := b.index - 1; ; i-- {
		if b.data[i] != backSlash {
			break
		}

		count++

		if i == 0 {
			break
		}
	}

	return count%2 != 0
}

// numIndex holds a map of valid numeric characters
var numIndex = [256]bool{
	'0': true,
	'1': true,
	'2': true,
	'3': true,
	'4': true,
	'5': true,
	'6': true,
	'7': true,
	'8': true,
	'9': true,
	'.': true,
	'e': true,
	'E': true,
}

// pathToken checks if the current token is a valid JSON path token.
func (b *buffer) pathToken() error {
	var stack []byte

	inToken := false
	inNumber := false
	first := b.index

	for b.index < b.length {
		c := b.data[b.index]

		switch {
		case c == doubleQuote || c == singleQuote:
			inToken = true
			if err := b.step(); err != nil {
				return errors.New("error stepping through buffer")
			}

			if err := b.skip(c); err != nil {
				return errUnmatchedQuotePath
			}

			if b.index >= b.length {
				return errUnmatchedQuotePath
			}

		case c == bracketOpen || c == parenOpen:
			inToken = true
			stack = append(stack, c)

		case c == bracketClose || c == parenClose:
			inToken = true
			if len(stack) == 0 || (c == bracketClose && stack[len(stack)-1] != bracketOpen) || (c == parenClose && stack[len(stack)-1] != parenOpen) {
				return errUnmatchedParenthesis
			}

			stack = stack[:len(stack)-1]

		case pathStateContainsValidPathToken(c):
			inToken = true

		case c == plus || c == minus:
			if inNumber || (b.index > 0 && numIndex[b.data[b.index-1]]) {
				inToken = true
			} else if !inToken && (b.index+1 < b.length && numIndex[b.data[b.index+1]]) {
				inToken = true
				inNumber = true
			} else if !inToken {
				return errInvalidToken
			}

		default:
			if len(stack) != 0 || inToken {
				inToken = true
			} else {
				goto end
			}
		}

		b.index++
	}

end:
	if len(stack) != 0 {
		return errUnmatchedParenthesis
	}

	if first == b.index {
		return errors.New("no token found")
	}

	if inNumber && !numIndex[b.data[b.index-1]] {
		inNumber = false
	}

	return nil
}

func pathStateContainsValidPathToken(c byte) bool {
	if significantTokens[c] {
		return true
	}

	if filterTokens[c] {
		return true
	}

	if numIndex[c] {
		return true
	}

	if 'A' <= c && c <= 'Z' || 'a' <= c && c <= 'z' {
		return true
	}

	return false
}

func (b *buffer) numeric(token bool) error {
	if token {
		b.last = GO
	}

	for ; b.index < b.length; b.index++ {
		b.class = b.getClasses(doubleQuote)
		if b.class == __ {
			return errInvalidToken
		}

		b.state = StateTransitionTable[b.last][b.class]
		if b.state == __ {
			if token {
				break
			}

			return errInvalidToken
		}

		if b.state < __ {
			return nil
		}

		if b.state < MI || b.state > E3 {
			return nil
		}

		b.last = b.state
	}

	if b.last != ZE && b.last != IN && b.last != FR && b.last != E3 {
		return errInvalidToken
	}

	return nil
}

func (b *buffer) getClasses(c byte) Classes {
	if b.data[b.index] >= 128 {
		return C_ETC
	}

	if c == singleQuote {
		return QuoteAsciiClasses[b.data[b.index]]
	}

	return AsciiClasses[b.data[b.index]]
}

func (b *buffer) getState() States {
	b.last = b.state

	b.class = b.getClasses(doubleQuote)
	if b.class == __ {
		return __
	}

	b.state = StateTransitionTable[b.last][b.class]

	return b.state
}

// string parses a string token from the buffer.
func (b *buffer) string(search byte, token bool) error {
	if token {
		b.last = GO
	}

	for ; b.index < b.length; b.index++ {
		b.class = b.getClasses(search)

		if b.class == __ {
			return errInvalidToken
		}

		b.state = StateTransitionTable[b.last][b.class]
		if b.state == __ {
			return errInvalidToken
		}

		if b.state < __ {
			break
		}

		b.last = b.state
	}

	return nil
}

func (b *buffer) word(bs []byte) error {
	var c byte

	max := len(bs)
	index := 0

	for ; b.index < b.length && index < max; b.index++ {
		c = b.data[b.index]

		if c != bs[index] {
			return errInvalidToken
		}

		index++
		if index >= max {
			break
		}
	}

	if index != max {
		return errInvalidToken
	}

	return nil
}

func numberKind2f64(value any) (result float64, err error) {
	switch typed := value.(type) {
	case float64:
		result = typed
	case float32:
		result = float64(typed)
	case int:
		result = float64(typed)
	case int8:
		result = float64(typed)
	case int16:
		result = float64(typed)
	case int32:
		result = float64(typed)
	case int64:
		result = float64(typed)
	case uint:
		result = float64(typed)
	case uint8:
		result = float64(typed)
	case uint16:
		result = float64(typed)
	case uint32:
		result = float64(typed)
	case uint64:
		result = float64(typed)
	default:
		err = ufmt.Errorf("invalid number type: %T", value)
	}

	return
}

