				}
			}

			if err == nil && tt.isErr {
				t.Errorf("Expected an error for path `%s`, but got none", tt.path)
			}

			if buf.index != tt.index {
				t.Errorf("Expected final index %d, got %d (token: `%s`) for path `%s`", tt.index, buf.index, string(buf.data[buf.index]), tt.path)
			}
		})
	}
