		{name: "1e3", input: []byte("1e3"), _type: Number, value: []byte("1e3")},
		{name: "1e+3", input: []byte("1e+3"), _type: Number, value: []byte("1e+3")},
		{name: "1e-3", input: []byte("1e-3"), _type: Number, value: []byte("1e-3")},
